import React, { Component } from 'react';

class ChildRegistration extends Component {
  constructor(props) {
    super(props);
    this.state = {
      formData: {
        childName: '',
        dateOfBirth: '',
        parentName: '',
        contactInfo: '',
        parentEmail: '',
        parentAddress: ''
      },
      loading: false,
      registeredChild: null,
      isAuthorizedHospital: false,
      hospitalInfo: null,
      error: null,
      txHash: null
    };
  }

  componentDidMount() {
    this.checkHospitalAuthorization();
  }

  checkHospitalAuthorization = async () => {
    try {
      const { contract, connectedWallet } = this.props;
      
      if (!contract || !connectedWallet) return;

      console.log('Checking hospital authorization for:', connectedWallet);
      
      const hospitalInfo = await contract.hospitals(connectedWallet);
      const isAuthorized = await contract.authorizedHospitals(connectedWallet);
      
      console.log('Hospital info:', hospitalInfo);
      console.log('Is authorized:', isAuthorized);

      if (hospitalInfo[0] && hospitalInfo[0] !== '' && isAuthorized) {
        this.setState({
          isAuthorizedHospital: true,
          hospitalInfo: {
            name: hospitalInfo[0],
            licenseNumber: hospitalInfo[1],
            contactInfo: hospitalInfo[2]
          }
        });
        console.log('Hospital authorized for child registration');
      } else {
        this.setState({
          isAuthorizedHospital: false,
          hospitalInfo: null
        });
        console.log('Hospital not authorized');
      }

    } catch (error) {
      console.error('Error checking hospital authorization:', error);
      this.setState({ 
        error: `Failed to check authorization: ${error.message}`,
        isAuthorizedHospital: false
      });
    }
  };

  handleInputChange = (e) => {
    const { name, value } = e.target;
    this.setState({
      formData: {
        ...this.state.formData,
        [name]: value
      }
    });
  };

  validateParentAddress = (address) => {
    const ethRegex = /^0x[a-fA-F0-9]{40}$/;
    return ethRegex.test(address);
  };

  validateForm = () => {
    const { childName, dateOfBirth, parentName, parentAddress, contactInfo, parentEmail } = this.state.formData;
    
    if (!childName.trim()) {
      this.setState({ error: 'Child name is required' });
      return false;
    }
    if (!dateOfBirth) {
      this.setState({ error: 'Date of birth is required' });
      return false;
    }
    if (!parentName.trim()) {
      this.setState({ error: 'Parent name is required' });
      return false;
    }
    if (!parentAddress.trim()) {
      this.setState({ error: 'Parent wallet address is required' });
      return false;
    }
    if (!this.validateParentAddress(parentAddress)) {
      this.setState({ error: 'Invalid parent wallet address format' });
      return false;
    }
    if (!contactInfo.trim()) {
      this.setState({ error: 'Contact information is required' });
      return false;
    }
    if (!parentEmail.trim()) {
      this.setState({ error: 'Parent email is required' });
      return false;
    }

    const birthDate = new Date(dateOfBirth);
    const today = new Date();
    if (birthDate > today) {
      this.setState({ error: 'Birth date cannot be in the future' });
      return false;
    }

    return true;
  };

  registerChild = async () => {
    try {
      const { contract, sendTransaction, connectedWallet } = this.props;
      
      if (!this.validateForm()) {
        return;
      }

      this.setState({ loading: true, error: null, txHash: null });

      const { childName, dateOfBirth, parentName, contactInfo, parentAddress, parentEmail } = this.state.formData;

      // Convert date to timestamp
      const birthTimestamp = Math.floor(new Date(dateOfBirth).getTime() / 1000);

      console.log('Registering child on blockchain:', {
        name: childName,
        birthDate: dateOfBirth,
        birthTimestamp,
        parentName,
        contactInfo,
        parentAddress,
        parentEmail
      });

      // Create token URI (simple JSON metadata)
      const tokenURI = JSON.stringify({
        name: childName,
        description: `Birth certificate NFT for ${childName}`,
        image: '', // Could be IPFS hash of child's photo
        attributes: [
          { trait_type: 'Birth Date', value: dateOfBirth },
          { trait_type: 'Hospital', value: this.state.hospitalInfo.name },
          { trait_type: 'Registration Date', value: new Date().toISOString() }
        ]
      });

      const receipt = await sendTransaction(
        contract.registerChild,
        [childName, birthTimestamp, parentName, contactInfo, parentAddress, tokenURI]
      );

      console.log('Child registration successful:', receipt.transactionHash);

      // Get token ID from events
      let tokenId = null;
      if (receipt.events && receipt.events.length > 0) {
        const childRegisteredEvent = receipt.events.find(event => event.event === 'ChildRegistered');
        if (childRegisteredEvent) {
          tokenId = childRegisteredEvent.args.tokenId.toString();
        }
      }

      if (!tokenId) {
        tokenId = Date.now().toString(); // Fallback
      }

      const childRecord = {
        tokenId: tokenId,
        ...this.state.formData,
        registeredByHospital: this.state.hospitalInfo.name,
        hospitalAddress: connectedWallet,
        registrationDate: new Date().toISOString(),
        transactionHash: receipt.transactionHash
      };

      this.setState({
        loading: false,
        registeredChild: childRecord,
        txHash: receipt.transactionHash,
        error: null
      });

      alert(`Child registered successfully!\nToken ID: ${tokenId}\nNFT sent to parent's wallet: ${parentAddress}`);

    } catch (error) {
      console.error('Child registration failed:', error);
      
      let errorMessage = 'Registration failed';
      
      if (error.message.includes('user rejected')) {
        errorMessage = 'Transaction cancelled by user';
      } else if (error.message.includes('insufficient funds')) {
        errorMessage = 'Insufficient ETH for gas fees';
      } else if (error.message.includes('unauthorized')) {
        errorMessage = 'Hospital not authorized to register children';
      }

      this.setState({ 
        loading: false,
        error: errorMessage
      });

      alert(`Registration failed: ${errorMessage}`);
    }
  };

  resetForm = () => {
    this.setState({
      formData: {
        childName: '',
        dateOfBirth: '',
        parentName: '',
        contactInfo: '',
        parentEmail: '',
        parentAddress: ''
      },
      registeredChild: null,
      error: null,
      txHash: null
    });
  };

  renderUnauthorized() {
    return (
      <div className="authorization-error">
        <div className="error-container">
          <div className="error-icon">üè•</div>
          <h2>Hospital Authorization Required</h2>
          <p>Only authorized hospitals can register children in the vaccination system.</p>
          
          <div className="registration-prompt">
            <p>Your hospital needs to be registered and authorized first.</p>
            <div className="requirements-list">
              <h4>Requirements:</h4>
              <ol>
                <li>‚úÖ Connect your hospital's wallet</li>
                <li>üìù Register your hospital</li>
                <li>‚è≥ Wait for admin authorization</li>
                <li>üéâ Return here to register children</li>
              </ol>
            </div>
            
            <p><strong>Status:</strong> Hospital not authorized on blockchain</p>
          </div>

          <div className="action-buttons">
            <button 
              onClick={() => this.props.onChangeView && this.props.onChangeView('hospital-register')}
              className="btn-primary"
            >
              üè• Register Hospital
            </button>
            <button onClick={this.props.onBack} className="btn-secondary">
              ‚Üê Back to Dashboard
            </button>
          </div>
        </div>
      </div>
    );
  }

  renderSuccess() {
    const { registeredChild, txHash } = this.state;

    return (
      <div className="registration-success">
        <div className="success-icon">‚úÖ</div>
        <h2>Child Successfully Registered!</h2>
        <p>NFT created on blockchain and sent to parent's wallet.</p>
        
        <div className="child-info-card">
          <h3>{registeredChild.childName}</h3>
          <div className="info-grid">
            <div className="info-item">
              <label>Token ID:</label>
              <span className="token-id">{registeredChild.tokenId}</span>
            </div>
            <div className="info-item">
              <label>Parent:</label>
              <span>{registeredChild.parentName}</span>
            </div>
            <div className="info-item">
              <label>Parent Email:</label>
              <span>{registeredChild.parentEmail}</span>
            </div>
            <div className="info-item">
              <label>Parent Wallet:</label>
              <span className="wallet-address">{registeredChild.parentAddress}</span>
            </div>
            <div className="info-item">
              <label>Birth Date:</label>
              <span>{registeredChild.dateOfBirth}</span>
            </div>
            <div className="info-item">
              <label>Hospital:</label>
              <span>{registeredChild.registeredByHospital}</span>
            </div>
            <div className="info-item">
              <label>Transaction Hash:</label>
              <span className="tx-hash" title={txHash}>
                {txHash ? `${txHash.slice(0, 10)}...${txHash.slice(-8)}` : 'N/A'}
              </span>
            </div>
          </div>
        </div>

        <div className="blockchain-features">
          <h4>üîó Blockchain Features Activated</h4>
          <div className="features-list">
            <div className="feature-item">
              <span className="feature-icon">üé´</span>
              <span>Birth certificate NFT minted and transferred to parent</span>
            </div>
            <div className="feature-item">
              <span className="feature-icon">üîí</span>
              <span>Immutable record stored on blockchain</span>
            </div>
            <div className="feature-item">
              <span className="feature-icon">üîç</span>
              <span>QR codes ready for vaccination verification</span>
            </div>
            <div className="feature-item">
              <span className="feature-icon">üì±</span>
              <span>Parent can view record in their wallet</span>
            </div>
          </div>
        </div>

        <div className="action-buttons">
          <button onClick={this.resetForm} className="btn-primary">
            üë∂ Register Another Child
          </button>
          <button 
            onClick={() => this.props.onChangeView && this.props.onChangeView('vaccination-record')}
            className="btn-secondary"
          >
            üíâ Record Vaccination
          </button>
          <button onClick={this.props.onBack} className="btn-secondary">
            ‚Üê Back to Dashboard
          </button>
        </div>
      </div>
    );
  }

  renderForm() {
    const { formData, loading, error, hospitalInfo } = this.state;

    return (
      <div className="hospital-child-registration">
        <div className="registration-container">
          <div className="hospital-header">
            <h2>üë∂ Blockchain Child Registration</h2>
            <div className="hospital-info">
              <p><strong>Hospital:</strong> {hospitalInfo?.name}</p>
              <p><strong>License:</strong> {hospitalInfo?.licenseNumber}</p>
              <p><strong>Wallet:</strong> {this.props.connectedWallet?.slice(0, 8)}...{this.props.connectedWallet?.slice(-4)}</p>
            </div>
          </div>

          {error && (
            <div className="error-message">
              ‚ùå {error}
              <button onClick={() => this.setState({ error: null })}>‚úï</button>
            </div>
          )}
          
          <div className="form-row">
            <div className="form-group">
              <label>Child's Full Name *</label>
              <input
                type="text"
                name="childName"
                value={formData.childName}
                onChange={this.handleInputChange}
                placeholder="Enter child's full name"
                required
                disabled={loading}
              />
            </div>

            <div className="form-group">
              <label>Date of Birth *</label>
              <input
                type="date"
                name="dateOfBirth"
                value={formData.dateOfBirth}
                onChange={this.handleInputChange}
                max={new Date().toISOString().split('T')[0]}
                required
                disabled={loading}
              />
            </div>
          </div>

          <div className="form-row">
            <div className="form-group">
              <label>Parent/Guardian Name *</label>
              <input
                type="text"
                name="parentName"
                value={formData.parentName}
                onChange={this.handleInputChange}
                placeholder="Enter parent/guardian name"
                required
                disabled={loading}
              />
            </div>

            <div className="form-group">
              <label>Parent Email Address *</label>
              <input
                type="email"
                name="parentEmail"
                value={formData.parentEmail}
                onChange={this.handleInputChange}
                placeholder="parent@email.com"
                required
                disabled={loading}
              />
            </div>
          </div>

          <div className="form-group">
            <label>Parent's Wallet Address *
              <span className="help-text">The NFT will be sent to this address</span>
            </label>
            <input
              type="text"
              name="parentAddress"
              value={formData.parentAddress}
              onChange={this.handleInputChange}
              placeholder="0x... (Parent's blockchain wallet address)"
              className={this.validateParentAddress(formData.parentAddress) || !formData.parentAddress ? '' : 'error'}
              required
              disabled={loading}
            />
            {formData.parentAddress && !this.validateParentAddress(formData.parentAddress) && (
              <span className="input-error">Invalid wallet address format</span>
            )}
            {this.validateParentAddress(formData.parentAddress) && (
              <span className="input-success">‚úÖ Valid wallet address</span>
            )}
          </div>

          <div className="form-group">
            <label>Parent Contact Information *</label>
            <input
              type="tel"
              name="contactInfo"
              value={formData.contactInfo}
              onChange={this.handleInputChange}
              placeholder="Phone number"
              required
              disabled={loading}
            />
          </div>

          <div className="form-actions">
            <button onClick={this.props.onBack} className="btn-secondary" disabled={loading}>
              ‚Üê Back to Dashboard
            </button>
            
            <button 
              onClick={this.registerChild}
              className="btn-primary"
              disabled={loading || !this.validateForm()}
            >
              {loading ? '‚è≥ Creating NFT...' : 'üîê Register on Blockchain'}
            </button>
          </div>

          {loading && (
            <div className="loading-info">
              <div className="loading-spinner">‚è≥</div>
              <p><strong>üì° Processing Child Registration...</strong></p>
              <p>üîó Sending transaction to blockchain...</p>
              <p>üë∂ Creating child NFT record...</p>
              <p>üì§ Sending NFT to parent's wallet...</p>
              <p>‚è≥ Please confirm transaction in MetaMask...</p>
            </div>
          )}
        </div>
      </div>
    );
  }

  render() {
    const { isAuthorizedHospital, registeredChild } = this.state;

    if (!isAuthorizedHospital) {
      return (
        <div className="App">
          <div className="main-content">
            {this.renderUnauthorized()}
          </div>
        </div>
      );
    }

    if (registeredChild) {
      return (
        <div className="App">
          <div className="main-content">
            {this.renderSuccess()}
          </div>
        </div>
      );
    }

    return (
      <div className="App">
        <div className="main-content">
          {this.renderForm()}
        </div>
      </div>
    );
  }
}

export default ChildRegistration;
