import React, { Component } from 'react';
import './App.css';

const CONTRACT_ADDRESS = "0xb5a28A2c3715BA080576A2EaF80eB91e44E4499D";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      account: '',
      loading: false,
      currentView: 'welcome'
    };
  }

  connectWallet = async () => {
    try {
      if (typeof window.ethereum !== 'undefined') {
        this.setState({ loading: true });

        const accounts = await window.ethereum.request({ 
          method: 'eth_requestAccounts' 
        });

        if (accounts.length > 0) {
          this.setState({ 
            account: accounts[0], 
            currentView: 'dashboard',
            loading: false 
          });
        } else {
          this.setState({ loading: false });
        }
      } else {
        alert('Please install MetaMask wallet!');
      }
    } catch (error) {
      console.error('Connection error:', error);
      alert('Error connecting wallet: ' + error.message);
      this.setState({ loading: false });
    }
  }

  disconnectWallet = () => {
    this.setState({ 
      account: '', 
      currentView: 'welcome' 
    });
  }

  changeView = (view) => {
    this.setState({ currentView: view });
  }

  render() {
    const { account, loading, currentView } = this.state;

    // Welcome Screen
    if (currentView === 'welcome') {
      return (
        <div className="App">
          <div className="welcome-screen">
            <div className="welcome-content">
              <h1>🏥 Blockchain Vaccination System</h1>
              <p>Secure, transparent, and tamper-proof vaccination records</p>
              <div className="contract-info">
                <p><strong>Contract Address:</strong></p>
                <code>{CONTRACT_ADDRESS}</code>
              </div>
              <button 
                className="connect-btn"
                onClick={this.connectWallet}
                disabled={loading}
              >
                {loading ? 'Connecting...' : '🦊 Connect MetaMask'}
              </button>
              <div className="features">
                <p>✅ Immutable Records</p>
                <p>✅ Global Verification</p>
                <p>✅ Automated Reminders</p>
                <p>✅ Privacy Protection</p>
              </div>
            </div>
          </div>
        </div>
      );
    }

    // Dashboard
    if (currentView === 'dashboard') {
      return (
        <div className="App">
          <div className="dashboard">
            <div className="dashboard-header">
              <h2>🏥 Vaccination Dashboard</h2>
              <div className="account-info">
                <p>Connected: {account.slice(0, 6)}...{account.slice(-4)}</p>
                <button onClick={this.disconnectWallet} className="disconnect-btn">
                  Disconnect
                </button>
              </div>
            </div>

            <div className="dashboard-actions">
              <div className="action-card" onClick={() => this.changeView('register')}>
                <div className="card-icon">👶</div>
                <h3>Register Child</h3>
                <p>Create new vaccination record</p>
              </div>

              <div className="action-card" onClick={() => this.changeView('record')}>
                <div className="card-icon">💉</div>
                <h3>Record Vaccination</h3>
                <p>Add vaccination entry</p>
              </div>

              <div className="action-card" onClick={() => this.changeView('verify')}>
                <div className="card-icon">🔍</div>
                <h3>Verify Records</h3>
                <p>Check vaccination status</p>
              </div>
            </div>
          </div>
        </div>
      );
    }

    // Registration Form
    if (currentView === 'register') {
      return (
        <RegistrationForm 
          onBack={() => this.changeView('dashboard')} 
          account={account} 
        />
      );
    }

    // Vaccination Record Form
    if (currentView === 'record') {
      return (
        <VaccinationForm 
          onBack={() => this.changeView('dashboard')} 
          account={account} 
        />
      );
    }

    // Verification
    if (currentView === 'verify') {
      return (
        <VerificationPage 
          onBack={() => this.changeView('dashboard')} 
        />
      );
    }

    return null;
  }
}

// Registration Component (Class)
class RegistrationForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      childName: '',
      dateOfBirth: '',
      parentName: '',
      contactInfo: ''
    };
  }

  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value
    });
  }

  handleSubmit = (e) => {
    e.preventDefault();

    const registrationData = {
      ...this.state,
      timestamp: new Date().toISOString(),
      parentWallet: this.props.account,
      birthTimestamp: Math.floor(new Date(this.state.dateOfBirth).getTime() / 1000)
    };

    console.log('Child Registration Data:', registrationData);

    alert(`✅ Child "${this.state.childName}" registered successfully!\n\nParent: ${this.state.parentName}\nBirth Date: ${this.state.dateOfBirth}\nWallet: ${this.props.account}\n\n(Check console for full data)`);

    // Reset form
    this.setState({
      childName: '',
      dateOfBirth: '',
      parentName: '',
      contactInfo: ''
    });
  }

  render() {
    const { childName, dateOfBirth, parentName, contactInfo } = this.state;
    const { account, onBack } = this.props;

    return (
      <div className="App">
        <div className="form-container">
          <h2>👶 Register Child</h2>
          <p className="wallet-info">Parent Wallet: {account}</p>

          <form onSubmit={this.handleSubmit}>
            <div className="form-group">
              <label>Child's Full Name *</label>
              <input
                type="text"
                name="childName"
                value={childName}
                onChange={this.handleChange}
                placeholder="Enter child's full name"
                required
              />
            </div>

            <div className="form-group">
              <label>Date of Birth *</label>
              <input
                type="date"
                name="dateOfBirth"
                value={dateOfBirth}
                onChange={this.handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label>Parent/Guardian Name *</label>
              <input
                type="text"
                name="parentName"
                value={parentName}
                onChange={this.handleChange}
                placeholder="Enter parent/guardian name"
                required
              />
            </div>

            <div className="form-group">
              <label>Contact Information *</label>
              <input
                type="text"
                name="contactInfo"
                value={contactInfo}
                onChange={this.handleChange}
                placeholder="Phone number or email"
                required
              />
            </div>

            <div className="form-actions">
              <button type="button" onClick={onBack} className="btn-secondary">
                ← Back
              </button>
              <button type="submit" className="btn-primary">
                🔐 Register on Blockchain
              </button>
            </div>
          </form>
        </div>
      </div>
    );
  }
}

// Vaccination Form Component (Class)
class VaccinationForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tokenId: '',
      vaccineName: '',
      hospitalName: '',
      batchNumber: '',
      notes: ''
    };
  }

  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value
    });
  }

  handleSubmit = (e) => {
    e.preventDefault();

    const vaccinationData = {
      ...this.state,
      timestamp: new Date().toISOString(),
      administeredBy: this.props.account,
      dateAdministered: Math.floor(Date.now() / 1000)
    };

    console.log('Vaccination Record Data:', vaccinationData);

    alert(`💉 Vaccination recorded successfully!\n\nChild Token ID: ${this.state.tokenId}\nVaccine: ${this.state.vaccineName}\nHospital: ${this.state.hospitalName}\nBatch: ${this.state.batchNumber}\n\n(Check console for full data)`);

    // Reset form
    this.setState({
      tokenId: '',
      vaccineName: '',
      hospitalName: '',
      batchNumber: '',
      notes: ''
    });
  }

  render() {
    const { tokenId, vaccineName, hospitalName, batchNumber, notes } = this.state;
    const { account, onBack } = this.props;

    return (
      <div className="App">
        <div className="form-container">
          <h2>💉 Record Vaccination</h2>
          <p className="wallet-info">Hospital Account: {account}</p>

          <form onSubmit={this.handleSubmit}>
            <div className="form-group">
              <label>Child Token ID *</label>
              <input
                type="text"
                name="tokenId"
                value={tokenId}
                onChange={this.handleChange}
                placeholder="Enter child's blockchain token ID"
                required
              />
            </div>

            <div className="form-group">
              <label>Vaccine Name *</label>
              <select
                name="vaccineName"
                value={vaccineName}
                onChange={this.handleChange}
                required
              >
                <option value="">Select Vaccine</option>
                <option value="BCG">BCG (Tuberculosis)</option>
                <option value="Hepatitis B">Hepatitis B</option>
                <option value="DPT">DPT (Diphtheria, Pertussis, Tetanus)</option>
                <option value="Polio (OPV)">Polio (OPV)</option>
                <option value="Hib">Hib</option>
                <option value="Pneumococcal">Pneumococcal</option>
                <option value="Rotavirus">Rotavirus</option>
                <option value="MMR">MMR (Measles, Mumps, Rubella)</option>
                <option value="Varicella">Varicella (Chickenpox)</option>
                <option value="Hepatitis A">Hepatitis A</option>
              </select>
            </div>

            <div className="form-group">
              <label>Hospital/Clinic Name *</label>
              <input
                type="text"
                name="hospitalName"
                value={hospitalName}
                onChange={this.handleChange}
                placeholder="Enter hospital or clinic name"
                required
              />
            </div>

            <div className="form-group">
              <label>Vaccine Batch Number *</label>
              <input
                type="text"
                name="batchNumber"
                value={batchNumber}
                onChange={this.handleChange}
                placeholder="Enter vaccine batch number"
                required
              />
            </div>

            <div className="form-group">
              <label>Additional Notes</label>
              <textarea
                name="notes"
                value={notes}
                onChange={this.handleChange}
                placeholder="Any additional notes or observations"
                rows="3"
              />
            </div>

            <div className="form-actions">
              <button type="button" onClick={onBack} className="btn-secondary">
                ← Back
              </button>
              <button type="submit" className="btn-primary">
                💉 Record Vaccination
              </button>
            </div>
          </form>
        </div>
      </div>
    );
  }
}

// Verification Component (Class)
class VerificationPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tokenId: '',
      verificationResult: null
    };
  }

  handleChange = (e) => {
    this.setState({
      tokenId: e.target.value
    });
  }

  handleVerify = () => {
    if (!this.state.tokenId.trim()) {
      alert('Please enter a Token ID');
      return;
    }

    // Simulate verification
    const mockResult = {
      childName: 'Sample Child',
      parentName: 'Sample Parent',
      totalVaccinations: 5,
      lastVaccination: 'MMR',
      lastVaccinationDate: new Date().toLocaleDateString(),
      isUpToDate: true
    };

    this.setState({ verificationResult: mockResult });
    console.log('Verification for Token ID:', this.state.tokenId, mockResult);
  }

  render() {
    const { tokenId, verificationResult } = this.state;
    const { onBack } = this.props;

    return (
      <div className="App">
        <div className="form-container">
          <h2>🔍 Verify Vaccination Records</h2>

          <div className="form-group">
            <label>Child Token ID</label>
            <input
              type="text"
              value={tokenId}
              onChange={this.handleChange}
              placeholder="Enter blockchain token ID"
            />
          </div>

          <button onClick={this.handleVerify} className="btn-primary">
            🔍 Verify Records
          </button>

          {verificationResult && (
            <div className="verification-result">
              <h3>✅ Verification Result</h3>
              <p><strong>Child:</strong> {verificationResult.childName}</p>
              <p><strong>Parent:</strong> {verificationResult.parentName}</p>
              <p><strong>Total Vaccinations:</strong> {verificationResult.totalVaccinations}</p>
              <p><strong>Last Vaccination:</strong> {verificationResult.lastVaccination}</p>
              <p><strong>Date:</strong> {verificationResult.lastVaccinationDate}</p>
              <p><strong>Status:</strong> {verificationResult.isUpToDate ? '✅ Up to Date' : '⚠️ Behind Schedule'}</p>
            </div>
          )}

          <div className="verify-info">
            <h3>How Verification Works:</h3>
            <ul>
              <li>Each child gets a unique Token ID when registered</li>
              <li>Vaccination records are stored on blockchain</li>
              <li>Records are tamper-proof and globally accessible</li>
              <li>QR codes provide instant verification</li>
            </ul>
          </div>

          <button onClick={onBack} className="btn-secondary">
            ← Back to Dashboard
          </button>
        </div>
      </div>
    );
  }
}

export default App;
