import React, { Component } from 'react';

class HospitalRegistration extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: '',
      licenseNumber: '',
      contactInfo: '',
      loading: false,
      error: null,
      success: false,
      txHash: null
    };
  }

  handleInputChange = (e) => {
    const { name, value } = e.target;
    this.setState({ [name]: value });
  };

  registerHospital = async () => {
    const { name, licenseNumber, contactInfo } = this.state;
    const { contract, connectedWallet, sendTransaction } = this.props;

    if (!name || !licenseNumber || !contactInfo) {
      return this.setState({ error: 'Please fill in all fields.' });
    }

    if (!contract || !connectedWallet) {
      return this.setState({ error: 'Smart contract or wallet not connected' });
    }

    try {
      this.setState({ loading: true, error: null });

      const receipt = await sendTransaction(
        contract.registerHospital,
        [name, licenseNumber, contactInfo]
      );

      this.setState({
        loading: false,
        success: true,
        txHash: receipt.transactionHash,
        name: '',
        licenseNumber: '',
        contactInfo: ''
      });

      alert(`✅ Hospital Registered! Tx: ${receipt.transactionHash.slice(0, 10)}...`);
      this.props.onChangeView('dashboard');

    } catch (error) {
      console.error('Hospital registration failed:', error);

      let errorMessage = 'Hospital registration failed';
      if (error.message.includes('user rejected')) {
        errorMessage = 'Transaction cancelled by user';
      }

      this.setState({
        loading: false,
        error: errorMessage
      });
    }
  };

  render() {
    const { name, licenseNumber, contactInfo, loading, error } = this.state;

    return (
      <div className="hospital-registration">
        <div className="form-container">
          <h2>🏥 Register Hospital</h2>
          <p>Submit your hospital details to participate in the vaccination program.</p>

          {error && <div className="error-message">❌ {error}</div>}

          <div className="form-group">
            <label>Hospital Name *</label>
            <input type="text" name="name" value={name} onChange={this.handleInputChange} />
          </div>

          <div className="form-group">
            <label>License Number *</label>
            <input type="text" name="licenseNumber" value={licenseNumber} onChange={this.handleInputChange} />
          </div>

          <div className="form-group">
            <label>Contact Info *</label>
            <input type="text" name="contactInfo" value={contactInfo} onChange={this.handleInputChange} />
          </div>

          <div className="form-actions">
            <button onClick={this.registerHospital} disabled={loading}>
              {loading ? '⏳ Registering...' : '✅ Register Hospital'}
            </button>

            <button onClick={this.props.onBack}>← Back</button>
          </div>

          <p>🔐 Admin must authorize your hospital before recording vaccinations.</p>
        </div>
      </div>
    );
  }
}

export default HospitalRegistration;
