// Get vaccines due for age
export const getVaccinesDueForAge = (ageInDays) => {
  const dueVaccines = [];

  Object.values(AGE_GROUPS).forEach(ageGroup => {
    if (ageInDays >= ageGroup.min && ageInDays <= ageGroup.max) {
      dueVaccines.push(...ageGroup.vaccines);
    }
  });

  return dueVaccines;
};

// Calculate age in days from birth date
export const calculateAgeInDays = (birthDate) => {
  const birth = new Date(birthDate);
  const today = new Date();
  const diffTime = today - birth;
  return Math.floor(diffTime / (1000 * 60 * 60 * 24));
};

// Get next vaccination due
export const getNextVaccinationDue = (birthDate, completedVaccines = []) => {
  const ageInDays = calculateAgeInDays(birthDate);
  const allVaccines = Object.keys(VACCINE_SCHEDULE);

  // Find next vaccine that's not completed and is due
  for (const vaccine of allVaccines) {
    const vaccineInfo = VACCINE_SCHEDULE[vaccine];
    if (!completedVaccines.includes(vaccine) && ageInDays >= vaccineInfo.ageInDays) {
      return {
        vaccine,
        ...vaccineInfo,
        daysOverdue: ageInDays - vaccineInfo.ageInDays
      };
    }
  }

  return null;
};

