import React, { Component } from 'react';

const COMMON_VACCINES = [
  'BCG', 'Hepatitis B (Birth Dose)', 'DPT (1st Dose)', 'DPT (2nd Dose)', 'DPT (3rd Dose)',
  'OPV (1st Dose)', 'OPV (2nd Dose)', 'OPV (3rd Dose)', 'MMR (1st Dose)', 'MMR (2nd Dose)',
  'Pentavalent (1st Dose)', 'Pentavalent (2nd Dose)', 'Pentavalent (3rd Dose)',
  'Rotavirus (1st Dose)', 'Rotavirus (2nd Dose)', 'Rotavirus (3rd Dose)',
  'PCV (1st Dose)', 'PCV (2nd Dose)', 'PCV (3rd Dose)',
  'Japanese Encephalitis', 'Typhoid', 'Other'
];

class VaccinationRecording extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tokenId: '',
      childInfo: null,
      vaccineData: {
        vaccineName: '',
        batchNumber: '',
        nextDueDate: ''
      },
      hospitalInfo: null,
      isAuthorized: false,
      error: null,
      loading: false,
      txHash: null
    };
  }

  componentDidMount() {
    this.checkHospitalAuthorization();
  }

  checkHospitalAuthorization = async () => {
    try {
      const { contract, connectedWallet } = this.props;
      if (!contract || !connectedWallet) return;

      const hospitalInfo = await contract.hospitals(connectedWallet);
      const isAuthorized = await contract.authorizedHospitals(connectedWallet);

      if (hospitalInfo[0] && isAuthorized) {
        this.setState({
          isAuthorized: true,
          hospitalInfo: {
            name: hospitalInfo[0],
            licenseNumber: hospitalInfo[1],
            contactInfo: hospitalInfo[2]
          }
        });
      }
    } catch (err) {
      console.error('Auth check failed:', err);
      this.setState({ error: 'Failed to verify hospital authorization.' });
    }
  };

  searchChild = async () => {
    try {
      this.setState({ error: null, childInfo: null });
      const { contract } = this.props;
      const { tokenId } = this.state;
      if (!tokenId.trim()) {
        this.setState({ error: 'Please enter a valid token ID' });
        return;
      }

      const info = await contract.getChildInfo(tokenId);
      const history = await contract.getVaccinationHistory(tokenId);

      if (!info[0]) {
        throw new Error('No child found with this token ID');
      }

      const child = {
        tokenId,
        name: info[0],
        dateOfBirth: new Date(info[1].toNumber() * 1000),
        parentName: info[2],
        contactInfo: info[3],
        parentAddress: info[4],
        hospitalAddress: info[5],
        hospitalName: info[6],
        vaccinationHistory: history.map(v => ({
          vaccineName: v[0],
          dateAdministered: new Date(v[1].toNumber() * 1000),
          hospitalName: v[2],
          batchNumber: v[4],
          qrCodeHash: v[8]
        }))
      };

      this.setState({ childInfo: child });
    } catch (err) {
      console.error('Search failed:', err);
      this.setState({ error: err.message || 'Search failed' });
    }
  };

  handleInputChange = (e) => {
    const { name, value } = e.target;
    this.setState({
      vaccineData: {
        ...this.state.vaccineData,
        [name]: value
      }
    });
  };

  calculateNextDueDate = () => {
    const next = new Date();
    next.setDate(next.getDate() + 30);
    return Math.floor(next.getTime() / 1000);
  };

  recordVaccination = async () => {
    try {
      const { contract, sendTransaction } = this.props;
      const { tokenId, vaccineData, childInfo, hospitalInfo } = this.state;

      if (!vaccineData.vaccineName || !vaccineData.batchNumber) {
        this.setState({ error: 'Vaccine name and batch number required.' });
        return;
      }

      const nextDue = vaccineData.nextDueDate
        ? Math.floor(new Date(vaccineData.nextDueDate).getTime() / 1000)
        : this.calculateNextDueDate();

      const receipt = await sendTransaction(
        contract.recordVaccination,
        [tokenId, vaccineData.vaccineName, vaccineData.batchNumber, nextDue, '']
      );

      alert(`✅ Vaccination recorded!\nTx: ${receipt.transactionHash.slice(0, 10)}...`);

      this.setState({
        txHash: receipt.transactionHash,
        vaccineData: {
          vaccineName: '',
          batchNumber: '',
          nextDueDate: ''
        }
      });

      setTimeout(() => this.searchChild(), 1000);
    } catch (err) {
      console.error('Record failed:', err);
      this.setState({ error: 'Failed to record vaccination' });
    }
  };

  formatDate = (date) => date.toLocaleDateString();

  getChildAge = (dob) => {
    const days = Math.floor((Date.now() - dob.getTime()) / (1000 * 60 * 60 * 24));
    return days < 30
      ? `${days} days`
      : days < 365
      ? `${Math.floor(days / 30)} months`
      : `${Math.floor(days / 365)} years`;
  };

  renderUnauthorized() {
    return (
      <div className="unauthorized">
        <h2>⛔ Unauthorized</h2>
        <p>This hospital is not authorized to record vaccinations.</p>
        <button onClick={() => this.props.onChangeView('hospital-register')}>🏥 Register Hospital</button>
        <button onClick={this.props.onBack}>← Back to Dashboard</button>
      </div>
    );
  }

  render() {
    const {
      isAuthorized, hospitalInfo, tokenId, childInfo, vaccineData, error, loading
    } = this.state;

    if (!isAuthorized) return this.renderUnauthorized();

    return (
      <div className="vaccination-recording">
        <h2>💉 Record Vaccination</h2>

        {error && <div className="error-box">❌ {error}</div>}

        <div className="hospital-info">
          <p><strong>Hospital:</strong> {hospitalInfo.name}</p>
          <p><strong>License:</strong> {hospitalInfo.licenseNumber}</p>
        </div>

        <div className="search-section">
          <input
            type="text"
            placeholder="Enter Token ID"
            value={tokenId}
            onChange={(e) => this.setState({ tokenId: e.target.value })}
          />
          <button onClick={this.searchChild}>🔍 Search</button>
        </div>

        {childInfo && (
          <div className="child-details">
            <h3>👶 {childInfo.name}</h3>
            <p><strong>DOB:</strong> {this.formatDate(childInfo.dateOfBirth)}</p>
            <p><strong>Age:</strong> {this.getChildAge(childInfo.dateOfBirth)}</p>
            <p><strong>Parent:</strong> {childInfo.parentName}</p>
            <p><strong>Parent Wallet:</strong> {childInfo.parentAddress}</p>
            <p><strong>Registered By:</strong> {childInfo.hospitalName}</p>

            <h4>📋 Vaccination History</h4>
            <ul>
              {childInfo.vaccinationHistory.length === 0 && <li>No records found</li>}
              {childInfo.vaccinationHistory.map((v, i) => (
                <li key={i}>
                  ✅ {v.vaccineName} — {this.formatDate(v.dateAdministered)} (Batch: {v.batchNumber})
                </li>
              ))}
            </ul>

            <div className="form-row">
              <label>Vaccine Name *</label>
              <select name="vaccineName" value={vaccineData.vaccineName} onChange={this.handleInputChange}>
                <option value="">Select Vaccine</option>
                {COMMON_VACCINES.map((vax, idx) => (
                  <option key={idx} value={vax}>{vax}</option>
                ))}
              </select>
            </div>

            <div className="form-row">
              <label>Batch Number *</label>
              <input
                name="batchNumber"
                value={vaccineData.batchNumber}
                onChange={this.handleInputChange}
              />
            </div>

            <div className="form-row">
              <label>Next Due Date (optional)</label>
              <input
                type="date"
                name="nextDueDate"
                value={vaccineData.nextDueDate}
                onChange={this.handleInputChange}
                min={new Date().toISOString().split('T')[0]}
              />
            </div>

            <button
              onClick={this.recordVaccination}
              disabled={loading}
              className="record-btn"
            >
              {loading ? '⏳ Recording...' : '🔐 Record Vaccination'}
            </button>
          </div>
        )}

        <button onClick={this.props.onBack} className="btn-secondary">
          ← Back to Dashboard
        </button>
      </div>
    );
  }
}

export default VaccinationRecording;
