import React, { Component } from 'react';
import './App.css';
import HospitalRegistration from './components/HospitalRegistration';
import ChildRegistration from './components/ChildRegistration';
import VaccinationRecording from './components/VaccinationRecording';
import ParentDashboard from './components/ParentDashboard';
import QRVerification from './components/QRVerification';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentView: 'welcome',
      userRole: null, // 'hospital', 'parent', 'admin'
      connectedWallet: null,
      systemStats: {
        totalHospitals: 0,
        totalChildren: 0,
        totalVaccinations: 0,
        pendingAuthorizations: 0
      }
    };
  }

  componentDidMount() {
    this.loadSystemStats();
    this.detectWalletConnection();
  }

  // Load system statistics
  loadSystemStats = () => {
    const children = JSON.parse(localStorage.getItem('registeredChildren') || '[]');
    const isHospitalRegistered = localStorage.getItem('hospitalRegistered') === 'true';

    // Count total vaccinations across all children
    let totalVaccinations = 0;
    children.forEach(child => {
      const vaccinations = JSON.parse(localStorage.getItem(`vaccinations_${child.tokenId}`) || '[]');
      totalVaccinations += vaccinations.length;
    });

    this.setState({
      systemStats: {
        totalHospitals: isHospitalRegistered ? 1 : 0,
        totalChildren: children.length,
        totalVaccinations: totalVaccinations,
        pendingAuthorizations: isHospitalRegistered && localStorage.getItem('hospitalAuthorized') !== 'true' ? 1 : 0
      }
    });
  };

  // Detect wallet connection
  detectWalletConnection = async () => {
    if (typeof window.ethereum !== 'undefined') {
      try {
        const accounts = await window.ethereum.request({ method: 'eth_accounts' });
        if (accounts.length > 0) {
          this.setState({ connectedWallet: accounts[0] });
        }
      } catch (error) {
        console.error('Error detecting wallet:', error);
      }
    }
  };

  // Handle wallet connection
  handleConnectWallet = async () => {
    if (typeof window.ethereum !== 'undefined') {
      try {
        const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
        this.setState({ 
          connectedWallet: accounts[0],
          currentView: 'dashboard' 
        });
      } catch (error) {
        console.error('Error connecting wallet:', error);
        alert('Error connecting wallet. Please try again.');
      }
    } else {
      alert('Please install MetaMask wallet!');
    }
  };

  // Handle disconnect
  handleDisconnect = () => {
    this.setState({ 
      currentView: 'welcome',
      connectedWallet: null,
      userRole: null
    });
  };

  // Change view
  changeView = (view, role = null) => {
    this.setState({ 
      currentView: view,
      userRole: role
    });
    // Refresh stats when returning to dashboard
    if (view === 'dashboard') {
      this.loadSystemStats();
    }
  };

  // Back to dashboard
  backToDashboard = () => {
    this.setState({ currentView: 'dashboard' });
    this.loadSystemStats();
  };

  // Render welcome screen
  renderWelcomeScreen() {
    const { connectedWallet } = this.state;

    return (
      <div className="App">
        <div className="welcome-screen">
          <div className="welcome-content">
            <h1>üè• Blockchain Vaccination Registry</h1>
            <p>Secure, transparent, and tamper-proof vaccination records managed by authorized healthcare providers</p>

            <div className="features">
              <div className="feature-item">
                <span className="feature-icon">üè•</span>
                <span>Hospital Registration</span>
              </div>
              <div className="feature-item">
                <span className="feature-icon">üë∂</span>
                <span>Child Birth Records</span>
              </div>
              <div className="feature-item">
                <span className="feature-icon">üíâ</span>
                <span>Vaccination Tracking</span>
              </div>
              <div className="feature-item">
                <span className="feature-icon">üë®‚Äçüë©‚Äçüëß‚Äçüë¶</span>
                <span>Parent Dashboard</span>
              </div>
              <div className="feature-item">
                <span className="feature-icon">üì±</span>
                <span>QR Verification</span>
              </div>
              <div className="feature-item">
                <span className="feature-icon">üîí</span>
                <span>Blockchain Security</span>
              </div>
            </div>

            <div className="contract-info">
              <h4>Smart Contract Details:</h4>
              <p><strong>Network:</strong> Ethereum Testnet (Demo Mode)</p>
              <div className="contract-address">
                0xb5a28A2c3715BA080576A2EaF80eB91e44E4499D
              </div>
              {connectedWallet && (
                <div className="connected-wallet-info">
                  <p><strong>Connected Wallet:</strong></p>
                  <div className="wallet-address">{connectedWallet}</div>
                </div>
              )}
            </div>

            <button 
              className="connect-btn"
              onClick={this.handleConnectWallet}
              disabled={!!connectedWallet}
            >
              {connectedWallet ? '‚úÖ Wallet Connected' : 'ü¶ä Connect MetaMask Wallet'}
            </button>

            <div className="workflow-info">
              <h4>How It Works:</h4>
              <ol>
                <li><strong>Hospitals Register:</strong> Healthcare providers register and get authorized</li>
                <li><strong>Child Birth Registration:</strong> Hospital creates NFT record, sends to parents</li>
                <li><strong>Vaccination Records:</strong> Authorized hospitals record vaccinations with QR codes</li>
                <li><strong>Parent Access:</strong> Parents view and manage their children's records</li>
                <li><strong>QR Verification:</strong> Instant verification of vaccination status</li>
              </ol>
            </div>
          </div>
        </div>
      </div>
    );
  }

  // Render dashboard
  renderDashboard() {
    const { connectedWallet, systemStats } = this.state;

    return (
      <div className="App">
        <div className="main-content">
          <div className="dashboard">
            <div className="dashboard-header">
              <h1>üè• Vaccination Registry Dashboard</h1>
              <div className="user-info">
                <div className="account-info">
                  <p><strong>Status:</strong> Connected</p>
                  <p><strong>Wallet:</strong> {connectedWallet ? `${connectedWallet.slice(0, 6)}...${connectedWallet.slice(-4)}` : 'Not Connected'}</p>
                </div>
                <button onClick={this.handleDisconnect} className="disconnect-btn">
                  Disconnect
                </button>
              </div>
            </div>

            {/* System Statistics */}
            <div className="dashboard-stats">
              <div className="stat-card">
                <h3>Registered Hospitals</h3>
                <div className="stat-number">{systemStats.totalHospitals}</div>
              </div>
              <div className="stat-card">
                <h3>Registered Children</h3>
                <div className="stat-number">{systemStats.totalChildren}</div>
              </div>
              <div className="stat-card">
                <h3>Total Vaccinations</h3>
                <div className="stat-number">{systemStats.totalVaccinations}</div>
              </div>
              <div className="stat-card">
                <h3>Pending Authorizations</h3>
                <div className="stat-number">{systemStats.pendingAuthorizations}</div>
              </div>
            </div>

            {/* Role Selection */}
            <div className="role-selection">
              <h3>Select Your Role:</h3>
              <div className="dashboard-actions">
                <div className="action-card hospital-card" onClick={() => this.changeView('hospital-register', 'hospital')}>
                  <div className="card-icon">üè•</div>
                  <h3>Hospital</h3>
                  <p>Register hospital & record vaccinations</p>
                  <div className="card-features">
                    <span>‚Ä¢ Register & get authorized</span>
                    <span>‚Ä¢ Register newborn children</span>
                    <span>‚Ä¢ Record vaccinations</span>
                    <span>‚Ä¢ Generate QR codes</span>
                  </div>
                </div>

                <div className="action-card parent-card" onClick={() => this.changeView('parent-dashboard', 'parent')}>
                  <div className="card-icon">üë®‚Äçüë©‚Äçüëß‚Äçüë¶</div>
                  <h3>Parent</h3>
                  <p>View & manage children's records</p>
                  <div className="card-features">
                    <span>‚Ä¢ View vaccination history</span>
                    <span>‚Ä¢ Check upcoming schedules</span>
                    <span>‚Ä¢ Receive reminders</span>
                    <span>‚Ä¢ Download certificates</span>
                  </div>
                </div>

                <div className="action-card verifier-card" onClick={() => this.changeView('qr-verification', 'verifier')}>
                  <div className="card-icon">üîç</div>
                  <h3>Verifier</h3>
                  <p>Verify vaccination records</p>
                  <div className="card-features">
                    <span>‚Ä¢ Scan QR codes</span>
                    <span>‚Ä¢ Verify authenticity</span>
                    <span>‚Ä¢ Check vaccination status</span>
                    <span>‚Ä¢ Access secure records</span>
                  </div>
                </div>
              </div>
            </div>

            {/* Quick Actions for Hospitals */}
            <div className="quick-actions">
              <h3>Quick Actions:</h3>
              <div className="action-buttons">
                <button 
                  className="quick-btn hospital-btn"
                  onClick={() => this.changeView('child-register')}
                >
                  üë∂ Register Child
                </button>
                <button 
                  className="quick-btn vaccination-btn"
                  onClick={() => this.changeView('vaccination-record')}
                >
                  üíâ Record Vaccination
                </button>
                <button 
                  className="quick-btn verification-btn"
                  onClick={() => this.changeView('qr-verification')}
                >
                  üîç Verify QR Code
                </button>
              </div>
            </div>

            {/* Important Information */}
            <div className="role-info">
              <div className="info-card demo-info">
                <h4>üß™ Demo Mode Active</h4>
                <p>This is a demonstration of the blockchain vaccination system. All data is stored locally for testing purposes.</p>
              </div>
            </div>

            {/* System Status */}
            <div className="connection-status">
              <h4>üîó System Status</h4>
              <div className="status-grid">
                <div className="status-item">
                  <label>Frontend:</label>
                  <span className="status-connected">‚úÖ Working</span>
                </div>
                <div className="status-item">
                  <label>Components:</label>
                  <span className="status-connected">‚úÖ All Loaded</span>
                </div>
                <div className="status-item">
                  <label>Wallet:</label>
                  <span className={connectedWallet ? 'status-connected' : 'status-disconnected'}>
                    {connectedWallet ? '‚úÖ Connected' : '‚ùå Not Connected'}
                  </span>
                </div>
                <div className="status-item">
                  <label>MetaMask:</label>
                  <span className={typeof window.ethereum !== 'undefined' ? 'status-connected' : 'status-disconnected'}>
                    {typeof window.ethereum !== 'undefined' ? '‚úÖ Detected' : '‚ùå Not Found'}
                  </span>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  // Render about page
  renderAboutPage() {
    return (
      <div className="App">
        <div className="main-content">
          <div className="about-section">
            <h2>About the Vaccination Registry System</h2>

            <div className="about-content">
              <section>
                <h3>üè• Complete Workflow</h3>
                <ol>
                  <li><strong>Hospital Registration:</strong> Healthcare providers register with license information</li>
                  <li><strong>Admin Authorization:</strong> System administrators verify and authorize hospitals</li>
                  <li><strong>Child Birth Registration:</strong> Authorized hospitals register newborns and create NFT records</li>
                  <li><strong>Automatic Scheduling:</strong> Personalized vaccination schedules generated based on birth date</li>
                  <li><strong>NFT Distribution:</strong> Vaccination NFTs automatically sent to parent wallets</li>
                  <li><strong>Vaccination Recording:</strong> Hospitals record vaccinations with automatic QR code generation</li>
                  <li><strong>Parent Dashboard:</strong> Parents view and manage their children's records</li>
                  <li><strong>QR Verification:</strong> Instant verification of vaccination status</li>
                </ol>
              </section>

              <section>
                <h3>üîí Security Features</h3>
                <ul>
                  <li><strong>Blockchain Immutability:</strong> Records cannot be altered once confirmed</li>
                  <li><strong>Role-Based Access:</strong> Only authorized hospitals can create and update records</li>
                  <li><strong>NFT Ownership:</strong> Parents own their children's vaccination NFTs</li>
                  <li><strong>QR Verification:</strong> Instant verification without revealing personal data</li>
                  <li><strong>Automated Reminders:</strong> Smart scheduling system with notifications</li>
                  <li><strong>Wallet Integration:</strong> Secure MetaMask wallet connections</li>
                </ul>
              </section>

              <section>
                <h3>üë• User Roles</h3>
                <div className="roles-grid">
                  <div className="role-card">
                    <h4>üè• Hospital</h4>
                    <p>Register children, record vaccinations, generate QR codes</p>
                  </div>
                  <div className="role-card">
                    <h4>üë®‚Äçüë©‚Äçüëß‚Äçüë¶ Parent</h4>
                    <p>View vaccination records, receive reminders, track schedules</p>
                  </div>
                  <div className="role-card">
                    <h4>üîç Verifier</h4>
                    <p>Verify vaccination status through QR code scanning</p>
                  </div>
                </div>
              </section>

              <section>
                <h3>üöÄ System Components</h3>
                <div className="development-status">
                  <div className="status-item">
                    <span className="status-indicator status-up-to-date"></span>
                    <span>Hospital Registration: ‚úÖ Complete</span>
                  </div>
                  <div className="status-item">
                    <span className="status-indicator status-up-to-date"></span>
                    <span>Child Registration: ‚úÖ Complete</span>
                  </div>
                  <div className="status-item">
                    <span className="status-indicator status-up-to-date"></span>
                    <span>Vaccination Recording: ‚úÖ Complete</span>
                  </div>
                  <div className="status-item">
                    <span className="status-indicator status-up-to-date"></span>
                    <span>Parent Dashboard: ‚úÖ Complete</span>
                  </div>
                  <div className="status-item">
                    <span className="status-indicator status-up-to-date"></span>
                    <span>QR Verification: ‚úÖ Complete</span>
                  </div>
                </div>
              </section>

              <section>
                <h3>üì± Technology Stack</h3>
                <ul>
                  <li><strong>Blockchain:</strong> Ethereum with ERC-721 NFT standard</li>
                  <li><strong>Smart Contracts:</strong> Solidity with OpenZeppelin libraries</li>
                  <li><strong>Frontend:</strong> React.js with Web3 integration</li>
                  <li><strong>Wallet:</strong> MetaMask for blockchain interaction</li>
                  <li><strong>Storage:</strong> Local storage (Demo) / IPFS (Production)</li>
                  <li><strong>QR Codes:</strong> Cryptographic verification system</li>
                </ul>
              </section>
            </div>

            <button 
              onClick={() => this.changeView('dashboard')}
              className="btn-primary"
            >
              ‚Üê Back to Dashboard
            </button>
          </div>
        </div>
      </div>
    );
  }

  render() {
    const { currentView } = this.state;

    // Route to different views
    switch (currentView) {
      case 'welcome':
        return this.renderWelcomeScreen();

      case 'dashboard':
        return this.renderDashboard();

      case 'about':
        return this.renderAboutPage();

      case 'hospital-register':
        return <HospitalRegistration onBack={this.backToDashboard} />;

      case 'child-register':
        return <ChildRegistration onBack={this.backToDashboard} onChangeView={this.changeView} />;

      case 'vaccination-record':
        return <VaccinationRecording onBack={this.backToDashboard} onChangeView={this.changeView} />;

      case 'parent-dashboard':
        return <ParentDashboard onBack={this.backToDashboard} onChangeView={this.changeView} />;

      case 'qr-verification':
        return <QRVerification onBack={this.backToDashboard} onChangeView={this.changeView} />;

      default:
        return this.renderWelcomeScreen();
    }
  }
}

export default App;
