import React, { Component } from 'react';
import config, { 
  ERROR_MESSAGES, 
  SUCCESS_MESSAGES, 
  GAS_LIMITS,
  HOSPITAL_ROLES 
} from '../config';

class HospitalRegistration extends Component {
  constructor(props) {
    super(props);
    this.state = {
      formData: {
        hospitalName: '',
        licenseNumber: '',
        contactInfo: '',
        address: '',
        website: '',
        specializations: '',
        emergencyContact: ''
      },
      loading: false,
      hospitalStatus: null,
      isRegistered: false,
      isAuthorized: false,
      blockchainData: null,
      txHash: null,
      error: null,
      step: 1
    };
  }

  componentDidMount() {
    this.checkHospitalStatus();
  }

  // Check hospital status from blockchain
  checkHospitalStatus = async () => {
    try {
      const { contract, connectedWallet, contractDeployed } = this.props;
      
      if (!contractDeployed || !contract || !connectedWallet) {
        console.log('⚠️ Contract not deployed or wallet not connected');
        return;
      }

      this.setState({ loading: true, error: null });

      console.log('🔍 Checking hospital status for wallet:', connectedWallet);
      
      // Get hospital info from smart contract
      const hospitalInfo = await contract.hospitals(connectedWallet);
      
      console.log('📊 Hospital info from blockchain:', hospitalInfo);
      
      // Check if hospital is registered (hospitalInfo[4] = isRegistered)
      if (hospitalInfo && hospitalInfo[4]) { // isRegistered = true
        const blockchainData = {
          address: hospitalInfo[0],
          name: hospitalInfo[1],
          licenseNumber: hospitalInfo[2],
          isAuthorized: hospitalInfo[3],
          isRegistered: hospitalInfo[4],
          registrationDate: new Date(hospitalInfo[5].toNumber() * 1000)
        };

        this.setState({
          isRegistered: true,
          isAuthorized: hospitalInfo[3], // isAuthorized
          hospitalStatus: blockchainData,
          blockchainData: blockchainData,
          formData: {
            ...this.state.formData,
            hospitalName: hospitalInfo[1],
            licenseNumber: hospitalInfo[2]
          },
          loading: false
        });

        console.log('✅ Hospital found on blockchain:', blockchainData);
      } else {
        console.log('ℹ️ Hospital not registered on blockchain');
        this.setState({ loading: false });
      }

    } catch (error) {
      console.error('❌ Error checking hospital status:', error);
      this.setState({ 
        error: `Failed to check hospital status: ${error.message}`,
        loading: false
      });
    }
  };

  // Handle input changes
  handleInputChange = (e) => {
    const { name, value } = e.target;
    this.setState({
      formData: {
        ...this.state.formData,
        [name]: value
      }
    });
  };

  // Validate form
  validateForm = () => {
    const { hospitalName, licenseNumber, contactInfo } = this.state.formData;
    if (!hospitalName.trim()) {
      this.setState({ error: 'Hospital name is required' });
      return false;
    }
    if (!licenseNumber.trim()) {
      this.setState({ error: 'License number is required' });
      return false;
    }
    if (!contactInfo.trim()) {
      this.setState({ error: 'Contact information is required' });
      return false;
    }
    return true;
  };

  // Register hospital on blockchain
  registerHospital = async () => {
    try {
      const { contract, sendTransaction, connectedWallet, contractDeployed } = this.props;
      
      if (!contractDeployed) {
        alert(ERROR_MESSAGES.CONTRACT_NOT_DEPLOYED);
        return;
      }

      if (!contract) {
        alert(ERROR_MESSAGES.WALLET_NOT_CONNECTED);
        return;
      }

      if (!this.validateForm()) {
        return;
      }

      this.setState({ loading: true, error: null, txHash: null });

      const { hospitalName, licenseNumber } = this.state.formData;

      console.log('📝 Registering hospital on blockchain:', {
        name: hospitalName,
        license: licenseNumber,
        wallet: connectedWallet
      });

      // Call smart contract registerHospital function
      const receipt = await sendTransaction(
        contract.registerHospital,
        [hospitalName, licenseNumber],
        { gasLimit: GAS_LIMITS.REGISTER_HOSPITAL }
      );

      console.log('✅ Hospital registration transaction successful:', receipt.transactionHash);

      // Update state with successful registration
      this.setState({
        loading: false,
        isRegistered: true,
        isAuthorized: false, // Needs admin approval
        txHash: receipt.transactionHash,
        hospitalStatus: {
          name: hospitalName,
          licenseNumber: licenseNumber,
          walletAddress: connectedWallet,
          registrationDate: new Date(),
          isAuthorized: false,
          isRegistered: true
        },
        error: null
      });

      // Show success message
      alert(SUCCESS_MESSAGES.HOSPITAL_REGISTERED + '\n\nTransaction Hash: ' + receipt.transactionHash.slice(0, 10) + '...');

      // Refresh hospital status from blockchain
      setTimeout(() => {
        this.checkHospitalStatus();
      }, 2000);

    } catch (error) {
      console.error('❌ Hospital registration failed:', error);
      
      let errorMessage = ERROR_MESSAGES.TRANSACTION_FAILED;
      
      if (error.message.includes('user rejected')) {
        errorMessage = 'Transaction cancelled by user';
      } else if (error.message.includes('insufficient funds')) {
        errorMessage = 'Insufficient ETH for gas fees';
      } else if (error.message.includes('already registered')) {
        errorMessage = 'Hospital is already registered';
      }

      this.setState({ 
        loading: false,
        error: errorMessage
      });

      alert(`Registration failed: ${errorMessage}`);
    }
  };

  // Admin authorization simulation (for development/testing)
  simulateAuthorization = async () => {
    try {
      const { contract, sendTransaction, contractDeployed } = this.props;
      
      if (!contractDeployed) {
        alert('Feature requires contract deployment');
        return;
      }

      if (!contract) {
        alert('Contract not available');
        return;
      }

      this.setState({ loading: true, error: null });

      console.log('🔐 Simulating admin authorization...');

      // In a real app, this would be called by an admin wallet
      // For demo purposes, we'll try to authorize (might fail if not admin)
      const receipt = await sendTransaction(
        contract.authorizeHospital,
        [this.props.connectedWallet, true],
        { gasLimit: GAS_LIMITS.AUTHORIZE_HOSPITAL }
      );

      console.log('✅ Authorization transaction successful:', receipt.transactionHash);

      this.setState({
        loading: false,
        isAuthorized: true,
        txHash: receipt.transactionHash
      });

      alert('🎉 Hospital authorized successfully!\n\nTransaction Hash: ' + receipt.transactionHash.slice(0, 10) + '...');

      // Refresh status
      setTimeout(() => {
        this.checkHospitalStatus();
      }, 2000);

    } catch (error) {
      console.error('❌ Authorization failed:', error);
      this.setState({ loading: false });
      alert('Authorization failed: ' + (error.message || 'Unknown error'));
    }
  };

  // Reset registration (for testing)
  resetRegistration = () => {
    if (window.confirm('Are you sure you want to reset? This will clear the current form data.')) {
      this.setState({
        isRegistered: false,
        isAuthorized: false,
        hospitalStatus: null,
        blockchainData: null,
        txHash: null,
        error: null,
        formData: {
          hospitalName: '',
          licenseNumber: '',
          contactInfo: '',
          address: '',
          website: '',
          specializations: '',
          emergencyContact: ''
        }
      });
    }
  };

  // Refresh data from blockchain
  refreshStatus = () => {
    this.checkHospitalStatus();
  };

  // Render hospital status (if already registered)
  renderHospitalStatus() {
    const { hospitalStatus, isAuthorized, blockchainData, txHash, loading } = this.state;
    const { contractDeployed } = this.props;

    return (
      <div className="hospital-status">
        <div className="status-container">
          <div className="hospital-header">
            <h2>🏥 Hospital Status</h2>
            <div className="header-actions">
              <button 
                onClick={this.refreshStatus}
                className="btn-refresh"
                disabled={loading}
                title="Refresh from blockchain"
              >
                {loading ? '🔄' : '🔄 Refresh'}
              </button>
            </div>
            <div className={`status-badge ${isAuthorized ? 'authorized' : 'pending'}`}>
              {isAuthorized ? '✅ Authorized - Can register children' : '⏳ Pending Authorization'}
            </div>
          </div>

          <div className="hospital-info-card">
            <h3>{hospitalStatus?.name || hospitalStatus?.hospitalName}</h3>
            
            <div className="info-grid">
              <div className="info-item">
                <label>License Number:</label>
                <span>{hospitalStatus?.licenseNumber}</span>
              </div>
              <div className="info-item">
                <label>Wallet Address:</label>
                <span className="wallet-address">
                  {this.props.connectedWallet || hospitalStatus?.walletAddress}
                </span>
              </div>
              <div className="info-item">
                <label>Registration Date:</label>
                <span>
                  {hospitalStatus?.registrationDate ? 
                    new Date(hospitalStatus.registrationDate).toLocaleDateString() : 
                    'N/A'
                  }
                </span>
              </div>
              <div className="info-item">
                <label>Authorization Status:</label>
                <span className={isAuthorized ? 'authorized' : 'pending'}>
                  {isAuthorized ? 'Authorized ✅' : 'Pending Admin Approval ⏳'}
                </span>
              </div>
              {contractDeployed && (
                <div className="info-item">
                  <label>Blockchain Status:</label>
                  <span className="blockchain-connected">✅ Connected to Contract</span>
                </div>
              )}
              {txHash && (
                <div className="info-item">
                  <label>Last Transaction:</label>
                  <span className="tx-hash" title={txHash}>
                    {txHash.slice(0, 10)}...{txHash.slice(-8)}
                  </span>
                </div>
              )}
            </div>

            {blockchainData && (
              <div className="blockchain-info">
                <h4>🔗 Blockchain Data</h4>
                <div className="blockchain-details">
                  <div><strong>On-chain Name:</strong> {blockchainData.name}</div>
                  <div><strong>On-chain License:</strong> {blockchainData.licenseNumber}</div>
                  <div><strong>Registration Block:</strong> {blockchainData.registrationDate.toLocaleString()}</div>
                  <div><strong>Is Authorized:</strong> {blockchainData.isAuthorized ? 'Yes ✅' : 'No ⏳'}</div>
                </div>
              </div>
            )}
          </div>

          {!isAuthorized && (
            <div className="pending-message">
              <div className="pending-icon">⏳</div>
              <h3>Waiting for Authorization</h3>
              <p>Your hospital registration is recorded on the blockchain but requires administrative approval before you can register children.</p>
              
              <div className="next-steps">
                <h4>What to expect:</h4>
                <ul>
                  <li>✅ Registration completed on blockchain</li>
                  <li>🔍 Admin will review your hospital credentials</li>
                  <li>⏳ Authorization typically takes 24-48 hours</li>
                  <li>🎉 Once authorized, you can register newborn children</li>
                  <li>📱 NFTs will be automatically sent to parents' wallets</li>
                </ul>
              </div>
              
              {contractDeployed && (
                <div className="demo-controls">
                  <h4>🧪 Demo Controls:</h4>
                  <p><small>For testing purposes only</small></p>
                  <button 
                    onClick={this.simulateAuthorization} 
                    className="btn-primary"
                    disabled={loading}
                  >
                    {loading ? '⏳ Processing...' : '🔐 Simulate Admin Authorization'}
                  </button>
                </div>
              )}
            </div>
          )}

          {isAuthorized && (
            <div className="authorized-message">
              <div className="success-icon">✅</div>
              <h3>Hospital Authorized!</h3>
              <p>Your hospital is now authorized to register children and record vaccinations on the blockchain.</p>
              
              <div className="capabilities">
                <h4>🎯 What you can now do:</h4>
                <ul>
                  <li>✅ Register newborn children (creates NFTs)</li>
                  <li>✅ Record vaccination data on blockchain</li>
                  <li>✅ Generate unique QR codes for verification</li>
                  <li>✅ Access vaccination histories from blockchain</li>
                  <li>✅ Issue vaccination certificates</li>
                </ul>
              </div>

              <div className="action-buttons">
                <button 
                  onClick={() => this.props.onChangeView && this.props.onChangeView('child-register')}
                  className="btn-primary"
                >
                  👶 Register Child
                </button>
                <button 
                  onClick={() => this.props.onChangeView && this.props.onChangeView('vaccination-record')}
                  className="btn-primary"
                >
                  💉 Record Vaccination
                </button>
              </div>
            </div>
          )}

          <div className="demo-controls">
            <h4>🔧 Development Controls:</h4>
            <div className="control-buttons">
              <button onClick={this.resetRegistration} className="btn-secondary">
                🔄 Reset Form
              </button>
              <button onClick={this.refreshStatus} className="btn-secondary" disabled={loading}>
                {loading ? '⏳ Loading...' : '🔄 Refresh Status'}
              </button>
            </div>
          </div>

          <button onClick={this.props.onBack} className="btn-secondary">
            ← Back to Dashboard
          </button>
        </div>
      </div>
    );
  }

  // Render registration form
  renderRegistrationForm() {
    const { formData, loading, error } = this.state;
    const { connectedWallet, contractDeployed } = this.props;

    return (
      <div className="hospital-registration">
        <div className="registration-container">
          <div className="registration-header">
            <h2>🏥 Hospital Registration</h2>
            <p>Register your hospital on the blockchain to start managing vaccination records</p>
            
            {!contractDeployed && (
              <div className="warning-banner">
                ⚠️ <strong>Contract Not Deployed:</strong> Currently in demo mode. Deploy smart contract for full functionality.
              </div>
            )}
          </div>

          {error && (
            <div className="error-message">
              ❌ {error}
              <button onClick={() => this.setState({ error: null })}>✕</button>
            </div>
          )}

          <div className="registration-form">
            <div className="form-section">
              <h3>🏢 Hospital Information</h3>
              
              <div className="form-group">
                <label>Hospital Name *</label>
                <input
                  type="text"
                  name="hospitalName"
                  value={formData.hospitalName}
                  onChange={this.handleInputChange}
                  placeholder="Enter official hospital name"
                  required
                  disabled={loading}
                />
              </div>

              <div className="form-group">
                <label>Medical License Number *</label>
                <input
                  type="text"
                  name="licenseNumber"
                  value={formData.licenseNumber}
                  onChange={this.handleInputChange}
                  placeholder="Enter hospital license number"
                  required
                  disabled={loading}
                />
              </div>

              <div className="form-group">
                <label>Primary Contact Information *</label>
                <input
                  type="text"
                  name="contactInfo"
                  value={formData.contactInfo}
                  onChange={this.handleInputChange}
                  placeholder="Phone number and email"
                  required
                  disabled={loading}
                />
              </div>
            </div>

            <div className="form-section">
              <h3>📍 Additional Details</h3>
              
              <div className="form-group">
                <label>Hospital Address</label>
                <textarea
                  name="address"
                  value={formData.address}
                  onChange={this.handleInputChange}
                  placeholder="Full hospital address"
                  rows="3"
                  disabled={loading}
                />
              </div>

              <div className="form-group">
                <label>Website</label>
                <input
                  type="url"
                  name="website"
                  value={formData.website}
                  onChange={this.handleInputChange}
                  placeholder="https://your-hospital-website.com"
                  disabled={loading}
                />
              </div>

              <div className="form-group">
                <label>Specializations</label>
                <textarea
                  name="specializations"
                  value={formData.specializations}
                  onChange={this.handleInputChange}
                  placeholder="List hospital specializations (e.g., Pediatrics, Maternity, General Medicine)"
                  rows="3"
                  disabled={loading}
                />
              </div>

              <div className="form-group">
                <label>Emergency Contact</label>
                <input
                  type="tel"
                  name="emergencyContact"
                  value={formData.emergencyContact}
                  onChange={this.handleInputChange}
                  placeholder="24/7 emergency contact number"
                  disabled={loading}
                />
              </div>
            </div>

            <div className="wallet-info">
              <h3>🔐 Blockchain Information</h3>
              <div className="wallet-display">
                <label>Hospital Wallet Address:</label>
                <div className="wallet-address">
                  {connectedWallet || 'Not Connected'}
                </div>
                <small>
                  {contractDeployed ? 
                    'This wallet address will be registered on the blockchain and used for all hospital operations' :
                    'Demo mode - wallet integration ready for contract deployment'
                  }
                </small>
              </div>
            </div>

            <div className="terms-section">
              <div className="terms-item">
                <input type="checkbox" id="accuracy" required disabled={loading} />
                <label htmlFor="accuracy">
                  I certify that all provided information is accurate and up-to-date
                </label>
              </div>
              <div className="terms-item">
                <input type="checkbox" id="authority" required disabled={loading} />
                <label htmlFor="authority">
                  I have the authority to register this hospital in the vaccination system
                </label>
              </div>
              <div className="terms-item">
                <input type="checkbox" id="compliance" required disabled={loading} />
                <label htmlFor="compliance">
                  This hospital complies with all relevant medical and data protection regulations
                </label>
              </div>
              <div className="terms-item">
                <input type="checkbox" id="responsibility" required disabled={loading} />
                <label htmlFor="responsibility">
                  I understand the responsibility of managing vaccination records on the blockchain
                </label>
              </div>
            </div>

            <div className="registration-info">
              <div className="info-box">
                <h4>📋 {contractDeployed ? 'Blockchain Registration Process:' : 'Demo Registration Process:'}</h4>
                <ol>
                  <li><strong>Submit Registration:</strong> Complete this form with accurate hospital information</li>
                  {contractDeployed ? (
                    <>
                      <li><strong>Blockchain Transaction:</strong> Registration data will be stored on the blockchain</li>
                      <li><strong>Admin Review:</strong> System administrators will verify your credentials on-chain</li>
                      <li><strong>Authorization:</strong> Once approved, you can register children and record vaccinations</li>
                      <li><strong>NFT Management:</strong> Children's NFTs will be automatically sent to parents' wallets</li>
                    </>
                  ) : (
                    <>
                      <li><strong>Demo Storage:</strong> Data will be stored locally for demonstration</li>
                      <li><strong>Mock Review:</strong> Simulated admin approval process</li>
                      <li><strong>Ready for Blockchain:</strong> Deploy contract to enable full functionality</li>
                    </>
                  )}
                </ol>
              </div>
            </div>

            <div className="form-actions">
              <button onClick={this.props.onBack} className="btn-secondary" disabled={loading}>
                ← Back to Dashboard
              </button>
              
              <button 
                onClick={this.registerHospital}
                className="btn-primary"
                disabled={loading || !this.validateForm() || !connectedWallet}
              >
                {loading ? '⏳ Registering...' : 
                 contractDeployed ? '🏥 Register on Blockchain' : 
                 '🏥 Register Hospital (Demo)'}
              </button>
            </div>

            {loading && (
              <div className="loading-info">
                <div className="loading-spinner">⏳</div>
                <p><strong>📡 Processing Registration...</strong></p>
                {contractDeployed ? (
                  <>
                    <p>🔗 Sending transaction to blockchain...</p>
                    <p>⏳ Please confirm the transaction in MetaMask</p>
                    <p>🔍 Waiting for blockchain confirmation...</p>
                  </>
                ) : (
                  <>
                    <p>💾 Saving registration data...</p>
                    <p>🔍 Validating hospital credentials...</p>
                  </>
                )}
              </div>
            )}
          </div>
        </div>
      </div>
    );
  }

  render() {
    const { isRegistered } = this.state;

    return (
      <div className="App">
        <div className="main-content">
          {isRegistered ? this.renderHospitalStatus() : this.renderRegistrationForm()}
        </div>
      </div>
    );
  }
}

export default HospitalRegistration;
