import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';

const AdminPanel = ({ contract, account, onSuccess, onBack }) => {
  const [pendingHospitals, setPendingHospitals] = useState([]);
  const [authorizedHospitals, setAuthorizedHospitals] = useState([]);
  const [loading, setLoading] = useState(true);
  const [processingHospital, setProcessingHospital] = useState(null);
  const [isOwner, setIsOwner] = useState(false);

  useEffect(() => {
    if (contract && account) {
      checkOwnership();
      loadHospitals();
    }
  }, [contract, account]);

  const checkOwnership = async () => {
    try {
      const owner = await contract.owner();
      const userIsOwner = owner.toLowerCase() === account.toLowerCase();
      setIsOwner(userIsOwner);

      if (!userIsOwner) {
        alert('❌ Access denied: Only the contract owner can access this panel.');
        onBack();
      }
    } catch (error) {
      console.error('Error in checkOwnership:', error);
      alert('Error: Failed to check contract owner.\n' + (error.reason || error.message));
      onBack();
    }
  };

  const loadHospitals = async () => {
    try {
      setLoading(true);

      const filter = contract.filters.HospitalRegistered();
      const events = await contract.queryFilter(filter);

      const hospitalPromises = events.map(async (event) => {
        const hospitalAddress = event.args.hospital;
        const hospitalInfo = await contract.getHospitalInfo(hospitalAddress);
        const isAuthorized = await contract.authorizedHospitals(hospitalAddress);

        return {
          address: hospitalAddress,
          name: hospitalInfo.name,
          licenseNumber: hospitalInfo.licenseNumber,
          contactInfo: hospitalInfo.contactInfo,
          registrationDate: hospitalInfo.registrationDate.toString(),
          isAuthorized
        };
      });

      const hospitals = await Promise.all(hospitalPromises);
      setPendingHospitals(hospitals.filter(h => !h.isAuthorized));
      setAuthorizedHospitals(hospitals.filter(h => h.isAuthorized));
      setLoading(false);

    } catch (error) {
      console.error('Error loading hospitals:', error);
      setLoading(false);
    }
  };

  const authorizeHospital = async (hospitalAddress, authorize = true) => {
    try {
      setProcessingHospital(hospitalAddress);

      const tx = await contract.setHospitalAuthorization(hospitalAddress, authorize);
      await tx.wait();

      alert(`✅ Hospital ${authorize ? 'authorized' : 'deauthorized'} successfully.`);
      setProcessingHospital(null);
      loadHospitals();

    } catch (error) {
      console.error('Error authorizing hospital:', error);
      alert('❌ Authorization failed: ' + (error.reason || error.message));
      setProcessingHospital(null);
    }
  };

  const formatDate = (timestamp) => {
    return new Date(Number(timestamp) * 1000).toLocaleDateString();
  };

  const HospitalCard = ({ hospital, isPending }) => (
    <div className="hospital-card">
      <h3>{hospital.name}</h3>
      <p><strong>License:</strong> {hospital.licenseNumber}</p>
      <p><strong>Contact:</strong> {hospital.contactInfo}</p>
      <p><strong>Wallet:</strong> {hospital.address}</p>
      <p><strong>Date:</strong> {formatDate(hospital.registrationDate)}</p>

      <button
        onClick={() => authorizeHospital(hospital.address, isPending)}
        disabled={processingHospital === hospital.address}
        className={isPending ? 'btn-primary' : 'btn-danger'}
      >
        {processingHospital === hospital.address
          ? '⏳ Processing...'
          : isPending ? '✅ Authorize' : '❌ Revoke'}
      </button>
    </div>
  );

  if (!isOwner) return null;

  return (
    <div className="admin-panel">
      <h1>👑 Admin Panel</h1>
      <p>Welcome, contract owner. You can authorize or revoke hospitals here.</p>

      <button onClick={onBack} className="btn-secondary">← Back to Dashboard</button>

      {loading ? (
        <div>⏳ Loading hospitals...</div>
      ) : (
        <>
          <section>
            <h2>⏳ Pending Hospitals ({pendingHospitals.length})</h2>
            {pendingHospitals.length === 0 ? (
              <p>No pending registrations.</p>
            ) : (
              pendingHospitals.map((h, i) => (
                <HospitalCard key={i} hospital={h} isPending={true} />
              ))
            )}
          </section>

          <section>
            <h2>✅ Authorized Hospitals ({authorizedHospitals.length})</h2>
            {authorizedHospitals.length === 0 ? (
              <p>No hospitals have been authorized yet.</p>
            ) : (
              authorizedHospitals.map((h, i) => (
                <HospitalCard key={i} hospital={h} isPending={false} />
              ))
            )}
          </section>
        </>
      )}
    </div>
  );
};

export default AdminPanel;
