import React, { Component } from 'react';

// Enhanced vaccine schedules with detailed information
const VACCINE_SCHEDULE = {
  'BCG': { 
    minAge: 0, 
    nextDose: null, 
    description: 'Bacillus Calmette-GuÃ©rin',
    ageGroup: 'Birth',
    boosterNeeded: false
  },
  'Hepatitis B (Birth)': { 
    minAge: 0, 
    nextDose: 30, 
    description: 'Hepatitis B Vaccine - Birth Dose',
    ageGroup: 'Birth',
    boosterNeeded: true
  },
  'Hepatitis B (1st)': { 
    minAge: 30, 
    nextDose: 30, 
    description: 'Hepatitis B Vaccine - 1st Dose',
    ageGroup: '1 Month',
    boosterNeeded: true
  },
  'Hepatitis B (2nd)': { 
    minAge: 60, 
    nextDose: null, 
    description: 'Hepatitis B Vaccine - 2nd Dose',
    ageGroup: '2 Months',
    boosterNeeded: false
  },
  'DPT (1st)': { 
    minAge: 45, 
    nextDose: 30, 
    description: 'Diphtheria, Pertussis, Tetanus - 1st Dose',
    ageGroup: '6 Weeks',
    boosterNeeded: true
  },
  'DPT (2nd)': { 
    minAge: 75, 
    nextDose: 30, 
    description: 'Diphtheria, Pertussis, Tetanus - 2nd Dose',
    ageGroup: '10 Weeks',
    boosterNeeded: true
  },
  'DPT (3rd)': { 
    minAge: 105, 
    nextDose: 365, 
    description: 'Diphtheria, Pertussis, Tetanus - 3rd Dose',
    ageGroup: '14 Weeks',
    boosterNeeded: true
  },
  'Polio (OPV-1)': { 
    minAge: 45, 
    nextDose: 30, 
    description: 'Oral Polio Vaccine - 1st Dose',
    ageGroup: '6 Weeks',
    boosterNeeded: true
  },
  'Polio (OPV-2)': { 
    minAge: 75, 
    nextDose: 30, 
    description: 'Oral Polio Vaccine - 2nd Dose',
    ageGroup: '10 Weeks',
    boosterNeeded: true
  },
  'Polio (OPV-3)': { 
    minAge: 105, 
    nextDose: 365, 
    description: 'Oral Polio Vaccine - 3rd Dose',
    ageGroup: '14 Weeks',
    boosterNeeded: true
  },
  'MMR (1st)': { 
    minAge: 270, 
    nextDose: 365, 
    description: 'Measles, Mumps, Rubella - 1st Dose',
    ageGroup: '9 Months',
    boosterNeeded: true
  },
  'MMR (2nd)': { 
    minAge: 450, 
    nextDose: null, 
    description: 'Measles, Mumps, Rubella - 2nd Dose',
    ageGroup: '15 Months',
    boosterNeeded: false
  },
  'Rotavirus (1st)': { 
    minAge: 45, 
    nextDose: 30, 
    description: 'Rotavirus Vaccine - 1st Dose',
    ageGroup: '6 Weeks',
    boosterNeeded: true
  },
  'Rotavirus (2nd)': { 
    minAge: 75, 
    nextDose: 30, 
    description: 'Rotavirus Vaccine - 2nd Dose',
    ageGroup: '10 Weeks',
    boosterNeeded: true
  },
  'Rotavirus (3rd)': { 
    minAge: 105, 
    nextDose: null, 
    description: 'Rotavirus Vaccine - 3rd Dose',
    ageGroup: '14 Weeks',
    boosterNeeded: false
  }
};

class VaccinationRecording extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tokenId: '',
      childInfo: null,
      vaccineData: {
        vaccineName: '',
        batchNumber: '',
        notes: '',
        sideEffects: '',
        administeredBy: '',
        dosage: '',
        manufacturer: ''
      },
      loading: false,
      searchLoading: false,
      vaccineImage: null,
      isAuthorized: false,
      hospitalInfo: null,
      recordedVaccination: null,
      showQRCode: false,
      vaccinationHistory: []
    };
  }

  componentDidMount() {
    this.checkHospitalAuthorization();
  }

  // Check if current user is authorized hospital
  checkHospitalAuthorization = () => {
    const isRegistered = localStorage.getItem('hospitalRegistered') === 'true';
    const isAuthorized = localStorage.getItem('hospitalAuthorized') === 'true';
    
    if (isRegistered && isAuthorized) {
      const hospitalData = JSON.parse(localStorage.getItem('hospitalData') || '{}');
      this.setState({
        isAuthorized: true,
        hospitalInfo: hospitalData
      });
    }
  };

  // Search for child by Token ID
  searchChild = async () => {
    if (!this.state.tokenId.trim()) {
      alert('Please enter a Token ID');
      return;
    }

    this.setState({ searchLoading: true });

    try {
      // Simulate searching blockchain
      await new Promise(resolve => setTimeout(resolve, 1000));
      
      // Get all registered children
      const allChildren = JSON.parse(localStorage.getItem('registeredChildren') || '[]');
      const child = allChildren.find(c => c.tokenId === this.state.tokenId);

      if (!child) {
        alert('Child not found with this Token ID');
        this.setState({ searchLoading: false });
        return;
      }

      // Get vaccination history for this child
      const childVaccinations = JSON.parse(localStorage.getItem(`vaccinations_${this.state.tokenId}`) || '[]');
      
      // Calculate age for recommendations
      const birthDate = new Date(child.dateOfBirth);
      const ageInDays = Math.floor((Date.now() - birthDate.getTime()) / (24 * 60 * 60 * 1000));

      this.setState({
        childInfo: {
          ...child,
          ageInDays: ageInDays,
          vaccinationHistory: childVaccinations
        },
        vaccinationHistory: childVaccinations,
        searchLoading: false
      });

    } catch (error) {
      console.error('Error searching for child:', error);
      alert('Error searching for child: ' + error.message);
      this.setState({ searchLoading: false });
    }
  };

  // Handle input changes
  handleInputChange = (e) => {
    const { name, value } = e.target;
    this.setState({
      vaccineData: {
        ...this.state.vaccineData,
        [name]: value
      }
    });
  };

  // Handle image upload
  handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      this.setState({ vaccineImage: file });
    }
  };

  // Calculate next due date
  calculateNextDueDate = () => {
    const vaccine = VACCINE_SCHEDULE[this.state.vaccineData.vaccineName];
    if (vaccine && vaccine.nextDose) {
      const now = new Date();
      const nextDate = new Date(now.getTime() + (vaccine.nextDose * 24 * 60 * 60 * 1000));
      return nextDate.toISOString();
    }
    return null;
  };

  // Generate QR code hash
  generateQRCodeHash = () => {
    const { tokenId, vaccineData } = this.state;
    const timestamp = Date.now();
    return `QR${tokenId}_${vaccineData.vaccineName}_${timestamp}`;
  };

  // Record vaccination
  recordVaccination = async () => {
    if (!this.state.vaccineData.vaccineName || !this.state.vaccineData.batchNumber) {
      alert('Please fill in all required fields');
      return;
    }

    this.setState({ loading: true });

    try {
      // Simulate blockchain transaction
      await new Promise(resolve => setTimeout(resolve, 2500));

      const qrCodeHash = this.generateQRCodeHash();
      const nextDueDate = this.calculateNextDueDate();

      // Create vaccination record
      const vaccinationRecord = {
        id: Date.now().toString(),
        vaccineName: this.state.vaccineData.vaccineName,
        dateAdministered: new Date().toISOString(),
        hospitalName: this.state.hospitalInfo.hospitalName,
        administeredBy: this.state.vaccineData.administeredBy || this.state.hospitalInfo.hospitalName,
        batchNumber: this.state.vaccineData.batchNumber,
        dosage: this.state.vaccineData.dosage,
        manufacturer: this.state.vaccineData.manufacturer,
        nextDueDate: nextDueDate,
        isVerified: true,
        qrCodeHash: qrCodeHash,
        notes: this.state.vaccineData.notes,
        sideEffects: this.state.vaccineData.sideEffects,
        image: this.state.vaccineImage ? URL.createObjectURL(this.state.vaccineImage) : null
      };

      // Save to localStorage (simulating blockchain)
      const existingVaccinations = JSON.parse(localStorage.getItem(`vaccinations_${this.state.tokenId}`) || '[]');
      existingVaccinations.push(vaccinationRecord);
      localStorage.setItem(`vaccinations_${this.state.tokenId}`, JSON.stringify(existingVaccinations));

      // Store recorded vaccination info for display
      this.setState({
        recordedVaccination: {
          ...vaccinationRecord,
          childName: this.state.childInfo.childName,
          tokenId: this.state.tokenId,
          txHash: `0x${Math.random().toString(16).substr(2, 64)}`, // Mock transaction hash
          date: new Date().toLocaleDateString()
        },
        loading: false,
        showQRCode: true,
        vaccinationHistory: existingVaccinations
      });

      // Reset form
      this.setState({
        vaccineData: {
          vaccineName: '',
          batchNumber: '',
          notes: '',
          sideEffects: '',
          administeredBy: '',
          dosage: '',
          manufacturer: ''
        },
        vaccineImage: null
      });

      // Update child info with new vaccination history
      this.setState(prevState => ({
        childInfo: {
          ...prevState.childInfo,
          vaccinationHistory: existingVaccinations
        }
      }));

    } catch (error) {
      console.error('Error recording vaccination:', error);
      alert('Error recording vaccination: ' + error.message);
      this.setState({ loading: false });
    }
  };

  // Format date
  formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString();
  };

  // Get child age
  getChildAge = (dateOfBirth) => {
    const birthDate = new Date(dateOfBirth);
    const ageInDays = Math.floor((Date.now() - birthDate.getTime()) / (24 * 60 * 60 * 1000));
    if (ageInDays < 30) return `${ageInDays} days`;
    if (ageInDays < 365) return `${Math.floor(ageInDays/30)} months`;
    return `${Math.floor(ageInDays/365)} years`;
  };

  // Get recommended vaccines for age
  getRecommendedVaccines = (ageInDays) => {
    return Object.entries(VACCINE_SCHEDULE).filter(([vaccine, info]) => 
      ageInDays >= info.minAge && ageInDays <= info.minAge + 90 // Within 3 months of recommended age
    );
  };

  // Render unauthorized access
  renderUnauthorized() {
    return (
      <div className="vaccination-record">
        <div className="unauthorized-message">
          <div className="error-icon">ðŸ¥</div>
          <h2>Hospital Authorization Required</h2>
          <p>Only authorized hospitals can record vaccinations.</p>
          <div className="registration-prompt">
            <p>To record vaccinations, you need to:</p>
            <ol>
              <li>Register your hospital</li>
              <li>Get authorization from system admin</li>
              <li>Return here to record vaccinations</li>
            </ol>
          </div>
          <div className="demo-controls">
            <button onClick={() => this.props.onChangeView('hospital-register')} className="btn-primary">
              Go to Hospital Registration
            </button>
          </div>
        </div>
      </div>
    );
  }

  // Render QR code modal
  renderQRCodeModal() {
    const { recordedVaccination } = this.state;

    return (
      <div className="qr-modal">
        <div className="qr-modal-content">
          <div className="qr-header">
            <h3>âœ… Vaccination Recorded Successfully!</h3>
            <button onClick={() => this.setState({ showQRCode: false })} className="close-btn">Ã—</button>
          </div>
          
          <div className="vaccination-success-info">
            <div className="success-details">
              <p><strong>Child:</strong> {recordedVaccination.childName}</p>
              <p><strong>Vaccine:</strong> {recordedVaccination.vaccineName}</p>
              <p><strong>Hospital:</strong> {recordedVaccination.hospitalName}</p>
              <p><strong>Date:</strong> {recordedVaccination.date}</p>
              <p><strong>Batch:</strong> {recordedVaccination.batchNumber}</p>
              {recordedVaccination.nextDueDate && (
                <p><strong>Next Due:</strong> {this.formatDate(recordedVaccination.nextDueDate)}</p>
              )}
            </div>
          </div>

          <div className="qr-code-section">
            <h4>ðŸ” Vaccination QR Code</h4>
            <p>This QR code contains verification data for this vaccination:</p>
            <div className="qr-container">
              <div className="qr-placeholder">
                ðŸ“± QR Code: {recordedVaccination.qrCodeHash}
                <br />
                <small>Child: {recordedVaccination.childName}</small>
                <br />
                <small>Vaccine: {recordedVaccination.vaccineName}</small>
                <br />
                <small>Hospital: {recordedVaccination.hospitalName}</small>
              </div>
            </div>
            <div className="qr-info">
              <p><strong>QR Hash:</strong> {recordedVaccination.qrCodeHash}</p>
              <p><strong>Transaction:</strong> {recordedVaccination.txHash.slice(0, 10)}...</p>
              <p><strong>Verification URL:</strong> /verify/{recordedVaccination.tokenId}/{recordedVaccination.qrCodeHash}</p>
            </div>
          </div>

          <div className="qr-actions">
            <button className="btn-primary">
              ðŸ“± Download QR Code
            </button>
            <button 
              onClick={() => this.setState({ showQRCode: false })}
              className="btn-secondary"
            >
              Continue
            </button>
          </div>
        </div>
      </div>
    );
  }

  render() {
    const { 
      isAuthorized, 
      hospitalInfo, 
      tokenId, 
      childInfo, 
      vaccineData, 
      loading, 
      searchLoading, 
      showQRCode,
      vaccinationHistory
    } = this.state;

    if (!isAuthorized) {
      return (
        <div className="App">
          <div className="main-content">
            {this.renderUnauthorized()}
            <button onClick={this.props.onBack} className="btn-secondary">
              â† Back to Dashboard
            </button>
          </div>
        </div>
      );
    }

    return (
      <div className="App">
        <div className="main-content">
          <div className="vaccination-record">
            <div className="record-container">
              <div className="hospital-header">
                <h2>ðŸ’‰ Record Vaccination</h2>
                <div className="hospital-info">
                  <p><strong>Hospital:</strong> {hospitalInfo?.hospitalName}</p>
                  <p><strong>License:</strong> {hospitalInfo?.licenseNumber}</p>
                </div>
              </div>
              
              {/* Search Child Section */}
              <div className="search-section">
                <h3>ðŸ” Find Child by Token ID</h3>
                <div className="search-form">
                  <input
                    type="text"
                    value={tokenId}
                    onChange={(e) => this.setState({ tokenId: e.target.value })}
                    placeholder="Enter Child's Token ID (e.g., 1648123456789)"
                    className="search-input"
                  />
                  <button 
                    onClick={this.searchChild}
                    disabled={searchLoading}
                    className="search-btn"
                  >
                    {searchLoading ? 'â³ Searching...' : 'ðŸ” Search Child'}
                  </button>
                </div>
                <div className="search-help">
                  <small>ðŸ’¡ Parents can provide their child's Token ID for vaccination recording</small>
                </div>
              </div>

              {/* Child Information Display */}
              {childInfo && (
                <div className="child-info-section">
                  <div className="child-card">
                    <div className="child-header">
                      <h3>ðŸ‘¶ {childInfo.childName}</h3>
                      <div className="child-status">
                        <span className="token-id">Token ID: {childInfo.tokenId}</span>
                      </div>
                    </div>
                    <div className="child-details-grid">
                      <div className="detail-item">
                        <label>Parent:</label>
                        <span>{childInfo.parentName}</span>
                      </div>
                      <div className="detail-item">
                        <label>Date of Birth:</label>
                        <span>{this.formatDate(childInfo.dateOfBirth)}</span>
                      </div>
                      <div className="detail-item">
                        <label>Age:</label>
                        <span>{this.getChildAge(childInfo.dateOfBirth)}</span>
                      </div>
                      <div className="detail-item">
                        <label>Total Vaccinations:</label>
                        <span>{vaccinationHistory.length}</span>
                      </div>
                      <div className="detail-item">
                        <label>Parent Contact:</label>
                        <span>{childInfo.contactInfo}</span>
                      </div>
                    </div>
                  </div>

                  {/* Recommended Vaccines for Age */}
                  <div className="recommended-vaccines">
                    <h4>ðŸ’¡ Recommended for Current Age</h4>
                    <div className="vaccine-recommendations">
                      {this.getRecommendedVaccines(childInfo.ageInDays).map(([vaccine, info]) => (
                        <div key={vaccine} className="recommendation-item">
                          <span className="vaccine-name">{vaccine}</span>
                          <span className="age-group">{info.ageGroup}</span>
                          <span className="description">{info.description}</span>
                        </div>
                      ))}
                    </div>
                  </div>

                  {/* Previous Vaccinations */}
                  {vaccinationHistory.length > 0 && (
                    <div className="vaccination-history">
                      <h4>ðŸ“‹ Vaccination History</h4>
                      <div className="history-list">
                        {vaccinationHistory.map((vaccine, index) => (
                          <div key={index} className="history-item">
                            <div className="vaccine-main-info">
                              <strong>{vaccine.vaccineName}</strong>
                              <span className="date">{this.formatDate(vaccine.dateAdministered)}</span>
                            </div>
                            <div className="vaccine-sub-info">
                              <span>{vaccine.hospitalName}</span>
                              <span>Batch: {vaccine.batchNumber}</span>
                              {vaccine.qrCodeHash && (
                                <span className="qr-indicator">ðŸ” QR Available</span>
                              )}
                            </div>
                            {vaccine.nextDueDate && (
                              <div className="next-due">
                                Next due: {this.formatDate(vaccine.nextDueDate)}
                              </div>
                            )}
                          </div>
                        ))}
                      </div>
                    </div>
                  )}

                  {/* Vaccination Form */}
                  <div className="vaccination-form">
                    <h4>ðŸ’‰ Record New Vaccination</h4>
                    
                    <div className="form-row">
                      <div className="form-group">
                        <label>Vaccine Name *</label>
                        <select
                          name="vaccineName"
                          value={vaccineData.vaccineName}
                          onChange={this.handleInputChange}
                          required
                        >
                          <option value="">Select Vaccine</option>
                          <optgroup label="Recommended for Age">
                            {this.getRecommendedVaccines(childInfo.ageInDays).map(([vaccine, info]) => (
                              <option key={vaccine} value={vaccine}>
                                {vaccine} - {info.description}
                              </option>
                            ))}
                          </optgroup>
                          <optgroup label="All Vaccines">
                            {Object.entries(VACCINE_SCHEDULE).map(([vaccine, info]) => (
                              <option key={vaccine} value={vaccine}>
                                {vaccine} - {info.description}
                              </option>
                            ))}
                          </optgroup>
                        </select>
                      </div>

                      <div className="form-group">
                        <label>Batch Number *</label>
                        <input
                          type="text"
                          name="batchNumber"
                          value={vaccineData.batchNumber}
                          onChange={this.handleInputChange}
                          placeholder="Vaccine batch number"
                          required
                        />
                      </div>
                    </div>

                    <div className="form-row">
                      <div className="form-group">
                        <label>Dosage</label>
                        <input
                          type="text"
                          name="dosage"
                          value={vaccineData.dosage}
                          onChange={this.handleInputChange}
                          placeholder="e.g., 0.5ml, 1ml"
                        />
                      </div>

                      <div className="form-group">
                        <label>Manufacturer</label>
                        <input
                          type="text"
                          name="manufacturer"
                          value={vaccineData.manufacturer}
                          onChange={this.handleInputChange}
                          placeholder="Vaccine manufacturer"
                        />
                      </div>
                    </div>

                    <div className="form-group">
                      <label>Administered By</label>
                      <input
                        type="text"
                        name="administeredBy"
                        value={vaccineData.administeredBy}
                        onChange={this.handleInputChange}
                        placeholder="Doctor/Nurse name (optional)"
                      />
                    </div>

                    <div className="form-group">
                      <label>Vaccination Certificate/Photo</label>
                      <input
                        type="file"
                        accept="image/*"
                        onChange={this.handleImageUpload}
                        className="file-input"
                      />
                      {this.state.vaccineImage && (
                        <div className="image-preview">
                          <img 
                            src={URL.createObjectURL(this.state.vaccineImage)} 
                            alt="Vaccine certificate"
                            className="preview-img"
                          />
                        </div>
                      )}
                    </div>

                    <div className="form-group">
                      <label>Medical Notes</label>
                      <textarea
                        name="notes"
                        value={vaccineData.notes}
                        onChange={this.handleInputChange}
                        placeholder="Additional medical notes about the vaccination"
                        rows="3"
                      />
                    </div>

                    <div className="form-group">
                      <label>Side Effects (if any)</label>
                      <textarea
                        name="sideEffects"
                        value={vaccineData.sideEffects}
                        onChange={this.handleInputChange}
                        placeholder="Record any observed side effects or reactions"
                        rows="2"
                      />
                    </div>

                    <div className="vaccination-summary">
                      <h5>ðŸ“‹ Vaccination Summary</h5>
                      <p><strong>Child:</strong> {childInfo.childName}</p>
                      <p><strong>Hospital:</strong> {hospitalInfo?.hospitalName}</p>
                      <p><strong>Date:</strong> {new Date().toLocaleDateString()}</p>
                      {vaccineData.vaccineName && (
                        <p><strong>Vaccine:</strong> {vaccineData.vaccineName}</p>
                      )}
                    </div>

                    <div className="form-actions">
                      <button
                        onClick={this.recordVaccination}
                        disabled={loading || !vaccineData.vaccineName || !vaccineData.batchNumber}
                        className="record-btn"
                      >
                        {loading ? 'â³ Recording & Generating QR...' : 'ðŸ” Record Vaccination'}
                      </button>
                    </div>
                  </div>
                </div>
              )}

              <button onClick={this.props.onBack} className="btn-secondary">
                â† Back to Dashboard
              </button>

              {loading && (
                <div className="loading-info">
                  <div className="loading-spinner">â³</div>
                  <p><strong>ðŸ“¡ Processing Vaccination Record...</strong></p>
                  <p>â³ Recording vaccination on blockchain</p>
                  <p>ðŸ” Generating verification QR code</p>
                  <p>ðŸ“± Creating permanent record</p>
                </div>
              )}
            </div>
          </div>

          {/* QR Code Display Modal */}
          {showQRCode && this.renderQRCodeModal()}
        </div>
      </div>
    );
  }
}

export default VaccinationRecording;
