import React, { Component } from 'react';

class AdminPanel extends Component {
  state = {
    hospitals: [],
    loading: false,
    error: null,
    success: null
  };

  componentDidMount() {
    this.loadHospitals();
  }

  loadHospitals = async () => {
    try {
      this.setState({ loading: true, error: null });

      const { contract } = this.props;
      const filter = contract.filters.HospitalRegistered();
      const events = await contract.queryFilter(filter, 0, 'latest');

      const uniqueAddresses = [...new Set(events.map(e => e.args.hospital))];

      const hospitals = await Promise.all(
        uniqueAddresses.map(async (address) => {
          const hospitalData = await contract.hospitals(address);
          const isAuthorized = await contract.authorizedHospitals(address);

          return {
            address,
            name: hospitalData[0],          // name
            license: hospitalData[1],       // license
            contact: hospitalData[2],       // contact info
            isAuthorized
          };
        })
      );

      this.setState({ hospitals, loading: false });

    } catch (error) {
      console.error('Failed to load hospitals:', error);
      this.setState({ error: 'Error loading hospital list', loading: false });
    }
  };

  toggleAuthorization = async (address, currentStatus) => {
    try {
      const { contract } = this.props;

      const tx = await contract.setHospitalAuthorization(address, !currentStatus);
      await tx.wait();

      this.setState({
        success: `Hospital ${address} is now ${!currentStatus ? 'authorized' : 'unauthorized'}.`
      });

      this.loadHospitals();

    } catch (error) {
      console.error('Authorization error:', error);
      this.setState({ error: 'Failed to change authorization status' });
    }
  };

  render() {
    const { hospitals, loading, error, success } = this.state;

    return (
      <div className="admin-panel">
        <h2>üëë Admin Panel</h2>
        <p>Manage hospital access and authorization</p>

        {loading && <p>‚è≥ Loading hospital data...</p>}
        {error && <p style={{ color: 'red' }}>‚ùå {error}</p>}
        {success && <p style={{ color: 'green' }}>‚úÖ {success}</p>}

        {hospitals.length === 0 && !loading && (
          <p>No hospitals found. Ensure some are registered.</p>
        )}

        <div className="hospital-list">
          {hospitals.map((h, i) => (
            <div key={i} className="hospital-card">
              <h3>üè• {h.name || 'Unnamed Hospital'}</h3>
              <p><strong>License:</strong> {h.license}</p>
              <p><strong>Contact:</strong> {h.contact}</p>
              <p><strong>Wallet Address:</strong> {h.address}</p>
              <p><strong>Status:</strong> {h.isAuthorized ? '‚úÖ Authorized' : '‚ùå Not Authorized'}</p>

              <button onClick={() => this.toggleAuthorization(h.address, h.isAuthorized)}>
                {h.isAuthorized ? 'üö´ Revoke Access' : '‚úÖ Authorize'}
              </button>
            </div>
          ))}
        </div>

        <button onClick={this.props.onBack}>‚Üê Back to Dashboard</button>
      </div>
    );
  }
}

export default AdminPanel;
