import React, { Component } from 'react';
import { ethers } from 'ethers';
import './App.css';
import HospitalRegistration from './components/HospitalRegistration';
import ChildRegistration from './components/ChildRegistration';
import VaccinationRecording from './components/VaccinationRecording';
import ParentDashboard from './components/ParentDashboard';
import QRVerification from './components/QRVerification';

// Import contract ABI (you'll need to update this path)
import VaccinationRegistryABI from './contracts/VaccinationRegistry.json';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentView: 'welcome',
      userRole: null, // 'hospital', 'parent', 'admin'
      connectedWallet: null,
      web3Provider: null,
      contract: null,
      contractAddress: "", // Update with your deployed address
      systemStats: {
        totalHospitals: 0,
        totalChildren: 0,
        totalVaccinations: 0,
        pendingAuthorizations: 0
      },
      isLoading: false,
      error: null,
      chainId: null,
      blockNumber: 0
    };
  }

  componentDidMount() {
    this.detectWalletConnection();
    this.setupEventListeners();

    // Auto-navigate if wallet is already connected
    setTimeout(() => {
      if (this.state.connectedWallet && this.state.currentView === 'welcome') {
        this.setState({ currentView: 'dashboard' });
        this.loadSystemStats();
      }
    }, 1000);
  }

  componentWillUnmount() {
    this.removeEventListeners();
  }

  // Setup blockchain event listeners
  setupEventListeners = () => {
    if (window.ethereum) {
      window.ethereum.on('accountsChanged', this.handleAccountsChanged);
      window.ethereum.on('chainChanged', this.handleChainChanged);
      window.ethereum.on('disconnect', this.handleDisconnect);
    }
  };

  // Remove event listeners
  removeEventListeners = () => {
    if (window.ethereum) {
      window.ethereum.removeListener('accountsChanged', this.handleAccountsChanged);
      window.ethereum.removeListener('chainChanged', this.handleChainChanged);
      window.ethereum.removeListener('disconnect', this.handleDisconnect);
    }
  };

  // Handle account changes
  handleAccountsChanged = (accounts) => {
    if (accounts.length === 0) {
      this.handleDisconnect();
    } else {
      this.setState({ connectedWallet: accounts[0] });
      this.initializeContract();
    }
  };

  // Handle chain changes
  handleChainChanged = (chainId) => {
    console.log('Chain changed to:', chainId);
    this.setState({ chainId });
    window.location.reload(); // Reload to reset state
  };

  // Initialize Web3 Provider and Contract
  initializeContract = async () => {
    try {
      if (!window.ethereum) {
        throw new Error('MetaMask not found');
      }

      const provider = new ethers.providers.Web3Provider(window.ethereum);
      const signer = provider.getSigner();

      // Get network info
      const network = await provider.getNetwork();
      const blockNumber = await provider.getBlockNumber();

      console.log('üîó Connected to network:', network.name, 'Chain ID:', network.chainId);
      console.log('üì¶ Current block number:', blockNumber);

      // Initialize contract
      const contract = new ethers.Contract(
        this.state.contractAddress,
        VaccinationRegistryABI.abi || VaccinationRegistryABI,
        signer
      );

      this.setState({
        web3Provider: provider,
        contract: contract,
        chainId: network.chainId,
        blockNumber: blockNumber,
        error: null
      });

      // Load system stats from blockchain
      await this.loadSystemStats();

      return contract;
    } catch (error) {
      console.error('‚ùå Contract initialization failed:', error);
      this.setState({ 
        error: `Contract initialization failed: ${error.message}`,
        contract: null,
        web3Provider: null
      });
      return null;
    }
  };

  // Detect existing wallet connection
  detectWalletConnection = async () => {
    if (typeof window.ethereum !== 'undefined') {
      try {
        const accounts = await window.ethereum.request({ method: 'eth_accounts' });
        if (accounts.length > 0) {
          console.log('üë§ Wallet detected:', accounts[0]);
          this.setState({ connectedWallet: accounts[0] });
          await this.initializeContract();
        }
      } catch (error) {
        console.error('‚ùå Error detecting wallet:', error);
        this.setState({ error: `Wallet detection failed: ${error.message}` });
      }
    }
  };

  // Handle wallet connection
  handleConnectWallet = async () => {
    if (typeof window.ethereum !== 'undefined') {
      try {
        this.setState({ isLoading: true, error: null });

        // Request account access
        const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });

        // Get network info
        const chainId = await window.ethereum.request({ method: 'eth_chainId' });
        console.log('üîó Connected to chain ID:', chainId);

        // Check if we're on Ganache (0x539 = 1337 in hex)
        if (chainId !== '0x539' && chainId !== '0x5777') {
          alert('‚ö†Ô∏è Please switch to Ganache Local Network!\n\nNetwork: Ganache Local\nRPC URL: http://127.0.0.1:7545\nChain ID: 1337');
          this.setState({ isLoading: false });
          return;
        }

        this.setState({ 
          connectedWallet: accounts[0],
          currentView: 'dashboard',
          isLoading: false
        });

        // Initialize contract
        await this.initializeContract();

      } catch (error) {
        console.error('‚ùå Error connecting wallet:', error);
        this.setState({ 
          error: `Wallet connection failed: ${error.message}`,
          isLoading: false
        });
        alert('Error connecting wallet. Please try again.');
      }
    } else {
      alert('Please install MetaMask wallet!');
    }
  };

  // Load system statistics from blockchain
  loadSystemStats = async () => {
    try {
      if (!this.state.contract) {
        console.log('‚ö†Ô∏è Contract not initialized, using default stats');
        return;
      }

      console.log('üìä Loading system stats from blockchain...');
      this.setState({ isLoading: true });

      const contract = this.state.contract;

      // Call smart contract functions to get real data
      const totalChildren = await contract.getTotalChildren();
      const totalHospitals = await contract.getTotalHospitals();

      // Get vaccination count (sum across all children)
      let totalVaccinations = 0;
      const childrenCount = totalChildren.toNumber();

      for (let i = 1; i <= childrenCount; i++) {
        try {
          const vaccinationCount = await contract.getVaccinationCount(i);
          totalVaccinations += vaccinationCount.toNumber();
        } catch (error) {
          console.log(`Child ${i} vaccination count error:`, error.message);
        }
      }

      // Get pending authorizations
      const pendingAuthorizations = await contract.getPendingHospitalCount();

      this.setState({
        systemStats: {
          totalHospitals: totalHospitals.toNumber(),
          totalChildren: childrenCount,
          totalVaccinations: totalVaccinations,
          pendingAuthorizations: pendingAuthorizations.toNumber()
        },
        isLoading: false,
        error: null
      });

      console.log('‚úÖ System stats loaded:', {
        hospitals: totalHospitals.toNumber(),
        children: childrenCount,
        vaccinations: totalVaccinations,
        pending: pendingAuthorizations.toNumber()
      });

    } catch (error) {
      console.error('‚ùå Error loading system stats:', error);
      this.setState({ 
        error: `Failed to load system stats: ${error.message}`,
        isLoading: false,
        systemStats: {
          totalHospitals: 0,
          totalChildren: 0,
          totalVaccinations: 0,
          pendingAuthorizations: 0
        }
      });
    }
  };

  // Manual navigation to dashboard
  goToDashboard = () => {
    this.setState({ currentView: 'dashboard' });
    this.loadSystemStats();
  };

  // Handle disconnect
  handleDisconnect = () => {
    this.setState({ 
      currentView: 'welcome',
      connectedWallet: null,
      userRole: null,
      web3Provider: null,
      contract: null,
      error: null
    });
  };

  // Change view
  changeView = (view, role = null) => {
    this.setState({ 
      currentView: view,
      userRole: role
    });
    // Refresh stats when returning to dashboard
    if (view === 'dashboard') {
      this.loadSystemStats();
    }
  };

  // Back to dashboard
  backToDashboard = () => {
    this.setState({ currentView: 'dashboard' });
    this.loadSystemStats();
  };

  // Send transaction helper
  sendTransaction = async (contractMethod, params = [], value = 0) => {
    try {
      if (!this.state.contract) {
        throw new Error('Contract not initialized');
      }

      this.setState({ isLoading: true, error: null });

      const tx = await contractMethod(...params, value > 0 ? { value } : {});
      console.log('üì§ Transaction sent:', tx.hash);

      // Wait for confirmation
      const receipt = await tx.wait();
      console.log('‚úÖ Transaction confirmed:', receipt.transactionHash);

      // Refresh stats after successful transaction
      await this.loadSystemStats();

      this.setState({ isLoading: false });
      return receipt;

    } catch (error) {
      console.error('‚ùå Transaction failed:', error);
      this.setState({ 
        error: `Transaction failed: ${error.message}`,
        isLoading: false
      });
      throw error;
    }
  };

  // Render welcome screen
  renderWelcomeScreen() {
    const { connectedWallet, isLoading, error, chainId } = this.state;

    return (
      <div className="App">
        <div className="welcome-screen">
          <div className="welcome-content">
            <h1>üè• Blockchain Vaccination Registry</h1>
            <p>Secure, transparent, and tamper-proof vaccination records powered by Ethereum blockchain</p>

            {error && (
              <div className="error-message">
                ‚ùå {error}
              </div>
            )}

            <div className="features">
              <div className="feature-item">
                <span className="feature-icon">üè•</span>
                <span>Hospital Registration</span>
              </div>
              <div className="feature-item">
                <span className="feature-icon">üë∂</span>
                <span>Child Birth Records</span>
              </div>
              <div className="feature-item">
                <span className="feature-icon">üíâ</span>
                <span>Vaccination Tracking</span>
              </div>
              <div className="feature-item">
                <span className="feature-icon">üë®‚Äçüë©‚Äçüëß‚Äçüë¶</span>
                <span>Parent Dashboard</span>
              </div>
              <div className="feature-item">
                <span className="feature-icon">üì±</span>
                <span>QR Verification</span>
              </div>
              <div className="feature-item">
                <span className="feature-icon">üîí</span>
                <span>Blockchain Security</span>
              </div>
            </div>

            <div className="contract-info">
              <h4>Smart Contract Details:</h4>
              <p><strong>Network:</strong> Ganache Local Blockchain</p>
              <p><strong>Chain ID:</strong> {chainId || 'Not Connected'}</p>
              <div className="contract-address">
                {this.state.contractAddress}
              </div>
              {connectedWallet && (
                <div className="connected-wallet-info">
                  <p><strong>Connected Wallet:</strong></p>
                  <div className="wallet-address">{connectedWallet}</div>
                </div>
              )}
            </div>

            <button 
              className="connect-btn"
              onClick={this.handleConnectWallet}
              disabled={!!connectedWallet || isLoading}
            >
              {isLoading ? 'üîÑ Connecting...' : 
               connectedWallet ? '‚úÖ Wallet Connected' : 
               'ü¶ä Connect MetaMask Wallet'}
            </button>

            {connectedWallet && (
              <button 
                className="connect-btn"
                onClick={this.goToDashboard}
                style={{ marginTop: '15px', background: 'linear-gradient(135deg, #28a745, #20c997)' }}
              >
                üöÄ Go to Dashboard
              </button>
            )}

            <div className="workflow-info">
              <h4>How It Works:</h4>
              <ol>
                <li><strong>Hospitals Register:</strong> Healthcare providers register and get authorized</li>
                <li><strong>Child Birth Registration:</strong> Hospital creates NFT record, sends to parents</li>
                <li><strong>Vaccination Records:</strong> Authorized hospitals record vaccinations with QR codes</li>
                <li><strong>Parent Access:</strong> Parents view and manage their children's records</li>
                <li><strong>QR Verification:</strong> Instant verification of vaccination status</li>
              </ol>
            </div>

            <div className="blockchain-info">
              <h4>üîó Blockchain Connection Requirements:</h4>
              <div className="requirements">
                <div className="requirement-item">
                  <span>1Ô∏è‚É£ Install MetaMask wallet extension</span>
                </div>
                <div className="requirement-item">
                  <span>2Ô∏è‚É£ Start Ganache local blockchain</span>
                </div>
                <div className="requirement-item">
                  <span>3Ô∏è‚É£ Add Ganache network to MetaMask</span>
                </div>
                <div className="requirement-item">
                  <span>4Ô∏è‚É£ Import Ganache account to MetaMask</span>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  // Render dashboard
  renderDashboard() {
    const { connectedWallet, systemStats, isLoading, error, blockNumber, chainId } = this.state;

    return (
      <div className="App">
        <div className="main-content">
          <div className="dashboard">
            <div className="dashboard-header">
              <h1>üè• Vaccination Registry Dashboard</h1>
              <div className="user-info">
                <div className="account-info">
                  <p><strong>Status:</strong> {connectedWallet ? 'Connected' : 'Disconnected'}</p>
                  <p><strong>Wallet:</strong> {connectedWallet ? `${connectedWallet.slice(0, 6)}...${connectedWallet.slice(-4)}` : 'Not Connected'}</p>
                  <p><strong>Chain ID:</strong> {chainId}</p>
                  <p><strong>Block:</strong> #{blockNumber}</p>
                </div>
                <button onClick={this.handleDisconnect} className="disconnect-btn">
                  Disconnect
                </button>
              </div>
            </div>

            {error && (
              <div className="error-banner">
                ‚ùå {error}
                <button onClick={() => this.setState({ error: null })}>‚úï</button>
              </div>
            )}

            {isLoading && (
              <div className="loading-banner">
                üîÑ Loading blockchain data...
              </div>
            )}

            {/* System Statistics */}
            <div className="dashboard-stats">
              <div className="stat-card">
                <h3>Registered Hospitals</h3>
                <div className="stat-number">{systemStats.totalHospitals}</div>
                <div className="stat-subtitle">On blockchain</div>
              </div>
              <div className="stat-card">
                <h3>Registered Children</h3>
                <div className="stat-number">{systemStats.totalChildren}</div>
                <div className="stat-subtitle">NFT tokens issued</div>
              </div>
              <div className="stat-card">
                <h3>Total Vaccinations</h3>
                <div className="stat-number">{systemStats.totalVaccinations}</div>
                <div className="stat-subtitle">Recorded on-chain</div>
              </div>
              <div className="stat-card">
                <h3>Pending Authorizations</h3>
