import React, { Component } from 'react';

class QRVerification extends Component {
  constructor(props) {
    super(props);
    this.state = {
      verificationMode: 'token-search',
      tokenId: '',
      loading: false,
      verificationResult: null,
      error: null
    };
  }

  handleInputChange = (e) => {
    const { name, value } = e.target;
    this.setState({ [name]: value });
  };

  switchMode = (mode) => {
    this.setState({
      verificationMode: mode,
      verificationResult: null,
      error: null,
      tokenId: ''
    });
  };

  verifyByTokenId = async () => {
    const { tokenId } = this.state;
    
    if (!tokenId.trim()) {
      this.setState({ error: 'Please enter a Token ID' });
      return;
    }

    this.setState({ loading: true, error: null });

    try {
      const { contract } = this.props;
      
      if (!contract) {
        throw new Error('Contract not available');
      }

      console.log('Verifying token ID:', tokenId);

      // Get child information
      const childInfo = await contract.getChildInfo(tokenId);
      console.log('Child info:', childInfo);

      if (!childInfo[0] || childInfo[0] === '') {
        throw new Error('No child found with this Token ID');
      }

      // Get vaccination history
      const vaccinationHistory = await contract.getVaccinationHistory(tokenId);
      console.log('Vaccination history:', vaccinationHistory);

      // Generate verification QR data
      const qrData = await contract.generateVerificationQR(tokenId);
      console.log('QR data:', qrData);

      // Parse child information
      const child = {
        tokenId: tokenId,
        name: childInfo[0],
        dateOfBirth: new Date(childInfo[1].toNumber() * 1000),
        parentName: childInfo[2],
        contactInfo: childInfo[3],
        parentAddress: childInfo[4],
        hospitalAddress: childInfo[5],
        hospitalName: childInfo[6],
        isRegistered: childInfo[7],
        registrationDate: new Date(childInfo[8].toNumber() * 1000)
      };

      // Parse vaccination history
      const vaccinations = vaccinationHistory.map(vax => ({
        vaccineName: vax[0],
        dateAdministered: new Date(vax[1].toNumber() * 1000),
        hospitalName: vax[2],
        administeredBy: vax[3],
        batchNumber: vax[4],
        nextDueDate: vax[5].toNumber() > 0 ? new Date(vax[5].toNumber() * 1000) : null,
        isVerified: vax[6],
        ipfsHash: vax[7],
        qrCodeHash: vax[8]
      }));

      this.setState({
        loading: false,
        verificationResult: {
          status: 'valid',
          message: 'Child record found and verified on blockchain',
          child: child,
          vaccinations: vaccinations,
          qrData: qrData[0], // QR verification string
          totalVaccinations: qrData[2].toNumber(),
          isUpToDate: qrData[3],
          verifiedAt: new Date().toISOString()
        }
      });

    } catch (error) {
      console.error('Verification error:', error);
      this.setState({
        loading: false,
        error: error.message,
        verificationResult: {
          status: 'invalid',
          message: 'Token ID not found or invalid'
        }
      });
    }
  };

  formatDate = (date) => {
    if (!date) return 'N/A';
    return date.toLocaleDateString();
  };

  getChildAge = (dateOfBirth) => {
    const birthDate = new Date(dateOfBirth);
    const ageInDays = Math.floor((Date.now() - birthDate.getTime()) / (24 * 60 * 60 * 1000));
    if (ageInDays < 30) return `${ageInDays} days`;
    if (ageInDays < 365) return `${Math.floor(ageInDays/30)} months`;
    return `${Math.floor(ageInDays/365)} years`;
  };

  render() {
    const { 
      tokenId, 
      loading, 
      verificationResult, 
      error 
    } = this.state;

    return (
      <div className="App">
        <div className="main-content">
          <div className="qr-verification">
            <div className="verification-container">
              <div className="verification-header">
                <h1>🔍 Blockchain Vaccination Verification</h1>
                <p>Verify vaccination records directly from the blockchain</p>
                <button onClick={this.props.onBack} className="back-btn btn-secondary">
                  ← Back to Dashboard
                </button>
              </div>

              {/* Verification Form */}
              <div className="verification-form">
                <div className="form-section">
                  <h3>🔍 Token ID Verification</h3>
                  <p>Search for a child's vaccination record using their blockchain Token ID</p>
                  
                  <div className="input-group">
                    <input
                      type="text"
                      name="tokenId"
                      value={tokenId}
                      onChange={this.handleInputChange}
                      placeholder="Child's Token ID (e.g., 1, 2, 3...)"
                      className="verification-input"
                      disabled={loading}
                    />
                    <button 
                      onClick={this.verifyByTokenId}
                      disabled={loading || !tokenId.trim()}
                      className="verify-btn"
                    >
                      {loading ? '⏳ Verifying...' : '🔍 Verify on Blockchain'}
                    </button>
                  </div>
                  
                  <div className="qr-help">
                    <h4>How blockchain verification works:</h4>
                    <ul>
                      <li>Each child has a unique Token ID assigned at birth</li>
                      <li>Data is retrieved directly from the blockchain</li>
                      <li>Shows complete vaccination history and verification status</li>
                      <li>All records are cryptographically secured and immutable</li>
                    </ul>
                  </div>
                </div>
              </div>

              {/* Error Display */}
              {error && (
                <div className="verification-error">
                  <div className="error-icon">⚠️</div>
                  <h3>Verification Error</h3>
                  <p>{error}</p>
                </div>
              )}

              {/* Loading State */}
              {loading && (
                <div className="verification-loading">
                  <div className="loading-spinner">⏳</div>
                  <p>Verifying against blockchain records...</p>
                </div>
              )}

              {/* Verification Results */}
              {verificationResult && (
                <div className="verification-results">
                  {verificationResult.status === 'invalid' ? (
                    <div className="verification-error">
                      <div className="error-icon">❌</div>
                      <h3>Verification Failed</h3>
                      <p>{verificationResult.message}</p>
                    </div>
                  ) : (
                    <div className="verification-success">
                      <div className="success-icon">✅</div>
                      <h3>Blockchain Verification Successful</h3>
                      
                      <div className="verification-details">
                        {/* Child Information */}
                        <div className="detail-section">
                          <h4>👶 Child Information</h4>
                          <div className="detail-grid">
                            <div className="detail-item">
                              <label>Name:</label>
                              <span>{verificationResult.child.name}</span>
                            </div>
                            <div className="detail-item">
                              <label>Date of Birth:</label>
                              <span>{this.formatDate(verificationResult.child.dateOfBirth)}</span>
                            </div>
                            <div className="detail-item">
                              <label>Age:</label>
                              <span>{this.getChildAge(verificationResult.child.dateOfBirth)}</span>
                            </div>
                            <div className="detail-item">
                              <label>Token ID:</label>
                              <span className="token-id">{verificationResult.child.tokenId}</span>
                            </div>
                            <div className="detail-item">
                              <label>Registered by:</label>
                              <span>{verificationResult.child.hospitalName}</span>
                            </div>
                            <div className="detail-item">
                              <label>Parent:</label>
                              <span>{verificationResult.child.parentName}</span>
                            </div>
                            <div className="detail-item">
                              <label>Registration Date:</label>
                              <span>{this.formatDate(verificationResult.child.registrationDate)}</span>
                            </div>
                          </div>
                        </div>

                        {/* Vaccination Summary */}
                        <div className="detail-section">
                          <h4>📊 Vaccination Summary</h4>
                          <div className="vaccination-status">
                            <div className="status-item">
                              <label>Total Vaccinations</label>
                              <span className="status-number">{verificationResult.totalVaccinations}</span>
                            </div>
                            <div className="status-item">
                              <label>Status</label>
                              <span className={`status-badge ${verificationResult.isUpToDate ? 'up-to-date' : 'pending'}`}>
                                {verificationResult.isUpToDate ? '✅ Up to Date' : '⏳ Pending'}
                              </span>
                            </div>
                          </div>
                        </div>

                        {/* Full Vaccination History */}
                        <div className="detail-section">
                          <h4>📋 Vaccination History ({verificationResult.vaccinations.length})</h4>
                          
                          {verificationResult.vaccinations.length > 0 ? (
                            <div className="history-list">
                              {verificationResult.vaccinations.map((vaccine, index) => (
                                <div key={index} className="history-item">
                                  <div className="history-header">
                                    <strong>{vaccine.vaccineName}</strong>
                                    <span className="history-date">
                                      {this.formatDate(vaccine.dateAdministered)}
                                    </span>
                                  </div>
                                  <div className="history-details">
                                    <span><strong>Hospital:</strong> {vaccine.hospitalName}</span>
                                    <span><strong>Batch:</strong> {vaccine.batchNumber}</span>
                                    <span><strong>Verified:</strong> {vaccine.isVerified ? '✅ Yes' : '❌ No'}</span>
                                    {vaccine.qrCodeHash && (
                                      <span className="qr-available">🔍 QR: {vaccine.qrCodeHash}</span>
                                    )}
                                    {vaccine.nextDueDate && (
                                      <span><strong>Next Due:</strong> {this.formatDate(vaccine.nextDueDate)}</span>
                                    )}
                                  </div>
                                </div>
                              ))}
                            </div>
                          ) : (
                            <p>No vaccinations recorded yet.</p>
                          )}
                        </div>

                        {/* Blockchain Verification Footer */}
                        <div className="verification-footer">
                          <h4>🔗 Blockchain Verification Details</h4>
                          <div className="blockchain-details">
                            <p><strong>Verified at:</strong> {this.formatDate(new Date(verificationResult.verifiedAt))}</p>
                            <p><strong>Contract Address:</strong> {this.props.contract?.address}</p>
                            <p><strong>Parent Wallet:</strong> {verificationResult.child.parentAddress}</p>
                            <p><strong>Hospital Wallet:</strong> {verificationResult.child.hospitalAddress}</p>
                          </div>
                          
                          <div className="qr-data-section">
                            <h5>📱 QR Verification Data:</h5>
                            <div className="qr-data">{verificationResult.qrData}</div>
                          </div>
                        </div>
                      </div>
                    </div>
                  )}
                </div>
              )}

              {/* Information */}
              <div className="verification-info">
                <h3>How Blockchain Verification Works</h3>
                <div className="info-grid">
                  <div className="info-card">
                    <h4>🔒 Immutable</h4>
                    <p>All records are permanently stored on the blockchain and cannot be altered</p>
                  </div>
                  <div className="info-card">
                    <h4>⚡ Real-time</h4>
                    <p>Verification happens in real-time directly from the blockchain</p>
                  </div>
                  <div className="info-card">
                    <h4>🌐 Decentralized</h4>
                    <p>No central authority controls the data - it's distributed across the network</p>
                  </div>
                  <div className="info-card">
                    <h4>🔍 Transparent</h4>
                    <p>All verification data can be independently verified on the blockchain</p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default QRVerification;
