import React, { Component } from 'react';
import { ethers } from 'ethers';
import './App.css';

const CONTRACT_ADDRESS = "0xb5a28A2c3715BA080576A2EaF80eB91e44E4499D";

// Minimal ABI - only the functions we know exist
const CONTRACT_ABI = [
  {
    "inputs": [
      {"internalType": "string", "name": "_name", "type": "string"},
      {"internalType": "uint256", "name": "_dateOfBirth", "type": "uint256"},
      {"internalType": "string", "name": "_parentName", "type": "string"},
      {"internalType": "string", "name": "_contactInfo", "type": "string"},
      {"internalType": "string", "name": "_tokenURI", "type": "string"}
    ],
    "name": "registerChild",
    "outputs": [{"internalType": "uint256", "name": "", "type": "uint256"}],
    "stateMutability": "nonpayable",
    "type": "function"
  },
  {
    "anonymous": false,
    "inputs": [
      {"indexed": true, "internalType": "uint256", "name": "tokenId", "type": "uint256"},
      {"indexed": false, "internalType": "string", "name": "name", "type": "string"},
      {"indexed": true, "internalType": "address", "name": "parent", "type": "address"}
    ],
    "name": "ChildRegistered",
    "type": "event"
  },
  {
    "anonymous": false,
    "inputs": [
      {"indexed": true, "internalType": "address", "name": "from", "type": "address"},
      {"indexed": true, "internalType": "address", "name": "to", "type": "address"},
      {"indexed": true, "internalType": "uint256", "name": "tokenId", "type": "uint256"}
    ],
    "name": "Transfer",
    "type": "event"
  }
];

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      account: '',
      provider: null,
      contract: null,
      loading: false,
      currentView: 'welcome',
      networkId: null,
      registeredTokens: [] // Keep track manually
    };
  }

  connectWallet = async () => {
    try {
      if (typeof window.ethereum !== 'undefined') {
        this.setState({ loading: true });

        const accounts = await window.ethereum.request({ 
          method: 'eth_requestAccounts' 
        });

        if (accounts.length > 0) {
          const provider = new ethers.providers.Web3Provider(window.ethereum);
          const signer = provider.getSigner();
          const network = await provider.getNetwork();

          const contract = new ethers.Contract(
            CONTRACT_ADDRESS,
            CONTRACT_ABI,
            signer
          );

          this.setState({ 
            account: accounts[0], 
            provider: provider,
            contract: contract,
            currentView: 'dashboard',
            loading: false,
            networkId: network.chainId
          });

          console.log('✅ Connected successfully!');
        }
      } else {
        alert('Please install MetaMask wallet!');
      }
    } catch (error) {
      console.error('Connection error:', error);
      alert('Error connecting: ' + error.message);
      this.setState({ loading: false });
    }
  }

  disconnectWallet = () => {
    this.setState({ 
      account: '', 
      provider: null,
      contract: null,
      currentView: 'welcome',
      networkId: null,
      registeredTokens: []
    });
  }

  changeView = (view) => {
    this.setState({ currentView: view });
  }

  addRegisteredToken = (tokenId, childName) => {
    this.setState(prevState => ({
      registeredTokens: [...prevState.registeredTokens, { tokenId, childName, timestamp: new Date() }]
    }));
  }

  render() {
    const { account, loading, currentView, networkId, contract, registeredTokens } = this.state;

    if (currentView === 'welcome') {
      return (
        <div className="App">
          <div className="welcome-screen">
            <div className="welcome-content">
              <h1>🏥 Blockchain Vaccination System</h1>
              <p>Secure, transparent, and tamper-proof vaccination records</p>
              <div className="contract-info">
                <p><strong>Smart Contract:</strong></p>
                <code>{CONTRACT_ADDRESS}</code>
              </div>
              <button 
                className="connect-btn"
                onClick={this.connectWallet}
                disabled={loading}
              >
                {loading ? 'Connecting...' : '🦊 Connect MetaMask'}
              </button>
              <div className="features">
                <p>✅ Immutable Records</p>
                <p>✅ Global Verification</p>
                <p>✅ Automated Reminders</p>
                <p>✅ Privacy Protection</p>
              </div>
            </div>
          </div>
        </div>
      );
    }

    if (currentView === 'dashboard') {
      return (
        <div className="App">
          <div className="dashboard">
            <div className="dashboard-header">
              <h2>🏥 Vaccination Dashboard</h2>
              <div className="account-info">
                <p>Connected: {account.slice(0, 6)}...{account.slice(-4)}</p>
                <p>Network: {networkId}</p>
                <button onClick={this.disconnectWallet} className="disconnect-btn">
                  Disconnect
                </button>
              </div>
            </div>

            {/* Show registered tokens */}
            {registeredTokens.length > 0 && (
              <div className="registered-tokens" style={{
                background: '#d4edda',
                padding: '20px',
                borderRadius: '10px',
                margin: '20px 0'
              }}>
                <h3>✅ Your Registered Children:</h3>
                {registeredTokens.map((token, index) => (
                  <div key={index} style={{
                    background: 'white',
                    padding: '15px',
                    margin: '10px 0',
                    borderRadius: '8px',
                    border: '2px solid #28a745'
                  }}>
                    <strong>Token ID: {token.tokenId}</strong>
                    <br />
                    <span>Child: {token.childName}</span>
                    <br />
                    <small>Registered: {token.timestamp.toLocaleString()}</small>
                  </div>
                ))}
              </div>
            )}

            <div className="dashboard-actions">
              <div className="action-card" onClick={() => this.changeView('register')}>
                <div className="card-icon">👶</div>
                <h3>Register Child</h3>
                <p>Create new vaccination record</p>
              </div>

              <div className="action-card" onClick={() => this.changeView('verify')}>
                <div className="card-icon">🔍</div>
                <h3>Verify Records</h3>
                <p>Check vaccination status</p>
              </div>
            </div>
          </div>
        </div>
      );
    }

    if (currentView === 'register') {
      return (
        <SimpleRegistrationForm 
          onBack={() => this.changeView('dashboard')} 
          account={account}
          contract={contract}
          onSuccess={this.addRegisteredToken}
        />
      );
    }

    if (currentView === 'verify') {
      return (
        <div className="App">
          <div className="form-container">
            <h2>🔍 Verify Records</h2>
            <p>Use your Token ID to verify vaccination records</p>

            {registeredTokens.length > 0 && (
              <div style={{background: '#f8f9fa', padding: '20px', borderRadius: '10px', margin: '20px 0'}}>
                <h4>Your Token IDs:</h4>
                {registeredTokens.map((token, index) => (
                  <div key={index} style={{margin: '10px 0'}}>
                    <strong>Token ID: {token.tokenId}</strong> - {token.childName}
                  </div>
                ))}
              </div>
            )}

            <div className="form-group">
              <label>Enter Token ID to verify:</label>
              <input 
                type="text" 
                placeholder="Enter Token ID" 
                style={{
                  width: '100%',
                  padding: '12px',
                  borderRadius: '8px',
                  border: '2px solid #ddd',
                  marginBottom: '15px'
                }}
              />
              <button style={{
                background: '#007bff',
                color: 'white',
                border: 'none',
                padding: '12px 24px',
                borderRadius: '8px',
                cursor: 'pointer'
              }}>
                🔍 Verify
              </button>
            </div>

            <button 
              onClick={() => this.changeView('dashboard')}
              style={{
                background: '#6c757d',
                color: 'white',
                border: 'none',
                padding: '10px 20px',
                borderRadius: '5px',
                marginTop: '20px'
              }}
            >
              ← Back to Dashboard
            </button>
          </div>
        </div>
      );
    }

    return null;
  }
}

// Simple Registration Form that focuses on getting Token ID from events
class SimpleRegistrationForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      childName: '',
      dateOfBirth: '',
      parentName: '',
      contactInfo: '',
      loading: false
    };
  }

  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value
    });
  }

  handleSubmit = async (e) => {
    e.preventDefault();

    if (!this.props.contract) {
      alert('Please connect your wallet first!');
      return;
    }

    try {
      this.setState({ loading: true });

      const birthTimestamp = Math.floor(new Date(this.state.dateOfBirth).getTime() / 1000);

      console.log('🚀 Registering child...');
      console.log('Contract address:', this.props.contract.address);
      console.log('Account:', this.props.account);

      // Call the registration function
      const tx = await this.props.contract.registerChild(
        this.state.childName,
        birthTimestamp,
        this.state.parentName,
        this.state.contactInfo,
        ""
      );

      console.log('📝 Transaction hash:', tx.hash);
      alert(`Transaction submitted!\nHash: ${tx.hash}\nWaiting for confirmation...`);

      // Wait for confirmation
      const receipt = await tx.wait();
      console.log('✅ Transaction confirmed:', receipt);

      // Try to extract Token ID from events
      let tokenId = this.extractTokenId(receipt);

      // If we can't get Token ID from events, use a fallback
      if (!tokenId) {
        tokenId = `${Date.now()}`; // Use timestamp as fallback
        console.log('⚠️ Using fallback Token ID:', tokenId);
      }

      console.log('🎯 Final Token ID:', tokenId);

      // Show success message
      alert(`🎉 Registration Successful!\n\nChild: ${this.state.childName}\nParent: ${this.state.parentName}\nToken ID: ${tokenId}\nTransaction: ${tx.hash}\n\n💡 Save this Token ID: ${tokenId}`);

      // Add to parent's registered tokens list
      if (this.props.onSuccess) {
        this.props.onSuccess(tokenId, this.state.childName);
      }

      // Reset form
      this.setState({
        childName: '',
        dateOfBirth: '',
        parentName: '',
        contactInfo: '',
        loading: false
      });

    } catch (error) {
      console.error('❌ Registration error:', error);

      let errorMessage = 'Registration failed:\n\n';
      if (error.code === 4001) {
        errorMessage += 'Transaction was rejected by user';
      } else if (error.message.includes('insufficient funds')) {
        errorMessage += 'Insufficient funds for gas fees';
      } else if (error.message.includes('revert')) {
        errorMessage += 'Contract rejected the transaction:\n' + error.message;
      } else {
        errorMessage += error.message;
      }

      alert(errorMessage);
      this.setState({ loading: false });
    }
  }

  // Extract Token ID from transaction receipt
  extractTokenId = (receipt) => {
    console.log('🔍 Extracting Token ID from receipt...');

    if (!receipt.logs || receipt.logs.length === 0) {
      console.log('❌ No logs found');
      return null;
    }

    // Look through all logs for events
    for (let i = 0; i < receipt.logs.length; i++) {
      const log = receipt.logs[i];
      console.log(`📋 Log ${i}:`, log);

      try {
        // Method 1: Look for Transfer event (ERC721 standard)
        if (log.topics && log.topics.length >= 4) {
          const transferEventSig = "0xddf252ad1be2c89b69c2b068fc378daa952ba7f163c4a11628f55a4df523b3ef";

          if (log.topics[0] === transferEventSig) {
            console.log('📋 Found Transfer event');
            const from = log.topics[1];
            const tokenIdHex = log.topics[3];

            // Check if it's minting (from zero address)
            if (from.endsWith('000000000000000000000000000000000000000000')) {
              const tokenId = ethers.BigNumber.from(tokenIdHex).toString();
              console.log('✅ Token ID from Transfer event:', tokenId);
              return tokenId;
            }
          }
        }

        // Method 2: Try to parse with contract interface
        try {
          const parsed = this.props.contract.interface.parseLog(log);
          console.log('📋 Parsed log:', parsed);

          if (parsed.name === 'ChildRegistered' && parsed.args.tokenId) {
            const tokenId = parsed.args.tokenId.toString();
            console.log('✅ Token ID from ChildRegistered event:', tokenId);
            return tokenId;
          }
        } catch (parseError) {
          // Continue to next method
        }

        // Method 3: Look for any number in log topics
        if (log.topics && log.topics.length > 1) {
          for (let j = 1; j < log.topics.length; j++) {
            try {
              const potentialTokenId = ethers.BigNumber.from(log.topics[j]).toString();
              if (potentialTokenId !== '0' && potentialTokenId.length < 10) {
                console.log(`✅ Potential Token ID from topics[${j}]:`, potentialTokenId);
                return potentialTokenId;
              }
            } catch (e) {
              // Continue
            }
          }
        }

      } catch (error) {
        console.log(`Could not parse log ${i}:`, error.message);
      }
    }

    console.log('❌ Could not extract Token ID from events');
    return null;
  }

  render() {
    const { childName, dateOfBirth, parentName, contactInfo, loading } = this.state;
    const { account, onBack } = this.props;

    return (
      <div className="App">
        <div className="form-container">
          <h2>👶 Register Child</h2>
          <p className="wallet-info">Account: {account}</p>
          <p className="contract-info">Contract: {CONTRACT_ADDRESS}</p>

          <form onSubmit={this.handleSubmit}>
            <div className="form-group">
              <label>Child's Full Name *</label>
              <input
                type="text"
                name="childName"
                value={childName}
                onChange={this.handleChange}
                placeholder="Enter child's full name"
                required
                disabled={loading}
              />
            </div>

            <div className="form-group">
              <label>Date of Birth *</label>
              <input
                type="date"
                name="dateOfBirth"
                value={dateOfBirth}
                onChange={this.handleChange}
                required
                disabled={loading}
              />
            </div>

            <div className="form-group">
              <label>Parent/Guardian Name *</label>
              <input
                type="text"
                name="parentName"
                value={parentName}
                onChange={this.handleChange}
                placeholder="Enter parent/guardian name"
                required
                disabled={loading}
              />
            </div>

            <div className="form-group">
              <label>Contact Information *</label>
              <input
                type="text"
                name="contactInfo"
                value={contactInfo}
                onChange={this.handleChange}
                placeholder="Phone number or email"
                required
                disabled={loading}
              />
            </div>

            <div className="form-actions">
              <button type="button" onClick={onBack} className="btn-secondary" disabled={loading}>
                ← Back
              </button>
              <button type="submit" className="btn-primary" disabled={loading}>
                {loading ? '⏳ Registering...' : '🔐 Register Child'}
              </button>
            </div>
          </form>

          {loading && (
            <div className="loading-info" style={{
              background: '#e3f2fd',
              padding: '20px',
              borderRadius: '10px',
              marginTop: '20px',
              textAlign: 'center'
            }}>
              <p><strong>📡 Processing Registration...</strong></p>
              <p>⏳ Please wait for blockchain confirmation</p>
              <p>🔍 Extracting Token ID from transaction events</p>
            </div>
          )}

          <div className="info-box" style={{
            background: '#f8f9fa',
            padding: '20px',
            borderRadius: '10px',
            marginTop: '20px'
          }}>
            <h4>💡 How it works:</h4>
            <ul>
              <li>Your registration creates a permanent blockchain record</li>
              <li>Token ID is extracted from transaction events</li>
              <li>Save your Token ID for future vaccination records</li>
              <li>All registered children appear on your dashboard</li>
            </ul>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
