import React, { Component } from 'react';

class ParentDashboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      walletAddress: '',
      children: [],
      loading: false,
      selectedChild: null,
      showDetails: false,
      pendingReminders: [],
      upcomingVaccinations: []
    };
  }

  componentDidMount() {
    // Auto-detect wallet or prompt for address
    this.detectWallet();
  }

  // Detect MetaMask wallet address
  detectWallet = async () => {
    if (typeof window.ethereum !== 'undefined') {
      try {
        const accounts = await window.ethereum.request({ method: 'eth_accounts' });
        if (accounts.length > 0) {
          this.setState({ walletAddress: accounts[0] });
          this.loadChildrenData(accounts[0]);
        }
      } catch (error) {
        console.error('Error detecting wallet:', error);
      }
    }
  };

  // Load children data for wallet address
  loadChildrenData = async (address) => {
    this.setState({ loading: true });

    try {
      // Get child token IDs for this parent
      const parentKey = `parent_${address}`;
      const childTokenIds = JSON.parse(localStorage.getItem(parentKey) || '[]');
      
      if (childTokenIds.length === 0) {
        this.setState({ loading: false });
        return;
      }

      // Load complete data for each child
      const allChildren = JSON.parse(localStorage.getItem('registeredChildren') || '[]');
      const parentChildren = [];

      for (const tokenId of childTokenIds) {
        const child = allChildren.find(c => c.tokenId === tokenId);
        if (child) {
          // Load vaccination history
          const vaccinations = JSON.parse(localStorage.getItem(`vaccinations_${tokenId}`) || '[]');
          
          // Load vaccination schedule
          const schedule = JSON.parse(localStorage.getItem(`schedule_${tokenId}`) || []);
          
          // Load reminders
          const reminders = JSON.parse(localStorage.getItem(`reminders_${tokenId}`) || []);
          
          // Calculate upcoming vaccinations
          const now = new Date();
          const upcoming = schedule.filter(vax => {
            const dueDate = new Date(vax.dueDate);
            const isCompleted = vaccinations.some(completed => completed.vaccineName === vax.vaccine);
            return !isCompleted && dueDate > now;
          }).slice(0, 3);

          // Calculate overdue vaccinations
          const overdue = schedule.filter(vax => {
            const dueDate = new Date(vax.dueDate);
            const isCompleted = vaccinations.some(completed => completed.vaccineName === vax.vaccine);
            return !isCompleted && dueDate <= now;
          });

          parentChildren.push({
            ...child,
            vaccinationHistory: vaccinations,
            vaccinationSchedule: schedule,
            upcomingVaccinations: upcoming,
            overdueVaccinations: overdue,
            completedVaccinations: vaccinations.length,
            totalScheduled: schedule.length,
            reminders: reminders
          });
        }
      }

      // Calculate overall pending reminders
      const allReminders = parentChildren.flatMap(child => 
        child.reminders.filter(reminder => !reminder.sent && new Date(reminder.reminderDate) <= new Date())
      );

      this.setState({
        children: parentChildren,
        pendingReminders: allReminders,
        loading: false
      });

    } catch (error) {
      console.error('Error loading children data:', error);
      this.setState({ loading: false });
    }
  };

  // Handle wallet address input
  handleWalletChange = (e) => {
    this.setState({ walletAddress: e.target.value });
  };

  // Connect wallet
  connectWallet = async () => {
    if (typeof window.ethereum !== 'undefined') {
      try {
        const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
        this.setState({ walletAddress: accounts[0] });
        this.loadChildrenData(accounts[0]);
      } catch (error) {
        console.error('Error connecting wallet:', error);
        alert('Error connecting wallet. Please try again.');
      }
    } else {
      alert('MetaMask not detected. Please enter your wallet address manually.');
    }
  };

  // Search by wallet address
  searchByWallet = () => {
    if (!this.state.walletAddress) {
      alert('Please enter a wallet address');
      return;
    }
    this.loadChildrenData(this.state.walletAddress);
  };

  // Show child details
  showChildDetails = (child) => {
    this.setState({
      selectedChild: child,
      showDetails: true
    });
  };

  // Calculate vaccination status
  getVaccinationStatus = (child) => {
    const completed = child.completedVaccinations;
    const total = child.totalScheduled;
    const overdue = child.overdueVaccinations.length;
    
    if (overdue > 0) return { status: 'overdue', text: `${overdue} Overdue`, color: '#dc3545' };
    if (completed === total) return { status: 'complete', text: 'Up to Date', color: '#28a745' };
    if (completed > 0) return { status: 'partial', text: 'In Progress', color: '#ffc107' };
    return { status: 'pending', text: 'Not Started', color: '#6c757d' };
  };

  // Calculate child age
  getChildAge = (dateOfBirth) => {
    const birthDate = new Date(dateOfBirth);
    const ageInDays = Math.floor((Date.now() - birthDate.getTime()) / (24 * 60 * 60 * 1000));
    if (ageInDays < 30) return `${ageInDays} days`;
    if (ageInDays < 365) return `${Math.floor(ageInDays/30)} months`;
    return `${Math.floor(ageInDays/365)} years`;
  };

  // Format date
  formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString();
  };

  // Render wallet connection
  renderWalletConnection() {
    const { walletAddress, loading } = this.state;

    return (
      <div className="wallet-connection">
        <h3>üîó Connect Your Wallet</h3>
        <p>Enter your wallet address to view your children's vaccination records</p>
        
        <div className="wallet-input-group">
          <input
            type="text"
            value={walletAddress}
            onChange={this.handleWalletChange}
            placeholder="0x... Your wallet address"
            className="wallet-input"
          />
          <button onClick={this.connectWallet} className="btn-primary">
            ü¶ä Connect MetaMask
          </button>
          <button onClick={this.searchByWallet} className="btn-secondary" disabled={loading}>
            {loading ? '‚è≥ Loading...' : 'üîç Search'}
          </button>
        </div>
        
        <div className="wallet-help">
          <small>üí° This is the wallet address where your children's NFTs were sent</small>
        </div>
      </div>
    );
  }

  // Render children overview
  renderChildrenOverview() {
    const { children, pendingReminders } = this.state;

    if (children.length === 0) {
      return (
        <div className="no-children">
          <div className="empty-icon">üë∂</div>
          <h3>No Children Found</h3>
          <p>No vaccination records found for this wallet address.</p>
          <div className="help-info">
            <p>Make sure you're using the correct wallet address that was provided during hospital registration.</p>
          </div>
        </div>
      );
    }

    return (
      <div className="children-overview">
        {/* Summary Cards */}
        <div className="summary-cards">
          <div className="summary-card">
            <div className="card-icon">üë∂</div>
            <div className="card-content">
              <h4>Registered Children</h4>
              <span className="stat-number">{children.length}</span>
            </div>
          </div>
          
          <div className="summary-card">
            <div className="card-icon">üíâ</div>
            <div className="card-content">
              <h4>Total Vaccinations</h4>
              <span className="stat-number">{children.reduce((sum, child) => sum + child.completedVaccinations, 0)}</span>
            </div>
          </div>
          
          <div className="summary-card">
            <div className="card-icon">‚è∞</div>
            <div className="card-content">
              <h4>Upcoming Due</h4>
              <span className="stat-number">{children.reduce((sum, child) => sum + child.upcomingVaccinations.length, 0)}</span>
            </div>
          </div>
          
          <div className="summary-card overdue">
            <div className="card-icon">üö®</div>
            <div className="card-content">
              <h4>Overdue</h4>
              <span className="stat-number">{children.reduce((sum, child) => sum + child.overdueVaccinations.length, 0)}</span>
            </div>
          </div>
        </div>

        {/* Pending Reminders */}
        {pendingReminders.length > 0 && (
          <div className="pending-reminders">
            <h4>üîî Pending Vaccination Reminders</h4>
            <div className="reminders-list">
              {pendingReminders.map((reminder, index) => {
                const child = children.find(c => c.tokenId === reminder.tokenId);
                return (
                  <div key={index} className="reminder-item">
                    <div className="reminder-icon">‚è∞</div>
                    <div className="reminder-content">
                      <strong>{child?.childName} - {reminder.vaccine}</strong>
                      <p>Due: {this.formatDate(reminder.dueDate)}</p>
                    </div>
                    <button className="reminder-action">Schedule</button>
                  </div>
                );
              })}
            </div>
          </div>
        )}

        {/* Children Cards */}
        <div className="children-grid">
          {children.map((child, index) => {
            const status = this.getVaccinationStatus(child);
            return (
              <div key={index} className="child-card" onClick={() => this.showChildDetails(child)}>
                <div className="child-header">
                  <div className="child-basic-info">
                    <h3>{child.childName}</h3>
                    <p>Age: {this.getChildAge(child.dateOfBirth)}</p>
                    <p>Token ID: {child.tokenId}</p>
                  </div>
                  <div className="vaccination-status" style={{ background: status.color }}>
                    {status.text}
                  </div>
                </div>

                <div className="child-stats">
                  <div className="stat">
                    <label>Completed</label>
                    <span>{child.completedVaccinations}/{child.totalScheduled}</span>
                  </div>
                  <div className="stat">
                    <label>Upcoming</label>
                    <span>{child.upcomingVaccinations.length}</span>
                  </div>
                  <div className="stat">
                    <label>Overdue</label>
                    <span>{child.overdueVaccinations.length}</span>
                  </div>
                </div>

                {/* Next Vaccination */}
                {child.upcomingVaccinations.length > 0 && (
                  <div className="next-vaccination">
                    <h5>Next: {child.upcomingVaccinations[0].vaccine}</h5>
                    <p>Due: {this.formatDate(child.upcomingVaccinations[0].dueDate)}</p>
                  </div>
                )}

                {/* Overdue Alert */}
                {child.overdueVaccinations.length > 0 && (
                  <div className="overdue-alert">
                    <span>üö® {child.overdueVaccinations.length} vaccination(s) overdue</span>
                  </div>
                )}

                <div className="child-actions">
                  <button className="view-details-btn">View Details</button>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    );
  }

  // Render child details modal
  renderChildDetails() {
    const { selectedChild } = this.state;
    if (!selectedChild) return null;

    return (
      <div className="child-details-modal">
        <div className="modal-content">
          <div className="modal-header">
            <h2>üë∂ {selectedChild.childName} - Vaccination Details</h2>
            <button 
              onClick={() => this.setState({ showDetails: false, selectedChild: null })}
              className="close-btn"
            >
              √ó
            </button>
          </div>

          <div className="modal-body">
            {/* Child Information */}
            <div className="detail-section">
              <h4>üìã Child Information</h4>
              <div className="detail-grid">
                <div className="detail-item">
                  <label>Full Name:</label>
                  <span>{selectedChild.childName}</span>
                </div>
                <div className="detail-item">
                  <label>Date of Birth:</label>
                  <span>{this.formatDate(selectedChild.dateOfBirth)}</span>
                </div>
                <div className="detail-item">
                  <label>Age:</label>
                  <span>{this.getChildAge(selectedChild.dateOfBirth)}</span>
                </div>
                <div className="detail-item">
                  <label>Token ID:</label>
                  <span>{selectedChild.tokenId}</span>
                </div>
                <div className="detail-item">
                  <label>Registered By:</label>
                  <span>{selectedChild.registeredByHospital}</span>
                </div>
                <div className="detail-item">
                  <label>Registration Date:</label>
                  <span>{this.formatDate(selectedChild.registrationDate)}</span>
                </div>
              </div>
            </div>

            {/* Vaccination History */}
            <div className="detail-section">
              <h4>üíâ Vaccination History ({selectedChild.vaccinationHistory.length})</h4>
              {selectedChild.vaccinationHistory.length > 0 ? (
                <div className="vaccination-list">
                  {selectedChild.vaccinationHistory.map((vaccine, index) => (
                    <div key={index} className="vaccination-item">
                      <div className="vaccination-header">
                        <strong>{vaccine.vaccineName}</strong>
                        <span className="vaccination-date">{this.formatDate(vaccine.dateAdministered)}</span>
                      </div>
                      <div className="vaccination-info">
                        <p><strong>Hospital:</strong> {vaccine.hospitalName}</p>
                        <p><strong>Batch:</strong> {vaccine.batchNumber}</p>
                        {vaccine.qrCodeHash && (
                          <p><strong>QR Code:</strong> {vaccine.qrCodeHash}</p>
                        )}
                        {vaccine.notes && (
                          <p><strong>Notes:</strong> {vaccine.notes}</p>
                        )}
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <p className="no-vaccinations">No vaccinations recorded yet.</p>
              )}
            </div>

            {/* Upcoming Vaccinations */}
            <div className="detail-section">
              <h4>üìÖ Upcoming Vaccinations ({selectedChild.upcomingVaccinations.length})</h4>
              {selectedChild.upcomingVaccinations.length > 0 ? (
                <div className="upcoming-list">
                  {selectedChild.upcomingVaccinations.map((vaccine, index) => (
                    <div key={index} className="upcoming-item">
                      <div className="upcoming-info">
                        <strong>{vaccine.vaccine}</strong>
                        <span className="due-date">Due: {this.formatDate(vaccine.dueDate)}</span>
                      </div>
                      <div className="vaccine-description">
                        <p>{vaccine.description}</p>
                        <p><strong>Age Group:</strong> {vaccine.ageGroup}</p>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <p className="no-upcoming">All scheduled vaccinations completed! üéâ</p>
              )}
            </div>

            {/* Overdue Vaccinations */}
            {selectedChild.overdueVaccinations.length > 0 && (
              <div className="detail-section overdue-section">
                <h4>üö® Overdue Vaccinations ({selectedChild.overdueVaccinations.length})</h4>
                <div className="overdue-list">
                  {selectedChild.overdueVaccinations.map((vaccine, index) => (
                    <div key={index} className="overdue-item">
                      <div className="overdue-info">
                        <strong>{vaccine.vaccine}</strong>
                        <span className="overdue-date">Was due: {this.formatDate(vaccine.dueDate)}</span>
                      </div>
                      <div className="overdue-action">
                        <button className="urgent-btn">Schedule Now</button>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>

          <div className="modal-footer">
            <button 
              onClick={() => this.setState({ showDetails: false, selectedChild: null })}
              className="btn-secondary"
            >
              Close
            </button>
            <button className="btn-primary">
              üì± Download Report
            </button>
          </div>
        </div>
      </div>
    );
  }

  render() {
    const { walletAddress, children, loading, showDetails } = this.state;

    return (
      <div className="App">
        <div className="main-content">
          <div className="parent-dashboard">
            <div className="dashboard-header">
              <h1>üë®‚Äçüë©‚Äçüëß‚Äçüë¶ Parent Dashboard</h1>
              <p>View and manage your children's vaccination records</p>
              {walletAddress && (
                <div className="connected-wallet">
                  <span>üîó Connected: {walletAddress.slice(0, 6)}...{walletAddress.slice(-4)}</span>
                </div>
              )}
            </div>

            {!walletAddress && this.renderWalletConnection()}

            {walletAddress && !loading && this.renderChildrenOverview()}

            {loading && (
              <div className="loading-container">
                <div className="loading-spinner">‚è≥</div>
                <p>Loading your children's vaccination records...</p>
              </div>
            )}

            {showDetails && this.renderChildDetails()}

            <button onClick={this.props.onBack} className="btn-secondary">
              ‚Üê Back to Dashboard
            </button>
          </div>
        </div>
      </div>
    );
  }
}

export default ParentDashboard;
