import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import { create as ipfsHttpClient } from 'ipfs-http-client';
import QRCode from 'qrcode.react';

// IPFS client setup
const ipfs = ipfsHttpClient({
  host: 'ipfs.infura.io',
  port: 5001,
  protocol: 'https',
  headers: {
    authorization: `Basic ${btoa(
      `${process.env.REACT_APP_IPFS_PROJECT_ID}:${process.env.REACT_APP_IPFS_SECRET}`
    )}`,
  },
});

const HospitalChildRegistration = ({ contract, account, onSuccess }) => {
  const [formData, setFormData] = useState({
    childName: '',
    dateOfBirth: '',
    parentName: '',
    contactInfo: '',
    parentAddress: '', // NEW: Parent's wallet address
    emergencyContact: '',
    medicalHistory: '',
    allergies: ''
  });
  const [loading, setLoading] = useState(false);
  const [registeredChild, setRegisteredChild] = useState(null);
  const [profileImage, setProfileImage] = useState(null);
  const [step, setStep] = useState(1);
  const [isAuthorizedHospital, setIsAuthorizedHospital] = useState(false);
  const [hospitalInfo, setHospitalInfo] = useState(null);

  // Check if current account is an authorized hospital
  useEffect(() => {
    checkHospitalAuthorization();
  }, [account, contract]);

  const checkHospitalAuthorization = async () => {
    if (!contract || !account) return;
    
    try {
      const isAuthorized = await contract.authorizedHospitals(account);
      setIsAuthorizedHospital(isAuthorized);
      
      if (isAuthorized) {
        const hospitalData = await contract.getHospitalInfo(account);
        setHospitalInfo(hospitalData);
      }
    } catch (error) {
      console.error('Error checking hospital authorization:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setProfileImage(file);
    }
  };

  const validateParentAddress = (address) => {
    try {
      ethers.utils.getAddress(address);
      return true;
    } catch {
      return false;
    }
  };

  const uploadToIPFS = async (data) => {
    try {
      const result = await ipfs.add(JSON.stringify(data));
      return `https://ipfs.infura.io/ipfs/${result.path}`;
    } catch (error) {
      console.error('Error uploading to IPFS:', error);
      throw error;
    }
  };

  const uploadImageToIPFS = async (file) => {
    try {
      const result = await ipfs.add(file);
      return `https://ipfs.infura.io/ipfs/${result.path}`;
    } catch (error) {
      console.error('Error uploading image:', error);
      return '';
    }
  };

  const registerChild = async () => {
    if (!formData.childName || !formData.dateOfBirth || !formData.parentName || !formData.parentAddress) {
      alert('Please fill in all required fields');
      return;
    }

    if (!validateParentAddress(formData.parentAddress)) {
      alert('Please enter a valid parent wallet address');
      return;
    }

    try {
      setLoading(true);
      
      // Convert date to timestamp
      const birthTimestamp = Math.floor(new Date(formData.dateOfBirth).getTime() / 1000);

      // Prepare metadata for IPFS
      const metadata = {
        name: formData.childName,
        description: `Vaccination record for ${formData.childName}`,
        image: profileImage ? await uploadImageToIPFS(profileImage) : '',
        attributes: [
          {
            trait_type: "Parent Name",
            value: formData.parentName
          },
          {
            trait_type: "Parent Address",
            value: formData.parentAddress
          },
          {
            trait_type: "Date of Birth",
            value: formData.dateOfBirth
          },
          {
            trait_type: "Registration Date",
            value: new Date().toISOString()
          },
          {
            trait_type: "Registered By Hospital",
            value: hospitalInfo?.name || 'Unknown'
          }
        ],
        medical_info: {
          allergies: formData.allergies,
          medical_history: formData.medicalHistory,
          emergency_contact: formData.emergencyContact
        },
        hospital_info: {
          name: hospitalInfo?.name,
          license: hospitalInfo?.licenseNumber,
          address: account
        }
      };

      // Upload metadata to IPFS
      const metadataURI = await uploadToIPFS(metadata);

      // Register child on blockchain (NEW: Hospital registers, NFT goes to parent)
      const tx = await contract.registerChild(
        formData.childName,
        birthTimestamp,
        formData.parentName,
        formData.contactInfo,
        formData.parentAddress, // Parent's wallet address
        metadataURI
      );

      console.log('Transaction submitted:', tx.hash);
      
      // Wait for transaction confirmation
      const receipt = await tx.wait();
      console.log('Transaction confirmed:', receipt);

      // Get the token ID from the event
      const event = receipt.events?.find(e => e.event === 'ChildRegistered');
      const tokenId = event?.args?.tokenId;

      if (tokenId) {
        const childInfo = {
          tokenId: tokenId.toString(),
          name: formData.childName,
          parentName: formData.parentName,
          parentAddress: formData.parentAddress,
          hospitalName: hospitalInfo?.name,
          qrData: {
            tokenId: tokenId.toString(),
            contractAddress: contract.address,
            childName: formData.childName,
            parentAddress: formData.parentAddress,
            registeredBy: hospitalInfo?.name,
            verificationUrl: `${window.location.origin}/verify/${tokenId}`
          }
        };
        
        setRegisteredChild(childInfo);
        setStep(3);
      }

      setLoading(false);
    } catch (error) {
      console.error('Error registering child:', error);
      alert('Error registering child: ' + error.message);
      setLoading(false);
    }
  };

  // Show authorization error if not authorized hospital
  if (!isAuthorizedHospital) {
    return (
      <div className="authorization-error">
        <div className="error-container">
          <div className="error-icon">üè•</div>
          <h2>Hospital Authorization Required</h2>
          <p>Only authorized hospitals can register children in the vaccination system.</p>
          
          {hospitalInfo ? (
            <div className="hospital-status">
              <p><strong>Hospital:</strong> {hospitalInfo.name}</p>
              <p><strong>Status:</strong> Pending Authorization</p>
              <p>Please contact the system administrator to get your hospital authorized.</p>
            </div>
          ) : (
            <div className="registration-prompt">
              <p>Your hospital needs to be registered first.</p>
              <p>Please register your hospital using the Hospital Registration form.</p>
            </div>
          )}
        </div>
      </div>
    );
  }

  const renderStep1 = () => (
    <div className="registration-step">
      <div className="hospital-header">
        <h2>üè• Child Birth Registration</h2>
        <div className="hospital-info">
          <p><strong>Hospital:</strong> {hospitalInfo?.name}</p>
          <p><strong>License:</strong> {hospitalInfo?.licenseNumber}</p>
        </div>
      </div>
      
      <div className="form-group">
        <label>Child's Full Name *</label>
        <input
          type="text"
          name="childName"
          value={formData.childName}
          onChange={handleInputChange}
          placeholder="Enter child's full name"
          required
        />
      </div>

      <div className="form-group">
        <label>Date of Birth *</label>
        <input
          type="date"
          name="dateOfBirth"
          value={formData.dateOfBirth}
          onChange={handleInputChange}
          required
        />
      </div>

      <div className="form-group">
        <label>Parent/Guardian Name *</label>
        <input
          type="text"
          name="parentName"
          value={formData.parentName}
          onChange={handleInputChange}
          placeholder="Enter parent/guardian name"
          required
        />
      </div>

      <div className="form-group">
        <label>Parent's Wallet Address * 
          <span className="help-text">The NFT will be sent to this address</span>
        </label>
        <input
          type="text"
          name="parentAddress"
          value={formData.parentAddress}
          onChange={handleInputChange}
          placeholder="0x... (Parent's blockchain wallet address)"
          className={validateParentAddress(formData.parentAddress) || !formData.parentAddress ? '' : 'error'}
          required
        />
        {formData.parentAddress && !validateParentAddress(formData.parentAddress) && (
          <span className="input-error">Invalid wallet address format</span>
        )}
      </div>

      <div className="form-group">
        <label>Parent Contact Information *</label>
        <input
          type="tel"
          name="contactInfo"
          value={formData.contactInfo}
          onChange={handleInputChange}
          placeholder="Phone number or email"
          required
        />
      </div>

      <div className="form-group">
        <label>Child's Photo (Optional)</label>
        <input
          type="file"
          accept="image/*"
          onChange={handleImageUpload}
          className="file-input"
        />
        {profileImage && (
          <div className="image-preview">
            <img 
              src={URL.createObjectURL(profileImage)} 
              alt="Preview" 
              className="preview-img"
            />
          </div>
        )}
      </div>
    </div>
  );

  const renderStep2 = () => (
    <div className="registration-step">
      <h2>üè• Medical Information</h2>
      <div className="form-group">
        <label>Emergency Contact</label>
        <input
          type="tel"
          name="emergencyContact"
          value={formData.emergencyContact}
          onChange={handleInputChange}
          placeholder="Emergency contact number"
        />
      </div>

      <div className="form-group">
        <label>Known Allergies</label>
        <textarea
          name="allergies"
          value={formData.allergies}
          onChange={handleInputChange}
          placeholder="List any known allergies (food, medicine, etc.)"
          rows="3"
        />
      </div>

      <div className="form-group">
        <label>Medical History</label>
        <textarea
          name="medicalHistory"
          value={formData.medicalHistory}
          onChange={handleInputChange}
          placeholder="Any relevant medical history or conditions"
          rows="4"
        />
      </div>

      <div className="hospital-verification">
        <div className="verification-item">
          <input type="checkbox" id="identityVerified" required />
          <label htmlFor="identityVerified">
            I have verified the identity of the parent/guardian
          </label>
        </div>
        <div className="verification-item">
          <input type="checkbox" id="informationAccurate" required />
          <label htmlFor="informationAccurate">
            I confirm that all provided information is accurate
          </label>
        </div>
        <div className="verification-item">
          <input type="checkbox" id="hospitalAuthorization" required />
          <label htmlFor="hospitalAuthorization">
            I am authorized to register children on behalf of {hospitalInfo?.name}
          </label>
        </div>
      </div>
    </div>
  );

  const renderStep3 = () => (
    <div className="registration-success">
      <div className="success-icon">‚úÖ</div>
      <h2>Child Successfully Registered!</h2>
      <p>The child has been registered and the NFT has been sent to the parent's wallet.</p>
      
      <div className="child-info-card">
        <h3>{registeredChild.name}</h3>
        <p><strong>Token ID:</strong> {registeredChild.tokenId}</p>
        <p><strong>Parent:</strong> {registeredChild.parentName}</p>
        <p><strong>Parent Wallet:</strong> {registeredChild.parentAddress}</p>
        <p><strong>Registered by:</strong> {registeredChild.hospitalName}</p>
      </div>

      <div className="qr-section">
        <h4>üîç Child Verification QR Code</h4>
        <p>This QR code contains the child's blockchain verification data:</p>
        <div className="qr-container">
          <QRCode 
            value={JSON.stringify(registeredChild.qrData)}
            size={200}
            level="M"
          />
        </div>
        <div className="qr-actions">
          <button 
            onClick={() => {
              const canvas = document.querySelector('canvas');
              const url = canvas.toDataURL();
              const link = document.createElement('a');
              link.download = `${registeredChild.name}-verification-qr.png`;
              link.href = url;
              link.click();
            }}
            className="download-btn"
          >
            üì± Download QR Code
          </button>
        </div>
      </div>

      <div className="parent-instructions">
        <h4>üìã Instructions for Parents</h4>
        <div className="instructions-card">
          <ul>
            <li><strong>NFT Token ID:</strong> {registeredChild.tokenId} (saved to wallet: {registeredChild.parentAddress})</li>
            <li><strong>QR Code:</strong> Use for instant verification at healthcare facilities</li>
            <li><strong>Vaccination Reminders:</strong> Will be sent automatically based on age</li>
            <li><strong>Updates:</strong> All vaccinations will be recorded by authorized hospitals</li>
          </ul>
        </div>
      </div>

      <div className="next-steps">
        <h4>What Happens Next?</h4>
        <ul>
          <li>‚úÖ NFT automatically sent to parent's wallet</li>
          <li>üìÖ Vaccination reminders will be triggered automatically</li>
          <li>üè• Future vaccinations can be recorded by any authorized hospital</li>
          <li>üì± QR code enables instant verification</li>
        </ul>
      </div>

      <button onClick={onSuccess} className="continue-btn">
        Register Another Child
      </button>
    </div>
  );

  return (
    <div className="hospital-child-registration">
      <div className="registration-container">
        <div className="progress-bar">
          <div className={`step ${step >= 1 ? 'active' : ''}`}>
            <span>1</span>
            <label>Child Info</label>
          </div>
          <div className={`step ${step >= 2 ? 'active' : ''}`}>
            <span>2</span>
            <label>Medical</label>
          </div>
          <div className={`step ${step >= 3 ? 'active' : ''}`}>
            <span>3</span>
            <label>Complete</label>
          </div>
        </div>

        {step === 1 && renderStep1()}
        {step === 2 && renderStep2()}
        {step === 3 && renderStep3()}

        {step < 3 && (
          <div className="form-actions">
            {step > 1 && (
              <button 
                onClick={() => setStep(step - 1)}
                className="btn-secondary"
                disabled={loading}
              >
                ‚Üê Previous
              </button>
            )}
            
            {step < 2 && (
              <button 
                onClick={() => setStep(2)}
                className="btn-primary"
                disabled={
                  !formData.childName || 
                  !formData.dateOfBirth || 
                  !formData.parentName || 
                  !formData.parentAddress ||
                  !validateParentAddress(formData.parentAddress)
                }
              >
                Next ‚Üí
              </button>
            )}
            
            {step === 2 && (
              <button 
                onClick={registerChild}
                className="btn-primary"
                disabled={loading}
              >
                {loading ? '‚è≥ Registering Child...' : 'üîê Register on Blockchain'}
              </button>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default HospitalChildRegistration;
