import React, { Component } from 'react';

class ChildRegistration extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: '',
      dateOfBirth: '',
      parentName: '',
      contactInfo: '',
      parentAddress: '',
      loading: false,
      error: null,
      txHash: null,
      tokenId: null
    };
  }

  handleInputChange = (e) => {
    const { name, value } = e.target;
    this.setState({ [name]: value });
  };

  registerChild = async () => {
    const { name, dateOfBirth, parentName, contactInfo, parentAddress } = this.state;
    const { contract, sendTransaction } = this.props;

    if (!name || !dateOfBirth || !parentName || !contactInfo || !parentAddress) {
      this.setState({ error: 'Please fill in all fields' });
      return;
    }

    if (!contract) {
      this.setState({ error: 'Smart contract is not connected' });
      return;
    }

    try {
      this.setState({ loading: true, error: null });

      const dobTimestamp = Math.floor(new Date(dateOfBirth).getTime() / 1000);
      const dummyTokenURI = ''; // You can use IPFS if needed later

      const receipt = await sendTransaction(
        contract.registerChild,
        [name, dobTimestamp, parentName, contactInfo, parentAddress, dummyTokenURI]
      );

      const tokenId = receipt.events?.find(e => e.event === "ChildRegistered")?.args?.tokenId?.toString();

      this.setState({
        loading: false,
        txHash: receipt.transactionHash,
        tokenId,
        name: '',
        dateOfBirth: '',
        parentName: '',
        contactInfo: '',
        parentAddress: ''
      });

      alert(`üë∂ Child Registered!\nToken ID: ${tokenId}\nTx: ${receipt.transactionHash.slice(0, 10)}...`);
      this.props.onChangeView('dashboard');

    } catch (error) {
      console.error('Child registration failed:', error);

      let message = 'Registration failed.';
      if (error.message.includes('user rejected')) {
        message = 'Transaction cancelled by user.';
      }

      this.setState({
        loading: false,
        error: message
      });
    }
  };

  render() {
    const { name, dateOfBirth, parentName, contactInfo, parentAddress, loading, error } = this.state;

    return (
      <div className="child-registration">
        <div className="form-container">
          <h2>üë∂ Child Registration</h2>
          <p>Register a newborn child to create their vaccination NFT identity.</p>

          {error && (
            <div className="error-message">
              ‚ùå {error}
              <button onClick={() => this.setState({ error: null })}>‚úï</button>
            </div>
          )}

          <div className="form-group">
            <label>Child's Full Name *</label>
            <input
              type="text"
              name="name"
              value={name}
              onChange={this.handleInputChange}
              placeholder="e.g. Aarya Sharma"
              required
            />
          </div>

          <div className="form-group">
            <label>Date of Birth *</label>
            <input
              type="date"
              name="dateOfBirth"
              value={dateOfBirth}
              onChange={this.handleInputChange}
              required
              max={new Date().toISOString().split('T')[0]}
            />
          </div>

          <div className="form-group">
            <label>Parent's Name *</label>
            <input
              type="text"
              name="parentName"
              value={parentName}
              onChange={this.handleInputChange}
              placeholder="e.g. Anjali Sharma"
              required
            />
          </div>

          <div className="form-group">
            <label>Contact Info *</label>
            <input
              type="text"
              name="contactInfo"
              value={contactInfo}
              onChange={this.handleInputChange}
              placeholder="e.g. +91-9000000000 | email@example.com"
              required
            />
          </div>

          <div className="form-group">
            <label>Parent Wallet Address *</label>
            <input
              type="text"
              name="parentAddress"
              value={parentAddress}
              onChange={this.handleInputChange}
              placeholder="e.g. 0xABC123..."
              required
            />
          </div>

          <div className="form-actions">
            <button
              onClick={this.registerChild}
              disabled={loading || !name || !dateOfBirth || !parentName || !contactInfo || !parentAddress}
              className="btn-primary"
            >
              {loading ? '‚è≥ Registering...' : 'üë∂ Register Child'}
            </button>

            <button onClick={this.props.onBack} className="btn-secondary">
              ‚Üê Back to Dashboard
            </button>
          </div>

          <div className="info-box">
            <p>üìå The child will be minted as an NFT and linked to the parent‚Äôs wallet. Vaccinations can be recorded once hospital is authorized.</p>
          </div>
        </div>
      </div>
    );
  }
}

export default ChildRegistration;
