            {contractDeployed && !isAuthorized && <p style={{ color: '#f44336' }}>‚ö†Ô∏è Authorization required</p>}
          </div>
        </div>
      </div>
    );
  }

  renderParentActions() {
    return (
      <div className="role-actions">
        <h3>üë®‚Äçüë©‚Äçüëß‚Äçüë¶ Parent Actions</h3>
        <div className="dashboard-actions">
          <div 
            className="action-card parent-card"
            onClick={() => this.changeView('parent-dashboard')}
          >
            <div className="card-icon">üìã</div>
            <h3>View Children's Records</h3>
            <p>Access your children's vaccination records and schedules</p>
            <div className="card-features">
              <span>Vaccination history</span>
              <span>Upcoming reminders</span>
              <span>QR code access</span>
            </div>
          </div>
        </div>
      </div>
    );
  }

  renderVerifierActions() {
    return (
      <div className="role-actions">
        <h3>üîç Verifier Actions</h3>
        <div className="dashboard-actions">
          <div 
            className="action-card verifier-card"
            onClick={() => this.changeView('qr-verification')}
          >
            <div className="card-icon">üì±</div>
            <h3>Scan QR Code</h3>
            <p>Verify vaccination records and authenticity</p>
            <div className="card-features">
              <span>QR code scanning</span>
              <span>Blockchain verification</span>
              <span>Instant results</span>
            </div>
          </div>
        </div>
      </div>
    );
  }

  renderAdminActions() {
    return (
      <div className="role-actions">
        <h3>‚öôÔ∏è Admin Actions</h3>
        <div className="dashboard-actions">
          <div 
            className="action-card"
            onClick={() => this.changeView('admin-panel')}
          >
            <div className="card-icon">üëë</div>
            <h3>Admin Panel</h3>
            <p>Manage hospital authorizations and system settings</p>
            <div className="card-features">
              <span>Hospital management</span>
              <span>Authorization control</span>
              <span>System monitoring</span>
            </div>
          </div>
        </div>
      </div>
    );
  }

  render() {
    const { currentView, showAuthModal } = this.state;

    // Show auth modal
    if (showAuthModal) {
      return (
        <div>
          {this.renderWelcomeScreen()}
        </div>
      );
    }

    // Route to different views
    switch (currentView) {
      case 'welcome':
        return this.renderWelcomeScreen();

      case 'dashboard':
        return this.renderDashboard();

      case 'hospital-register':
        return (
          <HospitalRegistration 
            onBack={this.backToDashboard} 
            onChangeView={this.changeView}
            contract={this.state.contract}
            web3Provider={this.state.web3Provider}
            connectedWallet={this.state.connectedWallet}
            currentUser={this.state.currentUser}
            sendTransaction={this.sendTransaction}
            contractDeployed={this.state.contractDeployed}
          />
        );

      case 'child-register':
        return (
          <ChildRegistration 
            onBack={this.backToDashboard} 
            onChangeView={this.changeView}
            contract={this.state.contract}
            web3Provider={this.state.web3Provider}
            connectedWallet={this.state.connectedWallet}
            currentUser={this.state.currentUser}
            sendTransaction={this.sendTransaction}
            generateQRData={this.generateQRData}
            contractDeployed={this.state.contractDeployed}
          />
        );

      case 'vaccination-record':
        return (
          <VaccinationRecording 
            onBack={this.backToDashboard} 
            onChangeView={this.changeView}
            contract={this.state.contract}
            web3Provider={this.state.web3Provider}
            connectedWallet={this.state.connectedWallet}
            currentUser={this.state.currentUser}
            sendTransaction={this.sendTransaction}
            generateQRData={this.generateQRData}
            contractDeployed={this.state.contractDeployed}
          />
        );

      case 'parent-dashboard':
        return (
          <ParentDashboard 
            onBack={this.backToDashboard} 
            onChangeView={this.changeView}
            contract={this.state.contract}
            web3Provider={this.state.web3Provider}
            connectedWallet={this.state.connectedWallet}
            currentUser={this.state.currentUser}
            userBlockchainData={this.state.userBlockchainData}
            generateQRData={this.generateQRData}
            contractDeployed={this.state.contractDeployed}
          />
        );

      case 'qr-verification':
        return (
          <QRVerification 
            onBack={this.backToDashboard} 
            onChangeView={this.changeView}
            contract={this.state.contract}
            web3Provider={this.state.web3Provider}
            connectedWallet={this.state.connectedWallet}
            currentUser={this.state.currentUser}
            contractDeployed={this.state.contractDeployed}
          />
        );

      case 'admin-panel':
        return (
          <AdminPanel 
            onBack={this.backToDashboard} 
            contract={this.state.contract}
            account={this.state.connectedWallet}
            onSuccess={() => this.loadBlockchainData()}
          />
        );

      default:
        return this.renderWelcomeScreen();
    }
  }
}

export default App;
