import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import { create as ipfsHttpClient } from 'ipfs-http-client';
import QRCode from 'qrcode.react';

// IPFS client setup
const ipfs = ipfsHttpClient({
  host: 'ipfs.infura.io',
  port: 5001,
  protocol: 'https',
  headers: {
    authorization: `Basic ${btoa(
      `${process.env.REACT_APP_IPFS_PROJECT_ID}:${process.env.REACT_APP_IPFS_SECRET}`
    )}`,
  },
});

// Enhanced vaccine schedules with more detailed information
const VACCINE_SCHEDULE = {
  'BCG': { 
    minAge: 0, 
    nextDose: null, 
    description: 'Bacillus Calmette-GuÃ©rin',
    ageGroup: 'Birth',
    boosterNeeded: false
  },
  'Hepatitis B (Birth)': { 
    minAge: 0, 
    nextDose: 30, 
    description: 'Hepatitis B Vaccine - Birth Dose',
    ageGroup: 'Birth',
    boosterNeeded: true
  },
  'Hepatitis B (1st)': { 
    minAge: 30, 
    nextDose: 30, 
    description: 'Hepatitis B Vaccine - 1st Dose',
    ageGroup: '1 Month',
    boosterNeeded: true
  },
  'Hepatitis B (2nd)': { 
    minAge: 60, 
    nextDose: null, 
    description: 'Hepatitis B Vaccine - 2nd Dose',
    ageGroup: '2 Months',
    boosterNeeded: false
  },
  'DPT (1st)': { 
    minAge: 45, 
    nextDose: 30, 
    description: 'Diphtheria, Pertussis, Tetanus - 1st Dose',
    ageGroup: '6 Weeks',
    boosterNeeded: true
  },
  'DPT (2nd)': { 
    minAge: 75, 
    nextDose: 30, 
    description: 'Diphtheria, Pertussis, Tetanus - 2nd Dose',
    ageGroup: '10 Weeks',
    boosterNeeded: true
  },
  'DPT (3rd)': { 
    minAge: 105, 
    nextDose: 365, 
    description: 'Diphtheria, Pertussis, Tetanus - 3rd Dose',
    ageGroup: '14 Weeks',
    boosterNeeded: true
  },
  'Polio (OPV-1)': { 
    minAge: 45, 
    nextDose: 30, 
    description: 'Oral Polio Vaccine - 1st Dose',
    ageGroup: '6 Weeks',
    boosterNeeded: true
  },
  'Polio (OPV-2)': { 
    minAge: 75, 
    nextDose: 30, 
    description: 'Oral Polio Vaccine - 2nd Dose',
    ageGroup: '10 Weeks',
    boosterNeeded: true
  },
  'Polio (OPV-3)': { 
    minAge: 105, 
    nextDose: 365, 
    description: 'Oral Polio Vaccine - 3rd Dose',
    ageGroup: '14 Weeks',
    boosterNeeded: true
  },
  'MMR (1st)': { 
    minAge: 270, 
    nextDose: 365, 
    description: 'Measles, Mumps, Rubella - 1st Dose',
    ageGroup: '9 Months',
    boosterNeeded: true
  },
  'MMR (2nd)': { 
    minAge: 450, 
    nextDose: null, 
    description: 'Measles, Mumps, Rubella - 2nd Dose',
    ageGroup: '15 Months',
    boosterNeeded: false
  },
  'Rotavirus (1st)': { 
    minAge: 45, 
    nextDose: 30, 
    description: 'Rotavirus Vaccine - 1st Dose',
    ageGroup: '6 Weeks',
    boosterNeeded: true
  },
  'Rotavirus (2nd)': { 
    minAge: 75, 
    nextDose: 30, 
    description: 'Rotavirus Vaccine - 2nd Dose',
    ageGroup: '10 Weeks',
    boosterNeeded: true
  },
  'Rotavirus (3rd)': { 
    minAge: 105, 
    nextDose: null, 
    description: 'Rotavirus Vaccine - 3rd Dose',
    ageGroup: '14 Weeks',
    boosterNeeded: false
  }
};

const VaccinationRecord = ({ contract, account, onSuccess }) => {
  const [tokenId, setTokenId] = useState('');
  const [childInfo, setChildInfo] = useState(null);
  const [vaccineData, setVaccineData] = useState({
    vaccineName: '',
    batchNumber: '',
    notes: '',
    sideEffects: '',
    administeredBy: '',
    dosage: '',
    manufacturer: ''
  });
  const [loading, setLoading] = useState(false);
  const [searchLoading, setSearchLoading] = useState(false);
  const [vaccineImage, setVaccineImage] = useState(null);
  const [isAuthorized, setIsAuthorized] = useState(false);
  const [hospitalInfo, setHospitalInfo] = useState(null);
  const [recordedVaccination, setRecordedVaccination] = useState(null);
  const [showQRCode, setShowQRCode] = useState(false);

  // Check if current user is authorized hospital
  useEffect(() => {
    checkHospitalAuthorization();
  }, [contract, account]);

  const checkHospitalAuthorization = async () => {
    if (!contract || !account) return;
    
    try {
      const authorized = await contract.authorizedHospitals(account);
      setIsAuthorized(authorized);
      
      if (authorized) {
        const hospitalData = await contract.getHospitalInfo(account);
        setHospitalInfo(hospitalData);
      }
    } catch (error) {
      console.error('Error checking authorization:', error);
    }
  };

  const searchChild = async () => {
    if (!tokenId.trim()) {
      alert('Please enter a Token ID');
      return;
    }

    try {
      setSearchLoading(true);
      const child = await contract.getChildInfo(tokenId);
      const vaccinationHistory = await contract.getVaccinationHistory(tokenId);
      const upcomingVacs = await contract.getUpcomingVaccinations(tokenId);
      
      setChildInfo({
        ...child,
        tokenId,
        vaccinationHistory: vaccinationHistory || [],
        upcomingVaccinations: upcomingVacs
      });
      setSearchLoading(false);
    } catch (error) {
      console.error('Error fetching child info:', error);
      alert('Child not found or invalid Token ID');
      setSearchLoading(false);
      setChildInfo(null);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setVaccineData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setVaccineImage(file);
    }
  };

  const uploadToIPFS = async (data) => {
    try {
      const result = await ipfs.add(JSON.stringify(data));
      return result.path;
    } catch (error) {
      console.error('Error uploading to IPFS:', error);
      return '';
    }
  };

  const uploadImageToIPFS = async () => {
    if (!vaccineImage) return null;
    
    try {
      const result = await ipfs.add(vaccineImage);
      return `https://ipfs.infura.io/ipfs/${result.path}`;
    } catch (error) {
      console.error('Error uploading image:', error);
      return null;
    }
  };

  const calculateNextDueDate = () => {
    const vaccine = VACCINE_SCHEDULE[vaccineData.vaccineName];
    if (vaccine && vaccine.nextDose) {
      const now = new Date();
      const nextDate = new Date(now.getTime() + (vaccine.nextDose * 24 * 60 * 60 * 1000));
      return Math.floor(nextDate.getTime() / 1000);
    }
    return 0; // No next dose needed
  };

  const recordVaccination = async () => {
    if (!isAuthorized) {
      alert('You are not authorized to record vaccinations');
      return;
    }

    if (!vaccineData.vaccineName || !vaccineData.batchNumber) {
      alert('Please fill in all required fields');
      return;
    }

    try {
      setLoading(true);

      // Prepare enhanced vaccination document for IPFS
      const vaccinationDoc = {
        vaccine: vaccineData.vaccineName,
        hospital: hospitalInfo.name,
        hospitalLicense: hospitalInfo.licenseNumber,
        hospitalAddress: account,
        batch: vaccineData.batchNumber,
        dosage: vaccineData.dosage,
        manufacturer: vaccineData.manufacturer,
        notes: vaccineData.notes,
        sideEffects: vaccineData.sideEffects,
        administeredBy: vaccineData.administeredBy || hospitalInfo.name,
        timestamp: new Date().toISOString(),
        vaccinationDate: new Date().toISOString(),
        childTokenId: tokenId,
        image: vaccineImage ? await uploadImageToIPFS() : null,
        verificationHash: ethers.utils.keccak256(
          ethers.utils.toUtf8Bytes(`${tokenId}-${vaccineData.vaccineName}-${Date.now()}`)
        )
      };

      // Upload to IPFS
      const ipfsHash = await uploadToIPFS(vaccinationDoc);
      const nextDueDate = calculateNextDueDate();

      // Record on blockchain using updated contract method
      const tx = await contract.recordVaccination(
        tokenId,
        vaccineData.vaccineName,
        vaccineData.batchNumber,
        nextDueDate,
        ipfsHash
      );

      console.log('Transaction submitted:', tx.hash);
      const receipt = await tx.wait();
      console.log('Transaction confirmed:', receipt);

      // Get the QR code hash from the event
      const qrEvent = receipt.events?.find(e => e.event === 'QRCodeGenerated');
      const vaccinationEvent = receipt.events?.find(e => e.event === 'VaccinationRecorded');
      
      const qrCodeHash = qrEvent?.args?.qrCodeHash;
      
      // Store recorded vaccination info for display
      setRecordedVaccination({
        vaccineName: vaccineData.vaccineName,
        batchNumber: vaccineData.batchNumber,
        qrCodeHash: qrCodeHash,
        txHash: tx.hash,
        childName: childInfo.name,
        hospitalName: hospitalInfo.name,
        date: new Date().toLocaleDateString(),
        nextDueDate: nextDueDate > 0 ? new Date(nextDueDate * 1000).toLocaleDateString() : null
      });

      alert('Vaccination recorded successfully with QR code generated!');
      setShowQRCode(true);
      
      // Reset form
      setVaccineData({
        vaccineName: '',
        batchNumber: '',
        notes: '',
        sideEffects: '',
        administeredBy: '',
        dosage: '',
        manufacturer: ''
      });
      setVaccineImage(null);
      
      // Refresh child info
      await searchChild();
      
      setLoading(false);
    } catch (error) {
      console.error('Error recording vaccination:', error);
      alert('Error recording vaccination: ' + error.message);
      setLoading(false);
    }
  };

  const formatDate = (timestamp) => {
    return new Date(timestamp * 1000).toLocaleDateString();
  };

  const getChildAge = (birthTimestamp) => {
    const ageInDays = Math.floor((Date.now()/1000 - birthTimestamp) / (24 * 60 * 60));
    if (ageInDays < 30) return `${ageInDays} days`;
    if (ageInDays < 365) return `${Math.floor(ageInDays/30)} months`;
    return `${Math.floor(ageInDays/365)} years`;
  };

  const getRecommendedVaccines = (birthTimestamp) => {
    const ageInDays = Math.floor((Date.now()/1000 - birthTimestamp) / (24 * 60 * 60));
    return Object.entries(VACCINE_SCHEDULE).filter(([vaccine, info]) => 
      ageInDays >= info.minAge && ageInDays <= info.minAge + 90 // Within 3 months of recommended age
    );
  };

  if (!isAuthorized) {
    return (
      <div className="vaccination-record">
        <div className="unauthorized-message">
          <div className="error-icon">ðŸ¥</div>
          <h2>Hospital Authorization Required</h2>
          <p>Only authorized hospitals can record vaccinations.</p>
          {hospitalInfo ? (
            <div className="hospital-status">
              <p><strong>Hospital:</strong> {hospitalInfo.name}</p>
              <p><strong>Status:</strong> Pending Authorization</p>
              <p>Please contact the system administrator to get your hospital authorized.</p>
            </div>
          ) : (
            <div className="registration-prompt">
              <p>Your hospital needs to be registered first.</p>
              <p>Please register your hospital using the Hospital Registration form.</p>
            </div>
          )}
        </div>
      </div>
    );
  }

  return (
    <div className="vaccination-record">
      <div className="record-container">
        <div className="hospital-header">
          <h2>ðŸ’‰ Record Vaccination</h2>
          <div className="hospital-info">
            <p><strong>Hospital:</strong> {hospitalInfo?.name}</p>
            <p><strong>License:</strong> {hospitalInfo?.licenseNumber}</p>
          </div>
        </div>
        
        {/* Search Child Section */}
        <div className="search-section">
          <h3>ðŸ” Find Child by Token ID</h3>
          <div className="search-form">
            <input
              type="text"
              value={tokenId}
              onChange={(e) => setTokenId(e.target.value)}
              placeholder="Enter Child's Token ID (e.g., 1, 2, 3...)"
              className="search-input"
            />
            <button 
              onClick={searchChild}
              disabled={searchLoading}
              className="search-btn"
            >
              {searchLoading ? 'â³ Searching...' : 'ðŸ” Search Child'}
            </button>
          </div>
          <div className="search-help">
            <small>ðŸ’¡ Parents can provide their child's Token ID for vaccination recording</small>
          </div>
        </div>

        {/* Child Information Display */}
        {childInfo && (
          <div className="child-info-section">
            <div className="child-card">
              <div className="child-header">
                <h3>ðŸ‘¶ {childInfo.name}</h3>
                <div className="child-status">
                  <span className="token-id">Token ID: {childInfo.tokenId}</span>
                </div>
              </div>
              <div className="child-details-grid">
                <div className="detail-item">
                  <label>Parent:</label>
                  <span>{childInfo.parentName}</span>
                </div>
                <div className="detail-item">
                  <label>Date of Birth:</label>
                  <span>{formatDate(childInfo.dateOfBirth)}</span>
                </div>
                <div className="detail-item">
                  <label>Age:</label>
                  <span>{getChildAge(childInfo.dateOfBirth)}</span>
                </div>
                <div className="detail-item">
                  <label>Total Vaccinations:</label>
                  <span>{childInfo.vaccinationHistory.length}</span>
                </div>
                <div className="detail-item">
                  <label>Registered by:</label>
                  <span>{childInfo.hospitalName}</span>
                </div>
                <div className="detail-item">
                  <label>Parent Contact:</label>
                  <span>{childInfo.contactInfo}</span>
                </div>
              </div>
            </div>

            {/* Recommended Vaccines for Age */}
            <div className="recommended-vaccines">
              <h4>ðŸ’¡ Recommended for Current Age</h4>
              <div className="vaccine-recommendations">
                {getRecommendedVaccines(childInfo.dateOfBirth).map(([vaccine, info]) => (
                  <div key={vaccine} className="recommendation-item">
                    <span className="vaccine-name">{vaccine}</span>
                    <span className="age-group">{info.ageGroup}</span>
                    <span className="description">{info.description}</span>
                  </div>
                ))}
              </div>
            </div>

            {/* Previous Vaccinations */}
            {childInfo.vaccinationHistory.length > 0 && (
              <div className="vaccination-history">
                <h4>ðŸ“‹ Vaccination History</h4>
                <div className="history-list">
                  {childInfo.vaccinationHistory.map((vaccine, index) => (
                    <div key={index} className="history-item">
                      <div className="vaccine-main-info">
                        <strong>{vaccine.vaccineName}</strong>
                        <span className="date">{formatDate(vaccine.dateAdministered)}</span>
                      </div>
                      <div className="vaccine-sub-info">
                        <span>{vaccine.hospitalName}</span>
                        <span>Batch: {vaccine.batchNumber}</span>
                        {vaccine.qrCodeHash && (
                          <span className="qr-indicator">ðŸ” QR Available</span>
                        )}
                      </div>
                      {vaccine.nextDueDate > 0 && (
                        <div className="next-due">
                          Next due: {formatDate(vaccine.nextDueDate)}
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              </div>
            )}

            {/* Upcoming Vaccinations */}
            {childInfo.upcomingVaccinations?.vaccineNames?.length > 0 && (
              <div className="upcoming-vaccinations">
                <h4>ðŸ“… Upcoming Vaccinations</h4>
                <div className="upcoming-list">
                  {childInfo.upcomingVaccinations.vaccineNames.map((name, index) => (
                    <div key={index} className="upcoming-item">
                      <span className="vaccine-name">{name}</span>
                      <span className="due-date">
                        Due: {formatDate(childInfo.upcomingVaccinations.dueDates[index])}
                      </span>
                    </div>
                  ))}
                </div>
              </div>
            )}

            {/* Vaccination Form */}
            <div className="vaccination-form">
              <h4>ðŸ’‰ Record New Vaccination</h4>
              
              <div className="form-row">
                <div className="form-group">
                  <label>Vaccine Name *</label>
                  <select
                    name="vaccineName"
                    value={vaccineData.vaccineName}
                    onChange={handleInputChange}
                    required
                  >
                    <option value="">Select Vaccine</option>
                    <optgroup label="Recommended for Age">
                      {getRecommendedVaccines(childInfo.dateOfBirth).map(([vaccine, info]) => (
                        <option key={vaccine} value={vaccine}>
                          {vaccine} - {info.description}
                        </option>
                      ))}
                    </optgroup>
                    <optgroup label="All Vaccines">
                      {Object.entries(VACCINE_SCHEDULE).map(([vaccine, info]) => (
                        <option key={vaccine} value={vaccine}>
                          {vaccine} - {info.description}
                        </option>
                      ))}
                    </optgroup>
                  </select>
                </div>

                <div className="form-group">
                  <label>Batch Number *</label>
                  <input
                    type="text"
                    name="batchNumber"
                    value={vaccineData.batchNumber}
                    onChange={handleInputChange}
                    placeholder="Vaccine batch number"
                    required
                  />
                </div>
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label>Dosage</label>
                  <input
                    type="text"
                    name="dosage"
                    value={vaccineData.dosage}
                    onChange={handleInputChange}
                    placeholder="e.g., 0.5ml, 1ml"
                  />
                </div>

                <div className="form-group">
                  <label>Manufacturer</label>
                  <input
                    type="text"
                    name="manufacturer"
                    value={vaccineData.manufacturer}
                    onChange={handleInputChange}
                    placeholder="Vaccine manufacturer"
                  />
                </div>
              </div>

              <div className="form-group">
                <label>Administered By</label>
                <input
                  type="text"
                  name="administeredBy"
                  value={vaccineData.administeredBy}
                  onChange={handleInputChange}
                  placeholder="Doctor/Nurse name (optional)"
                />
              </div>

              <div className="form-group">
                <label>Vaccination Certificate/Photo</label>
                <input
                  type="file"
                  accept="image/*"
                  onChange={handleImageUpload}
                  className="file-input"
                />
                {vaccineImage && (
                  <div className="image-preview">
                    <img 
                      src={URL.createObjectURL(vaccineImage)} 
                      alt="Vaccine certificate"
                      className="preview-img"
                    />
                  </div>
                )}
              </div>

              <div className="form-group">
                <label>Medical Notes</label>
                <textarea
                  name="notes"
                  value={vaccineData.notes}
                  onChange={handleInputChange}
                  placeholder="Additional medical notes about the vaccination"
                  rows="3"
                />
              </div>

              <div className="form-group">
                <label>Side Effects (if any)</label>
                <textarea
                  name="sideEffects"
                  value={vaccineData.sideEffects}
                  onChange={handleInputChange}
                  placeholder="Record any observed side effects or reactions"
                  rows="2"
                />
              </div>

              <div className="vaccination-summary">
                <h5>ðŸ“‹ Vaccination Summary</h5>
                <p><strong>Child:</strong> {childInfo.name}</p>
                <p><strong>Hospital:</strong> {hospitalInfo?.name}</p>
                <p><strong>Date:</strong> {new Date().toLocaleDateString()}</p>
                {vaccineData.vaccineName && (
                  <p><strong>Vaccine:</strong> {vaccineData.vaccineName}</p>
                )}
              </div>

              <div className="form-actions">
                <button
                  onClick={recordVaccination}
                  disabled={loading || !vaccineData.vaccineName || !vaccineData.batchNumber}
                  className="record-btn"
                >
                  {loading ? 'â³ Recording & Generating QR...' : 'ðŸ” Record Vaccination'}
                </button>
              </div>
            </div>
          </div>
        )}

        {/* QR Code Display Modal */}
        {showQRCode && recordedVaccination && (
          <div className="qr-modal">
            <div className="qr-modal-content">
              <div className="qr-header">
                <h3>âœ… Vaccination Recorded Successfully!</h3>
                <button onClick={() => setShowQRCode(false)} className="close-btn">Ã—</button>
              </div>
              
              <div className="vaccination-success-info">
                <div className="success-details">
                  <p><strong>Child:</strong> {recordedVaccination.childName}</p>
                  <p><strong>Vaccine:</strong> {recordedVaccination.vaccineName}</p>
                  <p><strong>Hospital:</strong> {recordedVaccination.hospitalName}</p>
                  <p><strong>Date:</strong> {recordedVaccination.date}</p>
                  <p><strong>Batch:</strong> {recordedVaccination.batchNumber}</p>
                  {recordedVaccination.nextDueDate && (
                    <p><strong>Next Due:</strong> {recordedVaccination.nextDueDate}</p>
                  )}
                </div>
              </div>

              <div className="qr-code-section">
                <h4>ðŸ” Vaccination QR Code</h4>
                <p>This QR code contains verification data for this vaccination:</p>
                <div className="qr-code-container">
                  <QRCode 
                    value={JSON.stringify({
                      tokenId: tokenId,
                      vaccineName: recordedVaccination.vaccineName,
                      qrCodeHash: recordedVaccination.qrCodeHash,
                      hospitalName: recordedVaccination.hospitalName,
                      date: recordedVaccination.date,
                      txHash: recordedVaccination.txHash,
                      verificationUrl: `${window.location.origin}/verify/${tokenId}/${recordedVaccination.qrCodeHash}`
                    })}
                    size={250}
                    level="M"
                  />
                </div>
                <div className="qr-info">
                  <p><strong>QR Hash:</strong> {recordedVaccination.qrCodeHash}</p>
                  <p><strong>Transaction:</strong> <a href={`https://etherscan.io/tx/${recordedVaccination.txHash}`} target="_blank" rel="noopener noreferrer">{recordedVaccination.txHash.slice(0, 10)}...</a></p>
                </div>
              </div>

              <div className="qr-actions">
                <button 
                  onClick={() => {
                    const canvas = document.querySelector('.qr-modal canvas');
                    const url = canvas.toDataURL();
                    const link = document.createElement('a');
                    link.download = `${recordedVaccination.childName}-${recordedVaccination.vaccineName}-qr.png`;
                    link.href = url;
                    link.click();
                  }}
                  className="btn-primary"
                >
                  ðŸ“± Download QR Code
                </button>
                <button 
                  onClick={() => setShowQRCode(false)}
                  className="btn-secondary"
                >
                  Continue
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default VaccinationRecord;
