import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import QRCode from 'qrcode.react';

const ParentDashboard = ({ contract, account }) => {
  const [children, setChildren] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedChild, setSelectedChild] = useState(null);
  const [upcomingVaccinations, setUpcomingVaccinations] = useState([]);
  const [contactInfo, setContactInfo] = useState('');
  const [showUpdateContact, setShowUpdateContact] = useState(false);

  useEffect(() => {
    loadParentChildren();
  }, [account, contract]);

  const loadParentChildren = async () => {
    if (!contract || !account) return;
    
    try {
      setLoading(true);
      
      // Get all children for this parent
      const childTokenIds = await contract.getParentChildren(account);
      
      const childrenData = [];
      for (const tokenId of childTokenIds) {
        const childInfo = await contract.getChildInfo(tokenId);
        const vaccinationHistory = await contract.getVaccinationHistory(tokenId);
        const upcomingVacs = await contract.getUpcomingVaccinations(tokenId);
        
        childrenData.push({
          tokenId: tokenId.toString(),
          info: childInfo,
          vaccinations: vaccinationHistory,
          upcomingVaccinations: upcomingVacs
        });
      }
      
      setChildren(childrenData);
      setLoading(false);
    } catch (error) {
      console.error('Error loading children:', error);
      setLoading(false);
    }
  };

  const loadUpcomingVaccinations = async (tokenId) => {
    try {
      const upcoming = await contract.getUpcomingVaccinations(tokenId);
      setUpcomingVaccinations(upcoming);
    } catch (error) {
      console.error('Error loading upcoming vaccinations:', error);
    }
  };

  const updateContactInfo = async (tokenId) => {
    if (!contactInfo.trim()) {
      alert('Please enter contact information');
      return;
    }

    try {
      const tx = await contract.updateChildInfo(tokenId, contactInfo);
      await tx.wait();
      
      alert('Contact information updated successfully!');
      setShowUpdateContact(false);
      setContactInfo('');
      await loadParentChildren();
    } catch (error) {
      console.error('Error updating contact info:', error);
      alert('Error updating contact information: ' + error.message);
    }
  };

  const generateVerificationQR = async (tokenId) => {
    try {
      const verification = await contract.generateVerificationQR(tokenId);
      return {
        qrData: verification.qrData,
        childName: verification.childName,
        totalVaccinations: verification.totalVaccinations.toString(),
        isUpToDate: verification.isUpToDate
      };
    } catch (error) {
      console.error('Error generating QR:', error);
      return null;
    }
  };

  const formatDate = (timestamp) => {
    return new Date(timestamp * 1000).toLocaleDateString();
  };

  const getVaccinationStatus = (child) => {
    const ageInDays = Math.floor((Date.now() / 1000 - child.info.dateOfBirth) / 86400);
    const expectedVaccinations = getExpectedVaccinationCount(ageInDays);
    const actualVaccinations = child.vaccinations.length;
    
    if (actualVaccinations >= expectedVaccinations) {
      return { status: 'up-to-date', color: '#10b981', text: '✅ Up to Date' };
    } else if (actualVaccinations >= expectedVaccinations * 0.8) {
      return { status: 'nearly-complete', color: '#f59e0b', text: '⚠️ Nearly Complete' };
    } else {
      return { status: 'behind', color: '#ef4444', text: '❌ Behind Schedule' };
    }
  };

  const getExpectedVaccinationCount = (ageInDays) => {
    if (ageInDays < 42) return 3;
    if (ageInDays < 84) return 6;
    if (ageInDays < 126) return 9;
    if (ageInDays < 365) return 12;
    if (ageInDays < 730) return 15;
    return 18;
  };

  const ChildCard = ({ child }) => {
    const status = getVaccinationStatus(child);
    const [showQR, setShowQR] = useState(false);
    const [qrData, setQrData] = useState(null);

    const handleShowQR = async () => {
      const qr = await generateVerificationQR(child.tokenId);
      setQrData(qr);
      setShowQR(true);
    };

    return (
      <div className="child-card">
        <div className="child-header">
          <div className="child-basic-info">
            <h3>{child.info.name}</h3>
            <p>Token ID: {child.tokenId}</p>
            <p>Born: {formatDate(child.info.dateOfBirth)}</p>
          </div>
          <div className="vaccination-status" style={{ backgroundColor: status.color }}>
            {status.text}
          </div>
        </div>

        <div className="child-stats">
          <div className="stat">
            <label>Total Vaccinations:</label>
            <span>{child.vaccinations.length}</span>
          </div>
          <div className="stat">
            <label>Registered by:</label>
            <span>{child.info.hospitalName || 'Hospital'}</span>
          </div>
          <div className="stat">
            <label>Last Updated:</label>
            <span>
              {child.vaccinations.length > 0 
                ? formatDate(child.vaccinations[child.vaccinations.length - 1].dateAdministered)
                : 'No vaccinations yet'}
            </span>
          </div>
        </div>

        <div className="child-actions">
          <button 
            onClick={() => setSelectedChild(selectedChild === child.tokenId ? null : child.tokenId)}
            className="btn-secondary"
          >
            {selectedChild === child.tokenId ? 'Hide Details' : 'View Details'}
          </button>
          <button onClick={handleShowQR} className="btn-primary">
            📱 Generate QR Code
          </button>
        </div>

        {selectedChild === child.tokenId && (
          <div className="child-details">
            <div className="vaccination-history">
              <h4>💉 Vaccination History</h4>
              {child.vaccinations.length === 0 ? (
                <p className="no-vaccinations">No vaccinations recorded yet</p>
              ) : (
                <div className="vaccination-list">
                  {child.vaccinations.map((vac, index) => (
                    <div key={index} className="vaccination-item">
                      <div className="vac-info">
                        <strong>{vac.vaccineName}</strong>
                        <span className="vac-date">{formatDate(vac.dateAdministered)}</span>
                      </div>
                      <div className="vac-details">
                        <p>Hospital: {vac.hospitalName}</p>
                        <p>Batch: {vac.batchNumber}</p>
                        {vac.nextDueDate > 0 && (
                          <p>Next Due: {formatDate(vac.nextDueDate)}</p>
                        )}
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>

            <div className="upcoming-vaccinations">
              <h4>📅 Upcoming Vaccinations</h4>
              {child.upcomingVaccinations.vaccineNames?.length > 0 ? (
                <div className="upcoming-list">
                  {child.upcomingVaccinations.vaccineNames.map((name, index) => (
                    <div key={index} className="upcoming-item">
                      <span className="vaccine-name">{name}</span>
                      <span className="due-date">
                        Due: {formatDate(child.upcomingVaccinations.dueDates[index])}
                      </span>
                    </div>
                  ))}
                </div>
              ) : (
                <p className="no-upcoming">No upcoming vaccinations scheduled</p>
              )}
            </div>

            <div className="contact-update">
              <h4>📞 Update Contact Information</h4>
              <div className="current-contact">
                <p><strong>Current:</strong> {child.info.contactInfo}</p>
              </div>
              {!showUpdateContact ? (
                <button 
                  onClick={() => setShowUpdateContact(true)}
                  className="btn-secondary"
                >
                  Update Contact Info
                </button>
              ) : (
                <div className="update-form">
                  <input
                    type="text"
                    value={contactInfo}
                    onChange={(e) => setContactInfo(e.target.value)}
                    placeholder="New contact information"
                  />
                  <button 
                    onClick={() => updateContactInfo(child.tokenId)}
                    className="btn-primary"
                  >
                    Update
                  </button>
                  <button 
                    onClick={() => {
                      setShowUpdateContact(false);
                      setContactInfo('');
                    }}
                    className="btn-secondary"
                  >
                    Cancel
                  </button>
                </div>
              )}
            </div>
        </div>
        )}

        {showQR && qrData && (
          <div className="qr-modal">
            <div className="qr-modal-content">
              <div className="qr-header">
                <h3>🔍 Verification QR Code</h3>
                <button onClick={() => setShowQR(false)} className="close-btn">×</button>
              </div>
              <div className="qr-info">
                <p><strong>Child:</strong> {qrData.childName}</p>
                <p><strong>Vaccinations:</strong> {qrData.totalVaccinations}</p>
                <p><strong>Status:</strong> {qrData.isUpToDate ? 'Up to Date' : 'Pending'}</p>
              </div>
              <div className="qr-code-container">
                <QRCode 
                  value={qrData.qrData}
                  size={250}
                  level="M"
                />
              </div>
              <div className="qr-actions">
                <button 
                  onClick={() => {
                    const canvas = document.querySelector('.qr-modal canvas');
                    const url = canvas.toDataURL();
                    const link = document.createElement('a');
                    link.download = `${qrData.childName}-verification-qr.png`;
                    link.href = url;
                    link.click();
                  }}
                  className="btn-primary"
                >
                  📱 Download QR Code
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    );
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner">⏳</div>
        <p>Loading your children's vaccination records...</p>
      </div>
    );
  }

  if (children.length === 0) {
    return (
      <div className="no-children">
        <div className="empty-state">
          <div className="empty-icon">👶</div>
          <h2>No Children Registered</h2>
          <p>You don't have any children registered in the vaccination system yet.</p>
          <div className="instructions">
            <h3>How to get started:</h3>
            <ol>
              <li><strong>Hospital Registration:</strong> When your child is born, the hospital will register them automatically</li>
              <li><strong>Receive NFT:</strong> You'll receive a vaccination NFT in this wallet</li>
              <li><strong>Track Vaccinations:</strong> All vaccinations will be recorded by healthcare providers</li>
              <li><strong>Get Reminders:</strong> You'll receive automatic vaccination reminders</li>
            </ol>
          </div>
          <div className="wallet-info">
            <p><strong>Your Wallet:</strong> {account}</p>
            <p><small>Make sure to provide this address to your healthcare provider</small></p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="parent-dashboard">
      <div className="dashboard-header">
        <h1>👨‍👩‍👧‍👦 My Children's Vaccination Records</h1>
        <p>Manage and track vaccination records for your children</p>
        <div className="parent-info">
          <span>📱 Wallet: {account}</span>
          <span>👶 Children: {children.length}</span>
        </div>
      </div>

      <div className="dashboard-stats">
        <div className="stat-card">
          <h3>Total Children</h3>
          <div className="stat-number">{children.length}</div>
        </div>
        <div className="stat-card">
          <h3>Total Vaccinations</h3>
          <div className="stat-number">
            {children.reduce((total, child) => total + child.vaccinations.length, 0)}
          </div>
        </div>
        <div className="stat-card">
          <h3>Up to Date</h3>
          <div className="stat-number">
            {children.filter(child => getVaccinationStatus(child).status === 'up-to-date').length}
          </div>
        </div>
        <div className="stat-card">
          <h3>Pending Vaccinations</h3>
          <div className="stat-number">
            {children.reduce((total, child) => 
              total + (child.upcomingVaccinations.vaccineNames?.length || 0), 0)}
          </div>
        </div>
      </div>

      <div className="children-grid">
        {children.map((child) => (
          <ChildCard key={child.tokenId} child={child} />
        ))}
      </div>

      <div className="dashboard-footer">
        <div className="important-info">
          <h3>📋 Important Information</h3>
          <ul>
            <li><strong>NFT Ownership:</strong> Each child has a unique NFT in your wallet</li>
            <li><strong>Hospital Updates:</strong> Only authorized hospitals can record vaccinations</li>
            <li><strong>QR Verification:</strong> Use QR codes for instant verification at healthcare facilities</li>
            <li><strong>Automatic Reminders:</strong> You'll receive notifications when vaccinations are due</li>
            <li><strong>Data Security:</strong> All records are stored securely on the blockchain</li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default ParentDashboard;
