import React, { Component } from 'react';

class ParentDashboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      children: [],
      loading: false,
      error: null
    };
  }

  componentDidMount() {
    this.loadChildrenData();
  }

  loadChildrenData = async () => {
    try {
      const { contract, connectedWallet } = this.props;
      
      if (!contract || !connectedWallet) {
        this.setState({ error: 'Contract or wallet not available' });
        return;
      }

      this.setState({ loading: true, error: null });

      console.log('Loading children for parent:', connectedWallet);

      // Get child token IDs for this parent
      const childTokenIds = await contract.getParentChildren(connectedWallet);
      console.log('Child token IDs:', childTokenIds);

      if (childTokenIds.length === 0) {
        this.setState({ loading: false, children: [] });
        return;
      }

      // Load complete data for each child
      const childrenData = [];

      for (let i = 0; i < childTokenIds.length; i++) {
        const tokenId = childTokenIds[i].toString();
        
        try {
          // Get child information
          const childInfo = await contract.getChildInfo(tokenId);
          console.log(`Child ${tokenId} info:`, childInfo);

          // Get vaccination history
          const vaccinationHistory = await contract.getVaccinationHistory(tokenId);
          console.log(`Child ${tokenId} vaccinations:`, vaccinationHistory);

          // Parse child information
          const child = {
            tokenId: tokenId,
            name: childInfo[0],
            dateOfBirth: new Date(childInfo[1].toNumber() * 1000).toISOString(),
            parentName: childInfo[2],
            contactInfo: childInfo[3],
            parentAddress: childInfo[4],
            hospitalAddress: childInfo[5],
            hospitalName: childInfo[6],
            isRegistered: childInfo[7],
            registrationDate: new Date(childInfo[8].toNumber() * 1000).toISOString(),
            vaccinationHistory: vaccinationHistory.map(vax => ({
              vaccineName: vax[0],
              dateAdministered: new Date(vax[1].toNumber() * 1000).toISOString(),
              hospitalName: vax[2],
              administeredBy: vax[3],
              batchNumber: vax[4],
              nextDueDate: vax[5].toNumber() > 0 ? new Date(vax[5].toNumber() * 1000).toISOString() : null,
              isVerified: vax[6],
              ipfsHash: vax[7],
              qrCodeHash: vax[8]
            }))
          };

          childrenData.push(child);

        } catch (error) {
          console.error(`Error loading child ${tokenId}:`, error);
        }
      }

      this.setState({
        children: childrenData,
        loading: false
      });

      console.log('Loaded children data:', childrenData);

    } catch (error) {
      console.error('Error loading children data:', error);
      this.setState({ 
        error: `Failed to load children data: ${error.message}`,
        loading: false 
      });
    }
  };

  getChildAge = (dateOfBirth) => {
    const birthDate = new Date(dateOfBirth);
    const ageInDays = Math.floor((Date.now() - birthDate.getTime()) / (24 * 60 * 60 * 1000));
    if (ageInDays < 30) return `${ageInDays} days`;
    if (ageInDays < 365) return `${Math.floor(ageInDays/30)} months`;
    return `${Math.floor(ageInDays/365)} years`;
  };

  formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString();
  };

  generateVerificationQR = async (tokenId) => {
    try {
      const { contract } = this.props;
      const qrData = await contract.generateVerificationQR(tokenId);
      console.log('QR Data:', qrData);
      
      alert(`QR Verification Data:\n\nChild: ${qrData[1]}\nTotal Vaccinations: ${qrData[2]}\nStatus: ${qrData[3] ? 'Up to Date' : 'Pending'}\n\nQR Data: ${qrData[0]}`);
    } catch (error) {
      console.error('Error generating QR:', error);
      alert('Error generating QR code');
    }
  };

  renderNoChildren() {
    return (
      <div className="no-children">
        <div className="empty-icon">👶</div>
        <h3>No Children Found</h3>
        <p>No vaccination records found for this wallet address.</p>
        <div className="help-info">
          <p>Make sure you're using the correct wallet address that was provided during hospital registration.</p>
          <p>If you recently registered a child, please wait for the transaction to be confirmed.</p>
        </div>
        <button onClick={this.loadChildrenData} className="btn-primary">
          🔄 Refresh Data
        </button>
      </div>
    );
  }

  renderChildCard(child, index) {
    const totalVaccinations = child.vaccinationHistory.length;
    
    return (
      <div key={index} className="child-card">
        <div className="child-header">
          <div className="child-basic-info">
            <h3>{child.name}</h3>
            <p>Age: {this.getChildAge(child.dateOfBirth)}</p>
            <p>Token ID: {child.tokenId}</p>
            <p>Birth Date: {this.formatDate(child.dateOfBirth)}</p>
          </div>
          <div className="vaccination-status">
            {totalVaccinations} Vaccines
          </div>
        </div>

        <div className="child-details">
          <div className="detail-item">
            <label>Hospital:</label>
            <span>{child.hospitalName}</span>
          </div>
          <div className="detail-item">
            <label>Registration Date:</label>
            <span>{this.formatDate(child.registrationDate)}</span>
          </div>
          <div className="detail-item">
            <label>Parent:</label>
            <span>{child.parentName}</span>
          </div>
          <div className="detail-item">
            <label>Contact:</label>
            <span>{child.contactInfo}</span>
          </div>
        </div>

        {/* Vaccination History */}
        <div className="vaccination-history">
          <h4>💉 Vaccination History ({totalVaccinations})</h4>
          {totalVaccinations > 0 ? (
            <div className="vaccination-list">
              {child.vaccinationHistory.map((vaccine, vaxIndex) => (
                <div key={vaxIndex} className="vaccination-item">
                  <div className="vaccination-header">
                    <strong>{vaccine.vaccineName}</strong>
                    <span className="vaccination-date">{this.formatDate(vaccine.dateAdministered)}</span>
                  </div>
                  <div className="vaccination-info">
                    <p><strong>Hospital:</strong> {vaccine.hospitalName}</p>
                    <p><strong>Batch:</strong> {vaccine.batchNumber}</p>
                    {vaccine.qrCodeHash && (
                      <p><strong>QR Code:</strong> {vaccine.qrCodeHash}</p>
                    )}
                    {vaccine.nextDueDate && (
                      <p><strong>Next Due:</strong> {this.formatDate(vaccine.nextDueDate)}</p>
                    )}
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <p className="no-vaccinations">No vaccinations recorded yet.</p>
          )}
        </div>

        <div className="child-actions">
          <button 
            onClick={() => this.generateVerificationQR(child.tokenId)}
            className="btn-primary"
          >
            📱 Generate QR Code
          </button>
        </div>
      </div>
    );
  }

  render() {
    const { children, loading, error } = this.state;
    const { connectedWallet } = this.props;

    return (
      <div className="App">
        <div className="main-content">
          <div className="parent-dashboard">
            <div className="dashboard-header">
              <h1>👨‍👩‍👧‍👦 Parent Dashboard</h1>
              <p>View and manage your children's vaccination records from the blockchain</p>
              <div className="connected-wallet">
                <span>🔗 Connected: {connectedWallet.slice(0, 6)}...{connectedWallet.slice(-4)}</span>
              </div>
            </div>

            {error && (
              <div className="error-message">
                ❌ {error}
                <button onClick={() => this.setState({ error: null })}>✕</button>
              </div>
            )}

            {loading && (
              <div className="loading-container">
                <div className="loading-spinner">⏳</div>
                <p>Loading your children's vaccination records from blockchain...</p>
              </div>
            )}

            {!loading && !error && (
              <>
                {/* Summary */}
                <div className="summary-cards">
                  <div className="summary-card">
                    <div className="card-icon">👶</div>
                    <div className="card-content">
                      <h4>Registered Children</h4>
                      <span className="stat-number">{children.length}</span>
                    </div>
                  </div>
                  
                  <div className="summary-card">
                    <div className="card-icon">💉</div>
                    <div className="card-content">
                      <h4>Total Vaccinations</h4>
                      <span className="stat-number">
                        {children.reduce((sum, child) => sum + child.vaccinationHistory.length, 0)}
                      </span>
                    </div>
                  </div>
                  
                  <div className="summary-card">
                    <div className="card-icon">🔗</div>
                    <div className="card-content">
                      <h4>Blockchain Records</h4>
                      <span className="stat-number">{children.length}</span>
                    </div>
                  </div>
                </div>

                {/* Children Grid */}
                {children.length > 0 ? (
                  <div className="children-grid">
                    {children.map((child, index) => this.renderChildCard(child, index))}
                  </div>
                ) : (
                  this.renderNoChildren()
                )}
              </>
            )}

            <div className="dashboard-actions">
              <button onClick={this.loadChildrenData} className="btn-secondary" disabled={loading}>
                {loading ? '⏳ Loading...' : '🔄 Refresh Data'}
              </button>
              <button onClick={this.props.onBack} className="btn-secondary">
                ← Back to Dashboard
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default ParentDashboard;
