import React, { Component } from 'react';

const COMMON_VACCINES = [
  'BCG',
  'Hepatitis B (Birth Dose)',
  'DPT (1st Dose)',
  'DPT (2nd Dose)', 
  'DPT (3rd Dose)',
  'OPV (1st Dose)',
  'OPV (2nd Dose)',
  'OPV (3rd Dose)',
  'MMR (1st Dose)',
  'MMR (2nd Dose)',
  'Pentavalent (1st Dose)',
  'Pentavalent (2nd Dose)',
  'Pentavalent (3rd Dose)',
  'Rotavirus (1st Dose)',
  'Rotavirus (2nd Dose)',
  'Rotavirus (3rd Dose)',
  'PCV (1st Dose)',
  'PCV (2nd Dose)',
  'PCV (3rd Dose)',
  'Japanese Encephalitis',
  'Typhoid',
  'Other'
];

class VaccinationRecording extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tokenId: '',
      childInfo: null,
      vaccineData: {
        vaccineName: '',
        batchNumber: '',
        nextDueDate: ''
      },
      loading: false,
      searchLoading: false,
      isAuthorized: false,
      hospitalInfo: null,
      recordedVaccination: null,
      error: null,
      txHash: null
    };
  }

  componentDidMount() {
    this.checkHospitalAuthorization();
  }

  checkHospitalAuthorization = async () => {
    try {
      const { contract, connectedWallet } = this.props;
      
      if (!contract || !connectedWallet) return;

      console.log('Checking hospital authorization for:', connectedWallet);
      
      const hospitalInfo = await contract.hospitals(connectedWallet);
      const isAuthorized = await contract.authorizedHospitals(connectedWallet);
      
      console.log('Hospital info:', hospitalInfo);
      console.log('Is authorized:', isAuthorized);

      if (hospitalInfo[0] && hospitalInfo[0] !== '' && isAuthorized) {
        this.setState({
          isAuthorized: true,
          hospitalInfo: {
            name: hospitalInfo[0],
            licenseNumber: hospitalInfo[1],
            contactInfo: hospitalInfo[2]
          }
        });
      } else {
        this.setState({
          isAuthorized: false,
          hospitalInfo: null
        });
      }

    } catch (error) {
      console.error('Error checking hospital authorization:', error);
      this.setState({ 
        error: `Failed to check authorization: ${error.message}`,
        isAuthorized: false
      });
    }
  };

  searchChild = async () => {
    if (!this.state.tokenId.trim()) {
      this.setState({ error: 'Please enter a Token ID' });
      return;
    }

    this.setState({ searchLoading: true, error: null });

    try {
      const { contract } = this.props;
      
      if (!contract) {
        throw new Error('Contract not available');
      }

      console.log('Searching for child with token ID:', this.state.tokenId);
      
      const childInfo = await contract.getChildInfo(this.state.tokenId);
      console.log('Child info:', childInfo);

      if (!childInfo[0] || childInfo[0] === '') {
        throw new Error('Child not found with this Token ID');
      }

      const vaccinationHistory = await contract.getVaccinationHistory(this.state.tokenId);
      console.log('Vaccination history:', vaccinationHistory);

      const child = {
        tokenId: this.state.tokenId,
        name: childInfo[0],
        dateOfBirth: new Date(childInfo[1].toNumber() * 1000),
        parentName: childInfo[2],
        contactInfo: childInfo[3],
        parentAddress: childInfo[4],
        hospitalAddress: childInfo[5],
        hospitalName: childInfo[6],
        vaccinationHistory: vaccinationHistory.map(vax => ({
          vaccineName: vax[0],
          dateAdministered: new Date(vax[1].toNumber() * 1000),
          hospitalName: vax[2],
          batchNumber: vax[4],
          qrCodeHash: vax[8]
        }))
      };

      this.setState({
        childInfo: child,
        searchLoading: false
      });

    } catch (error) {
      console.error('Error searching for child:', error);
      this.setState({ 
        error: error.message,
        searchLoading: false 
      });
    }
  };

  handleInputChange = (e) => {
    const { name, value } = e.target;
    this.setState({
      vaccineData: {
        ...this.state.vaccineData,
        [name]: value
      }
    });
  };

  calculateNextDueDate = () => {
    // Simple calculation - 30 days from now for next dose
    const nextDate = new Date();
    nextDate.setDate(nextDate.getDate() + 30);
    return Math.floor(nextDate.getTime() / 1000);
  };

  recordVaccination = async () => {
    if (!this.state.vaccineData.vaccineName || !this.state.vaccineData.batchNumber) {
      this.setState({ error: 'Please fill in vaccine name and batch number' });
      return;
    }

    this.setState({ loading: true, error: null });

    try {
      const { contract, sendTransaction } = this.props;
      
      if (!contract) {
        throw new Error('Contract not available');
      }

      const nextDueDate = this.state.vaccineData.nextDueDate ? 
        Math.floor(new Date(this.state.vaccineData.nextDueDate).getTime() / 1000) :
        this.calculateNextDueDate();

      console.log('Recording vaccination:', {
        tokenId: this.state.tokenId,
        vaccineName: this.state.vaccineData.vaccineName,
        batchNumber: this.state.vaccineData.batchNumber,
        nextDueDate: nextDueDate
      });

      const receipt = await sendTransaction(
        contract.recordVaccination,
        [
          this.state.tokenId,
          this.state.vaccineData.vaccineName,
          this.state.vaccineData.batchNumber,
          nextDueDate,
          '' // IPFS hash (empty for now)
        ]
      );

      console.log('Vaccination recorded successfully:', receipt.transactionHash);

      this.setState({
        loading: false,
        recordedVaccination: {
          vaccineName: this.state.vaccineData.vaccineName,
          batchNumber: this.state.vaccineData.batchNumber,
          childName: this.state.childInfo.name,
          tokenId: this.state.tokenId,
          hospitalName: this.state.hospitalInfo.name,
          transactionHash: receipt.transactionHash,
          date: new Date().toLocaleDateString()
        },
        txHash: receipt.transactionHash,
        error: null
      });

      // Reset form
      this.setState({
        vaccineData: {
          vaccineName: '',
          batchNumber: '',
          nextDueDate: ''
        }
      });

      alert(`Vaccination recorded successfully!\nTransaction: ${receipt.transactionHash.slice(0, 10)}...`);

      // Refresh child data
      setTimeout(() => {
        this.searchChild();
      }, 2000);

    } catch (error) {
      console.error('Error recording vaccination:', error);
      
      let errorMessage = 'Recording failed';
      
      if (error.message.includes('user rejected')) {
        errorMessage = 'Transaction cancelled by user';
      } else if (error.message.includes('insufficient funds')) {
        errorMessage = 'Insufficient ETH for gas fees';
      }

      this.setState({ 
        loading: false,
        error: errorMessage
      });

      alert(`Recording failed: ${errorMessage}`);
    }
  };

  formatDate = (date) => {
    return date.toLocaleDateString();
  };

  getChildAge = (dateOfBirth) => {
    const ageInDays = Math.floor((Date.now() - dateOfBirth.getTime()) / (24 * 60 * 60 * 1000));
    if (ageInDays < 30) return `${ageInDays} days`;
    if (ageInDays < 365) return `${Math.floor(ageInDays/30)} months`;
    return `${Math.floor(ageInDays/365)} years`;
  };

  renderUnauthorized() {
    return (
      <div className="vaccination-record">
        <div className="unauthorized-message">
          <div className="error-icon">🏥</div>
          <h2>Hospital Authorization Required</h2>
          <p>Only authorized hospitals can record vaccinations on the blockchain.</p>
          <div className="registration-prompt">
            <p>To record vaccinations, you need to:</p>
            <ol>
              <li>Register your hospital</li>
              <li>Get authorization from system admin</li>
              <li>Return here to record vaccinations</li>
            </ol>
          </div>
          <div className="action-buttons">
            <button onClick={() => this.props.onChangeView('hospital-register')} className="btn-primary">
              Go to Hospital Registration
            </button>
            <button onClick={this.props.onBack} className="btn-secondary">
              ← Back to Dashboard
            </button>
          </div>
        </div>
      </div>
    );
  }

  render() {
    const { 
      isAuthorized, 
      hospitalInfo, 
      tokenId, 
      childInfo, 
      vaccineData, 
      loading, 
      searchLoading,
      recordedVaccination,
      error
    } = this.state;

    if (!isAuthorized) {
      return (
        <div className="App">
          <div className="main-content">
            {this.renderUnauthorized()}
          </div>
        </div>
      );
    }

    return (
      <div className="App">
        <div className="main-content">
          <div className="vaccination-record">
            <div className="record-container">
              <div className="hospital-header">
                <h2>💉 Record Vaccination on Blockchain</h2>
                <div className="hospital-info">
                  <p><strong>Hospital:</strong> {hospitalInfo?.name}</p>
                  <p><strong>License:</strong> {hospitalInfo?.licenseNumber}</p>
                  <p><strong>Wallet:</strong> {this.props.connectedWallet?.slice(0, 8)}...{this.props.connectedWallet?.slice(-4)}</p>
                </div>
              </div>

              {error && (
                <div className="error-message">
                  ❌ {error}
                  <button onClick={() => this.setState({ error: null })}>✕</button>
                </div>
              )}

              {/* Search Child Section */}
              <div className="search-section">
                <h3>🔍 Find Child by Token ID</h3>
                <div className="search-form">
                  <input
                    type="text"
                    value={tokenId}
                    onChange={(e) => this.setState({ tokenId: e.target.value })}
                    placeholder="Enter Child's Token ID"
                    className="search-input"
                  />
                  <button 
                    onClick={this.searchChild}
                    disabled={searchLoading}
                    className="search-btn"
                  >
                    {searchLoading ? '⏳ Searching...' : '🔍 Search Blockchain'}
                  </button>
                </div>
                <div className="search-help">
                  <small>💡 Parents can provide their child's Token ID for vaccination recording</small>
                </div>
              </div>

              {/* Child Information Display */}
              {childInfo && (
                <div className="child-info-section">
                  <div className="child-card">
                    <div className="child-header">
                      <h3>👶 {childInfo.name}</h3>
                      <div className="child-status">
                        <span className="token-id">Token ID: {childInfo.tokenId}</span>
                      </div>
                    </div>
                    <div className="child-details-grid">
                      <div className="detail-item">
                        <label>Parent:</label>
                        <span>{childInfo.parentName}</span>
                      </div>
                      <div className="detail-item">
                        <label>Date of Birth:</label>
                        <span>{this.formatDate(childInfo.dateOfBirth)}</span>
                      </div>
                      <div className="detail-item">
                        <label>Age:</label>
                        <span>{this.getChildAge(childInfo.dateOfBirth)}</span>
                      </div>
                      <div className="detail-item">
                        <label>Total Vaccinations:</label>
                        <span>{childInfo.vaccinationHistory.length}</span>
                      </div>
                      <div className="detail-item">
                        <label>Parent Wallet:</label>
                        <span className="wallet-address">{childInfo.parentAddress}</span>
                      </div>
                      <div className="detail-item">
                        <label>Registered by:</label>
                        <span>{childInfo.hospitalName}</span>
                      </div>
                    </div>
                  </div>

                  {/* Previous Vaccinations */}
                  {childInfo.vaccinationHistory.length > 0 && (
                    <div className="vaccination-history">
                      <h4>📋 Previous Vaccinations</h4>
                      <div className="history-list">
                        {childInfo.vaccinationHistory.map((vaccine, index) => (
                          <div key={index} className="history-item">
                            <div className="vaccine-main-info">
                              <strong>{vaccine.vaccineName}</strong>
                              <span className="date">{this.formatDate(vaccine.dateAdministered)}</span>
                            </div>
                            <div className="vaccine-sub-info">
                              <span>Hospital: {vaccine.hospitalName}</span>
                              <span>Batch: {vaccine.batchNumber}</span>
                              {vaccine.qrCodeHash && (
                                <span className="qr-indicator">🔍 QR: {vaccine.qrCodeHash}</span>
                              )}
                            </div>
                          </div>
                        ))}
                      </div>
                    </div>
                  )}

                  {/* Vaccination Form */}
                  <div className="vaccination-form">
                    <h4>💉 Record New Vaccination</h4>
                    
                    <div className="form-row">
                      <div className="form-group">
                        <label>Vaccine Name *</label>
                        <select
                          name="vaccineName"
                          value={vaccineData.vaccineName}
                          onChange={this.handleInputChange}
                          required
                        >
                          <option value="">Select Vaccine</option>
                          {COMMON_VACCINES.map((vaccine, index) => (
                            <option key={index} value={vaccine}>
                              {vaccine}
                            </option>
                          ))}
                        </select>
                      </div>

                      <div className="form-group">
                        <label>Batch Number *</label>
                        <input
                          type="text"
                          name="batchNumber"
                          value={vaccineData.batchNumber}
                          onChange={this.handleInputChange}
                          placeholder="Vaccine batch number"
                          required
                        />
                      </div>
                    </div>

                    <div className="form-group">
                      <label>Next Due Date (Optional)</label>
                      <input
                        type="date"
                        name="nextDueDate"
                        value={vaccineData.nextDueDate}
                        onChange={this.handleInputChange}
                        min={new Date().toISOString().split('T')[0]}
                      />
                      <small>If not specified, will be set to 30 days from today</small>
                    </div>

                    <div className="vaccination-summary">
                      <h5>📋 Vaccination Summary</h5>
                      <p><strong>Child:</strong> {childInfo.name}</p>
                      <p><strong>Hospital:</strong> {hospitalInfo?.name}</p>
                      <p><strong>Date:</strong> {new Date().toLocaleDateString()}</p>
                      {vaccineData.vaccineName && (
                        <p><strong>Vaccine:</strong> {vaccineData.vaccineName}</p>
                      )}
                    </div>

                    <div className="form-actions">
                      <button
                        onClick={this.recordVaccination}
                        disabled={loading || !vaccineData.vaccineName || !vaccineData.batchNumber}
                        className="record-btn"
                      >
                        {loading ? '⏳ Recording on Blockchain...' : '🔐 Record Vaccination'}
                      </button>
                    </div>
                  </div>
                </div>
              )}

              {/* Success Message */}
              {recordedVaccination && (
                <div className="vaccination-success">
                  <div className="success-icon">✅</div>
                  <h3>Vaccination Recorded Successfully!</h3>
                  <div className="success-details">
                    <p><strong>Child:</strong> {recordedVaccination.childName}</p>
                    <p><strong>Vaccine:</strong> {recordedVaccination.vaccineName}</p>
                    <p><strong>Hospital:</strong> {recordedVaccination.hospitalName}</p>
                    <p><strong>Batch:</strong> {recordedVaccination.batchNumber}</p>
                    <p><strong>Date:</strong> {recordedVaccination.date}</p>
                    <p><strong>Transaction:</strong> {recordedVaccination.transactionHash.slice(0, 10)}...</p>
                  </div>
                  <button onClick={() => this.setState({ recordedVaccination: null })}>
                    Continue
                  </button>
                </div>
              )}

              <button onClick={this.props.onBack} className="btn-secondary">
                ← Back to Dashboard
              </button>

              {loading && (
                <div className="loading-info">
                  <div className="loading-spinner">⏳</div>
                  <p><strong>📡 Processing Vaccination Record...</strong></p>
                  <p>⏳ Recording vaccination on blockchain</p>
                  <p>🔍 Generating verification QR code</p>
                  <p>📱 Creating permanent record</p>
                  <p>⏳ Please confirm transaction in MetaMask</p>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default VaccinationRecording;
