import React, { Component } from 'react';

class HospitalRegistration extends Component {
  constructor(props) {
    super(props);
    this.state = {
      formData: {
        hospitalName: '',
        licenseNumber: '',
        contactInfo: '',
        address: '',
        website: ''
      },
      loading: false,
      hospitalStatus: null,
      isRegistered: false,
      isAuthorized: false,
      error: null,
      txHash: null
    };
  }

  componentDidMount() {
    this.checkHospitalStatus();
  }

  checkHospitalStatus = async () => {
    try {
      const { contract, connectedWallet } = this.props;
      
      if (!contract || !connectedWallet) return;

      this.setState({ loading: true, error: null });

      console.log('Checking hospital status for:', connectedWallet);
      
      const hospitalInfo = await contract.hospitals(connectedWallet);
      const isAuthorized = await contract.authorizedHospitals(connectedWallet);
      
      console.log('Hospital info:', hospitalInfo);
      console.log('Is authorized:', isAuthorized);
      
      if (hospitalInfo[0] && hospitalInfo[0] !== '') { // Hospital name exists
        this.setState({
          isRegistered: true,
          isAuthorized: isAuthorized,
          hospitalStatus: {
            name: hospitalInfo[0],
            licenseNumber: hospitalInfo[1],
            contactInfo: hospitalInfo[2],
            isAuthorized: isAuthorized,
            registrationDate: hospitalInfo[4]
          },
          formData: {
            ...this.state.formData,
            hospitalName: hospitalInfo[0],
            licenseNumber: hospitalInfo[1],
            contactInfo: hospitalInfo[2]
          },
          loading: false
        });
      } else {
        this.setState({ loading: false });
      }

    } catch (error) {
      console.error('Error checking hospital status:', error);
      this.setState({ 
        error: `Failed to check hospital status: ${error.message}`,
        loading: false
      });
    }
  };

  handleInputChange = (e) => {
    const { name, value } = e.target;
    this.setState({
      formData: {
        ...this.state.formData,
        [name]: value
      }
    });
  };

  validateForm = () => {
    const { hospitalName, licenseNumber, contactInfo } = this.state.formData;
    if (!hospitalName.trim()) {
      this.setState({ error: 'Hospital name is required' });
      return false;
    }
    if (!licenseNumber.trim()) {
      this.setState({ error: 'License number is required' });
      return false;
    }
    if (!contactInfo.trim()) {
      this.setState({ error: 'Contact information is required' });
      return false;
    }
    return true;
  };

  registerHospital = async () => {
    try {
      const { contract, sendTransaction } = this.props;
      
      if (!contract) {
        throw new Error('Contract not available');
      }

      if (!this.validateForm()) {
        return;
      }

      this.setState({ loading: true, error: null, txHash: null });

      const { hospitalName, licenseNumber, contactInfo } = this.state.formData;

      console.log('Registering hospital on blockchain:', {
        name: hospitalName,
        license: licenseNumber,
        contact: contactInfo
      });

      const receipt = await sendTransaction(
        contract.registerHospital,
        [hospitalName, licenseNumber, contactInfo]
      );

      console.log('Hospital registration successful:', receipt.transactionHash);

      this.setState({
        loading: false,
        isRegistered: true,
        isAuthorized: false,
        txHash: receipt.transactionHash,
        hospitalStatus: {
          name: hospitalName,
          licenseNumber: licenseNumber,
          contactInfo: contactInfo,
          isAuthorized: false,
          registrationDate: new Date()
        },
        error: null
      });

      alert(`Hospital registered successfully!\nTransaction: ${receipt.transactionHash.slice(0, 10)}...`);

      // Refresh status
      setTimeout(() => {
        this.checkHospitalStatus();
      }, 2000);

    } catch (error) {
      console.error('Hospital registration failed:', error);
      
      let errorMessage = 'Registration failed';
      
      if (error.message.includes('user rejected')) {
        errorMessage = 'Transaction cancelled by user';
      } else if (error.message.includes('insufficient funds')) {
        errorMessage = 'Insufficient ETH for gas fees';
      } else if (error.message.includes('already registered')) {
        errorMessage = 'Hospital is already registered';
      }

      this.setState({ 
        loading: false,
        error: errorMessage
      });

      alert(`Registration failed: ${errorMessage}`);
    }
  };

  renderHospitalStatus() {
    const { hospitalStatus, isAuthorized, loading } = this.state;

    return (
      <div className="hospital-status">
        <div className="status-container">
          <div className="hospital-header">
            <h2>üè• Hospital Status</h2>
            <div className={`status-badge ${isAuthorized ? 'authorized' : 'pending'}`}>
              {isAuthorized ? '‚úÖ Authorized' : '‚è≥ Pending Authorization'}
            </div>
          </div>

          <div className="hospital-info-card">
            <h3>{hospitalStatus?.name}</h3>
            
            <div className="info-grid">
              <div className="info-item">
                <label>License Number:</label>
                <span>{hospitalStatus?.licenseNumber}</span>
              </div>
              <div className="info-item">
                <label>Contact Info:</label>
                <span>{hospitalStatus?.contactInfo}</span>
              </div>
              <div className="info-item">
                <label>Wallet Address:</label>
                <span className="wallet-address">{this.props.connectedWallet}</span>
              </div>
              <div className="info-item">
                <label>Authorization Status:</label>
                <span className={isAuthorized ? 'authorized' : 'pending'}>
                  {isAuthorized ? 'Authorized ‚úÖ' : 'Pending Admin Approval ‚è≥'}
                </span>
              </div>
              {this.state.txHash && (
                <div className="info-item">
                  <label>Transaction Hash:</label>
                  <span className="tx-hash" title={this.state.txHash}>
                    {this.state.txHash.slice(0, 10)}...{this.state.txHash.slice(-8)}
                  </span>
                </div>
              )}
            </div>
          </div>

          {!isAuthorized && (
            <div className="pending-message">
              <div className="pending-icon">‚è≥</div>
              <h3>Waiting for Authorization</h3>
              <p>Your hospital registration is recorded on the blockchain but requires administrative approval.</p>
              
              <div className="next-steps">
                <h4>What happens next:</h4>
                <ul>
                  <li>‚úÖ Registration completed on blockchain</li>
                  <li>üîç Admin will review your hospital credentials</li>
                  <li>‚è≥ Authorization typically takes 24-48 hours</li>
                  <li>üéâ Once authorized, you can register children and record vaccinations</li>
                </ul>
              </div>
            </div>
          )}

          {isAuthorized && (
            <div className="authorized-message">
              <div className="success-icon">‚úÖ</div>
              <h3>Hospital Authorized!</h3>
              <p>Your hospital is now authorized to register children and record vaccinations on the blockchain.</p>
              
              <div className="capabilities">
                <h4>üéØ Available Actions:</h4>
                <ul>
                  <li>‚úÖ Register newborn children (creates NFTs)</li>
                  <li>‚úÖ Record vaccination data on blockchain</li>
                  <li>‚úÖ Generate QR codes for verification</li>
                  <li>‚úÖ Access vaccination histories</li>
                  <li>‚úÖ Issue vaccination certificates</li>
                </ul>
              </div>

              <div className="action-buttons">
                <button 
                  onClick={() => this.props.onChangeView && this.props.onChangeView('child-register')}
                  className="btn-primary"
                >
                  üë∂ Register Child
                </button>
                <button 
                  onClick={() => this.props.onChangeView && this.props.onChangeView('vaccination-record')}
                  className="btn-primary"
                >
                  üíâ Record Vaccination
                </button>
              </div>
            </div>
          )}

          <button onClick={this.props.onBack} className="btn-secondary">
            ‚Üê Back to Dashboard
          </button>
        </div>
      </div>
    );
  }

  renderRegistrationForm() {
    const { formData, loading, error } = this.state;

    return (
      <div className="hospital-registration">
        <div className="registration-container">
          <div className="registration-header">
            <h2>üè• Hospital Registration</h2>
            <p>Register your hospital on the blockchain to manage vaccination records</p>
          </div>

          {error && (
            <div className="error-message">
              ‚ùå {error}
              <button onClick={() => this.setState({ error: null })}>‚úï</button>
            </div>
          )}

          <div className="registration-form">
            <div className="form-section">
              <h3>üè¢ Hospital Information</h3>
              
              <div className="form-group">
                <label>Hospital Name *</label>
                <input
                  type="text"
                  name="hospitalName"
                  value={formData.hospitalName}
                  onChange={this.handleInputChange}
                  placeholder="Enter official hospital name"
                  required
                  disabled={loading}
                />
              </div>

              <div className="form-group">
                <label>Medical License Number *</label>
                <input
                  type="text"
                  name="licenseNumber"
                  value={formData.licenseNumber}
                  onChange={this.handleInputChange}
                  placeholder="Enter hospital license number"
                  required
                  disabled={loading}
                />
              </div>

              <div className="form-group">
                <label>Contact Information *</label>
                <input
                  type="text"
                  name="contactInfo"
                  value={formData.contactInfo}
                  onChange={this.handleInputChange}
                  placeholder="Email and phone number"
                  required
                  disabled={loading}
                />
              </div>

              <div className="form-group">
                <label>Hospital Address</label>
                <textarea
                  name="address"
                  value={formData.address}
                  onChange={this.handleInputChange}
                  placeholder="Full hospital address"
                  rows="3"
                  disabled={loading}
                />
              </div>

              <div className="form-group">
                <label>Website</label>
                <input
                  type="url"
                  name="website"
                  value={formData.website}
                  onChange={this.handleInputChange}
                  placeholder="https://your-hospital-website.com"
                  disabled={loading}
                />
              </div>
            </div>

            <div className="wallet-info">
              <h3>üîê Blockchain Information</h3>
              <div className="wallet-display">
                <label>Hospital Wallet Address:</label>
                <div className="wallet-address">
                  {this.props.connectedWallet || 'Not Connected'}
                </div>
                <small>
                  This wallet address will be registered on the blockchain for all hospital operations
                </small>
              </div>
            </div>

            <div className="form-actions">
              <button onClick={this.props.onBack} className="btn-secondary" disabled={loading}>
                ‚Üê Back to Dashboard
              </button>
              
              <button 
                onClick={this.registerHospital}
                className="btn-primary"
                disabled={loading || !this.validateForm() || !this.props.connectedWallet}
              >
                {loading ? '‚è≥ Registering...' : 'üè• Register on Blockchain'}
              </button>
            </div>

            {loading && (
              <div className="loading-info">
                <div className="loading-spinner">‚è≥</div>
                <p><strong>üì° Processing Registration...</strong></p>
                <p>üîó Sending transaction to blockchain...</p>
                <p>‚è≥ Please confirm the transaction in MetaMask</p>
                <p>üîç Waiting for blockchain confirmation...</p>
              </div>
            )}
          </div>
        </div>
      </div>
    );
  }

  render() {
    const { isRegistered } = this.state;

    return (
      <div className="App">
        <div className="main-content">
          {isRegistered ? this.renderHospitalStatus() : this.renderRegistrationForm()}
        </div>
      </div>
    );
  }
}

export default HospitalRegistration;
