import React, { Component } from 'react';
import config, { 
  VACCINE_SCHEDULE,
  ERROR_MESSAGES, 
  SUCCESS_MESSAGES, 
  GAS_LIMITS,
  calculateAgeInDays,
  getVaccinesDueForAge
} from '../config';

class ChildRegistration extends Component {
  constructor(props) {
    super(props);
    this.state = {
      formData: {
        childName: '',
        dateOfBirth: '',
        parentName: '',
        contactInfo: '',
        parentEmail: '',
        parentAddress: '',
        emergencyContact: '',
        medicalHistory: '',
        allergies: '',
        preferredLanguage: 'English',
        notificationPreferences: {
          email: true,
          sms: false,
          push: true
        }
      },
      loading: false,
      registeredChild: null,
      profileImage: null,
      step: 1,
      isAuthorizedHospital: false,
      hospitalInfo: null,
      registeredChildren: [],
      generatedSchedule: [],
      txHash: null,
      error: null,
      blockchainData: null
    };
  }

  componentDidMount() {
    this.checkHospitalAuthorization();
    this.loadRegisteredChildren();
  }

  // Check hospital authorization from blockchain
  checkHospitalAuthorization = async () => {
    try {
      const { contract, connectedWallet, contractDeployed } = this.props;
      
      if (!contractDeployed || !contract || !connectedWallet) {
        console.log('âš ï¸ Contract not deployed or wallet not connected');
        return;
      }

      console.log('ðŸ” Checking hospital authorization for:', connectedWallet);
      
      // Get hospital info from smart contract
      const hospitalInfo = await contract.hospitals(connectedWallet);
      
      if (hospitalInfo && hospitalInfo[4] && hospitalInfo[3]) { // isRegistered && isAuthorized
        const hospitalData = {
          address: hospitalInfo[0],
          hospitalName: hospitalInfo[1],
          licenseNumber: hospitalInfo[2],
          isAuthorized: hospitalInfo[3],
          isRegistered: hospitalInfo[4],
          registrationDate: new Date(hospitalInfo[5].toNumber() * 1000)
        };

        this.setState({
          isAuthorizedHospital: true,
          hospitalInfo: hospitalData
        });

        console.log('âœ… Hospital authorized:', hospitalData);
      } else {
        console.log('âŒ Hospital not authorized');
        this.setState({
          isAuthorizedHospital: false,
          hospitalInfo: null
        });
      }

    } catch (error) {
      console.error('âŒ Error checking hospital authorization:', error);
      this.setState({ 
        error: `Failed to check authorization: ${error.message}`,
        isAuthorizedHospital: false
      });
    }
  };

  // Load registered children from blockchain (for this hospital)
  loadRegisteredChildren = async () => {
    try {
      const { contract, connectedWallet, contractDeployed } = this.props;
      
      if (!contractDeployed || !contract || !connectedWallet) {
        return;
      }

      // Get total children count
      const totalChildren = await contract.getTotalChildren();
      const childrenRegisteredByThisHospital = [];

      // Check each child to see if registered by this hospital
      for (let i = 1; i <= Math.min(totalChildren.toNumber(), 50); i++) { // Limit to prevent gas issues
        try {
          const childInfo = await contract.children(i);
          if (childInfo[4] && childInfo[4].toLowerCase() === connectedWallet.toLowerCase()) { // hospitalWallet
            childrenRegisteredByThisHospital.push({
              tokenId: i,
              name: childInfo[1],
              birthDate: new Date(childInfo[2].toNumber() * 1000),
              parentWallet: childInfo[3],
              hospitalWallet: childInfo[4]
            });
          }
        } catch (error) {
          console.log(`Error loading child ${i}:`, error.message);
        }
      }

      this.setState({ registeredChildren: childrenRegisteredByThisHospital });
      console.log('ðŸ“Š Children registered by this hospital:', childrenRegisteredByThisHospital.length);

    } catch (error) {
      console.error('âŒ Error loading registered children:', error);
    }
  };

  handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    
    if (name.includes('notificationPreferences.')) {
      const prefKey = name.split('.')[1];
      this.setState({
        formData: {
          ...this.state.formData,
          notificationPreferences: {
            ...this.state.formData.notificationPreferences,
            [prefKey]: checked
          }
        }
      });
    } else {
      this.setState({
        formData: {
          ...this.state.formData,
          [name]: type === 'checkbox' ? checked : value
        }
      });
    }

    // Generate vaccination schedule when birth date changes
    if (name === 'dateOfBirth' && value) {
      this.generateVaccinationSchedule(value);
    }
  };

  handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      this.setState({ profileImage: file });
    }
  };

  // Generate personalized vaccination schedule based on birth date
  generateVaccinationSchedule = (birthDate) => {
    const birth = new Date(birthDate);
    const schedule = [];

    Object.entries(VACCINE_SCHEDULE).forEach(([vaccine, info]) => {
      const dueDate = new Date(birth.getTime() + (info.ageInDays * 24 * 60 * 60 * 1000));
      schedule.push({
        vaccine,
        description: info.description,
        ageGroup: info.category,
        dueDate: dueDate.toISOString(),
        dueDateFormatted: dueDate.toLocaleDateString(),
        status: 'Scheduled',
        mandatory: info.mandatory,
        sideEffects: info.sideEffects
      });
    });

    // Sort by due date
    schedule.sort((a, b) => new Date(a.dueDate) - new Date(b.dueDate));
    
    this.setState({ generatedSchedule: schedule });
  };

  validateParentAddress = (address) => {
    const ethRegex = /^0x[a-fA-F0-9]{40}$/;
    return ethRegex.test(address);
  };

  validateForm = () => {
    const { childName, dateOfBirth, parentName, parentAddress, contactInfo, parentEmail } = this.state.formData;
    
    if (!childName.trim()) {
      this.setState({ error: 'Child name is required' });
      return false;
    }
    if (!dateOfBirth) {
      this.setState({ error: 'Date of birth is required' });
      return false;
    }
    if (!parentName.trim()) {
      this.setState({ error: 'Parent name is required' });
      return false;
    }
    if (!parentAddress.trim()) {
      this.setState({ error: 'Parent wallet address is required' });
      return false;
    }
    if (!this.validateParentAddress(parentAddress)) {
      this.setState({ error: 'Invalid parent wallet address format' });
      return false;
    }
    if (!contactInfo.trim()) {
      this.setState({ error: 'Contact information is required' });
      return false;
    }
    if (!parentEmail.trim()) {
      this.setState({ error: 'Parent email is required' });
      return false;
    }

    // Check if birth date is not in the future
    const birthDate = new Date(dateOfBirth);
    const today = new Date();
    if (birthDate > today) {
      this.setState({ error: 'Birth date cannot be in the future' });
      return false;
    }

    return true;
  };

  // Register child on blockchain
  registerChild = async () => {
    try {
      const { contract, sendTransaction, connectedWallet, contractDeployed, generateQRData } = this.props;
      
      if (!contractDeployed) {
        alert(ERROR_MESSAGES.CONTRACT_NOT_DEPLOYED);
        return;
      }

      if (!contract) {
        alert(ERROR_MESSAGES.WALLET_NOT_CONNECTED);
        return;
      }

      if (!this.validateForm()) {
        return;
      }

      this.setState({ loading: true, error: null, txHash: null });

      const { childName, dateOfBirth, parentAddress } = this.state.formData;
      
      // Convert birth date to timestamp
      const birthTimestamp = Math.floor(new Date(dateOfBirth).getTime() / 1000);

      console.log('ðŸ‘¶ Registering child on blockchain:', {
        name: childName,
        birthDate: dateOfBirth,
        birthTimestamp,
        parentWallet: parentAddress,
        hospitalWallet: connectedWallet
      });

      // Call smart contract registerChild function
      const receipt = await sendTransaction(
        contract.registerChild,
        [childName, birthTimestamp, parentAddress],
        { gasLimit: GAS_LIMITS.REGISTER_CHILD }
      );

      console.log('âœ… Child registration transaction successful:', receipt.transactionHash);

      // Get the token ID from the transaction receipt events
      let tokenId = null;
      if (receipt.events && receipt.events.length > 0) {
        const childRegisteredEvent = receipt.events.find(event => event.event === 'ChildRegistered');
        if (childRegisteredEvent) {
          tokenId = childRegisteredEvent.args.tokenId.toNumber();
        }
      }

      // If we couldn't get token ID from events, get total children count
      if (!tokenId) {
        const totalChildren = await contract.getTotalChildren();
        tokenId = totalChildren.toNumber();
      }

      console.log('ðŸ†” Child token ID:', tokenId);

      // Generate unique QR code data for this child
      const qrData = generateQRData('child', {
        tokenId: tokenId,
        childName: childName,
        parentAddress: parentAddress,
        hospitalWallet: connectedWallet,
        birthDate: dateOfBirth,
        registrationDate: new Date().toISOString()
      });

      // Create enhanced child record
      const childRecord = {
        tokenId: tokenId,
        ...this.state.formData,
        registeredByHospital: this.state.hospitalInfo.hospitalName,
        hospitalAddress: connectedWallet,
        registrationDate: new Date().toISOString(),
        vaccinationSchedule: this.state.generatedSchedule,
        qrData: qrData,
        blockchainData: {
          transactionHash: receipt.transactionHash,
          blockNumber: receipt.blockNumber,
          gasUsed: receipt.gasUsed.toString()
        }
      };

      this.setState({
        loading: false,
        registeredChild: childRecord,
        step: 3,
        txHash: receipt.transactionHash,
        blockchainData: {
          tokenId: tokenId,
          transactionHash: receipt.transactionHash,
          blockNumber: receipt.blockNumber
        },
        error: null
      });

      // Show success message
      alert(SUCCESS_MESSAGES.CHILD_REGISTERED + '\n\nToken ID: ' + tokenId + '\nTransaction: ' + receipt.transactionHash.slice(0, 10) + '...');

      // Refresh registered children list
      setTimeout(() => {
        this.loadRegisteredChildren();
      }, 2000);

    } catch (error) {
      console.error('âŒ Child registration failed:', error);
      
      let errorMessage = ERROR_MESSAGES.TRANSACTION_FAILED;
      
      if (error.message.includes('user rejected')) {
        errorMessage = 'Transaction cancelled by user';
      } else if (error.message.includes('insufficient funds')) {
        errorMessage = 'Insufficient ETH for gas fees';
      } else if (error.message.includes('unauthorized')) {
        errorMessage = 'Hospital not authorized to register children';
      }

      this.setState({ 
        loading: false,
        error: errorMessage
      });

      alert(`Registration failed: ${errorMessage}`);
    }
  };

  // Generate vaccination reminders (for future implementation)
  generateVaccinationReminders = (tokenId, schedule) => {
    const reminders = schedule.map(vaccination => ({
      id: `${tokenId}_${vaccination.vaccine}`,
      tokenId,
      vaccine: vaccination.vaccine,
      dueDate: vaccination.dueDate,
      reminderDate: new Date(new Date(vaccination.dueDate).getTime() - (7 * 24 * 60 * 60 * 1000)).toISOString(),
      sent: false,
      type: 'vaccination_due'
    }));

    console.log('ðŸ“… Generated reminders for token:', tokenId, reminders);
    return reminders;
  };

  resetForm = () => {
    this.setState({
      formData: {
        childName: '',
        dateOfBirth: '',
        parentName: '',
        contactInfo: '',
        parentEmail: '',
        parentAddress: '',
        emergencyContact: '',
        medicalHistory: '',
        allergies: '',
        preferredLanguage: 'English',
        notificationPreferences: {
          email: true,
          sms: false,
          push: true
        }
      },
      profileImage: null,
      registeredChild: null,
      step: 1,
      generatedSchedule: [],
      txHash: null,
      error: null,
      blockchainData: null
    });
  };

