        </div>

        <div className="action-buttons">
          <button onClick={this.resetForm} className="btn-primary">
            ðŸ‘¶ Register Another Child
          </button>
          <button 
            onClick={() => this.props.onChangeView && this.props.onChangeView('vaccination-record')}
            className="btn-secondary"
          >
            ðŸ’‰ Record Vaccination
          </button>
        </div>
      </div>
    );
  }

  render() {
    const { isAuthorizedHospital, step, loading, error } = this.state;
    const { contractDeployed } = this.props;

    if (!isAuthorizedHospital) {
      return (
        <div className="App">
          <div className="main-content">
            <div className="authorization-error">
              <div className="error-container">
                <div className="error-icon">ðŸ¥</div>
                <h2>Hospital Authorization Required</h2>
                <p>Only authorized hospitals can register children in the vaccination system.</p>
                
                <div className="registration-prompt">
                  <p>Your hospital needs to be registered and authorized first.</p>
                  <div className="requirements-list">
                    <h4>Requirements:</h4>
                    <ol>
                      <li>âœ… Connect your hospital's wallet</li>
                      <li>ðŸ“ Register your hospital using the Hospital Registration form</li>
                      <li>â³ Wait for admin authorization</li>
                      <li>ðŸŽ‰ Return here to register children</li>
                    </ol>
                  </div>
                  
                  {contractDeployed ? (
                    <p><strong>Status:</strong> Hospital not found on blockchain or not authorized</p>
                  ) : (
                    <p><strong>Status:</strong> Contract not deployed - currently in demo mode</p>
                  )}
                </div>

                <div className="action-buttons">
                  <button 
                    onClick={() => this.props.onChangeView && this.props.onChangeView('hospital-register')}
                    className="btn-primary"
                  >
                    ðŸ¥ Register Hospital
                  </button>
                  <button onClick={this.props.onBack} className="btn-secondary">
                    â† Back to Dashboard
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      );
    }

    return (
      <div className="App">
        <div className="main-content">
          <div className="hospital-child-registration">
            <div className="registration-container">
              <div className="progress-bar">
                <div className={`step ${step >= 1 ? 'active' : ''}`}>
                  <span>1</span>
                  <label>Child Info</label>
                </div>
                <div className={`step ${step >= 2 ? 'active' : ''}`}>
                  <span>2</span>
                  <label>Medical & Schedule</label>
                </div>
                <div className={`step ${step >= 3 ? 'active' : ''}`}>
                  <span>3</span>
                  <label>Complete</label>
                </div>
              </div>

              {error && (
                <div className="error-message" style={{
                  background: '#ffebee',
                  border: '1px solid #f44336',
                  padding: '12px',
                  borderRadius: '8px',
                  color: '#d32f2f',
                  margin: '15px 0'
                }}>
                  âŒ {error}
                  <button 
                    onClick={() => this.setState({ error: null })}
                    style={{
                      float: 'right',
                      background: 'none',
                      border: 'none',
                      color: '#d32f2f',
                      cursor: 'pointer',
                      fontSize: '18px'
                    }}
                  >âœ•</button>
                </div>
              )}

              {step === 1 && this.renderStep1()}
              {step === 2 && this.renderStep2()}
              {step === 3 && this.renderStep3()}

              {step < 3 && (
                <div className="form-actions">
                  {step > 1 && (
                    <button 
                      onClick={() => this.setState({ step: step - 1 })}
                      className="btn-secondary"
                      disabled={loading}
                    >
                      â† Previous
                    </button>
                  )}
                  
                  {step < 2 && (
                    <button 
                      onClick={() => this.setState({ step: 2 })}
                      className="btn-primary"
                      disabled={
                        !this.state.formData.childName || 
                        !this.state.formData.dateOfBirth || 
                        !this.state.formData.parentName || 
                        !this.state.formData.parentAddress ||
                        !this.state.formData.parentEmail ||
                        !this.validateParentAddress(this.state.formData.parentAddress) ||
                        loading
                      }
                    >
                      Next: Generate Schedule â†’
                    </button>
                  )}
                  
                  {step === 2 && (
                    <button 
                      onClick={this.registerChild}
                      className="btn-primary"
                      disabled={loading}
                    >
                      {loading ? 'â³ Creating NFT...' : 
                       contractDeployed ? 'ðŸ” Register on Blockchain' : 
                       'ðŸ” Complete Registration (Demo)'}
                    </button>
                  )}

                  <button onClick={this.props.onBack} className="btn-secondary" disabled={loading}>
                    â† Back to Dashboard
                  </button>
                </div>
              )}

              {loading && (
                <div className="loading-info">
                  <div className="loading-spinner">â³</div>
                  <p><strong>ðŸ“¡ Processing Child Registration...</strong></p>
                  {contractDeployed ? (
                    <>
                      <p>ðŸ”— Sending transaction to blockchain...</p>
                      <p>ðŸ‘¶ Creating child NFT record...</p>
                      <p>ðŸ“¤ Sending NFT to parent's wallet...</p>
                      <p>ðŸ“… Generating vaccination schedule...</p>
                      <p>â³ Please confirm transaction in MetaMask...</p>
                    </>
                  ) : (
                    <>
                      <p>ðŸ’¾ Saving registration data...</p>
                      <p>ðŸ“… Generating personalized vaccination schedule...</p>
                      <p>ðŸ” Creating verification QR codes...</p>
                      <p>ðŸ”” Setting up automated reminders...</p>
                    </>
                  )}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default ChildRegistration;
