import React, { useState } from 'react';
import { ethers } from 'ethers';

const QRVerification = ({ contract, account, onBack }) => {
  const [tokenId, setTokenId] = useState('');
  const [qrCodeHash, setQrCodeHash] = useState('');
  const [verificationResult, setVerificationResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [verificationMode, setVerificationMode] = useState('tokenId'); // 'tokenId' or 'qrHash'

  const verifyByTokenId = async () => {
    if (!tokenId.trim()) {
      alert('Please enter a valid Token ID');
      return;
    }

    try {
      setLoading(true);
      
      // Get child information
      const childInfo = await contract.getChildInfo(tokenId);
      
      // Get vaccination history
      const vaccinationHistory = await contract.getVaccinationHistory(tokenId);
      
      // Generate verification QR data
      const verificationData = await contract.generateVerificationQR(tokenId);
      
      // Get upcoming vaccinations
      const upcomingVacs = await contract.getUpcomingVaccinations(tokenId);
      
      const result = {
        isValid: true,
        tokenId: tokenId,
        childName: childInfo.name,
        parentName: childInfo.parentName,
        dateOfBirth: new Date(childInfo.dateOfBirth * 1000).toLocaleDateString(),
        registeredByHospital: childInfo.hospitalName,
        totalVaccinations: vaccinationHistory.length,
        isUpToDate: verificationData.isUpToDate,
        verificationData: verificationData.qrData,
        vaccinationHistory: vaccinationHistory,
        upcomingVaccinations: upcomingVacs,
        verificationTime: new Date().toLocaleString()
      };
      
      setVerificationResult(result);
      setLoading(false);
      
    } catch (error) {
      console.error('Verification error:', error);
      
      setVerificationResult({
        isValid: false,
        error: 'Token ID not found or invalid',
        errorDetails: error.message,
        verificationTime: new Date().toLocaleString()
      });
      
      setLoading(false);
    }
  };

  const verifyQRCode = async () => {
    if (!qrCodeHash.trim()) {
      alert('Please enter a QR code hash or scan QR code');
      return;
    }

    try {
      setLoading(true);
      
      // Try to parse QR code data if it's JSON
      let qrData;
      try {
        qrData = JSON.parse(qrCodeHash);
      } catch {
        // If not JSON, treat as simple hash
        qrData = { qrCodeHash: qrCodeHash };
      }
      
      // If QR data contains tokenId, verify that
      if (qrData.tokenId) {
        const childInfo = await contract.getChildInfo(qrData.tokenId);
        const vaccinationHistory = await contract.getVaccinationHistory(qrData.tokenId);
        
        // Find vaccination with matching QR hash
        const matchingVaccination = vaccinationHistory.find(vac => 
          vac.qrCodeHash === qrData.qrCodeHash
        );
        
        const result = {
          isValid: true,
          tokenId: qrData.tokenId,
          childName: childInfo.name,
          parentName: childInfo.parentName,
          qrCodeHash: qrData.qrCodeHash,
          vaccinationDetails: matchingVaccination,
          hospitalName: qrData.hospitalName || matchingVaccination?.hospitalName,
          vaccinationDate: qrData.date,
          totalVaccinations: vaccinationHistory.length,
          verificationTime: new Date().toLocaleString(),
          qrVerified: !!matchingVaccination
        };
        
        setVerificationResult(result);
      } else {
        // QR code doesn't contain recognizable data
        setVerificationResult({
          isValid: false,
          error: 'Invalid QR code format',
          errorDetails: 'QR code does not contain valid vaccination data',
          verificationTime: new Date().toLocaleString()
        });
      }
      
      setLoading(false);
      
    } catch (error) {
      console.error('QR verification error:', error);
      
      setVerificationResult({
        isValid: false,
        error: 'QR code verification failed',
        errorDetails: error.message,
        verificationTime: new Date().toLocaleString()
      });
      
      setLoading(false);
    }
  };

  const handleVerification = () => {
    setVerificationResult(null);
    
    if (verificationMode === 'tokenId') {
      verifyByTokenId();
    } else {
      verifyQRCode();
    }
  };

  const formatDate = (timestamp) => {
    return new Date(timestamp * 1000).toLocaleDateString();
  };

  const VerificationResult = ({ result }) => {
    if (!result.isValid) {
      return (
        <div className="verification-error">
          <div className="error-icon">❌</div>
          <h3>Verification Failed</h3>
          <p><strong>Error:</strong> {result.error}</p>
          {result.errorDetails && (
            <p><strong>Details:</strong> {result.errorDetails}</p>
          )}
          <p><strong>Verified at:</strong> {result.verificationTime}</p>
        </div>
      );
    }

    return (
      <div className="verification-success">
        <div className="success-icon">✅</div>
        <h3>Verification Successful</h3>
        
        <div className="verification-details">
          <div className="detail-section">
            <h4>👶 Child Information</h4>
            <div className="detail-grid">
              <div className="detail-item">
                <label>Name:</label>
                <span>{result.childName}</span>
              </div>
              <div className="detail-item">
                <label>Token ID:</label>
                <span>{result.tokenId}</span>
              </div>
              <div className="detail-item">
                <label>Parent:</label>
                <span>{result.parentName}</span>
              </div>
              <div className="detail-item">
                <label>Date of Birth:</label>
                <span>{result.dateOfBirth}</span>
              </div>
              {result.registeredByHospital && (
                <div className="detail-item">
                  <label>Registered by:</label>
                  <span>{result.registeredByHospital}</span>
                </div>
              )}
            </div>
          </div>

          <div className="detail-section">
            <h4>💉 Vaccination Status</h4>
            <div className="vaccination-status">
              <div className="status-item">
                <label>Total Vaccinations:</label>
                <span className="status-number">{result.totalVaccinations}</span>
              </div>
              <div className="status-item">
                <label>Status:</label>
                <span className={`status-badge ${result.isUpToDate ? 'up-to-date' : 'pending'}`}>
                  {result.isUpToDate ? '✅ Up to Date' : '⚠️ Pending Vaccinations'}
                </span>
              </div>
            </div>
          </div>

          {result.qrCodeHash && (
            <div className="detail-section">
              <h4>🔍 QR Code Verification</h4>
              <div className="qr-details">
                <div className="detail-item">
                  <label>QR Hash:</label>
                  <span className="qr-hash">{result.qrCodeHash}</span>
                </div>
                <div className="detail-item">
                  <label>QR Status:</label>
                  <span className={`status-badge ${result.qrVerified ? 'verified' : 'unverified'}`}>
                    {result.qrVerified ? '✅ Verified' : '❌ Not Found'}
                  </span>
                </div>
              </div>
            </div>
          )}

          {result.vaccinationDetails && (
            <div className="detail-section">
              <h4>💉 Specific Vaccination</h4>
              <div className="vaccination-card">
                <div className="vaccination-header">
                  <strong>{result.vaccinationDetails.vaccineName}</strong>
                  <span className="vaccination-date">
                    {formatDate(result.vaccinationDetails.dateAdministered)}
                  </span>
                </div>
                <div className="vaccination-info">
                  <p><strong>Hospital:</strong> {result.vaccinationDetails.hospitalName}</p>
                  <p><strong>Batch Number:</strong> {result.vaccinationDetails.batchNumber}</p>
                  {result.vaccinationDetails.nextDueDate > 0 && (
                    <p><strong>Next Due:</strong> {formatDate(result.vaccinationDetails.nextDueDate)}</p>
                  )}
                </div>
              </div>
            </div>
          )}

          {result.vaccinationHistory && result.vaccinationHistory.length > 0 && (
            <div className="detail-section">
              <h4>📋 Complete Vaccination History</h4>
              <div className="history-list">
                {result.vaccinationHistory.slice(-5).map((vac, index) => (
                  <div key={index} className="history-item">
                    <div className="history-header">
                      <strong>{vac.vaccineName}</strong>
                      <span className="history-date">{formatDate(vac.dateAdministered)}</span>
                    </div>
                    <div className="history-details">
                      <span>{vac.hospitalName}</span>
                      <span>Batch: {vac.batchNumber}</span>
                      {vac.qrCodeHash && <span className="qr-available">🔍 QR Available</span>}
                    </div>
                  </div>
                ))}
                {result.vaccinationHistory.length > 5 && (
                  <p className="more-history">+ {result.vaccinationHistory.length - 5} more vaccinations</p>
                )}
              </div>
            </div>
          )}

          <div className="verification-footer">
            <p><strong>Verified at:</strong> {result.verificationTime}</p>
            <p><strong>Blockchain Status:</strong> ✅ Confirmed</p>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="qr-verification">
      <div className="verification-container">
        <div className="verification-header">
          <h1>🔍 Vaccination Record Verification</h1>
          <p>Verify vaccination records using Token ID or QR code</p>
          <button onClick={onBack} className="btn-secondary back-btn">
            ← Back to Dashboard
          </button>
        </div>

        <div className="verification-modes">
          <div className="mode-selector">
            <button
              className={`mode-btn ${verificationMode === 'tokenId' ? 'active' : ''}`}
              onClick={() => {
                setVerificationMode('tokenId');
                setVerificationResult(null);
              }}
            >
              🆔 Token ID Verification
            </button>
            <button
              className={`mode-btn ${verificationMode === 'qrHash' ? 'active' : ''}`}
              onClick={() => {
                setVerificationMode('qrHash');
                setVerificationResult(null);
              }}
            >
              📱 QR Code Verification
            </button>
          </div>
        </div>

        <div className="verification-form">
          {verificationMode === 'tokenId' ? (
            <div className="form-section">
              <h3>🆔 Enter Token ID</h3>
              <p>Enter the child's Token ID to verify their complete vaccination record</p>
              <div className="input-group">
                <input
                  type="text"
                  value={tokenId}
                  onChange={(e) => setTokenId(e.target.value)}
                  placeholder="Enter Token ID (e.g., 1, 2, 3...)"
                  className="verification-input"
                  disabled={loading}
                />
                <button
                  onClick={handleVerification}
                  disabled={loading || !tokenId.trim()}
                  className="verify-btn"
                >
                  {loading ? '⏳ Verifying...' : '🔍 Verify Record'}
                </button>
              </div>
            </div>
          ) : (
            <div className="form-section">
              <h3>📱 QR Code Verification</h3>
              <p>Paste QR code data or hash to verify specific vaccination</p>
              <div className="input-group">
                <textarea
                  value={qrCodeHash}
                  onChange={(e) => setQrCodeHash(e.target.value)}
                  placeholder="Paste QR code data or hash here..."
                  className="verification-textarea"
                  rows="4"
                  disabled={loading}
                />
                <button
                  onClick={handleVerification}
                  disabled={loading || !qrCodeHash.trim()}
                  className="verify-btn"
                >
                  {loading ? '⏳ Verifying...' : '🔍 Verify QR Code'}
                </button>
              </div>
              <div className="qr-help">
                <h4>💡 QR Code Help</h4>
                <ul>
                  <li>QR codes are generated automatically when vaccinations are recorded</li>
                  <li>Each QR code contains unique verification data</li>
                  <li>QR codes can be scanned or the data can be pasted directly</li>
                  <li>Valid QR codes contain JSON data with tokenId and verification hash</li>
                </ul>
              </div>
            </div>
          )}
        </div>

        {loading && (
          <div className="verification-loading">
            <div className="loading-spinner">⏳</div>
            <p>Verifying record on blockchain...</p>
          </div>
        )}

        {verificationResult && (
          <div className="verification-results">
            <VerificationResult result={verificationResult} />
            
            <div className="verification-actions">
              <button
                onClick={() => {
                  setVerificationResult(null);
                  setTokenId('');
                  setQrCodeHash('');
                }}
                className="btn-secondary"
              >
                🔄 Verify Another Record
              </button>
              
              {verificationResult.isValid && (
                <button
                  onClick={() => {
                    const printWindow = window.open('', '_blank');
                    printWindow.document.write(`
                      <html>
                        <head><title>Vaccination Verification Report</title></head>
                        <body>
                          <h1>Vaccination Verification Report</h1>
                          <h2>Child: ${verificationResult.childName}</h2>
                          <p><strong>Token ID:</strong> ${verificationResult.tokenId}</p>
                          <p><strong>Parent:</strong> ${verificationResult.parentName}</p>
                          <p><strong>Total Vaccinations:</strong> ${verificationResult.totalVaccinations}</p>
                          <p><strong>Status:</strong> ${verificationResult.isUpToDate ? 'Up to Date' : 'Pending'}</p>
                          <p><strong>Verified:</strong> ${verificationResult.verificationTime}</p>
                          <p><strong>Blockchain Confirmed:</strong> Yes</p>
                        </body>
                      </html>
                    `);
                    printWindow.document.close();
                    printWindow.print();
                  }}
                  className="btn-primary"
                >
                  🖨️ Print Report
                </button>
              )}
            </div>
          </div>
        )}

        <div className="verification-info">
          <h3>🛡️ How Verification Works</h3>
          <div className="info-grid">
            <div className="info-card">
              <h4>🆔 Token ID Verification</h4>
              <p>Provides complete vaccination history and status for a child using their unique blockchain token ID.</p>
            </div>
            <div className="info-card">
              <h4>📱 QR Code Verification</h4>
              <p>Verifies specific vaccination records using QR codes generated during vaccination recording.</p>
            </div>
            <div className="info-card">
              <h4>🔒 Blockchain Security</h4>
              <p>All verifications are performed against immutable blockchain records that cannot be forged or altered.</p>
            </div>
            <div className="info-card">
              <h4>⚡ Instant Results</h4>
              <p>Get immediate verification results without requiring internet access to centralized databases.</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default QRVerification;
