import React, { Component } from 'react';

class HospitalRegistration extends Component {
  constructor(props) {
    super(props);
    this.state = {
      formData: {
        hospitalName: '',
        licenseNumber: '',
        contactInfo: '',
        address: '',
        website: ''
      },
      loading: false,
      hospitalStatus: null,
      isRegistered: false,
      isAuthorized: false,
      error: null,
      txHash: null
    };
  }

  componentDidMount() {
    this.checkHospitalStatus();
  }

  checkHospitalStatus = async () => {
    try {
      const { contract, connectedWallet } = this.props;
      if (!contract || !connectedWallet) return;

      this.setState({ loading: true, error: null });

      const hospitalInfo = await contract.getHospitalInfo(connectedWallet);
      const isAuthorized = await contract.authorizedHospitals(connectedWallet);

      if (hospitalInfo.name && hospitalInfo.name !== '') {
        this.setState({
          isRegistered: true,
          isAuthorized,
          hospitalStatus: {
            name: hospitalInfo.name,
            licenseNumber: hospitalInfo.licenseNumber,
            contactInfo: hospitalInfo.contactInfo,
            isAuthorized,
            registrationDate: hospitalInfo.registrationDate
          },
          formData: {
            ...this.state.formData,
            hospitalName: hospitalInfo.name,
            licenseNumber: hospitalInfo.licenseNumber,
            contactInfo: hospitalInfo.contactInfo
          },
          loading: false
        });
      } else {
        this.setState({ loading: false });
      }
    } catch (error) {
      this.setState({
        error: `Failed to check hospital status: ${error.message}`,
        loading: false
      });
    }
  };

  handleInputChange = (e) => {
    const { name, value } = e.target;
    this.setState({
      formData: {
        ...this.state.formData,
        [name]: value
      }
    });
  };

  validateForm = () => {
    const { hospitalName, licenseNumber, contactInfo } = this.state.formData;
    return (
      hospitalName.trim() !== '' &&
      licenseNumber.trim() !== '' &&
      contactInfo.trim() !== ''
    );
  };

  registerHospital = async () => {
    try {
      const { contract, sendTransaction } = this.props;
      const { hospitalName, licenseNumber, contactInfo } = this.state.formData;

      if (!contract) throw new Error('Contract not available');
      if (!this.validateForm()) return;

      this.setState({ loading: true, error: null, txHash: null });

      const receipt = await sendTransaction(
        contract.registerHospital,
        [hospitalName, licenseNumber, contactInfo]
      );

      this.setState({
        loading: false,
        isRegistered: true,
        isAuthorized: false,
        txHash: receipt.transactionHash,
        hospitalStatus: {
          name: hospitalName,
          licenseNumber,
          contactInfo,
          isAuthorized: false,
          registrationDate: new Date()
        }
      });

      alert(`Hospital registered successfully!\nTransaction: ${receipt.transactionHash.slice(0, 10)}...`);
      setTimeout(() => this.checkHospitalStatus(), 2000);
    } catch (error) {
      let errorMessage = 'Registration failed';
      if (error.message.includes('user rejected')) errorMessage = 'Transaction cancelled by user';
      else if (error.message.includes('insufficient funds')) errorMessage = 'Insufficient ETH for gas fees';
      else if (error.message.includes('already registered')) errorMessage = 'Hospital is already registered';

      this.setState({ loading: false, error: errorMessage });
      alert(`Registration failed: ${errorMessage}`);
    }
  };

  renderHospitalStatus() {
    const { hospitalStatus, isAuthorized, txHash } = this.state;

    return (
      <div className="hospital-status">
        <div className="status-container">
          <div className="hospital-header">
            <h2>🏥 Hospital Status</h2>
            <div className={`status-badge ${isAuthorized ? 'authorized' : 'pending'}`}>
              {isAuthorized ? '✅ Authorized' : '⏳ Pending Authorization'}
            </div>
          </div>

          <div className="hospital-info-card">
            <h3>{hospitalStatus?.name}</h3>
            <div className="info-grid">
              <div className="info-item"><label>License Number:</label><span>{hospitalStatus?.licenseNumber}</span></div>
              <div className="info-item"><label>Contact Info:</label><span>{hospitalStatus?.contactInfo}</span></div>
              <div className="info-item"><label>Wallet Address:</label><span className="wallet-address">{this.props.connectedWallet}</span></div>
              <div className="info-item"><label>Authorization Status:</label>
                <span className={isAuthorized ? 'authorized' : 'pending'}>
                  {isAuthorized ? 'Authorized ✅' : 'Pending Admin Approval ⏳'}
                </span>
              </div>
              {txHash && (
                <div className="info-item">
                  <label>Transaction Hash:</label>
                  <span className="tx-hash" title={txHash}>
                    {txHash.slice(0, 10)}...{txHash.slice(-8)}
                  </span>
                </div>
              )}
            </div>
          </div>

          {!isAuthorized ? (
            <div className="pending-message">
              <div className="pending-icon">⏳</div>
              <h3>Waiting for Authorization</h3>
              <p>Your hospital is registered on blockchain but needs admin approval.</p>
              <ul>
                <li>✅ Registration completed</li>
                <li>🔍 Admin will verify credentials</li>
                <li>⏳ Usually authorized within 24-48 hours</li>
              </ul>
            </div>
          ) : (
            <div className="authorized-message">
              <h3>✅ Hospital Authorized!</h3>
              <ul>
                <li>👶 Register newborns</li>
                <li>💉 Record vaccinations</li>
                <li>🔗 Issue QR codes</li>
              </ul>
              <div className="action-buttons">
                <button onClick={() => this.props.onChangeView?.('child-register')} className="btn-primary">👶 Register Child</button>
                <button onClick={() => this.props.onChangeView?.('vaccination-record')} className="btn-primary">💉 Record Vaccination</button>
              </div>
            </div>
          )}

          <button onClick={this.props.onBack} className="btn-secondary">← Back to Dashboard</button>
        </div>
      </div>
    );
  }

  renderRegistrationForm() {
    const { formData, loading, error } = this.state;

    return (
      <div className="hospital-registration">
        <div className="registration-container">
          <div className="registration-header">
            <h2>🏥 Hospital Registration</h2>
            <p>Register your hospital to manage child vaccinations on blockchain</p>
          </div>

          {error && (
            <div className="error-message">
              ❌ {error}
              <button onClick={() => this.setState({ error: null })}>✕</button>
            </div>
          )}

          <div className="form-group">
            <label>Hospital Name *</label>
            <input
              type="text"
              name="hospitalName"
              value={formData.hospitalName}
              onChange={this.handleInputChange}
              placeholder="Hospital name"
              disabled={loading}
            />
          </div>

          <div className="form-group">
            <label>License Number *</label>
            <input
              type="text"
              name="licenseNumber"
              value={formData.licenseNumber}
              onChange={this.handleInputChange}
              placeholder="License #"
              disabled={loading}
            />
          </div>

          <div className="form-group">
            <label>Contact Info *</label>
            <input
              type="text"
              name="contactInfo"
              value={formData.contactInfo}
              onChange={this.handleInputChange}
              placeholder="Email / Phone"
              disabled={loading}
            />
          </div>

          <div className="wallet-info">
            <label>Connected Wallet:</label>
            <div className="wallet-address">{this.props.connectedWallet || 'Not Connected'}</div>
          </div>

          <div className="form-actions">
            <button onClick={this.props.onBack} className="btn-secondary" disabled={loading}>
              ← Back to Dashboard
            </button>
            <button
              onClick={this.registerHospital}
              className="btn-primary"
              disabled={loading || !this.validateForm() || !this.props.connectedWallet}
            >
              {loading ? '⏳ Registering...' : '🏥 Register on Blockchain'}
            </button>
          </div>
        </div>
      </div>
    );
  }

  render() {
    return (
      <div className="App">
        <div className="main-content">
          {this.state.isRegistered ? this.renderHospitalStatus() : this.renderRegistrationForm()}
        </div>
      </div>
    );
  }
}

export default HospitalRegistration;
