import React, { Component } from 'react';

class QRVerification extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tokenId: '',
      qrHash: '',
      childInfo: null,
      verificationStatus: null,
      loading: false,
      error: null
    };
  }

  handleInputChange = (e) => {
    const { name, value } = e.target;
    this.setState({ [name]: value });
  };

  formatDate = (timestamp) => {
    const date = new Date(timestamp * 1000);
    return date.toLocaleDateString();
  };

  verifyQRCode = async () => {
    const { qrHash, tokenId } = this.state;

    if (!qrHash || !tokenId) {
      this.setState({ error: 'Please enter both Token ID and QR Hash' });
      return;
    }

    this.setState({ loading: true, error: null, verificationStatus: null });

    try {
      const { contract } = this.props;
      if (!contract) throw new Error('Contract not connected');

      const [qrCode, storedHash, timestamp, isValid] = await contract.generateVerificationQR(tokenId);

      if (storedHash === qrHash && isValid) {
        const childData = await contract.getChildInfo(tokenId);

        this.setState({
          verificationStatus: 'valid',
          childInfo: {
            name: childData[0],
            dateOfBirth: this.formatDate(childData[1].toNumber()),
            parentName: childData[2],
            hospitalName: childData[6],
            timestamp: this.formatDate(timestamp.toNumber())
          },
          loading: false
        });
      } else {
        this.setState({
          verificationStatus: 'invalid',
          childInfo: null,
          loading: false
        });
      }

    } catch (err) {
      console.error('Verification failed:', err);
      this.setState({ error: 'Verification failed', loading: false });
    }
  };

  render() {
    const { tokenId, qrHash, verificationStatus, childInfo, loading, error } = this.state;

    return (
      <div className="qr-verification">
        <h2>üì± QR Code Verification</h2>
        <p>Verify a child‚Äôs vaccination record using their Token ID and QR Hash</p>

        {error && <div className="error-box">‚ùå {error}</div>}

        <div className="form-group">
          <label>Token ID</label>
          <input
            type="text"
            name="tokenId"
            value={tokenId}
            onChange={this.handleInputChange}
            placeholder="Enter Token ID"
          />
        </div>

        <div className="form-group">
          <label>QR Code Hash</label>
          <input
            type="text"
            name="qrHash"
            value={qrHash}
            onChange={this.handleInputChange}
            placeholder="Enter QR Code Hash"
          />
        </div>

        <button 
          onClick={this.verifyQRCode} 
          disabled={loading}
          className="btn-primary"
        >
          {loading ? 'üîÑ Verifying...' : 'üîç Verify Record'}
        </button>

        {verificationStatus === 'valid' && childInfo && (
          <div className="verification-success">
            <h3>‚úÖ Valid Record</h3>
            <p><strong>Child:</strong> {childInfo.name}</p>
            <p><strong>Date of Birth:</strong> {childInfo.dateOfBirth}</p>
            <p><strong>Parent:</strong> {childInfo.parentName}</p>
            <p><strong>Hospital:</strong> {childInfo.hospitalName}</p>
            <p><strong>Verified On:</strong> {childInfo.timestamp}</p>
          </div>
        )}

        {verificationStatus === 'invalid' && (
          <div className="verification-failed">
            <h3>‚ùå Invalid or Tampered QR Code</h3>
            <p>Please check the Token ID and QR hash again.</p>
          </div>
        )}

        <button onClick={this.props.onBack} className="btn-secondary">
          ‚Üê Back to Dashboard
        </button>
      </div>
    );
  }
}

export default QRVerification;
