// Vaccination Record Component
class VaccinationRecord extends Component {
  constructor(props) {
    super(props);
    this.handleChange = e => {
      this.setState({
        [e.target.name]: e.target.value
      });
    };
    this.handleSubmit = async e => {
      e.preventDefault();
      if (!this.props.contract) {
        alert('Please connect your wallet first!');
        return;
      }
      this.setState({
        loading: true,
        step: 'processing'
      });
      try {
        const nextDueDate = Math.floor((Date.now() + 30 * 24 * 60 * 60 * 1000) / 1000); // 30 days from now

        const tx = await this.props.contract.recordVaccination(this.state.tokenId, this.state.vaccineName, this.state.hospitalName, this.state.batchNumber, nextDueDate, "");
        console.log('💉 Vaccination recorded:', tx.hash);
        const receipt = await tx.wait();
        console.log('✅ Vaccination confirmed!');
        this.setState({
          step: 'success',
          loading: false,
          transactionHash: tx.hash
        });

        // Generate QR code data
        this.generateQRCode();
      } catch (error) {
        console.error('❌ Vaccination recording failed:', error);
        alert('Vaccination recording failed: ' + error.message);
        this.setState({
          loading: false,
          step: 'form'
        });
