import React, { Component } from 'react';
import { ethers } from 'ethers';
import './App.css';

const CONTRACT_ADDRESS = "0xb5a28A2c3715BA080576A2EaF80eB91e44E4499D";

// Simple ABI for basic functions
const CONTRACT_ABI = [
  {
    "inputs": [
      {"internalType": "string", "name": "_name", "type": "string"},
      {"internalType": "uint256", "name": "_dateOfBirth", "type": "uint256"},
      {"internalType": "string", "name": "_parentName", "type": "string"},
      {"internalType": "string", "name": "_contactInfo", "type": "string"},
      {"internalType": "string", "name": "_tokenURI", "type": "string"}
    ],
    "name": "registerChild",
    "outputs": [{"internalType": "uint256", "name": "", "type": "uint256"}],
    "stateMutability": "nonpayable",
    "type": "function"
  },
  {
    "inputs": [
      {"internalType": "uint256", "name": "_tokenId", "type": "uint256"},
      {"internalType": "string", "name": "_vaccineName", "type": "string"},
      {"internalType": "string", "name": "_hospitalName", "type": "string"},
      {"internalType": "string", "name": "_batchNumber", "type": "string"},
      {"internalType": "uint256", "name": "_nextDueDate", "type": "uint256"},
      {"internalType": "string", "name": "_ipfsHash", "type": "string"}
    ],
    "name": "recordVaccination",
    "outputs": [],
    "stateMutability": "nonpayable",
    "type": "function"
  },
  {
    "inputs": [{"internalType": "uint256", "name": "_tokenId", "type": "uint256"}],
    "name": "getChildInfo",
    "outputs": [
      {
        "components": [
          {"internalType": "string", "name": "name", "type": "string"},
          {"internalType": "uint256", "name": "dateOfBirth", "type": "uint256"},
          {"internalType": "string", "name": "parentName", "type": "string"},
          {"internalType": "string", "name": "contactInfo", "type": "string"},
          {"internalType": "bool", "name": "isRegistered", "type": "bool"},
          {"internalType": "uint256", "name": "registrationDate", "type": "uint256"}
        ],
        "internalType": "struct VaccinationRegistry.Child",
        "name": "",
        "type": "tuple"
      }
    ],
    "stateMutability": "view",
    "type": "function"
  },
  {
    "inputs": [{"internalType": "address", "name": "", "type": "address"}],
    "name": "authorizedHospitals",
    "outputs": [{"internalType": "bool", "name": "", "type": "bool"}],
    "stateMutability": "view",
    "type": "function"
  },
  {
    "anonymous": false,
    "inputs": [
      {"indexed": true, "internalType": "address", "name": "from", "type": "address"},
      {"indexed": true, "internalType": "address", "name": "to", "type": "address"},
      {"indexed": true, "internalType": "uint256", "name": "tokenId", "type": "uint256"}
    ],
    "name": "Transfer",
    "type": "event"
  }
];

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      account: '',
      provider: null,
      contract: null,
      loading: false,
      currentView: 'welcome',
      networkId: null,
      userType: 'unknown', // 'hospital', 'parent', 'verifier'
      isHospital: false
    };
  }

  connectWallet = async () => {
    try {
      if (typeof window.ethereum !== 'undefined') {
        this.setState({ loading: true });

        const accounts = await window.ethereum.request({ 
          method: 'eth_requestAccounts' 
        });

        if (accounts.length > 0) {
          const provider = new ethers.providers.Web3Provider(window.ethereum);
          const signer = provider.getSigner();
          const network = await provider.getNetwork();

          const contract = new ethers.Contract(
            CONTRACT_ADDRESS,
            CONTRACT_ABI,
            signer
          );

          // Check if user is authorized hospital
          let isHospital = false;
          try {
            isHospital = await contract.authorizedHospitals(accounts[0]);
          } catch (error) {
            console.log('Could not check hospital authorization:', error);
          }

          this.setState({ 
            account: accounts[0], 
            provider: provider,
            contract: contract,
            currentView: 'role-selection',
            loading: false,
            networkId: network.chainId,
            isHospital: isHospital
          });
        }
      } else {
        alert('Please install MetaMask wallet!');
      }
    } catch (error) {
      console.error('Connection error:', error);
      alert('Error connecting: ' + error.message);
      this.setState({ loading: false });
    }
  }

  disconnectWallet = () => {
    this.setState({ 
      account: '', 
      provider: null,
      contract: null,
      currentView: 'welcome',
      networkId: null,
      userType: 'unknown',
      isHospital: false
    });
  }

  selectUserType = (type) => {
    this.setState({ userType: type, currentView: 'dashboard' });
  }

  changeView = (view) => {
    this.setState({ currentView: view });
  }

  render() {
    const { account, loading, currentView, networkId, contract, userType, isHospital } = this.state;

    // Welcome Screen
    if (currentView === 'welcome') {
      return (
        <div className="App">
          <div className="welcome-screen">
            <div className="welcome-content">
              <h1>🏥 Hospital Vaccination Management System</h1>
              <p>Complete workflow for newborn registration and vaccination tracking</p>

              <div className="workflow-info">
                <h3>📋 System Workflow:</h3>
                <div className="workflow-steps">
                  <div className="workflow-step">
                    <span className="step-number">1</span>
                    <p><strong>Birth Registration:</strong> Hospital generates NFT ID for newborn</p>
                  </div>
                  <div className="workflow-step">
                    <span className="step-number">2</span>
                    <p><strong>Parent Notification:</strong> Parents receive NFT ID and vaccination schedule</p>
                  </div>
                  <div className="workflow-step">
                    <span className="step-number">3</span>
                    <p><strong>Vaccination Alerts:</strong> Automated reminders sent to parents</p>
                  </div>
                  <div className="workflow-step">
                    <span className="step-number">4</span>
                    <p><strong>QR Generation:</strong> QR code generated after each vaccination</p>
                  </div>
                </div>
              </div>

              <div className="contract-info">
                <p><strong>Smart Contract:</strong></p>
                <code>{CONTRACT_ADDRESS}</code>
              </div>

              <button 
                className="connect-btn"
                onClick={this.connectWallet}
                disabled={loading}
              >
                {loading ? 'Connecting...' : '🦊 Connect MetaMask'}
              </button>
            </div>
          </div>
        </div>
      );
    }

    // Role Selection Screen
    if (currentView === 'role-selection') {
      return (
        <div className="App">
          <div className="role-selection">
            <div className="role-content">
              <h2>👋 Welcome! Select Your Role</h2>
              <p>Connected: {account.slice(0, 6)}...{account.slice(-4)}</p>
              {isHospital && <p className="hospital-badge">🏥 Authorized Hospital Account</p>}

              <div className="role-cards">
                <div 
                  className={`role-card ${!isHospital ? 'disabled' : ''}`}
                  onClick={() => isHospital && this.selectUserType('hospital')}
                >
                  <div className="role-icon">🏥</div>
                  <h3>Hospital Staff</h3>
                  <p>Register newborns and record vaccinations</p>
                  {!isHospital && <p className="disabled-text">⚠️ Not Authorized</p>}
                </div>

                <div 
                  className="role-card"
                  onClick={() => this.selectUserType('parent')}
                >
                  <div className="role-icon">👨‍👩‍👧‍👦</div>
                  <h3>Parent/Guardian</h3>
                  <p>View child records and vaccination alerts</p>
                </div>

                <div 
                  className="role-card"
                  onClick={() => this.selectUserType('verifier')}
                >
                  <div className="role-icon">🔍</div>
                  <h3>Verifier</h3>
                  <p>Verify vaccination records via QR code</p>
                </div>
              </div>

              <button onClick={this.disconnectWallet} className="disconnect-btn">
                Disconnect Wallet
              </button>
            </div>
          </div>
        </div>
      );
    }

    // Dashboard based on user type
    if (currentView === 'dashboard') {
      return this.renderDashboard();
    }

    // Specific views
    if (currentView === 'birth-registration') {
      return (
        <BirthRegistration 
          onBack={() => this.changeView('dashboard')} 
          account={account}
          contract={contract}
        />
      );
    }

    if (currentView === 'vaccination-record') {
      return (
        <VaccinationRecord 
          onBack={() => this.changeView('dashboard')} 
          account={account}
          contract={contract}
        />
      );
    }

    if (currentView === 'parent-dashboard') {
      return (
        <ParentDashboard 
          onBack={() => this.changeView('dashboard')} 
          account={account}
          contract={contract}
        />
      );
    }

    if (currentView === 'qr-verification') {
      return (
        <QRVerification 
          onBack={() => this.changeView('dashboard')} 
          contract={contract}
        />
      );
    }

    return null;
  }

  renderDashboard = () => {
    const { userType, account, networkId } = this.state;

    const dashboards = {
      hospital: {
        title: '🏥 Hospital Dashboard',
        subtitle: 'Manage newborn registrations and vaccinations',
        actions: [
          {
            icon: '👶',
            title: 'Register Newborn',
            description: 'Generate NFT ID for new birth',
            action: () => this.changeView('birth-registration')
          },
          {
            icon: '💉',
            title: 'Record Vaccination',
            description: 'Update vaccination records',
            action: () => this.changeView('vaccination-record')
          },
          {
            icon: '📋',
            title: 'View All Records',
            description: 'Hospital patient records',
            action: () => alert('Feature coming soon!')
          }
        ]
      },
      parent: {
        title: '👨‍👩‍👧‍👦 Parent Dashboard',
        subtitle: 'View your child\'s vaccination records and alerts',
        actions: [
          {
            icon: '📱',
            title: 'My Child\'s Records',
            description: 'View vaccination history',
            action: () => this.changeView('parent-dashboard')
          },
          {
            icon: '🔔',
            title: 'Vaccination Alerts',
            description: 'Upcoming vaccination reminders',
            action: () => alert('No pending alerts')
          },
          {
            icon: '📄',
            title: 'Download QR Code',
            description: 'Get verification QR code',
            action: () => alert('Enter Token ID to generate QR')
          }
        ]
      },
      verifier: {
        title: '🔍 Verification Dashboard',
