// Birth Registration Component (Hospital Only)
class BirthRegistration extends Component {
  constructor(props) {
    super(props);
    this.state = {
      childName: '',
      dateOfBirth: '',
      timeOfBirth: '',
      parentName: '',
      parentPhone: '',
      parentEmail: '',
      hospitalName: '',
      doctorName: '',
      loading: false,
      step: 'form'
    };
  }

  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value
    });
  }

  handleSubmit = async (e) => {
    e.preventDefault();

    if (!this.props.contract) {
      alert('Please connect your wallet first!');
      return;
    }

    this.setState({ loading: true, step: 'processing' });

    try {
      const birthTimestamp = Math.floor(new Date(this.state.dateOfBirth).getTime() / 1000);
      const contactInfo = `${this.state.parentPhone} | ${this.state.parentEmail}`;

      console.log('🏥 Hospital registering newborn...');

      const tx = await this.props.contract.registerChild(
        this.state.childName,
        birthTimestamp,
        this.state.parentName,
        contactInfo,
        ""
      );

      console.log('📝 Birth registration transaction:', tx.hash);

      const receipt = await tx.wait();
      console.log('✅ Birth registered successfully!');

      // Extract Token ID (simple method)
      let tokenId = null;
      if (receipt.logs && receipt.logs.length > 0) {
        for (let log of receipt.logs) {
          if (log.topics && log.topics.length >= 4) {
            try {
              const tokenIdHex = log.topics[3];
              tokenId = ethers.BigNumber.from(tokenIdHex).toString();
              console.log('🎯 Token ID extracted:', tokenId);
              break;
            } catch (e) {
              // Continue
            }
          }
        }
      }

      if (!tokenId) {
        tokenId = `BIRTH-${Date.now()}`;
      }

      this.setState({ 
        step: 'success',
        loading: false,
        finalTokenId: tokenId,
        transactionHash: tx.hash
      });

      // Send notification to parents (simulated)
      this.sendParentNotification(tokenId);

    } catch (error) {
      console.error('❌ Birth registration failed:', error);
      alert('Birth registration failed: ' + error.message);
      this.setState({ loading: false, step: 'form' });
    }
  }

  sendParentNotification = (tokenId) => {
    // Simulate sending notification to parents
    console.log('📧 Sending notification to parents...');
    console.log(`Dear ${this.state.parentName},`);
    console.log(`Your child ${this.state.childName} has been registered.`);
    console.log(`NFT Token ID: ${tokenId}`);
    console.log(`Please save this Token ID for vaccination records.`);

    // In real implementation, this would:
    // - Send SMS to parentPhone
    // - Send email to parentEmail
