// QR Verification Component
class QRVerification extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tokenId: '',
      verificationResult: null,
      loading: false
    };
  }

  handleVerify = async () => {
    if (!this.state.tokenId.trim()) {
      alert('Please enter Token ID');
      return;
    }

    this.setState({ loading: true });

    try {
      const childInfo = await this.props.contract.getChildInfo(this.state.tokenId);

      if (childInfo.isRegistered) {
        const result = {
          tokenId: this.state.tokenId,
          childName: childInfo.name,
          parentName: childInfo.parentName,
          birthDate: new Date(childInfo.dateOfBirth * 1000).toLocaleDateString(),
          registrationDate: new Date(childInfo.registrationDate * 1000).toLocaleDateString(),
          verified: true,
          status: 'Valid'
        };

        this.setState({ 
          verificationResult: result,
          loading: false 
        });
      } else {
        this.setState({ 
          verificationResult: {
            tokenId: this.state.tokenId,
            verified: false,
            status: 'Invalid - Record not found'
          },
          loading: false 
        });
      }
    } catch (error) {
      console.error('Verification error:', error);
      this.setState({ 
        verificationResult: {
          tokenId: this.state.tokenId,
          verified: false,
          status: 'Error - Could not verify'
        },
        loading: false 
      });
    }
  }

  render() {
    const { tokenId, verificationResult, loading } = this.state;
    const { onBack } = this.props;

    return (
      <div className="App">
        <div className="form-container">
          <h2>🔍 QR Code Verification</h2>

          <div className="verification-section">
            <h3>📱 Scan QR Code or Enter Token ID</h3>
            <div className="verification-form">
              <input
                type="text"
                value={tokenId}
                onChange={(e) => this.setState({ tokenId: e.target.value })}
                placeholder="Enter Token ID from QR code"
              />
              <button onClick={this.handleVerify} disabled={loading}>
                {loading ? '⏳ Verifying...' : '🔍 Verify'}
              </button>
            </div>
          </div>

          {verificationResult && (
            <div className={`verification-result ${verificationResult.verified ? 'valid' : 'invalid'}`}>
              <div className="result-header">
                <span className="result-icon">
                  {verificationResult.verified ? '✅' : '❌'}
                </span>
                <h3>{verificationResult.status}</h3>
              </div>

              {verificationResult.verified && (
                <div className="result-details">
                  <p><strong>Token ID:</strong> {verificationResult.tokenId}</p>
                  <p><strong>Child:</strong> {verificationResult.childName}</p>
                  <p><strong>Parent:</strong> {verificationResult.parentName}</p>
                  <p><strong>Birth Date:</strong> {verificationResult.birthDate}</p>
                  <p><strong>Registered:</strong> {verificationResult.registrationDate}</p>
                  <div className="verification-stamp">
                    <strong>🔒 BLOCKCHAIN VERIFIED</strong>
                    <small>This record is authentic and tamper-proof</small>
                  </div>
                </div>
              )}
            </div>
          )}

          <button onClick={onBack} className="btn-secondary">
            ← Back to Dashboard
          </button>
        </div>
      </div>
    );
  }
}

export default App;
