import React, { Component } from 'react';
import { ethers } from 'ethers';
import './App.css';
import ChildRegistration from './components/ChildRegistration';
import VaccinationRecording from './components/VaccinationRecording';
import ParentDashboard from './components/ParentDashboard';
import QRVerification from './components/QRVerification';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      // Navigation
      currentView: 'welcome',

      // User Authentication (linked to blockchain wallet)
      currentUser: null,
      isLoggedIn: false,
      userRole: null,

      // Blockchain Connection
      connectedWallet: null,
      web3Provider: null,
      contract: null,
      contractAddress: "", // Will be set after deployment

      // Real-time Blockchain Data
      systemStats: {
        totalHospitals: 0,
        totalChildren: 0,
        totalVaccinations: 0,
        pendingAuthorizations: 0,
        totalUsers: 0
      },

      // User's Blockchain Data
      userBlockchainData: {
        hospitalsRegistered: [],
        childrenRegistered: [],
        vaccinationsRecorded: [],
        ownedChildren: [], // For parents
        verificationHistory: [] // For verifiers
      },

      // UI States
      isLoading: false,
      error: null,
      chainId: null,
      blockNumber: 0,
      contractDeployed: false,

      // Authentication
      showAuthModal: false,
      authMode: 'register',

      // Local user database (wallet-based)
      users: JSON.parse(localStorage.getItem('vaccination_wallet_users') || '{}'), // Key: wallet address, Value: user data
    };

    // Contract ABI defined inline (no import needed)
    this.contractABI = [
      "function getTotalChildren() public view returns (uint256)",
      "function getTotalHospitals() public view returns (uint256)", 
      "function getTotalVaccinations() public view returns (uint256)",
      "function getVaccinationCount(uint256 tokenId) public view returns (uint256)",
      "function getPendingHospitalCount() public view returns (uint256)",
      "function registerChild(string memory name, uint256 birthDate, address parentWallet) public returns (uint256)",
      "function addVaccination(uint256 tokenId, string memory vaccineName, uint256 date, string memory batchNumber) public",
      "function registerHospital(string memory name, string memory licenseNumber) public",
      "function authorizeHospital(address hospitalAddress, bool isAuthorized) public",
      "function hospitals(address) public view returns (address, string, string, bool, bool, uint256)",
      "function children(uint256) public view returns (uint256, string, uint256, address, address, bool)",
      "function isHospitalAuthorized(address hospitalAddress) public view returns (bool)",
      "function getChildVaccinations(uint256 tokenId) public view returns (tuple(uint256,string,uint256,address,string,bool)[])",
      "function getParentChildren(address parentAddress) public view returns (uint256[])",
      "event ChildRegistered(uint256 indexed tokenId, string name, address indexed parentWallet, address indexed hospitalWallet)",
      "event HospitalRegistered(address indexed hospitalAddress, string name, string licenseNumber)",
      "event VaccinationRecorded(uint256 indexed tokenId, string vaccineName, uint256 vaccinationDate, address indexed hospitalAddress)"
    ];
  }

  componentDidMount() {
    this.detectWalletConnection();
    this.setupEventListeners();
    this.loadSystemStats();
  }

  componentWillUnmount() {
    this.removeEventListeners();
  }

  // ============ BLOCKCHAIN CONNECTION ============

  setupEventListeners = () => {
    if (window.ethereum) {
      window.ethereum.on('accountsChanged', this.handleAccountsChanged);
      window.ethereum.on('chainChanged', this.handleChainChanged);
    }
  };

  removeEventListeners = () => {
    if (window.ethereum) {
      window.ethereum.removeListener('accountsChanged', this.handleAccountsChanged);
      window.ethereum.removeListener('chainChanged', this.handleChainChanged);
    }
  };

  handleAccountsChanged = (accounts) => {
    if (accounts.length === 0) {
      this.handleDisconnect();
    } else {
      this.setState({ connectedWallet: accounts[0] });
      this.checkUserRegistration(accounts[0]);
    }
  };

  handleChainChanged = (chainId) => {
    this.setState({ chainId });
    window.location.reload();
  };

  // Detect wallet connection
  detectWalletConnection = async () => {
    if (typeof window.ethereum !== 'undefined') {
      try {
        const accounts = await window.ethereum.request({ method: 'eth_accounts' });
        if (accounts.length > 0) {
          console.log('üë§ Wallet detected:', accounts[0]);
          this.setState({ connectedWallet: accounts[0] });
          await this.initializeBlockchain();
          await this.checkUserRegistration(accounts[0]);
        }
      } catch (error) {
        console.error('‚ùå Error detecting wallet:', error);
      }
    }
  };

  // Connect wallet and initialize blockchain
  handleConnectWallet = async () => {
    if (typeof window.ethereum !== 'undefined') {
      try {
        this.setState({ isLoading: true, error: null });

        // Request account access
        const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
        const chainId = await window.ethereum.request({ method: 'eth_chainId' });

        console.log('üîó Connected to chain ID:', chainId);

        // Check for Ganache network
        if (chainId !== '0x539' && chainId !== '0x1691') {
          alert('‚ö†Ô∏è Please switch to Ganache Local Network!\n\nNetwork: Ganache Local\nRPC URL: http://127.0.0.1:7545\nChain ID: 1337');
          this.setState({ isLoading: false });
          return;
        }

        this.setState({ 
          connectedWallet: accounts[0],
          isLoading: false
        });

        // Initialize blockchain connection
        await this.initializeBlockchain();

        // Check if user is registered
        await this.checkUserRegistration(accounts[0]);

      } catch (error) {
        console.error('‚ùå Wallet connection failed:', error);
        this.setState({ 
          error: `Wallet connection failed: ${error.message}`,
          isLoading: false
        });
      }
    } else {
      alert('Please install MetaMask wallet!');
    }
  };

  // Initialize blockchain connection
  initializeBlockchain = async () => {
    try {
      if (!window.ethereum || !this.state.connectedWallet) return;

      const provider = new ethers.providers.Web3Provider(window.ethereum);
      const signer = provider.getSigner();
      const network = await provider.getNetwork();
      const blockNumber = await provider.getBlockNumber();

      console.log('üîó Network:', network.name, 'Chain ID:', network.chainId);
      console.log('üì¶ Block:', blockNumber);

      this.setState({
        web3Provider: provider,
        chainId: network.chainId,
        blockNumber: blockNumber,
        error: null
      });

      // Only initialize contract if address is set
      if (this.state.contractAddress && this.state.contractAddress !== "") {
        const contract = new ethers.Contract(
          this.state.contractAddress,
          this.contractABI,
          signer
        );

        this.setState({
          contract: contract,
          contractDeployed: true
        });

        console.log('‚úÖ Contract initialized at:', this.state.contractAddress);

        // Load blockchain data
        await this.loadBlockchainData();
      } else {
        console.log('‚ö†Ô∏è Contract address not set. Deploy contract first.');
        this.setState({ contractDeployed: false });
      }

    } catch (error) {
      console.error('‚ùå Blockchain initialization failed:', error);
      this.setState({ 
        error: `Blockchain initialization failed: ${error.message}`,
        contract: null,
        web3Provider: null,
        contractDeployed: false
      });
    }
  };

  // Set contract address after deployment
  setContractAddress = (address) => {
    console.log('üìù Setting contract address:', address);
    this.setState({ contractAddress: address }, () => {
      this.initializeBlockchain();
    });
  };

  // Load all blockchain data
  loadBlockchainData = async () => {
    try {
      if (!this.state.contract) return;

      console.log('üìä Loading blockchain data...');
      this.setState({ isLoading: true });

      // Load system statistics
      await this.loadSystemStats();

      // Load user-specific data
      if (this.state.connectedWallet && this.state.isLoggedIn) {
        await this.loadUserBlockchainData();
      }

      this.setState({ isLoading: false });

    } catch (error) {
      console.error('‚ùå Error loading blockchain data:', error);
      this.setState({ 
        error: `Failed to load blockchain data: ${error.message}`,
        isLoading: false
      });
    }
  };

  // Load system statistics from blockchain
  loadSystemStats = async () => {
    try {
      let stats = {
        totalHospitals: 0,
        totalChildren: 0,
        totalVaccinations: 0,
        pendingAuthorizations: 0,
        totalUsers: Object.keys(this.state.users).length
      };

      if (this.state.contract && this.state.contractDeployed) {
        // Get data from smart contract
        const totalChildren = await this.state.contract.getTotalChildren();
        const totalHospitals = await this.state.contract.getTotalHospitals();
        const totalVaccinations = await this.state.contract.getTotalVaccinations();

        stats.totalChildren = totalChildren.toNumber();
        stats.totalHospitals = totalHospitals.toNumber();
        stats.totalVaccinations = totalVaccinations.toNumber();

        console.log('‚úÖ System stats loaded from blockchain:', stats);
      } else {
        console.log('‚ö†Ô∏è Using default stats - contract not deployed');
      }

      this.setState({ systemStats: stats });

    } catch (error) {
      console.error('‚ùå Error loading system stats:', error);
      // Use default stats on error
      this.setState({ 
        systemStats: {
          totalHospitals: 0,
          totalChildren: 0,
          totalVaccinations: 0,
          pendingAuthorizations: 0,
          totalUsers: Object.keys(this.state.users).length
        }
      });
    }
  };

  // Load user-specific blockchain data
  loadUserBlockchainData = async () => {
    try {
      const contract = this.state.contract;
      const wallet = this.state.connectedWallet;

      if (!contract || !wallet || !this.state.contractDeployed) {
        console.log('‚ö†Ô∏è Cannot load user blockchain data - contract not ready');
        return;
      }

      console.log('üë§ Loading user blockchain data for:', wallet);

      let userBlockchainData = {
        hospitalsRegistered: [],
        childrenRegistered: [],
        vaccinationsRecorded: [],
        ownedChildren: [],
        verificationHistory: []
      };

      // Check if user is a registered hospital
      try {
        const hospitalInfo = await contract.hospitals(wallet);
        if (hospitalInfo[4]) { // isRegistered
          userBlockchainData.hospitalsRegistered.push({
            address: wallet,
            name: hospitalInfo[1],
            licenseNumber: hospitalInfo[2],
            isAuthorized: hospitalInfo[3],
            isRegistered: hospitalInfo[4],
            registrationDate: new Date(hospitalInfo[5].toNumber() * 1000)
          });
        }

        // Get children registered by this hospital if authorized
        if (hospitalInfo[3] && hospitalInfo[4]) { // isAuthorized && isRegistered
          try {
            const totalChildren = await contract.getTotalChildren();
            for (let i = 1; i <= Math.min(totalChildren.toNumber(), 50); i++) { // Limit to prevent gas issues
              const childInfo = await contract.children(i);
              if (childInfo[4] && childInfo[4].toLowerCase() === wallet.toLowerCase()) { // hospitalWallet
                userBlockchainData.childrenRegistered.push({
                  tokenId: i,
                  name: childInfo[1],
                  birthDate: new Date(childInfo[2].toNumber() * 1000),
                  parentWallet: childInfo[3]
                });
              }
            }
          } catch (error) {
            console.log('Error loading hospital children:', error.message);
          }
        }
      } catch (error) {
        console.log('User is not a registered hospital');
      }

      // Get children owned by this wallet (if parent)
      try {
        const ownedChildren = await contract.getParentChildren(wallet);
        for (let tokenId of ownedChildren) {
          const childInfo = await contract.children(tokenId.toNumber());
          const vaccinations = await contract.getChildVaccinations(tokenId.toNumber());

          userBlockchainData.ownedChildren.push({
            tokenId: tokenId.toNumber(),
            name: childInfo[1],
            birthDate: new Date(childInfo[2].toNumber() * 1000),
            parentWallet: childInfo[3],
            hospitalWallet: childInfo[4],
            vaccinations: vaccinations.map(v => ({
              vaccineName: v[1],
              vaccinationDate: new Date(v[2].toNumber() * 1000),
              hospitalAddress: v[3],
              batchNumber: v[4],
              isVerified: v[5]
            }))
          });
        }
      } catch (error) {
        console.log('No children found for this wallet');
      }

      this.setState({ userBlockchainData });
      console.log('‚úÖ User blockchain data loaded:', userBlockchainData);

    } catch (error) {
      console.error('‚ùå Error loading user blockchain data:', error);
    }
  };

  // ============ USER MANAGEMENT (WALLET-BASED) ============

  // Check if wallet address is registered as a user
  checkUserRegistration = async (walletAddress) => {
    const users = this.state.users;
    const user = users[walletAddress.toLowerCase()];

    if (user) {
      // User exists, log them in
      this.setState({
        currentUser: user,
        isLoggedIn: true,
        userRole: user.role,
        currentView: 'dashboard'
      });

      console.log('üë§ User logged in:', user.fullName);
      if (this.state.contractDeployed) {
        await this.loadUserBlockchainData();
      }
    } else {
      // New wallet, show registration
      this.setState({
        showAuthModal: true,
        authMode: 'register',
        currentView: 'welcome'
      });
    }
  };

  // Register new user (linked to wallet)
  registerUser = async (userData) => {
    try {
      const walletAddress = this.state.connectedWallet;
      if (!walletAddress) {
        throw new Error('Please connect your wallet first');
      }

      const { fullName, email, role, licenseNumber, phoneNumber } = userData;

      // Validate input
      if (!fullName || !email || !role) {
        throw new Error('All required fields must be filled');
      }

      // Create user profile
      const newUser = {
        walletAddress: walletAddress.toLowerCase(),
        fullName,
        email,
        role,
        licenseNumber: licenseNumber || '',
        phoneNumber: phoneNumber || '',
        createdAt: new Date().toISOString(),
        lastLogin: new Date().toISOString(),
        isActive: true,
        isVerified: role === 'parent' || role === 'verifier' ? true : false
      };

      // Save to localStorage (keyed by wallet address)
      const updatedUsers = {
        ...this.state.users,
        [walletAddress.toLowerCase()]: newUser
      };

      localStorage.setItem('vaccination_wallet_users', JSON.stringify(updatedUsers));

      // If hospital, register on blockchain (if contract is deployed)
      if (role === 'hospital' && this.state.contract && this.state.contractDeployed) {
        try {
          console.log('üìù Registering hospital on blockchain...');
          const tx = await this.state.contract.registerHospital(fullName, licenseNumber || 'TEMP-LICENSE');
          console.log('üì§ Transaction sent:', tx.hash);

          const receipt = await tx.wait();
          console.log('‚úÖ Hospital registered on blockchain:', receipt.transactionHash);

          // Reload blockchain data
          await this.loadBlockchainData();
        } catch (error) {
          console.error('‚ùå Hospital blockchain registration failed:', error);
          // Continue with local registration even if blockchain fails
        }
      }

      this.setState({ 
        users: updatedUsers,
        currentUser: newUser,
        isLoggedIn: true,
        userRole: role,
        showAuthModal: false,
        currentView: 'dashboard',
        error: null
      });

      if (this.state.contractDeployed) {
        await this.loadUserBlockchainData();
      }

      console.log('‚úÖ User registered successfully:', newUser.fullName);
      alert(`Welcome ${fullName}! Your ${role} account has been created and linked to your wallet.`);

    } catch (error) {
      console.error('‚ùå Registration failed:', error);
      this.setState({ error: error.message });
      throw error;
    }
  };

  // Logout user
  logoutUser = () => {
    this.setState({
      currentUser: null,
      isLoggedIn: false,
      userRole: null,
      currentView: 'welcome',
      userBlockchainData: {
        hospitalsRegistered: [],
        childrenRegistered: [],
        vaccinationsRecorded: [],
        ownedChildren: [],
        verificationHistory: []
      },
      error: null
    });
    console.log('üëã User logged out');
  };

  // Disconnect wallet
  handleDisconnect = () => {
    this.logoutUser();
    this.setState({
      connectedWallet: null,
      web3Provider: null,
      contract: null,
      contractDeployed: false
    });
  };

  // ============ BLOCKCHAIN TRANSACTIONS ============

  // Send transaction and refresh data
  sendTransaction = async (contractMethod, params = [], options = {}) => {
    try {
      if (!this.state.contract || !this.state.isLoggedIn || !this.state.contractDeployed) {
        throw new Error('Please ensure contract is deployed and you are logged in');
      }

      this.setState({ isLoading: true, error: null });

      console.log('üì§ Sending transaction...');
      const tx = await contractMethod(...params, options);
      console.log('üì§ Transaction sent:', tx.hash);

      // Wait for confirmation
      const receipt = await tx.wait();
      console.log('‚úÖ Transaction confirmed:', receipt.transactionHash);

      // Refresh all blockchain data
      await this.loadBlockchainData();

      this.setState({ isLoading: false });

      // Show success message
      alert(`Transaction successful! Hash: ${receipt.transactionHash.slice(0, 10)}...`);

      return receipt;

    } catch (error) {
      console.error('‚ùå Transaction failed:', error);
      this.setState({ 
        error: `Transaction failed: ${error.message}`,
        isLoading: false
      });

      // Show user-friendly error
      if (error.message.includes('user rejected')) {
        alert('Transaction cancelled by user');
      } else if (error.message.includes('insufficient funds')) {
        alert('Insufficient ETH for gas fees');
      } else {
        alert(`Transaction failed: ${error.message}`);
      }

      throw error;
    }
  };

  // Generate unique QR code data
  generateQRData = (type, data) => {
    const timestamp = Date.now();
    const blockNumber = this.state.blockNumber;
    const walletAddress = this.state.connectedWallet;

    const qrData = {
      type,
      data,
      timestamp,
      blockNumber,
      walletAddress,
      chainId: this.state.chainId,
      contractAddress: this.state.contractAddress,
      signature: `${walletAddress}_${timestamp}_${blockNumber}`
    };

    return JSON.stringify(qrData);
  };

  // ============ NAVIGATION ============

  changeView = (view, role = null) => {
    if (!this.state.isLoggedIn && view !== 'welcome') {
      alert('Please connect wallet and login first');
      return;
    }

    this.setState({ 
      currentView: view,
      userRole: role || this.state.userRole
    });
  };

  backToDashboard = () => {
    this.setState({ currentView: 'dashboard' });
    if (this.state.contractDeployed) {
      this.loadBlockchainData();
    }
  };

