import React, { useState, useEffect } from 'react';
import './App.css';

// Import ethers only if you have it installed
// import { ethers } from 'ethers';

// Your deployed contract address
const CONTRACT_ADDRESS = "0xb5a28A2c3715BA080576A2EaF80eB91e44E4499D";

function App() {
  const [account, setAccount] = useState('');
  const [loading, setLoading] = useState(false);
  const [currentView, setCurrentView] = useState('welcome');

  // Simple wallet connection using MetaMask
  const connectWallet = async () => {
    try {
      if (typeof window.ethereum !== 'undefined') {
        setLoading(true);

        // Request account access
        const accounts = await window.ethereum.request({ 
          method: 'eth_requestAccounts' 
        });

        if (accounts.length > 0) {
          setAccount(accounts[0]);
          setCurrentView('dashboard');
        }

        setLoading(false);
      } else {
        alert('Please install MetaMask wallet!');
      }
    } catch (error) {
      console.error('Error connecting wallet:', error);
      alert('Error connecting wallet: ' + error.message);
      setLoading(false);
    }
  };

  const disconnectWallet = () => {
    setAccount('');
    setCurrentView('welcome');
  };

  // Simple child registration form
  const ChildRegistrationForm = () => {
    const [formData, setFormData] = useState({
      childName: '',
      dateOfBirth: '',
      parentName: '',
      contactInfo: ''
    });

    const handleChange = (e) => {
      setFormData({
        ...formData,
        [e.target.name]: e.target.value
      });
    };

    const handleSubmit = (e) => {
      e.preventDefault();

      // For now, just show the data (you can add blockchain interaction later)
      console.log('Child Registration Data:', formData);
      alert('Child registration data saved! Check console for details.');

      // Reset form
      setFormData({
        childName: '',
        dateOfBirth: '',
        parentName: '',
        contactInfo: ''
      });
    };

    return (
      <div className="form-section">
        <h2>👶 Register Child</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Child's Name:</label>
            <input
              type="text"
              name="childName"
              value={formData.childName}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Date of Birth:</label>
            <input
              type="date"
              name="dateOfBirth"
              value={formData.dateOfBirth}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Parent Name:</label>
            <input
              type="text"
              name="parentName"
              value={formData.parentName}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Contact Info:</label>
            <input
              type="text"
              name="contactInfo"
              value={formData.contactInfo}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-actions">
            <button type="button" onClick={() => setCurrentView('dashboard')}>
              Back
            </button>
            <button type="submit">Register Child</button>
          </div>
        </form>
      </div>
    );
  };

  // Simple vaccination record form
  const VaccinationForm = () => {
    const [formData, setFormData] = useState({
      tokenId: '',
      vaccineName: '',
      hospitalName: '',
      batchNumber: ''
    });

    const handleChange = (e) => {
      setFormData({
        ...formData,
        [e.target.name]: e.target.value
      });
    };

    const handleSubmit = (e) => {
      e.preventDefault();

      // For now, just show the data
      console.log('Vaccination Record Data:', formData);
      alert('Vaccination record saved! Check console for details.');

      // Reset form
      setFormData({
        tokenId: '',
        vaccineName: '',
        hospitalName: '',
        batchNumber: ''
      });
    };

    return (
      <div className="form-section">
        <h2>💉 Record Vaccination</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Child Token ID:</label>
            <input
              type="text"
              name="tokenId"
              value={formData.tokenId}
              onChange={handleChange}
              placeholder="Enter token ID"
              required
            />
          </div>
          <div className="form-group">
            <label>Vaccine Name:</label>
            <select
              name="vaccineName"
              value={formData.vaccineName}
              onChange={handleChange}
              required
            >
              <option value="">Select Vaccine</option>
              <option value="BCG">BCG</option>
              <option value="Hepatitis B">Hepatitis B</option>
              <option value="DPT">DPT</option>
              <option value="Polio">Polio</option>
              <option value="MMR">MMR</option>
            </select>
          </div>
          <div className="form-group">
            <label>Hospital Name:</label>
            <input
              type="text"
              name="hospitalName"
              value={formData.hospitalName}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Batch Number:</label>
            <input
              type="text"
              name="batchNumber"
              value={formData.batchNumber}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-actions">
            <button type="button" onClick={() => setCurrentView('dashboard')}>
              Back
            </button>
            <button type="submit">Record Vaccination</button>
          </div>
        </form>
      </div>
    );
  };

  const renderContent = () => {
    switch (currentView) {
      case 'welcome':
        return (
          <div className="welcome-screen">
            <div className="welcome-content">
              <h1>🏥 Blockchain Vaccination System</h1>
              <p>Secure, transparent, and tamper-proof vaccination records</p>
              <p>Contract Address: {CONTRACT_ADDRESS}</p>
              <button 
                className="connect-btn"
                onClick={connectWallet}
                disabled={loading}
              >
                {loading ? 'Connecting...' : 'Connect MetaMask'}
              </button>
            </div>
          </div>
        );

      case 'dashboard':
        return (
          <div className="dashboard">
            <div className="dashboard-header">
              <h2>🏥 Vaccination System Dashboard</h2>
              <p>Connected: {account.slice(0, 6)}...{account.slice(-4)}</p>
              <button onClick={disconnectWallet} className="disconnect-btn">
                Disconnect
              </button>
            </div>

            <div className="dashboard-actions">
              <div className="action-card" onClick={() => setCurrentView('register')}>
                <h3>👶 Register Child</h3>
                <p>Create a new vaccination record</p>
              </div>

              <div className="action-card" onClick={() => setCurrentView('record')}>
                <h3>💉 Record Vaccination</h3>
                <p>Add vaccination entry (Hospital only)</p>
              </div>

              <div className="action-card" onClick={() => setCurrentView('verify')}>
                <h3>🔍 Verify Records</h3>
                <p>Check vaccination status</p>
              </div>
            </div>
          </div>
        );

      case 'register':
        return <ChildRegistrationForm />;

      case 'record':
        return <VaccinationForm />;

      case 'verify':
        return (
          <div className="verify-section">
            <h2>🔍 Verify Vaccination Records</h2>
            <p>Verification feature coming soon...</p>
            <div className="form-group">
              <label>Enter Token ID to verify:</label>
              <input type="text" placeholder="Token ID" />
              <button>Verify</button>
            </div>
            <button onClick={() => setCurrentView('dashboard')}>
              Back to Dashboard
            </button>
          </div>
        );

      default:
        return renderContent();
    }
  };

  return (
    <div className="App">
      <main className="main-content">
        {renderContent()}
      </main>
    </div>
  );
}

export default App;
