import React, { Component } from 'react';
import { ethers } from 'ethers';
import './App.css';
import ChildRegistration from './components/ChildRegistration';
import VaccinationRecording from './components/VaccinationRecording';
import ParentDashboard from './components/ParentDashboard';
import QRVerification from './components/QRVerification';
import HospitalRegistration from './components/HospitalRegistration';
import AdminPanel from './components/AdminPanel';

// Import contract ABI - Replace with your deployed contract ABI
const VACCINATION_ABI = [
  "function registerHospital(string memory _name, string memory _licenseNumber, string memory _contactInfo) public",
  "function setHospitalAuthorization(address _hospital, bool _authorized) public",
  "function registerChild(string memory _name, uint256 _dateOfBirth, string memory _parentName, string memory _contactInfo, address _parentAddress, string memory _tokenURI) public returns (uint256)",
  "function recordVaccination(uint256 _tokenId, string memory _vaccineName, string memory _batchNumber, uint256 _nextDueDate, string memory _ipfsHash) public",
  "function getChildInfo(uint256 _tokenId) public view returns (tuple(string,uint256,string,string,address,address,string,bool,uint256))",
  "function getVaccinationHistory(uint256 _tokenId) public view returns (tuple(string,uint256,string,address,string,uint256,bool,string,string)[])",
  "function getParentChildren(address _parent) public view returns (uint256[])",
  "function generateVerificationQR(uint256 _tokenId) public view returns (string,string,uint256,bool)",
  "function getHospitalInfo(address _hospital) public view returns (tuple(string,string,string,bool,uint256))",
  "function hospitals(address) public view returns (string,string,string,bool,uint256)",
  "function authorizedHospitals(address) public view returns (bool)",
  "function children(uint256) public view returns (string,uint256,string,string,address,address,string,bool,uint256)",
  "function owner() public view returns (address)",
  "event ChildRegistered(uint256 indexed tokenId, string name, address indexed parent, address indexed hospital)",
  "event VaccinationRecorded(uint256 indexed tokenId, string vaccineName, uint256 dateAdministered, string qrCodeHash)",
  "event HospitalRegistered(address indexed hospital, string name)",
  "event HospitalAuthorized(address indexed hospital, bool authorized)"
];

// Contract address - Replace with your deployed contract address
const CONTRACT_ADDRESS = "0x1b78066f1DD3D928e5dd59F2ad7538e23d76ef2c"; // Update after deployment

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentView: 'welcome',
      connectedWallet: null,
      contract: null,
      provider: null,
      signer: null,
      isLoading: false,
      error: null,
      networkId: null,
      isOwner: false,
      userRole: null,
      viewProps: {} 

    };
  }

  componentDidMount() {
    this.checkWalletConnection();
    this.setupEventListeners();
  }

  componentWillUnmount() {
    this.removeEventListeners();
  }

  setupEventListeners = () => {
    if (window.ethereum) {
      window.ethereum.on('accountsChanged', this.handleAccountsChanged);
      window.ethereum.on('chainChanged', this.handleChainChanged);
    }
  };

  removeEventListeners = () => {
    if (window.ethereum) {
      window.ethereum.removeListener('accountsChanged', this.handleAccountsChanged);
      window.ethereum.removeListener('chainChanged', this.handleChainChanged);
    }
  };

  handleAccountsChanged = (accounts) => {
    if (accounts.length === 0) {
      this.disconnectWallet();
    } else {
      this.setState({ connectedWallet: accounts[0] });
      this.checkUserRole();
    }
  };

  handleChainChanged = () => {
    window.location.reload();
  };

  checkWalletConnection = async () => {
    if (typeof window.ethereum !== 'undefined') {
      try {
        const accounts = await window.ethereum.request({ method: 'eth_accounts' });
        if (accounts.length > 0) {
          await this.connectWallet();
        }
      } catch (error) {
        console.error('Error checking wallet connection:', error);
      }
    }
  };

  connectWallet = async () => {
    try {
      this.setState({ isLoading: true, error: null });

      if (typeof window.ethereum === 'undefined') {
        throw new Error('MetaMask is not installed. Please install MetaMask to continue.');
      }

      // Request account access
      const accounts = await window.ethereum.request({ 
        method: 'eth_requestAccounts' 
      });

      // Check network
      const chainId = await window.ethereum.request({ method: 'eth_chainId' });
      console.log('Connected to chain:', chainId);

      // Create provider and signer
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      const signer = provider.getSigner();
      const network = await provider.getNetwork();

      // Create contract instance
      const contract = new ethers.Contract(CONTRACT_ADDRESS, VACCINATION_ABI, signer);

      // Check if user is contract owner
      const owner = await contract.owner();
      const isOwner = owner.toLowerCase() === accounts[0].toLowerCase();

      this.setState({
        connectedWallet: accounts[0],
        provider,
        signer,
        contract,
        networkId: network.chainId,
        isOwner,
        isLoading: false,
        currentView: 'dashboard'
      });

      console.log('Wallet connected:', accounts[0]);
      console.log('Contract address:', CONTRACT_ADDRESS);
      console.log('Is owner:', isOwner);

      await this.checkUserRole();

    } catch (error) {
      console.error('Error connecting wallet:', error);
      this.setState({ 
        error: error.message,
        isLoading: false 
      });
    }
  };

  disconnectWallet = () => {
    this.setState({
      connectedWallet: null,
      contract: null,
      provider: null,
      signer: null,
      isOwner: false,
      userRole: null,
      currentView: 'welcome'
    });
  };

  checkUserRole = async () => {
    try {
      if (!this.state.contract || !this.state.connectedWallet) return;

      const contract = this.state.contract;
      const wallet = this.state.connectedWallet;

      // Check if user is a hospital
      try {
        const hospitalInfo = await contract.hospitals(wallet);
        if (hospitalInfo[0] && hospitalInfo[0] !== '') { // Hospital name exists
          this.setState({ userRole: 'hospital' });
          return;
        }
      } catch (error) {
        console.log('Not a hospital:', error.message);
      }

      // Check if user has children (is a parent)
      try {
        const children = await contract.getParentChildren(wallet);
        if (children.length > 0) {
          this.setState({ userRole: 'parent' });
          return;
        }
      } catch (error) {
        console.log('Not a parent:', error.message);
      }

      // Default role
      this.setState({ userRole: 'verifier' });

    } catch (error) {
      console.error('Error checking user role:', error);
      this.setState({ userRole: 'verifier' });
    }
  };

  sendTransaction = async (contractMethod, params = [], options = {}) => {
    try {
      this.setState({ isLoading: true, error: null });

      console.log('Sending transaction...');
      const tx = await contractMethod(...params, options);
      console.log('Transaction sent:', tx.hash);

      const receipt = await tx.wait();
      console.log('Transaction confirmed:', receipt.transactionHash);

      this.setState({ isLoading: false });
      return receipt;

    } catch (error) {
      console.error('Transaction failed:', error);
      this.setState({ 
        error: error.message,
        isLoading: false 
      });
      throw error;
    }
  };

 changeView = (view, props = {}) => {
  this.setState({ currentView: view, viewProps: props });
};


  renderWelcome() {
    return (
      <div className="welcome-screen">
        <div className="welcome-content">
          <h1>üè• Blockchain Vaccination Registry</h1>
          <p>Secure, transparent, and immutable vaccination records on the blockchain</p>

          <div className="features">
            <div className="feature-item">
              <span className="feature-icon">üîí</span>
              <span>Blockchain Security</span>
            </div>
            <div className="feature-item">
              <span className="feature-icon">üì±</span>
              <span>QR Verification</span>
            </div>
            <div className="feature-item">
              <span className="feature-icon">üíä</span>
              <span>Vaccination Tracking</span>
            </div>
            <div className="feature-item">
              <span className="feature-icon">üè•</span>
              <span>Hospital Management</span>
            </div>
          </div>

          {this.state.error &
            <div className="error-message">
              ‚ùå {this.state.error}
            </div>
  }

          <button 
            onClick={this.connectWallet}
            disabled={this.state.isLoading}
            className="connect-btn"
          >
            {this.state.isLoading ? 'üîÑ Connecting...' : 'ü¶ä Connect MetaMask'}
          </button>

          <div className="contract-info">
            <h4>üìã Contract Information</h4>
            <div className="contract-address">
              <strong>Contract Address:</strong>
              <div>{CONTRACT_ADDRESS}</div>
            </div>
            <p><strong>Network:</strong> Ganache Local (Chain ID: 5777)</p>
          </div>
        </div>
      </div>
    );
  }

  renderDashboard() {
    const { connectedWallet, isOwner, userRole, networkId } = this.state;

    return (
      <div className="dashboard">
        <div className="dashboard-header">
          <h1>üè• Vaccination System Dashboard</h1>
          <div className="user-info">
            <div className="account-info">
              <p><strong>Connected Account:</strong></p>
              <div className="connected-wallet">{connectedWallet}</div>
              <p><strong>Network:</strong> {networkId}</p>
              <p><strong>Role:</strong> {userRole}</p>
              {isOwner && <p><strong>Status:</strong> Contract Owner</p>}
            </div>
            <button onClick={this.disconnectWallet} className="disconnect-btn">
              Disconnect
            </button>
          </div>
        </div>

        <div className="dashboard-actions">
          {/* Admin Panel - Only for contract owner */}
          {isOwner && (
            <div className="action-card" onClick={() => this.changeView('admin')}>
              <div className="card-icon">üëë</div>
              <h3>Admin Panel</h3>
              <p>Manage hospital authorizations and system settings</p>
            </div>
          )}

          {/* Hospital Registration */}
          <div className="action-card" onClick={() => this.changeView('hospital-register')}>
            <div className="card-icon">üè•</div>
            <h3>Hospital Registration</h3>
            <p>Register your hospital on the blockchain</p>
          </div>

          {/* Child Registration - For authorized hospitals */}
          <div className="action-card" onClick={() => this.changeView('child-register')}>
            <div className="card-icon">üë∂</div>
            <h3>Register Child</h3>
            <p>Register a new child and mint birth NFT</p>
          </div>

          {/* Vaccination Recording - For authorized hospitals */}
          <div className="action-card" onClick={() => this.changeView('vaccination-record')}>
            <div className="card-icon">üíâ</div>
            <h3>Record Vaccination</h3>
            <p>Record vaccination on blockchain with QR code</p>
          </div>

          {/* Parent Dashboard */}
          <div className="action-card" onClick={() => this.changeView('parent-dashboard')}>
            <div className="card-icon">üë®‚Äçüë©‚Äçüëß‚Äçüë¶</div>
            <h3>Parent Dashboard</h3>
            <p>View your children's vaccination records</p>
          </div>

          {/* QR Verification */}
          <div className="action-card" onClick={() => this.changeView('qr-verification')}>
            <div className="card-icon">üì±</div>
            <h3>QR Verification</h3>
            <p>Verify vaccination records using QR codes</p>
          </div>
        </div>
      </div>
    );
  }

  render() {
    const { currentView, contract, connectedWallet, isLoading } = this.state;

    if (!connectedWallet && currentView !== 'welcome') {
      return (
        <div className="App">
          {this.renderWelcome()}
        </div>
      );
    }

    if (isLoading) {
      return (
        <div className="App">
          <div className="loading-container">
            <div className="loading-spinner">‚è≥</div>
            <p>Processing blockchain transaction...</p>
          </div>
        </div>
      );
    }

    switch (currentView) {
      case 'welcome':
        return (
          <div className="App">
            {this.renderWelcome()}
          </div>
        );

      case 'dashboard':
        return (
          <div className="App">
            {this.renderDashboard()}
          </div>
        );

      case 'admin':
        return (
          <AdminPanel
            contract={contract}
            account={connectedWallet}
            onBack={() => this.changeView('dashboard')}
          />
        );

      case 'hospital-register':
        return (
          <HospitalRegistration
            contract={contract}
            connectedWallet={connectedWallet}
            sendTransaction={this.sendTransaction}
            onBack={() => this.changeView('dashboard')}
            onChangeView={this.changeView}
          />
        );

      case 'child-register':
        return (
          <ChildRegistration
            contract={contract}
            connectedWallet={connectedWallet}
            sendTransaction={this.sendTransaction}
            onBack={() => this.changeView('dashboard')}
            onChangeView={this.changeView}
          />
        );

      case 'vaccination-record':
        return (
          <VaccinationRecording
            contract={contract}
            connectedWallet={connectedWallet}
            sendTransaction={this.sendTransaction}
            onBack={() => this.changeView('dashboard')}
            onChangeView={this.changeView}
          />
        );

      case 'parent-dashboard':
        return (
          <ParentDashboard
            contract={contract}
            connectedWallet={connectedWallet}
            onBack={() => this.changeView('dashboard')}
            onChangeView={this.changeView}
          />
        );

      case 'qr-verification':
        return (
          <QRVerification
            contract={contract}
            connectedWallet={connectedWallet}
            onBack={() => this.changeView('dashboard')}
            onChangeView={this.changeView}
          />
        );

      default:
        return (
          <div className="App">
            {this.renderDashboard()}
          </div>
        );
    }
  }
}

export default App;
