// src/App.js
import React, { Component } from 'react';
import { ethers } from 'ethers';
import './App.css';
import ChildRegistration from './components/ChildRegistration';
import VaccinationRecording from './components/VaccinationRecording';
import ParentDashboard from './components/ParentDashboard';
import QRVerification from './components/QRVerification';
import HospitalRegistration from './components/HospitalRegistration';
import AdminPanel from './components/AdminPanel';

// Replace with your contract ABI and address
const VACCINATION_ABI = [
  "function registerHospital(string memory, string memory, string memory) public",
  "function setHospitalAuthorization(address, bool) public",
  "function registerChild(string memory, uint256, string memory, string memory, address, string memory) public returns (uint256)",
  "function recordVaccination(uint256, string memory, string memory, uint256, string memory) public",
  "function getParentChildren(address) public view returns (uint256[])",
  "function hospitals(address) public view returns (string,string,string,bool,uint256)",
  "function authorizedHospitals(address) public view returns (bool)",
  "function owner() public view returns (address)",
  "event HospitalRegistered(address indexed hospital, string name)"
];

const CONTRACT_ADDRESS = "0x1b78066f1DD3D928e5dd59F2ad7538e23d76ef2c"; // Replace with your address

class App extends Component {
  state = {
    currentView: 'welcome',
    connectedWallet: null,
    contract: null,
    provider: null,
    signer: null,
    isLoading: false,
    error: null,
    networkId: null,
    isOwner: false,
    userRole: null
  };

  componentDidMount() {
    this.checkWalletConnection();
    this.setupEventListeners();
  }

  componentWillUnmount() {
    this.removeEventListeners();
  }

  setupEventListeners = () => {
    if (window.ethereum) {
      window.ethereum.on('accountsChanged', this.handleAccountsChanged);
      window.ethereum.on('chainChanged', this.handleChainChanged);
    }
  };

  removeEventListeners = () => {
    if (window.ethereum) {
      window.ethereum.removeListener('accountsChanged', this.handleAccountsChanged);
      window.ethereum.removeListener('chainChanged', this.handleChainChanged);
    }
  };

  handleAccountsChanged = (accounts) => {
    if (accounts.length === 0) {
      this.disconnectWallet();
    } else {
      this.setState({ connectedWallet: accounts[0] });
      this.checkUserRole();
    }
  };

  handleChainChanged = () => {
    window.location.reload();
  };

  checkWalletConnection = async () => {
    if (typeof window.ethereum !== 'undefined') {
      const accounts = await window.ethereum.request({ method: 'eth_accounts' });
      if (accounts.length > 0) await this.connectWallet();
    }
  };

  connectWallet = async () => {
    try {
      this.setState({ isLoading: true, error: null });
      const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      const signer = provider.getSigner();
      const network = await provider.getNetwork();
      const contract = new ethers.Contract(CONTRACT_ADDRESS, VACCINATION_ABI, signer);
      const owner = await contract.owner();
      const isOwner = owner.toLowerCase() === accounts[0].toLowerCase();

      this.setState({
        connectedWallet: accounts[0],
        provider,
        signer,
        contract,
        networkId: network.chainId,
        isOwner,
        isLoading: false,
        currentView: 'dashboard'
      });

      await this.checkUserRole();
    } catch (error) {
      this.setState({ error: error.message, isLoading: false });
    }
  };

  disconnectWallet = () => {
    this.setState({
      connectedWallet: null,
      contract: null,
      provider: null,
      signer: null,
      isOwner: false,
      userRole: null,
      currentView: 'welcome'
    });
  };

  checkUserRole = async () => {
    const { contract, connectedWallet } = this.state;
    if (!contract || !connectedWallet) return;

    try {
      const hospitalInfo = await contract.hospitals(connectedWallet);
      if (hospitalInfo[0] && hospitalInfo[0] !== '') {
        this.setState({ userRole: 'hospital' });
        return;
      }

      const children = await contract.getParentChildren(connectedWallet);
      if (children.length > 0) {
        this.setState({ userRole: 'parent' });
        return;
      }

      this.setState({ userRole: 'verifier' });
    } catch (error) {
      this.setState({ userRole: 'verifier' });
    }
  };

  changeView = (view) => {
    this.setState({ currentView: view });
  };

  renderWelcome() {
    return (
      <div className="welcome-screen">
        <h1>Blockchain Vaccination Registry</h1>
        <button onClick={this.connectWallet} disabled={this.state.isLoading}>
          {this.state.isLoading ? 'Connecting...' : 'Connect Wallet'}
        </button>
        {this.state.error && <p style={{ color: 'red' }}>{this.state.error}</p>}
      </div>
    );
  }

  renderDashboard() {
    const { connectedWallet, isOwner, userRole, networkId } = this.state;

    return (
      <div className="dashboard">
        <h2>Welcome: {connectedWallet}</h2>
        <p>Network ID: {networkId}</p>
        <p>Role: {userRole}</p>
        {isOwner && <p><strong>Status:</strong> Contract Owner</p>}

        <div className="dashboard-actions">
          {isOwner && (
            <button onClick={() => this.changeView('admin')}>
              ðŸ‘‘ Admin Panel
            </button>
          )}
          <button onClick={() => this.changeView('hospital-register')}>Hospital Registration</button>
          <button onClick={() => this.changeView('child-register')}>Register Child</button>
          <button onClick={() => this.changeView('vaccination-record')}>Record Vaccination</button>
          <button onClick={() => this.changeView('parent-dashboard')}>Parent Dashboard</button>
          <button onClick={() => this.changeView('qr-verification')}>QR Verification</button>
        </div>
      </div>
    );
  }

  render() {
    const { currentView, contract, connectedWallet } = this.state;

    if (!connectedWallet && currentView !== 'welcome') {
      return <div>{this.renderWelcome()}</div>;
    }

    switch (currentView) {
      case 'welcome': return this.renderWelcome();
      case 'dashboard': return this.renderDashboard();
      case 'admin': return (
        <AdminPanel
          contract={contract}
          account={connectedWallet}
          onBack={() => this.changeView('dashboard')}
        />
      );
      case 'hospital-register':
        return <HospitalRegistration contract={contract} connectedWallet={connectedWallet} onBack={() => this.changeView('dashboard')} />;
      case 'child-register':
        return <ChildRegistration contract={contract} connectedWallet={connectedWallet} onBack={() => this.changeView('dashboard')} />;
      case 'vaccination-record':
        return <VaccinationRecording contract={contract} connectedWallet={connectedWallet} onBack={() => this.changeView('dashboard')} />;
      case 'parent-dashboard':
        return <ParentDashboard contract={contract} connectedWallet={connectedWallet} onBack={() => this.changeView('dashboard')} />;
      case 'qr-verification':
        return <QRVerification contract={contract} connectedWallet={connectedWallet} onBack={() => this.changeView('dashboard')} />;
      default:
        return this.renderDashboard();
    }
  }
}

export default App;
