import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';

const AdminPanel = ({ contract, account, onSuccess, onBack }) => {
  const [pendingHospitals, setPendingHospitals] = useState([]);
  const [authorizedHospitals, setAuthorizedHospitals] = useState([]);
  const [loading, setLoading] = useState(true);
  const [processingHospital, setProcessingHospital] = useState(null);
  const [isOwner, setIsOwner] = useState(false);

  useEffect(() => {
    checkOwnership();
    loadHospitals();
  }, [contract, account]);

  const checkOwnership = async () => {
    if (!contract || !account) return;
    
    try {
      const owner = await contract.owner();
      const userIsOwner = owner.toLowerCase() === account.toLowerCase();
      setIsOwner(userIsOwner);
      
      if (!userIsOwner) {
        alert('Access denied: Only contract owner can access admin panel');
        onBack();
      }
    } catch (error) {
      console.error('Error checking ownership:', error);
    }
  };

  const loadHospitals = async () => {
    if (!contract) return;
    
    try {
      setLoading(true);
      
      // Get hospital registration events
      const filter = contract.filters.HospitalRegistered();
      const events = await contract.queryFilter(filter);
      
      const hospitalPromises = events.map(async (event) => {
        const hospitalAddress = event.args.hospital;
        const hospitalInfo = await contract.getHospitalInfo(hospitalAddress);
        const isAuthorized = await contract.authorizedHospitals(hospitalAddress);
        
        return {
          address: hospitalAddress,
          name: hospitalInfo.name,
          licenseNumber: hospitalInfo.licenseNumber,
          contactInfo: hospitalInfo.contactInfo,
          registrationDate: hospitalInfo.registrationDate,
          isAuthorized: isAuthorized
        };
      });
      
      const hospitals = await Promise.all(hospitalPromises);
      
      // Separate pending and authorized hospitals
      const pending = hospitals.filter(h => !h.isAuthorized);
      const authorized = hospitals.filter(h => h.isAuthorized);
      
      setPendingHospitals(pending);
      setAuthorizedHospitals(authorized);
      setLoading(false);
    } catch (error) {
      console.error('Error loading hospitals:', error);
      setLoading(false);
    }
  };

  const authorizeHospital = async (hospitalAddress, authorize = true) => {
    if (!contract || !hospitalAddress) return;
    
    try {
      setProcessingHospital(hospitalAddress);
      
      const tx = await contract.setHospitalAuthorization(hospitalAddress, authorize);
      console.log(`${authorize ? 'Authorizing' : 'Revoking authorization for'} hospital:`, tx.hash);
      
      await tx.wait();
      
      alert(`Hospital ${authorize ? 'authorized' : 'deauthorized'} successfully!`);
      
      // Reload hospitals list
      await loadHospitals();
      setProcessingHospital(null);
      
    } catch (error) {
      console.error('Error updating hospital authorization:', error);
      alert('Error updating authorization: ' + error.message);
      setProcessingHospital(null);
    }
  };

  const formatDate = (timestamp) => {
    return new Date(timestamp * 1000).toLocaleDateString();
  };

  const HospitalCard = ({ hospital, isPending = false }) => (
    <div className="hospital-card">
      <div className="hospital-header">
        <h3>{hospital.name}</h3>
        <div className={`status-badge ${isPending ? 'pending' : 'authorized'}`}>
          {isPending ? '‚è≥ Pending' : '‚úÖ Authorized'}
        </div>
      </div>
      
      <div className="hospital-details">
        <div className="detail-row">
          <label>License Number:</label>
          <span>{hospital.licenseNumber}</span>
        </div>
        <div className="detail-row">
          <label>Contact Info:</label>
          <span>{hospital.contactInfo}</span>
        </div>
        <div className="detail-row">
          <label>Wallet Address:</label>
          <span className="wallet-address">{hospital.address}</span>
        </div>
        <div className="detail-row">
          <label>Registration Date:</label>
          <span>{formatDate(hospital.registrationDate)}</span>
        </div>
      </div>
      
      <div className="hospital-actions">
        {isPending ? (
          <button
            onClick={() => authorizeHospital(hospital.address, true)}
            disabled={processingHospital === hospital.address}
            className="btn-primary"
          >
            {processingHospital === hospital.address ? '‚è≥ Authorizing...' : '‚úÖ Authorize Hospital'}
          </button>
        ) : (
          <button
            onClick={() => authorizeHospital(hospital.address, false)}
            disabled={processingHospital === hospital.address}
            className="btn-danger"
          >
            {processingHospital === hospital.address ? '‚è≥ Revoking...' : '‚ùå Revoke Authorization'}
          </button>
        )}
      </div>
    </div>
  );

  if (!isOwner) {
    return (
      <div className="admin-panel">
        <div className="access-denied">
          <div className="error-icon">üö´</div>
          <h2>Access Denied</h2>
          <p>Only the contract owner can access the admin panel.</p>
          <button onClick={onBack} className="btn-secondary">
            ‚Üê Back to Dashboard
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="admin-panel">
      <div className="admin-container">
        <div className="admin-header">
          <div className="header-content">
            <h1>üëë Admin Panel</h1>
            <p>Manage hospital authorizations and system settings</p>
          </div>
          <button onClick={onBack} className="btn-secondary">
            ‚Üê Back to Dashboard
          </button>
        </div>

        {loading ? (
          <div className="loading-container">
            <div className="loading-spinner">‚è≥</div>
            <p>Loading hospital data...</p>
          </div>
        ) : (
          <div className="admin-content">
            {/* Pending Hospitals Section */}
            <section className="hospitals-section">
              <div className="section-header">
                <h2>‚è≥ Pending Hospital Authorizations ({pendingHospitals.length})</h2>
                {pendingHospitals.length === 0 && (
                  <p className="no-items">No hospitals pending authorization</p>
                )}
              </div>
              
              <div className="hospitals-grid">
                {pendingHospitals.map((hospital, index) => (
                  <HospitalCard key={index} hospital={hospital} isPending={true} />
                ))}
              </div>
            </section>

            {/* Authorized Hospitals Section */}
            <section className="hospitals-section">
              <div className="section-header">
                <h2>‚úÖ Authorized Hospitals ({authorizedHospitals.length})</h2>
                {authorizedHospitals.length === 0 && (
                  <p className="no-items">No hospitals authorized yet</p>
                )}
              </div>
              
              <div className="hospitals-grid">
                {authorizedHospitals.map((hospital, index) => (
                  <HospitalCard key={index} hospital={hospital} isPending={false} />
                ))}
              </div>
            </section>

            {/* System Statistics */}
            <section className="system-stats">
              <h2>üìä System Statistics</h2>
              <div className="stats-grid">
                <div className="stat-card">
                  <h3>Total Hospitals</h3>
                  <div className="stat-number">{pendingHospitals.length + authorizedHospitals.length}</div>
                </div>
                <div className="stat-card">
                  <h3>Authorized</h3>
                  <div className="stat-number">{authorizedHospitals.length}</div>
                </div>
                <div className="stat-card">
                  <h3>Pending</h3>
                  <div className="stat-number">{pendingHospitals.length}</div>
                </div>
                <div className="stat-card">
                  <h3>Admin Address</h3>
                  <div className="admin-address">{account.slice(0, 6)}...{account.slice(-4)}</div>
                </div>
              </div>
            </section>

            {/* Admin Actions */}
            <section className="admin-actions">
              <h2>‚öôÔ∏è Admin Actions</h2>
              <div className="actions-grid">
                <button 
                  onClick={loadHospitals}
                  className="action-btn"
                  disabled={loading}
                >
                  üîÑ Refresh Data
                </button>
                
                <button 
                  onClick={() => {
                    const count = pendingHospitals.length + authorizedHospitals.length;
                    alert(`System Status:\n\nTotal Hospitals: ${count}\nAuthorized: ${authorizedHospitals.length}\nPending: ${pendingHospitals.length}\n\nContract: ${contract.address}\nAdmin: ${account}`);
                  }}
                  className="action-btn"
                >
                  üìã System Info
                </button>
              </div>
            </section>

            {/* Important Notes */}
            <section className="admin-notes">
              <h3>‚ö†Ô∏è Important Notes</h3>
              <div className="notes-content">
                <ul>
                  <li><strong>Hospital Authorization:</strong> Only authorized hospitals can register children and record vaccinations</li>
                  <li><strong>Permanent Records:</strong> All authorization changes are permanently recorded on the blockchain</li>
                  <li><strong>Gas Fees:</strong> Each authorization transaction requires gas fees</li>
                  <li><strong>Verification:</strong> Always verify hospital credentials before authorization</li>
                  <li><strong>Revocation:</strong> Authorized hospitals can be deauthorized if needed</li>
                </ul>
              </div>
            </section>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminPanel;
