import React, { Component } from 'react';

class QRVerification extends Component {
  constructor(props) {
    super(props);
    this.state = {
      verificationMode: 'qr-scan', // 'qr-scan', 'token-search', 'manual-entry'
      qrCode: '',
      tokenId: '',
      verificationData: '',
      loading: false,
      verificationResult: null,
      error: null
    };
  }

  // Handle input changes
  handleInputChange = (e) => {
    const { name, value } = e.target;
    this.setState({ [name]: value });
  };

  // Switch verification mode
  switchMode = (mode) => {
    this.setState({
      verificationMode: mode,
      verificationResult: null,
      error: null,
      qrCode: '',
      tokenId: '',
      verificationData: ''
    });
  };

  // Verify QR Code
  verifyQRCode = async () => {
    const { qrCode } = this.state;
    
    if (!qrCode.trim()) {
      this.setState({ error: 'Please enter a QR code hash' });
      return;
    }

    this.setState({ loading: true, error: null });

    try {
      // Simulate verification delay
      await new Promise(resolve => setTimeout(resolve, 1500));

      // Search for vaccination record with this QR code
      const allChildren = JSON.parse(localStorage.getItem('registeredChildren') || '[]');
      let foundVaccination = null;
      let foundChild = null;

      for (const child of allChildren) {
        const vaccinations = JSON.parse(localStorage.getItem(`vaccinations_${child.tokenId}`) || '[]');
        foundVaccination = vaccinations.find(vax => vax.qrCodeHash === qrCode);
        if (foundVaccination) {
          foundChild = child;
          break;
        }
      }

      if (!foundVaccination) {
        this.setState({
          loading: false,
          error: 'QR Code not found or invalid',
          verificationResult: {
            status: 'invalid',
            message: 'This QR code is not valid or does not exist in our system'
          }
        });
        return;
      }

      // Successful verification
      this.setState({
        loading: false,
        verificationResult: {
          status: 'valid',
          message: 'Vaccination record verified successfully',
          vaccination: foundVaccination,
          child: foundChild,
          verifiedAt: new Date().toISOString()
        }
      });

    } catch (error) {
      console.error('Verification error:', error);
      this.setState({
        loading: false,
        error: 'Error during verification: ' + error.message
      });
    }
  };

  // Verify by Token ID
  verifyByTokenId = async () => {
    const { tokenId } = this.state;
    
    if (!tokenId.trim()) {
      this.setState({ error: 'Please enter a Token ID' });
      return;
    }

    this.setState({ loading: true, error: null });

    try {
      await new Promise(resolve => setTimeout(resolve, 1000));

      // Find child by token ID
      const allChildren = JSON.parse(localStorage.getItem('registeredChildren') || '[]');
      const child = allChildren.find(c => c.tokenId === tokenId);

      if (!child) {
        this.setState({
          loading: false,
          error: 'Token ID not found',
          verificationResult: {
            status: 'invalid',
            message: 'No child found with this Token ID'
          }
        });
        return;
      }

      // Get vaccination history
      const vaccinations = JSON.parse(localStorage.getItem(`vaccinations_${tokenId}`) || '[]');

      this.setState({
        loading: false,
        verificationResult: {
          status: 'valid',
          message: 'Child record found',
          child: child,
          vaccinations: vaccinations,
          verifiedAt: new Date().toISOString()
        }
      });

    } catch (error) {
      console.error('Verification error:', error);
      this.setState({
        loading: false,
        error: 'Error during verification: ' + error.message
      });
    }
  };

  // Manual verification
  verifyManualData = async () => {
    const { verificationData } = this.state;
    
    if (!verificationData.trim()) {
      this.setState({ error: 'Please enter verification data' });
      return;
    }

    this.setState({ loading: true, error: null });

    try {
      await new Promise(resolve => setTimeout(resolve, 1000));

      // Parse JSON data
      const data = JSON.parse(verificationData);
      
      if (!data.tokenId || !data.childName) {
        throw new Error('Invalid verification data format');
      }

      // Verify the data against stored records
      const allChildren = JSON.parse(localStorage.getItem('registeredChildren') || '[]');
      const child = allChildren.find(c => 
        c.tokenId === data.tokenId && 
        c.childName === data.childName
      );

      if (!child) {
        this.setState({
          loading: false,
          verificationResult: {
            status: 'invalid',
            message: 'Verification data does not match any records'
          }
        });
        return;
      }

      this.setState({
        loading: false,
        verificationResult: {
          status: 'valid',
          message: 'Data verified successfully',
          child: child,
          verifiedAt: new Date().toISOString()
        }
      });

    } catch (error) {
      console.error('Verification error:', error);
      this.setState({
        loading: false,
        error: 'Invalid verification data format'
      });
    }
  };

  // Format date
  formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString();
  };

  // Calculate child age
  getChildAge = (dateOfBirth) => {
    const birthDate = new Date(dateOfBirth);
    const ageInDays = Math.floor((Date.now() - birthDate.getTime()) / (24 * 60 * 60 * 1000));
    if (ageInDays < 30) return `${ageInDays} days`;
    if (ageInDays < 365) return `${Math.floor(ageInDays/30)} months`;
    return `${Math.floor(ageInDays/365)} years`;
  };

  render() {
    const { 
      verificationMode, 
      qrCode, 
      tokenId, 
      verificationData, 
      loading, 
      verificationResult, 
      error 
    } = this.state;

    return (
      <div className="App">
        <div className="main-content">
          <div className="qr-verification">
            <div className="verification-container">
              <div className="verification-header">
                <h1>🔍 Vaccination Verification</h1>
                <p>Verify vaccination records using QR codes, Token IDs, or manual data entry</p>
                <button onClick={this.props.onBack} className="back-btn btn-secondary">
                  ← Back to Dashboard
                </button>
              </div>

              {/* Verification Modes */}
              <div className="verification-modes">
                <div className="mode-selector">
                  <button 
                    className={`mode-btn ${verificationMode === 'qr-scan' ? 'active' : ''}`}
                    onClick={() => this.switchMode('qr-scan')}
                  >
                    📱 QR Code
                  </button>
                  <button 
                    className={`mode-btn ${verificationMode === 'token-search' ? 'active' : ''}`}
                    onClick={() => this.switchMode('token-search')}
                  >
                    🔍 Token ID
                  </button>
                  <button 
                    className={`mode-btn ${verificationMode === 'manual-entry' ? 'active' : ''}`}
                    onClick={() => this.switchMode('manual-entry')}
                  >
                    📝 Manual Entry
                  </button>
                </div>
              </div>

              {/* QR Code Form */}
              {verificationMode === 'qr-scan' && (
                <div className="verification-form">
                  <div className="form-section">
                    <h3>📱 QR Code Verification</h3>
                    <p>Enter the QR code hash from a vaccination certificate</p>
                    
                    <div className="input-group">
                      <input
                        type="text"
                        name="qrCode"
                        value={qrCode}
                        onChange={this.handleInputChange}
                        placeholder="QR code hash (e.g., QR1648123456789_BCG_1648123456789)"
                        className="verification-input"
                        disabled={loading}
                      />
                      <button 
                        onClick={this.verifyQRCode}
                        disabled={loading || !qrCode.trim()}
                        className="verify-btn"
                      >
                        {loading ? '⏳ Verifying...' : '🔍 Verify'}
                      </button>
                    </div>
                    
                    <div className="qr-help">
                      <h4>How to verify QR codes:</h4>
                      <ul>
                        <li>Scan the QR code from a vaccination certificate</li>
                        <li>Copy the QR code hash and paste it above</li>
                        <li>The system will verify authenticity and show vaccination details</li>
                      </ul>
                    </div>
                  </div>
                </div>
              )}

              {/* Token ID Form */}
              {verificationMode === 'token-search' && (
                <div className="verification-form">
                  <div className="form-section">
                    <h3>🔍 Token ID Verification</h3>
                    <p>Search for a child's vaccination record using their Token ID</p>
                    
                    <div className="input-group">
                      <input
                        type="text"
                        name="tokenId"
                        value={tokenId}
                        onChange={this.handleInputChange}
                        placeholder="Child's Token ID (e.g., 1648123456789)"
                        className="verification-input"
                        disabled={loading}
                      />
                      <button 
                        onClick={this.verifyByTokenId}
                        disabled={loading || !tokenId.trim()}
                        className="verify-btn"
                      >
                        {loading ? '⏳ Searching...' : '🔍 Search'}
                      </button>
                    </div>
                    
                    <div className="qr-help">
                      <h4>Token ID verification:</h4>
                      <ul>
                        <li>Each child has a unique Token ID assigned at birth</li>
                        <li>Parents can share this ID for verification purposes</li>
                        <li>Shows complete vaccination history and status</li>
                      </ul>
                    </div>
                  </div>
                </div>
              )}

              {/* Manual Entry Form */}
              {verificationMode === 'manual-entry' && (
                <div className="verification-form">
                  <div className="form-section">
                    <h3>📝 Manual Data Verification</h3>
                    <p>Verify vaccination data by entering JSON verification data</p>
                    
                    <div className="input-group-vertical">
                      <textarea
                        name="verificationData"
                        value={verificationData}
                        onChange={this.handleInputChange}
                        placeholder='{"tokenId": "1648123456789", "childName": "John Doe", "parentAddress": "0x..."}'
                        className="verification-textarea"
                        rows="4"
                        disabled={loading}
                      />
                      <button 
                        onClick={this.verifyManualData}
                        disabled={loading || !verificationData.trim()}
                        className="verify-btn"
                      >
                        {loading ? '⏳ Verifying...' : '🔍 Verify Data'}
                      </button>
                    </div>
                    
                    <div className="qr-help">
                      <h4>Manual verification format:</h4>
                      <ul>
                        <li>Enter JSON data with tokenId and childName</li>
                        <li>System will verify against blockchain records</li>
                        <li>Useful for custom verification workflows</li>
                      </ul>
                    </div>
                  </div>
                </div>
              )}

              {/* Error Display */}
              {error && (
                <div className="verification-error">
                  <div className="error-icon">⚠️</div>
                  <h3>Verification Error</h3>
                  <p>{error}</p>
                </div>
              )}

              {/* Loading State */}
              {loading && (
                <div className="verification-loading">
                  <div className="loading-spinner">⏳</div>
                  <p>Verifying against blockchain records...</p>
                </div>
              )}

              {/* Verification Results */}
              {verificationResult && (
                <div className="verification-results">
                  {verificationResult.status === 'invalid' ? (
                    <div className="verification-error">
                      <div className="error-icon">❌</div>
                      <h3>Verification Failed</h3>
                      <p>{verificationResult.message}</p>
                    </div>
                  ) : (
                    <div className="verification-success">
                      <div className="success-icon">✅</div>
                      <h3>Verification Successful</h3>
                      
                      <div className="verification-details">
                        {/* Child Information */}
                        <div className="detail-section">
                          <h4>👶 Child Information</h4>
                          <div className="detail-grid">
                            <div className="detail-item">
                              <label>Name:</label>
                              <span>{verificationResult.child.childName}</span>
                            </div>
                            <div className="detail-item">
                              <label>Date of Birth:</label>
                              <span>{this.formatDate(verificationResult.child.dateOfBirth)}</span>
                            </div>
                            <div className="detail-item">
                              <label>Age:</label>
                              <span>{this.getChildAge(verificationResult.child.dateOfBirth)}</span>
                            </div>
                            <div className="detail-item">
                              <label>Token ID:</label>
                              <span>{verificationResult.child.tokenId}</span>
                            </div>
                            <div className="detail-item">
                              <label>Registered by:</label>
                              <span>{verificationResult.child.registeredByHospital}</span>
                            </div>
                          </div>
                        </div>

                        {/* Specific Vaccination (if QR verification) */}
                        {verificationResult.vaccination && (
                          <div className="detail-section">
                            <h4>💉 Verified Vaccination</h4>
                            <div className="vaccination-card">
                              <div className="vaccination-header">
                                <strong>{verificationResult.vaccination.vaccineName}</strong>
                                <span className="vaccination-date">
                                  {this.formatDate(verificationResult.vaccination.dateAdministered)}
                                </span>
                              </div>
                              <div className="vaccination-info">
                                <p><strong>Hospital:</strong> {verificationResult.vaccination.hospitalName}</p>
                                <p><strong>Batch Number:</strong> {verificationResult.vaccination.batchNumber}</p>
                                <p><strong>Administered by:</strong> {verificationResult.vaccination.administeredBy}</p>
                                {verificationResult.vaccination.notes && (
                                  <p><strong>Notes:</strong> {verificationResult.vaccination.notes}</p>
                                )}
                              </div>
                              
                              <div className="qr-details">
                                <p><strong>QR Hash:</strong></p>
                                <div className="qr-hash">{verificationResult.vaccination.qrCodeHash}</div>
                              </div>
                            </div>
                          </div>
                        )}

                        {/* Full Vaccination History (if Token ID verification) */}
                        {verificationResult.vaccinations && (
                          <div className="detail-section">
                            <h4>📋 Vaccination History ({verificationResult.vaccinations.length})</h4>
                            <div className="vaccination-status">
                              <div className="status-item">
                                <label>Total Vaccinations</label>
                                <span className="status-number">{verificationResult.vaccinations.length}</span>
                              </div>
                              <div className="status-item">
                                <label>Status</label>
                                <span className="status-badge verified">
                                  {verificationResult.vaccinations.length > 0 ? 'Verified' : 'No Records'}
                                </span>
                              </div>
                            </div>
                            
                            {verificationResult.vaccinations.length > 0 && (
                              <div className="history-list">
                                {verificationResult.vaccinations.map((vaccine, index) => (
                                  <div key={index} className="history-item">
                                    <div className="history-header">
                                      <strong>{vaccine.vaccineName}</strong>
                                      <span className="history-date">
                                        {this.formatDate(vaccine.dateAdministered)}
                                      </span>
                                    </div>
                                    <div className="history-details">
                                      <span>{vaccine.hospitalName}</span>
                                      <span>Batch: {vaccine.batchNumber}</span>
                                      {vaccine.qrCodeHash && (
                                        <span className="qr-available">🔍 QR Available</span>
                                      )}
                                    </div>
                                  </div>
                                ))}
                              </div>
                            )}
                          </div>
                        )}

                        {/* Verification Footer */}
                        <div className="verification-footer">
                          <p><strong>Verified at:</strong> {this.formatDate(verificationResult.verifiedAt)}</p>
                          <p><strong>Verification ID:</strong> {Math.random().toString(36).substr(2, 9).toUpperCase()}</p>
                        </div>
                      </div>
                    </div>
                  )}
                </div>
              )}

              {/* Help Information */}
              <div className="verification-info">
                <h3>How Verification Works</h3>
                <div className="info-grid">
                  <div className="info-card">
                    <h4>🔒 Secure</h4>
                    <p>All records are cryptographically signed and stored on blockchain</p>
                  </div>
                  <div className="info-card">
                    <h4>⚡ Instant</h4>
                    <p>Verification happens in real-time with immediate results</p>
                  </div>
                  <div className="info-card">
                    <h4>🌐 Universal</h4>
                    <p>Works across all authorized healthcare providers globally</p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default QRVerification;
