import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';

const HospitalRegistration = ({ contract, account, onSuccess }) => {
  const [formData, setFormData] = useState({
    hospitalName: '',
    licenseNumber: '',
    contactInfo: '',
    address: '',
    website: '',
    specializations: '',
    emergencyContact: ''
  });
  const [loading, setLoading] = useState(false);
  const [hospitalStatus, setHospitalStatus] = useState(null);
  const [isRegistered, setIsRegistered] = useState(false);
  const [isAuthorized, setIsAuthorized] = useState(false);

  useEffect(() => {
    checkHospitalStatus();
  }, [account, contract]);

  const checkHospitalStatus = async () => {
    if (!contract || !account) return;
    
    try {
      const hospitalInfo = await contract.getHospitalInfo(account);
      const isAuth = await contract.authorizedHospitals(account);
      
      if (hospitalInfo.registrationDate > 0) {
        setIsRegistered(true);
        setIsAuthorized(isAuth);
        setHospitalStatus(hospitalInfo);
        setFormData({
          hospitalName: hospitalInfo.name,
          licenseNumber: hospitalInfo.licenseNumber,
          contactInfo: hospitalInfo.contactInfo,
          address: '',
          website: '',
          specializations: '',
          emergencyContact: ''
        });
      }
    } catch (error) {
      console.error('Error checking hospital status:', error);
      setIsRegistered(false);
      setIsAuthorized(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const registerHospital = async () => {
    if (!formData.hospitalName || !formData.licenseNumber || !formData.contactInfo) {
      alert('Please fill in all required fields');
      return;
    }

    try {
      setLoading(true);
      
      const tx = await contract.registerHospital(
        formData.hospitalName,
        formData.licenseNumber,
        formData.contactInfo
      );

      console.log('Hospital registration transaction submitted:', tx.hash);
      
      const receipt = await tx.wait();
      console.log('Hospital registration confirmed:', receipt);

      // Refresh hospital status
      await checkHospitalStatus();
      setLoading(false);
      
      alert('Hospital registered successfully! Please wait for admin authorization.');
    } catch (error) {
      console.error('Error registering hospital:', error);
      alert('Error registering hospital: ' + error.message);
      setLoading(false);
    }
  };

  const getStatusColor = (status) => {
    if (isAuthorized) return '#10b981'; // green
    if (isRegistered) return '#f59e0b'; // yellow
    return '#ef4444'; // red
  };

  const getStatusText = () => {
    if (isAuthorized) return '✅ Authorized - Can register children';
    if (isRegistered) return '⏳ Pending Authorization';
    return '❌ Not Registered';
  };

  if (isRegistered) {
    return (
      <div className="hospital-status">
        <div className="status-container">
          <div className="hospital-header">
            <h2>🏥 Hospital Status</h2>
            <div 
              className="status-badge"
              style={{ backgroundColor: getStatusColor(), color: 'white' }}
            >
              {getStatusText()}
            </div>
          </div>

          <div className="hospital-info-card">
            <h3>{hospitalStatus?.name}</h3>
            <div className="info-grid">
              <div className="info-item">
                <label>License Number:</label>
                <span>{hospitalStatus?.licenseNumber}</span>
              </div>
              <div className="info-item">
                <label>Contact Info:</label>
                <span>{hospitalStatus?.contactInfo}</span>
              </div>
              <div className="info-item">
                <label>Wallet Address:</label>
                <span className="wallet-address">{account}</span>
              </div>
              <div className="info-item">
                <label>Registration Date:</label>
                <span>{hospitalStatus?.registrationDate ? 
                  new Date(hospitalStatus.registrationDate * 1000).toLocaleDateString() : 'N/A'}</span>
              </div>
              <div className="info-item">
                <label>Authorization Status:</label>
                <span className={isAuthorized ? 'authorized' : 'pending'}>
                  {isAuthorized ? 'Authorized' : 'Pending Admin Approval'}
                </span>
              </div>
            </div>
          </div>

          {!isAuthorized && (
            <div className="pending-message">
              <div className="pending-icon">⏳</div>
              <h3>Waiting for Authorization</h3>
              <p>Your hospital registration is complete but requires administrative approval before you can register children.</p>
              <div className="next-steps">
                <h4>What to expect:</h4>
                <ul>
                  <li>Admin will review your hospital credentials</li>
                  <li>You'll receive authorization within 24-48 hours</li>
                  <li>Once authorized, you can register newborn children</li>
                  <li>NFTs will be automatically sent to parents' wallets</li>
                </ul>
              </div>
            </div>
          )}

          {isAuthorized && (
            <div className="authorized-message">
              <div className="success-icon">✅</div>
              <h3>Hospital Authorized!</h3>
              <p>Your hospital is now authorized to register children in the vaccination system.</p>
              <div className="capabilities">
                <h4>What you can now do:</h4>
                <ul>
                  <li>✅ Register newborn children</li>
                  <li>✅ Record vaccination data</li>
                  <li>✅ Generate QR codes for verification</li>
                  <li>✅ Access vaccination histories</li>
                </ul>
              </div>
              <button onClick={onSuccess} className="continue-btn">
                Start Registering Children
              </button>
            </div>
          )}
        </div>
      </div>
    );
  }

  return (
    <div className="hospital-registration">
      <div className="registration-container">
        <div className="registration-header">
          <h2>🏥 Hospital Registration</h2>
          <p>Register your hospital to start managing vaccination records</p>
        </div>

        <div className="registration-form">
          <div className="form-section">
            <h3>🏢 Hospital Information</h3>
            
            <div className="form-group">
              <label>Hospital Name *</label>
              <input
                type="text"
                name="hospitalName"
                value={formData.hospitalName}
                onChange={handleInputChange}
                placeholder="Enter official hospital name"
                required
              />
            </div>

            <div className="form-group">
              <label>Medical License Number *</label>
              <input
                type="text"
                name="licenseNumber"
                value={formData.licenseNumber}
                onChange={handleInputChange}
                placeholder="Enter hospital license number"
                required
              />
            </div>

            <div className="form-group">
              <label>Primary Contact Information *</label>
              <input
                type="text"
                name="contactInfo"
                value={formData.contactInfo}
                onChange={handleInputChange}
                placeholder="Phone number and email"
                required
              />
            </div>
          </div>

          <div className="form-section">
            <h3>📍 Additional Details</h3>
            
            <div className="form-group">
              <label>Hospital Address</label>
              <textarea
                name="address"
                value={formData.address}
                onChange={handleInputChange}
                placeholder="Full hospital address"
                rows="3"
              />
            </div>

            <div className="form-group">
              <label>Website</label>
              <input
                type="url"
                name="website"
                value={formData.website}
                onChange={handleInputChange}
                placeholder="https://your-hospital-website.com"
              />
            </div>

            <div className="form-group">
              <label>Specializations</label>
              <textarea
                name="specializations"
                value={formData.specializations}
                onChange={handleInputChange}
                placeholder="List hospital specializations (e.g., Pediatrics, Maternity, General Medicine)"
                rows="3"
              />
            </div>

            <div className="form-group">
              <label>Emergency Contact</label>
              <input
                type="tel"
                name="emergencyContact"
                value={formData.emergencyContact}
                onChange={handleInputChange}
                placeholder="24/7 emergency contact number"
              />
            </div>
          </div>

          <div className="wallet-info">
            <h3>🔐 Blockchain Information</h3>
            <div className="wallet-display">
              <label>Hospital Wallet Address:</label>
              <div className="wallet-address">{account}</div>
              <small>This address will be used to register children and record vaccinations</small>
            </div>
          </div>

          <div className="terms-section">
            <div className="terms-item">
              <input type="checkbox" id="accuracy" required />
              <label htmlFor="accuracy">
                I certify that all provided information is accurate and up-to-date
              </label>
            </div>
            <div className="terms-item">
              <input type="checkbox" id="authority" required />
              <label htmlFor="authority">
                I have the authority to register this hospital in the vaccination system
              </label>
            </div>
            <div className="terms-item">
              <input type="checkbox" id="compliance" required />
              <label htmlFor="compliance">
                This hospital complies with all relevant medical and data protection regulations
              </label>
            </div>
            <div className="terms-item">
              <input type="checkbox" id="responsibility" required />
              <label htmlFor="responsibility">
                I understand the responsibility of managing vaccination records on the blockchain
              </label>
            </div>
          </div>

          <div className="registration-info">
            <div className="info-box">
              <h4>📋 Registration Process:</h4>
              <ol>
                <li><strong>Submit Registration:</strong> Complete this form with accurate information</li>
                <li><strong>Admin Review:</strong> System administrators will verify your credentials</li>
                <li><strong>Authorization:</strong> Once approved, you can register children and record vaccinations</li>
                <li><strong>NFT Management:</strong> Children's NFTs will be automatically sent to parents' wallets</li>
              </ol>
            </div>
          </div>

          <div className="form-actions">
            <button 
              onClick={registerHospital}
              className="btn-primary"
              disabled={loading || !formData.hospitalName || !formData.licenseNumber || !formData.contactInfo}
            >
              {loading ? '⏳ Registering Hospital...' : '🏥 Register Hospital'}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default HospitalRegistration;
