import React, { Component } from 'react';

class HospitalRegistration extends Component {
  constructor(props) {
    super(props);
    this.state = {
      formData: {
        hospitalName: '',
        licenseNumber: '',
        contactInfo: '',
        address: '',
        website: '',
        specializations: '',
        emergencyContact: ''
      },
      loading: false,
      hospitalStatus: null,
      isRegistered: false,
      isAuthorized: false,
      step: 1
    };
  }

  componentDidMount() {
    this.checkHospitalStatus();
  }

  // Check if hospital is already registered
  checkHospitalStatus = () => {
    // For now, simulate checking status
    // Later we'll connect to real smart contract
    const isRegistered = localStorage.getItem('hospitalRegistered') === 'true';
    const isAuthorized = localStorage.getItem('hospitalAuthorized') === 'true';
    
    if (isRegistered) {
      const savedData = JSON.parse(localStorage.getItem('hospitalData') || '{}');
      this.setState({
        isRegistered: true,
        isAuthorized: isAuthorized,
        hospitalStatus: savedData,
        formData: { ...this.state.formData, ...savedData }
      });
    }
  };

  // Handle input changes
  handleInputChange = (e) => {
    const { name, value } = e.target;
    this.setState({
      formData: {
        ...this.state.formData,
        [name]: value
      }
    });
  };

  // Validate form
  validateForm = () => {
    const { hospitalName, licenseNumber, contactInfo } = this.state.formData;
    return hospitalName.trim() && licenseNumber.trim() && contactInfo.trim();
  };

  // Register hospital
  registerHospital = async () => {
    if (!this.validateForm()) {
      alert('Please fill in all required fields');
      return;
    }

    this.setState({ loading: true });

    try {
      // Simulate API call - replace with real smart contract call later
      await new Promise(resolve => setTimeout(resolve, 2000));

      const registrationData = {
        ...this.state.formData,
        registrationDate: new Date().toISOString(),
        walletAddress: '0x' + Math.random().toString(16).substr(2, 40) // Mock address
      };

      // Save to localStorage for demo
      localStorage.setItem('hospitalRegistered', 'true');
      localStorage.setItem('hospitalAuthorized', 'false'); // Needs admin approval
      localStorage.setItem('hospitalData', JSON.stringify(registrationData));

      this.setState({
        loading: false,
        isRegistered: true,
        isAuthorized: false,
        hospitalStatus: registrationData
      });

      alert('Hospital registered successfully! Please wait for admin authorization.');
    } catch (error) {
      console.error('Error registering hospital:', error);
      alert('Error registering hospital: ' + error.message);
      this.setState({ loading: false });
    }
  };

  // Simulate admin authorization (for testing)
  simulateAuthorization = () => {
    localStorage.setItem('hospitalAuthorized', 'true');
    this.setState({ isAuthorized: true });
    alert('üéâ Hospital authorized! You can now register children and record vaccinations.');
  };

  // Reset registration (for testing)
  resetRegistration = () => {
    localStorage.removeItem('hospitalRegistered');
    localStorage.removeItem('hospitalAuthorized');
    localStorage.removeItem('hospitalData');
    this.setState({
      isRegistered: false,
      isAuthorized: false,
      hospitalStatus: null,
      formData: {
        hospitalName: '',
        licenseNumber: '',
        contactInfo: '',
        address: '',
        website: '',
        specializations: '',
        emergencyContact: ''
      }
    });
  };

  // Render hospital status (if already registered)
  renderHospitalStatus() {
    const { hospitalStatus, isAuthorized } = this.state;

    return (
      <div className="hospital-status">
        <div className="status-container">
          <div className="hospital-header">
            <h2>üè• Hospital Status</h2>
            <div 
              className={`status-badge ${isAuthorized ? 'authorized' : 'pending'}`}
            >
              {isAuthorized ? '‚úÖ Authorized - Can register children' : '‚è≥ Pending Authorization'}
            </div>
          </div>

          <div className="hospital-info-card">
            <h3>{hospitalStatus?.hospitalName}</h3>
            <div className="info-grid">
              <div className="info-item">
                <label>License Number:</label>
                <span>{hospitalStatus?.licenseNumber}</span>
              </div>
              <div className="info-item">
                <label>Contact Info:</label>
                <span>{hospitalStatus?.contactInfo}</span>
              </div>
              <div className="info-item">
                <label>Wallet Address:</label>
                <span className="wallet-address">{hospitalStatus?.walletAddress}</span>
              </div>
              <div className="info-item">
                <label>Registration Date:</label>
                <span>{new Date(hospitalStatus?.registrationDate).toLocaleDateString()}</span>
              </div>
              <div className="info-item">
                <label>Authorization Status:</label>
                <span className={isAuthorized ? 'authorized' : 'pending'}>
                  {isAuthorized ? 'Authorized' : 'Pending Admin Approval'}
                </span>
              </div>
            </div>
          </div>

          {!isAuthorized && (
            <div className="pending-message">
              <div className="pending-icon">‚è≥</div>
              <h3>Waiting for Authorization</h3>
              <p>Your hospital registration is complete but requires administrative approval before you can register children.</p>
              <div className="next-steps">
                <h4>What to expect:</h4>
                <ul>
                  <li>Admin will review your hospital credentials</li>
                  <li>You'll receive authorization within 24-48 hours</li>
                  <li>Once authorized, you can register newborn children</li>
                  <li>NFTs will be automatically sent to parents' wallets</li>
                </ul>
              </div>
              
              {/* Demo button for testing */}
              <div className="demo-controls">
                <h4>üß™ Demo Controls:</h4>
                <button onClick={this.simulateAuthorization} className="btn-primary">
                  Simulate Admin Authorization
                </button>
                <button onClick={this.resetRegistration} className="btn-secondary">
                  Reset Registration
                </button>
              </div>
            </div>
          )}

          {isAuthorized && (
            <div className="authorized-message">
              <div className="success-icon">‚úÖ</div>
              <h3>Hospital Authorized!</h3>
              <p>Your hospital is now authorized to register children in the vaccination system.</p>
              <div className="capabilities">
                <h4>What you can now do:</h4>
                <ul>
                  <li>‚úÖ Register newborn children</li>
                  <li>‚úÖ Record vaccination data</li>
                  <li>‚úÖ Generate QR codes for verification</li>
                  <li>‚úÖ Access vaccination histories</li>
                </ul>
              </div>
              
              <div className="demo-controls">
                <h4>üß™ Demo Controls:</h4>
                <button onClick={this.resetRegistration} className="btn-secondary">
                  Reset Registration
                </button>
              </div>
            </div>
          )}

          <button onClick={this.props.onBack} className="btn-secondary">
            ‚Üê Back to Dashboard
          </button>
        </div>
      </div>
    );
  }

  // Render registration form
  renderRegistrationForm() {
    const { formData, loading } = this.state;

    return (
      <div className="hospital-registration">
        <div className="registration-container">
          <div className="registration-header">
            <h2>üè• Hospital Registration</h2>
            <p>Register your hospital to start managing vaccination records</p>
          </div>

          <div className="registration-form">
            <div className="form-section">
              <h3>üè¢ Hospital Information</h3>
              
              <div className="form-group">
                <label>Hospital Name *</label>
                <input
                  type="text"
                  name="hospitalName"
                  value={formData.hospitalName}
                  onChange={this.handleInputChange}
                  placeholder="Enter official hospital name"
                  required
                  disabled={loading}
                />
              </div>

              <div className="form-group">
                <label>Medical License Number *</label>
                <input
                  type="text"
                  name="licenseNumber"
                  value={formData.licenseNumber}
                  onChange={this.handleInputChange}
                  placeholder="Enter hospital license number"
                  required
                  disabled={loading}
                />
              </div>

              <div className="form-group">
                <label>Primary Contact Information *</label>
                <input
                  type="text"
                  name="contactInfo"
                  value={formData.contactInfo}
                  onChange={this.handleInputChange}
                  placeholder="Phone number and email"
                  required
                  disabled={loading}
                />
              </div>
            </div>

            <div className="form-section">
              <h3>üìç Additional Details</h3>
              
              <div className="form-group">
                <label>Hospital Address</label>
                <textarea
                  name="address"
                  value={formData.address}
                  onChange={this.handleInputChange}
                  placeholder="Full hospital address"
                  rows="3"
                  disabled={loading}
                />
              </div>

              <div className="form-group">
                <label>Website</label>
                <input
                  type="url"
                  name="website"
                  value={formData.website}
                  onChange={this.handleInputChange}
                  placeholder="https://your-hospital-website.com"
                  disabled={loading}
                />
              </div>

              <div className="form-group">
                <label>Specializations</label>
                <textarea
                  name="specializations"
                  value={formData.specializations}
                  onChange={this.handleInputChange}
                  placeholder="List hospital specializations (e.g., Pediatrics, Maternity, General Medicine)"
                  rows="3"
                  disabled={loading}
                />
              </div>

              <div className="form-group">
                <label>Emergency Contact</label>
                <input
                  type="tel"
                  name="emergencyContact"
                  value={formData.emergencyContact}
                  onChange={this.handleInputChange}
                  placeholder="24/7 emergency contact number"
                  disabled={loading}
                />
              </div>
            </div>

            <div className="wallet-info">
              <h3>üîê Blockchain Information</h3>
              <div className="wallet-display">
                <label>Hospital Wallet Address:</label>
                <div className="wallet-address">Demo Mode - Address will be auto-detected</div>
                <small>This address will be used to register children and record vaccinations</small>
              </div>
            </div>

            <div className="terms-section">
              <div className="terms-item">
                <input type="checkbox" id="accuracy" required disabled={loading} />
                <label htmlFor="accuracy">
                  I certify that all provided information is accurate and up-to-date
                </label>
              </div>
              <div className="terms-item">
                <input type="checkbox" id="authority" required disabled={loading} />
                <label htmlFor="authority">
                  I have the authority to register this hospital in the vaccination system
                </label>
              </div>
              <div className="terms-item">
                <input type="checkbox" id="compliance" required disabled={loading} />
                <label htmlFor="compliance">
                  This hospital complies with all relevant medical and data protection regulations
                </label>
              </div>
              <div className="terms-item">
                <input type="checkbox" id="responsibility" required disabled={loading} />
                <label htmlFor="responsibility">
                  I understand the responsibility of managing vaccination records on the blockchain
                </label>
              </div>
            </div>

            <div className="registration-info">
              <div className="info-box">
                <h4>üìã Registration Process:</h4>
                <ol>
                  <li><strong>Submit Registration:</strong> Complete this form with accurate information</li>
                  <li><strong>Admin Review:</strong> System administrators will verify your credentials</li>
                  <li><strong>Authorization:</strong> Once approved, you can register children and record vaccinations</li>
                  <li><strong>NFT Management:</strong> Children's NFTs will be automatically sent to parents' wallets</li>
                </ol>
              </div>
            </div>

            <div className="form-actions">
              <button onClick={this.props.onBack} className="btn-secondary" disabled={loading}>
                ‚Üê Back to Dashboard
              </button>
              
              <button 
                onClick={this.registerHospital}
                className="btn-primary"
                disabled={loading || !this.validateForm()}
              >
                {loading ? '‚è≥ Registering Hospital...' : 'üè• Register Hospital'}
              </button>
            </div>

            {loading && (
              <div className="loading-info">
                <div className="loading-spinner">‚è≥</div>
                <p><strong>üì° Processing Registration...</strong></p>
                <p>‚è≥ Please wait for registration confirmation</p>
                <p>üîç Validating hospital credentials</p>
              </div>
            )}
          </div>
        </div>
      </div>
    );
  }

  render() {
    const { isRegistered } = this.state;

    return (
      <div className="App">
        <div className="main-content">
          {isRegistered ? this.renderHospitalStatus() : this.renderRegistrationForm()}
        </div>
      </div>
    );
  }
}

export default HospitalRegistration;
