import React, { Component } from 'react';

class ChildRegistration extends Component {
  constructor(props) {
    super(props);
    this.state = {
      formData: {
        childName: '',
        dateOfBirth: '',
        parentName: '',
        contactInfo: '',
        parentAddress: '',
        emergencyContact: '',
        medicalHistory: '',
        allergies: ''
      },
      loading: false,
      registeredChild: null,
      profileImage: null,
      step: 1,
      isAuthorizedHospital: false,
      hospitalInfo: null,
      registeredChildren: []
    };
  }

  componentDidMount() {
    this.checkHospitalAuthorization();
    this.loadRegisteredChildren();
  }

  // Check if current user is an authorized hospital
  checkHospitalAuthorization = () => {
    const isRegistered = localStorage.getItem('hospitalRegistered') === 'true';
    const isAuthorized = localStorage.getItem('hospitalAuthorized') === 'true';
    
    if (isRegistered && isAuthorized) {
      const hospitalData = JSON.parse(localStorage.getItem('hospitalData') || '{}');
      this.setState({
        isAuthorizedHospital: true,
        hospitalInfo: hospitalData
      });
    }
  };

  // Load previously registered children
  loadRegisteredChildren = () => {
    const children = JSON.parse(localStorage.getItem('registeredChildren') || '[]');
    this.setState({ registeredChildren: children });
  };

  // Handle input changes
  handleInputChange = (e) => {
    const { name, value } = e.target;
    this.setState({
      formData: {
        ...this.state.formData,
        [name]: value
      }
    });
  };

  // Handle image upload
  handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      this.setState({ profileImage: file });
    }
  };

  // Validate Ethereum address format
  validateParentAddress = (address) => {
    const ethRegex = /^0x[a-fA-F0-9]{40}$/;
    return ethRegex.test(address);
  };

  // Validate form
  validateForm = () => {
    const { childName, dateOfBirth, parentName, parentAddress } = this.state.formData;
    return (
      childName.trim() && 
      dateOfBirth && 
      parentName.trim() && 
      parentAddress.trim() && 
      this.validateParentAddress(parentAddress)
    );
  };

  // Register child
  registerChild = async () => {
    if (!this.validateForm()) {
      alert('Please fill in all required fields with valid information');
      return;
    }

    this.setState({ loading: true });

    try {
      // Simulate blockchain transaction
      await new Promise(resolve => setTimeout(resolve, 3000));

      // Generate mock Token ID
      const tokenId = Date.now().toString();
      
      // Create child record
      const childRecord = {
        tokenId: tokenId,
        ...this.state.formData,
        registeredByHospital: this.state.hospitalInfo.hospitalName,
        hospitalAddress: this.state.hospitalInfo.walletAddress,
        registrationDate: new Date().toISOString(),
        profileImage: this.state.profileImage ? URL.createObjectURL(this.state.profileImage) : null,
        qrData: {
          tokenId: tokenId,
          childName: this.state.formData.childName,
          parentAddress: this.state.formData.parentAddress,
          registeredBy: this.state.hospitalInfo.hospitalName,
          verificationUrl: `${window.location.origin}/verify/${tokenId}`
        }
      };

      // Save to localStorage (simulating blockchain)
      const existingChildren = JSON.parse(localStorage.getItem('registeredChildren') || '[]');
      existingChildren.push(childRecord);
      localStorage.setItem('registeredChildren', JSON.stringify(existingChildren));

      // Also save to parent's records (simulate NFT transfer)
      const parentKey = `parent_${this.state.formData.parentAddress}`;
      const parentChildren = JSON.parse(localStorage.getItem(parentKey) || '[]');
      parentChildren.push(tokenId);
      localStorage.setItem(parentKey, JSON.stringify(parentChildren));

      this.setState({
        loading: false,
        registeredChild: childRecord,
        step: 3,
        registeredChildren: existingChildren
      });

    } catch (error) {
      console.error('Error registering child:', error);
      alert('Error registering child: ' + error.message);
      this.setState({ loading: false });
    }
  };

  // Reset form for new registration
  resetForm = () => {
    this.setState({
      formData: {
        childName: '',
        dateOfBirth: '',
        parentName: '',
        contactInfo: '',
        parentAddress: '',
        emergencyContact: '',
        medicalHistory: '',
        allergies: ''
      },
      profileImage: null,
      registeredChild: null,
      step: 1
    });
  };

  // Render step 1: Child Information
  renderStep1() {
    const { formData, loading, hospitalInfo } = this.state;

    return (
      <div className="registration-step">
        <div className="hospital-header">
          <h2>üè• Child Birth Registration</h2>
          <div className="hospital-info">
            <p><strong>Hospital:</strong> {hospitalInfo?.hospitalName}</p>
            <p><strong>License:</strong> {hospitalInfo?.licenseNumber}</p>
          </div>
        </div>
        
        <div className="form-group">
          <label>Child's Full Name *</label>
          <input
            type="text"
            name="childName"
            value={formData.childName}
            onChange={this.handleInputChange}
            placeholder="Enter child's full name"
            required
            disabled={loading}
          />
        </div>

        <div className="form-group">
          <label>Date of Birth *</label>
          <input
            type="date"
            name="dateOfBirth"
            value={formData.dateOfBirth}
            onChange={this.handleInputChange}
            required
            disabled={loading}
          />
        </div>

        <div className="form-group">
          <label>Parent/Guardian Name *</label>
          <input
            type="text"
            name="parentName"
            value={formData.parentName}
            onChange={this.handleInputChange}
            placeholder="Enter parent/guardian name"
            required
            disabled={loading}
          />
        </div>

        <div className="form-group">
          <label>Parent's Wallet Address * 
            <span className="help-text">The NFT will be sent to this address</span>
          </label>
          <input
            type="text"
            name="parentAddress"
            value={formData.parentAddress}
            onChange={this.handleInputChange}
            placeholder="0x... (Parent's blockchain wallet address)"
            className={this.validateParentAddress(formData.parentAddress) || !formData.parentAddress ? '' : 'error'}
            required
            disabled={loading}
          />
          {formData.parentAddress && !this.validateParentAddress(formData.parentAddress) && (
            <span className="input-error">Invalid wallet address format</span>
          )}
        </div>

        <div className="form-group">
          <label>Parent Contact Information *</label>
          <input
            type="tel"
            name="contactInfo"
            value={formData.contactInfo}
            onChange={this.handleInputChange}
            placeholder="Phone number or email"
            required
            disabled={loading}
          />
        </div>

        <div className="form-group">
          <label>Child's Photo (Optional)</label>
          <input
            type="file"
            accept="image/*"
            onChange={this.handleImageUpload}
            className="file-input"
            disabled={loading}
          />
          {this.state.profileImage && (
            <div className="image-preview">
              <img 
                src={URL.createObjectURL(this.state.profileImage)} 
                alt="Preview" 
                className="preview-img"
              />
            </div>
          )}
        </div>
      </div>
    );
  }

  // Render step 2: Medical Information
  renderStep2() {
    const { formData, loading, hospitalInfo } = this.state;

    return (
      <div className="registration-step">
        <h2>üè• Medical Information</h2>
        
        <div className="form-group">
          <label>Emergency Contact</label>
          <input
            type="tel"
            name="emergencyContact"
            value={formData.emergencyContact}
            onChange={this.handleInputChange}
            placeholder="Emergency contact number"
            disabled={loading}
          />
        </div>

        <div className="form-group">
          <label>Known Allergies</label>
          <textarea
            name="allergies"
            value={formData.allergies}
            onChange={this.handleInputChange}
            placeholder="List any known allergies (food, medicine, etc.)"
            rows="3"
            disabled={loading}
          />
        </div>

        <div className="form-group">
          <label>Medical History</label>
          <textarea
            name="medicalHistory"
            value={formData.medicalHistory}
            onChange={this.handleInputChange}
            placeholder="Any relevant medical history or conditions"
            rows="4"
            disabled={loading}
          />
        </div>

        <div className="hospital-verification">
          <div className="verification-item">
            <input type="checkbox" id="identityVerified" required disabled={loading} />
            <label htmlFor="identityVerified">
              I have verified the identity of the parent/guardian
            </label>
          </div>
          <div className="verification-item">
            <input type="checkbox" id="informationAccurate" required disabled={loading} />
            <label htmlFor="informationAccurate">
              I confirm that all provided information is accurate
            </label>
          </div>
          <div className="verification-item">
            <input type="checkbox" id="hospitalAuthorization" required disabled={loading} />
            <label htmlFor="hospitalAuthorization">
              I am authorized to register children on behalf of {hospitalInfo?.hospitalName}
            </label>
          </div>
        </div>
      </div>
    );
  }

  // Render step 3: Success
  renderStep3() {
    const { registeredChild } = this.state;

    return (
      <div className="registration-success">
        <div className="success-icon">‚úÖ</div>
        <h2>Child Successfully Registered!</h2>
        <p>The child has been registered and the NFT has been sent to the parent's wallet.</p>
        
        <div className="child-info-card">
          <h3>{registeredChild.childName}</h3>
          <p><strong>Token ID:</strong> {registeredChild.tokenId}</p>
          <p><strong>Parent:</strong> {registeredChild.parentName}</p>
          <p><strong>Parent Wallet:</strong> {registeredChild.parentAddress}</p>
          <p><strong>Registered by:</strong> {registeredChild.registeredByHospital}</p>
        </div>

        <div className="qr-section">
          <h4>üîç Child Verification QR Code</h4>
          <p>This QR code contains the child's blockchain verification data:</p>
          <div className="qr-container">
            <div className="qr-placeholder">
              üì± QR Code: {registeredChild.tokenId}
              <br />
              <small>Child: {registeredChild.childName}</small>
              <br />
              <small>Parent: {registeredChild.parentAddress.slice(0, 6)}...{registeredChild.parentAddress.slice(-4)}</small>
            </div>
          </div>
        </div>

        <div className="parent-instructions">
          <h4>üìã Instructions for Parents</h4>
          <div className="instructions-card">
            <ul>
              <li><strong>NFT Token ID:</strong> {registeredChild.tokenId} (saved to wallet: {registeredChild.parentAddress})</li>
              <li><strong>QR Code:</strong> Use for instant verification at healthcare facilities</li>
              <li><strong>Vaccination Reminders:</strong> Will be sent automatically based on age</li>
              <li><strong>Updates:</strong> All vaccinations will be recorded by authorized hospitals</li>
            </ul>
          </div>
        </div>

        <div className="next-steps">
          <h4>What Happens Next?</h4>
          <ul>
            <li>‚úÖ NFT automatically sent to parent's wallet</li>
            <li>üìÖ Vaccination reminders will be triggered automatically</li>
            <li>üè• Future vaccinations can be recorded by any authorized hospital</li>
            <li>üì± QR code enables instant verification</li>
          </ul>
        </div>

        <button onClick={this.resetForm} className="continue-btn">
          Register Another Child
        </button>
      </div>
    );
  }

  // Render unauthorized access
  renderUnauthorized() {
    return (
      <div className="authorization-error">
        <div className="error-container">
          <div className="error-icon">üè•</div>
          <h2>Hospital Authorization Required</h2>
          <p>Only authorized hospitals can register children in the vaccination system.</p>
          
          <div className="registration-prompt">
            <p>Your hospital needs to be registered and authorized first.</p>
            <p>Please:</p>
            <ol>
              <li>Register your hospital using the Hospital Registration form</li>
              <li>Wait for admin authorization</li>
              <li>Return here to register children</li>
            </ol>
          </div>

          <div className="demo-controls">
            <h4>üß™ Demo Mode:</h4>
            <p>To test this component, first complete Hospital Registration and get authorized.</p>
            <button onClick={() => this.props.onChangeView('hospital-register')} className="btn-primary">
              Go to Hospital Registration
            </button>
          </div>
        </div>
      </div>
    );
  }

  // Render registered children list
  renderRegisteredChildren() {
    const { registeredChildren } = this.state;

    if (registeredChildren.length === 0) return null;

    return (
      <div className="registered-children">
        <h3>üìã Recently Registered Children ({registeredChildren.length})</h3>
        <div className="children-list">
          {registeredChildren.slice(-5).map((child, index) => (
            <div key={index} className="child-summary">
              <div className="child-info">
                <strong>{child.childName}</strong>
                <span>Token ID: {child.tokenId}</span>
              </div>
              <div className="child-details">
                <span>Parent: {child.parentName}</span>
                <span>Date: {new Date(child.registrationDate).toLocaleDateString()}</span>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  }

  render() {
    const { isAuthorizedHospital, step, loading } = this.state;

    // Check authorization first
    if (!isAuthorizedHospital) {
      return (
        <div className="App">
          <div className="main-content">
            {this.renderUnauthorized()}
            <button onClick={this.props.onBack} className="btn-secondary">
              ‚Üê Back to Dashboard
            </button>
          </div>
        </div>
      );
    }

    return (
      <div className="App">
        <div className="main-content">
          <div className="hospital-child-registration">
            <div className="registration-container">
              <div className="progress-bar">
                <div className={`step ${step >= 1 ? 'active' : ''}`}>
                  <span>1</span>
                  <label>Child Info</label>
                </div>
                <div className={`step ${step >= 2 ? 'active' : ''}`}>
                  <span>2</span>
                  <label>Medical</label>
                </div>
                <div className={`step ${step >= 3 ? 'active' : ''}`}>
                  <span>3</span>
                  <label>Complete</label>
                </div>
              </div>

              {step === 1 && this.renderStep1()}
              {step === 2 && this.renderStep2()}
              {step === 3 && this.renderStep3()}

              {step < 3 && (
                <div className="form-actions">
                  {step > 1 && (
                    <button 
                      onClick={() => this.setState({ step: step - 1 })}
                      className="btn-secondary"
                      disabled={loading}
                    >
                      ‚Üê Previous
                    </button>
                  )}
                  
                  {step < 2 && (
                    <button 
                      onClick={() => this.setState({ step: 2 })}
                      className="btn-primary"
                      disabled={
                        !this.state.formData.childName || 
                        !this.state.formData.dateOfBirth || 
                        !this.state.formData.parentName || 
                        !this.state.formData.parentAddress ||
                        !this.validateParentAddress(this.state.formData.parentAddress)
                      }
                    >
                      Next ‚Üí
                    </button>
                  )}
                  
                  {step === 2 && (
                    <button 
                      onClick={this.registerChild}
                      className="btn-primary"
                      disabled={loading}
                    >
                      {loading ? '‚è≥ Registering Child...' : 'üîê Register on Blockchain'}
                    </button>
                  )}

                  <button onClick={this.props.onBack} className="btn-secondary" disabled={loading}>
                    Back to Dashboard
                  </button>
                </div>
              )}

              {loading && (
                <div className="loading-info">
                  <div className="loading-spinner">‚è≥</div>
                  <p><strong>üì° Processing Child Registration...</strong></p>
                  <p>‚è≥ Creating NFT record on blockchain</p>
                  <p>üì§ Sending NFT to parent's wallet</p>
                  <p>üîç Generating verification QR code</p>
                </div>
              )}

              {step < 3 && this.renderRegisteredChildren()}
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default ChildRegistration;
