import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import './App.css';

// Import all the new components
import HospitalRegistration from './components/HospitalRegistration';
import HospitalChildRegistration from './components/HospitalChildRegistration';
import ParentDashboard from './components/ParentDashboard';
import VaccinationRecord from './components/VaccinationRecord';
import AdminPanel from './components/AdminPanel';
import QRVerification from './components/QRVerification';

// Updated contract address - replace with your deployed contract
const CONTRACT_ADDRESS = "0xb5a28A2c3715BA080576A2EaF80eB91e44E4499D";

// Updated ABI for the new contract
const CONTRACT_ABI = [
  // Hospital Registration
  {
    "inputs": [
      {"internalType": "string", "name": "_name", "type": "string"},
      {"internalType": "string", "name": "_licenseNumber", "type": "string"},
      {"internalType": "string", "name": "_contactInfo", "type": "string"}
    ],
    "name": "registerHospital",
    "outputs": [],
    "stateMutability": "nonpayable",
    "type": "function"
  },
  // Child Registration (Hospital only)
  {
    "inputs": [
      {"internalType": "string", "name": "_name", "type": "string"},
      {"internalType": "uint256", "name": "_dateOfBirth", "type": "uint256"},
      {"internalType": "string", "name": "_parentName", "type": "string"},
      {"internalType": "string", "name": "_contactInfo", "type": "string"},
      {"internalType": "address", "name": "_parentAddress", "type": "address"},
      {"internalType": "string", "name": "_tokenURI", "type": "string"}
    ],
    "name": "registerChild",
    "outputs": [{"internalType": "uint256", "name": "", "type": "uint256"}],
    "stateMutability": "nonpayable",
    "type": "function"
  },
  // Vaccination Recording
  {
    "inputs": [
      {"internalType": "uint256", "name": "_tokenId", "type": "uint256"},
      {"internalType": "string", "name": "_vaccineName", "type": "string"},
      {"internalType": "string", "name": "_batchNumber", "type": "string"},
      {"internalType": "uint256", "name": "_nextDueDate", "type": "uint256"},
      {"internalType": "string", "name": "_ipfsHash", "type": "string"}
    ],
    "name": "recordVaccination",
    "outputs": [],
    "stateMutability": "nonpayable",
    "type": "function"
  },
  // Authorization
  {
    "inputs": [
      {"internalType": "address", "name": "_hospital", "type": "address"},
      {"internalType": "bool", "name": "_authorized", "type": "bool"}
    ],
    "name": "setHospitalAuthorization",
    "outputs": [],
    "stateMutability": "nonpayable",
    "type": "function"
  },
  // View Functions
  {
    "inputs": [{"internalType": "address", "name": "_hospital", "type": "address"}],
    "name": "getHospitalInfo",
    "outputs": [
      {
        "components": [
          {"internalType": "string", "name": "name", "type": "string"},
          {"internalType": "string", "name": "licenseNumber", "type": "string"},
          {"internalType": "string", "name": "contactInfo", "type": "string"},
          {"internalType": "bool", "name": "isAuthorized", "type": "bool"},
          {"internalType": "uint256", "name": "registrationDate", "type": "uint256"}
        ],
        "internalType": "struct VaccinationRegistry.Hospital",
        "name": "",
        "type": "tuple"
      }
    ],
    "stateMutability": "view",
    "type": "function"
  },
  {
    "inputs": [{"internalType": "uint256", "name": "_tokenId", "type": "uint256"}],
    "name": "getChildInfo",
    "outputs": [
      {
        "components": [
          {"internalType": "string", "name": "name", "type": "string"},
          {"internalType": "uint256", "name": "dateOfBirth", "type": "uint256"},
          {"internalType": "string", "name": "parentName", "type": "string"},
          {"internalType": "string", "name": "contactInfo", "type": "string"},
          {"internalType": "address", "name": "parentAddress", "type": "address"},
          {"internalType": "address", "name": "registeredByHospital", "type": "address"},
          {"internalType": "string", "name": "hospitalName", "type": "string"},
          {"internalType": "bool", "name": "isRegistered", "type": "bool"},
          {"internalType": "uint256", "name": "registrationDate", "type": "uint256"}
        ],
        "internalType": "struct VaccinationRegistry.Child",
        "name": "",
        "type": "tuple"
      }
    ],
    "stateMutability": "view",
    "type": "function"
  },
  {
    "inputs": [{"internalType": "address", "name": "_parent", "type": "address"}],
    "name": "getParentChildren",
    "outputs": [{"internalType": "uint256[]", "name": "", "type": "uint256[]"}],
    "stateMutability": "view",
    "type": "function"
  },
  {
    "inputs": [{"internalType": "uint256", "name": "_tokenId", "type": "uint256"}],
    "name": "getVaccinationHistory",
    "outputs": [
      {
        "components": [
          {"internalType": "string", "name": "vaccineName", "type": "string"},
          {"internalType": "uint256", "name": "dateAdministered", "type": "uint256"},
          {"internalType": "string", "name": "hospitalName", "type": "string"},
          {"internalType": "address", "name": "administeredBy", "type": "address"},
          {"internalType": "string", "name": "batchNumber", "type": "string"},
          {"internalType": "uint256", "name": "nextDueDate", "type": "uint256"},
          {"internalType": "bool", "name": "isVerified", "type": "bool"},
          {"internalType": "string", "name": "ipfsHash", "type": "string"},
          {"internalType": "string", "name": "qrCodeHash", "type": "string"}
        ],
        "internalType": "struct VaccinationRegistry.VaccinationRecord[]",
        "name": "",
        "type": "tuple[]"
      }
    ],
    "stateMutability": "view",
    "type": "function"
  },
  {
    "inputs": [{"internalType": "uint256", "name": "_tokenId", "type": "uint256"}],
    "name": "getUpcomingVaccinations",
    "outputs": [
      {"internalType": "string[]", "name": "vaccineNames", "type": "string[]"},
      {"internalType": "uint256[]", "name": "dueDates", "type": "uint256[]"}
    ],
    "stateMutability": "view",
    "type": "function"
  },
  {
    "inputs": [{"internalType": "uint256", "name": "_tokenId", "type": "uint256"}],
    "name": "generateVerificationQR",
    "outputs": [
      {"internalType": "string", "name": "qrData", "type": "string"},
      {"internalType": "string", "name": "childName", "type": "string"},
      {"internalType": "uint256", "name": "totalVaccinations", "type": "uint256"},
      {"internalType": "bool", "name": "isUpToDate", "type": "bool"}
    ],
    "stateMutability": "view",
    "type": "function"
  },
  // Mappings
  {
    "inputs": [{"internalType": "address", "name": "", "type": "address"}],
    "name": "authorizedHospitals",
    "outputs": [{"internalType": "bool", "name": "", "type": "bool"}],
    "stateMutability": "view",
    "type": "function"
  },
  // Events
  {
    "anonymous": false,
    "inputs": [
      {"indexed": true, "internalType": "uint256", "name": "tokenId", "type": "uint256"},
      {"indexed": false, "internalType": "string", "name": "name", "type": "string"},
      {"indexed": true, "internalType": "address", "name": "parent", "type": "address"},
      {"indexed": true, "internalType": "address", "name": "hospital", "type": "address"}
    ],
    "name": "ChildRegistered",
    "type": "event"
  },
  {
    "anonymous": false,
    "inputs": [
      {"indexed": true, "internalType": "uint256", "name": "tokenId", "type": "uint256"},
      {"indexed": false, "internalType": "string", "name": "vaccineName", "type": "string"},
      {"indexed": false, "internalType": "uint256", "name": "dateAdministered", "type": "uint256"},
      {"indexed": false, "internalType": "string", "name": "qrCodeHash", "type": "string"}
    ],
    "name": "VaccinationRecorded",
    "type": "event"
  },
  {
    "anonymous": false,
    "inputs": [
      {"indexed": true, "internalType": "address", "name": "hospital", "type": "address"},
      {"indexed": false, "internalType": "string", "name": "name", "type": "string"}
    ],
    "name": "HospitalRegistered",
    "type": "event"
  },
  {
    "anonymous": false,
    "inputs": [
      {"indexed": true, "internalType": "address", "name": "hospital", "type": "address"},
      {"indexed": false, "internalType": "bool", "name": "authorized", "type": "bool"}
    ],
    "name": "HospitalAuthorized",
    "type": "event"
  },
  {
    "anonymous": false,
    "inputs": [
      {"indexed": true, "internalType": "uint256", "name": "tokenId", "type": "uint256"},
      {"indexed": false, "internalType": "uint256", "name": "vaccinationIndex", "type": "uint256"},
      {"indexed": false, "internalType": "string", "name": "qrCodeHash", "type": "string"}
    ],
    "name": "QRCodeGenerated",
    "type": "event"
  },
  // Owner function
  {
    "inputs": [],
    "name": "owner",
    "outputs": [{"internalType": "address", "name": "", "type": "address"}],
    "stateMutability": "view",
    "type": "function"
  }
];

const App = () => {
  const [account, setAccount] = useState('');
  const [provider, setProvider] = useState(null);
  const [contract, setContract] = useState(null);
  const [loading, setLoading] = useState(false);
  const [currentView, setCurrentView] = useState('welcome');
  const [networkId, setNetworkId] = useState(null);
  const [userRole, setUserRole] = useState(null); // 'admin', 'hospital', 'parent'
  const [isOwner, setIsOwner] = useState(false);

  // Check user role when account changes
  useEffect(() => {
    if (account && contract) {
      checkUserRole();
    }
  }, [account, contract]);

  const checkUserRole = async () => {
    if (!contract || !account) return;

    try {
      // Check if user is contract owner (admin)
      const owner = await contract.owner();
      const isContractOwner = owner.toLowerCase() === account.toLowerCase();
      setIsOwner(isContractOwner);

      // Check if user is an authorized hospital
      const isAuthorizedHospital = await contract.authorizedHospitals(account);

      // Check if user has any children (is a parent)
      const parentChildren = await contract.getParentChildren(account);
      const isParent = parentChildren.length > 0;

      // Determine primary role
      if (isContractOwner) {
        setUserRole('admin');
      } else if (isAuthorizedHospital) {
        setUserRole('hospital');
      } else if (isParent) {
        setUserRole('parent');
      } else {
        // Check if hospital is registered but not authorized
        try {
          const hospitalInfo = await contract.getHospitalInfo(account);
          if (hospitalInfo.registrationDate > 0) {
            setUserRole('hospital');
          } else {
            setUserRole('parent'); // Default to parent view
          }
        } catch {
          setUserRole('parent'); // Default to parent view
        }
      }
    } catch (error) {
      console.error('Error checking user role:', error);
      setUserRole('parent'); // Default to parent view
    }
  };

  const connectWallet = async () => {
    try {
      if (typeof window.ethereum === 'undefined') {
        alert('Please install MetaMask wallet to use this application!');
        return;
      }

      setLoading(true);

      const accounts = await window.ethereum.request({ 
        method: 'eth_requestAccounts' 
      });

      if (accounts.length > 0) {
        const provider = new ethers.providers.Web3Provider(window.ethereum);
        const signer = provider.getSigner();
        const network = await provider.getNetwork();

        const contract = new ethers.Contract(
          CONTRACT_ADDRESS,
          CONTRACT_ABI,
          signer
        );

        setAccount(accounts[0]);
        setProvider(provider);
        setContract(contract);
        setNetworkId(network.chainId);
        setCurrentView('dashboard');
        setLoading(false);

        console.log('âœ… Connected successfully!');
        console.log('Account:', accounts[0]);
        console.log('Network:', network.chainId);
        console.log('Contract:', CONTRACT_ADDRESS);
      }
    } catch (error) {
      console.error('Connection error:', error);
      alert('Error connecting wallet: ' + error.message);
      setLoading(false);
    }
  };

  const disconnectWallet = () => {
    setAccount('');
    setProvider(null);
    setContract(null);
    setCurrentView('welcome');
    setNetworkId(null);
    setUserRole(null);
    setIsOwner(false);
  };

  const changeView = (view) => {
    setCurrentView(view);
  };

  // Handle successful operations
  const handleSuccess = (message) => {
    if (message) {
      alert(message);
    }
    // Refresh user role in case it changed
    checkUserRole();
    setCurrentView('dashboard');
  };

  // Welcome Screen
  if (currentView === 'welcome') {
    return (
      <div className="App">
        <div className="welcome-screen">
          <div className="welcome-content">
            <h1>ðŸ¥ Blockchain Vaccination Registry</h1>
            <p>Secure, transparent, and tamper-proof vaccination records managed by authorized healthcare providers</p>

            <div className="features">
              <div className="feature-item">
                <span className="feature-icon">ðŸ¥</span>
                <span>Hospital Registration</span>
              </div>
              <div className="feature-item">
                <span className="feature-icon">ðŸ‘¶</span>
                <span>Child Birth Records</span>
              </div>
              <div className="feature-item">
                <span className="feature-icon">ðŸ’‰</span>
                <span>Vaccination Tracking</span>
              </div>
              <div className="feature-item">
                <span className="feature-icon">ðŸ“±</span>
                <span>QR Verification</span>
              </div>
              <div className="feature-item">
                <span className="feature-icon">ðŸ”’</span>
                <span>Blockchain Security</span>
              </div>
              <div className="feature-item">
                <span className="feature-icon">ðŸŒ</span>
                <span>Global Access</span>
              </div>
            </div>

            <div className="contract-info">
              <h4>Smart Contract Details:</h4>
              <p><strong>Address:</strong></p>
              <div className="contract-address">
                {CONTRACT_ADDRESS}
              </div>
            </div>

            <button 
              className="connect-btn"
              onClick={connectWallet}
              disabled={loading}
            >
              {loading ? 'â³ Connecting...' : 'ðŸ¦Š Connect MetaMask Wallet'}
            </button>

            <div className="workflow-info">
              <h4>How It Works:</h4>
              <ol>
                <li><strong>Hospitals Register:</strong> Healthcare providers register and get authorized</li>
                <li><strong>Child Birth Registration:</strong> Hospital creates NFT record, sends to parents</li>
                <li><strong>Vaccination Records:</strong> Authorized hospitals record vaccinations with QR codes</li>
                <li><strong>Parent Access:</strong> Parents manage and verify their children's records</li>
              </ol>
            </div>
          </div>
        </div>
      </div>
    );
  }

  // Main Dashboard
  if (currentView === 'dashboard') {
    return (
      <div className="App">
        <div className="main-content">
          <div className="dashboard">
            <div className="dashboard-header">
              <h1>ðŸ¥ Vaccination Registry Dashboard</h1>
              <div className="user-info">
                <div className="account-info">
                  <p><strong>Account:</strong> {account.slice(0, 6)}...{account.slice(-4)}</p>
                  <p><strong>Network:</strong> {networkId}</p>
                  <p><strong>Role:</strong> {userRole ? userRole.charAt(0).toUpperCase() + userRole.slice(1) : 'Loading...'}</p>
                  {isOwner && <span className="admin-badge">ðŸ‘‘ Admin</span>}
                </div>
                <button onClick={disconnectWallet} className="disconnect-btn">
                  Disconnect
                </button>
              </div>
            </div>

            <div className="dashboard-actions">
              {/* Admin Functions */}
              {userRole === 'admin' && (
                <>
                  <div className="action-card" onClick={() => changeView('admin')}>
                    <div className="card-icon">ðŸ‘‘</div>
                    <h3>Admin Panel</h3>
                    <p>Manage hospital authorizations</p>
                  </div>
                  <div className="action-card" onClick={() => changeView('hospital-register')}>
                    <div className="card-icon">ðŸ¥</div>
                    <h3>Hospital Registration</h3>
                    <p>Register new hospitals</p>
                  </div>
                </>
              )}

              {/* Hospital Functions */}
              {(userRole === 'hospital' || userRole === 'admin') && (
                <>
                  <div className="action-card" onClick={() => changeView('hospital-register')}>
                    <div className="card-icon">ðŸ¥</div>
                    <h3>Hospital Registration</h3>
                    <p>Register hospital or check status</p>
                  </div>
                  <div className="action-card" onClick={() => changeView('child-register')}>
                    <div className="card-icon">ðŸ‘¶</div>
                    <h3>Register Child</h3>
                    <p>Register newborn children</p>
                  </div>
                  <div className="action-card" onClick={() => changeView('record-vaccination')}>
                    <div className="card-icon">ðŸ’‰</div>
                    <h3>Record Vaccination</h3>
                    <p>Record vaccination with QR code</p>
                  </div>
                </>
              )}

              {/* Parent Functions */}
              {(userRole === 'parent' || userRole === 'admin') && (
                <>
                  <div className="action-card" onClick={() => changeView('parent-dashboard')}>
                    <div className="card-icon">ðŸ‘¨â€ðŸ‘©â€ðŸ‘§â€ðŸ‘¦</div>
                    <h3>My Children</h3>
                    <p>View vaccination records</p>
                  </div>
                </>
              )}

              {/* Universal Functions */}
              <div className="action-card" onClick={() => changeView('verify')}>
                <div className="card-icon">ðŸ”</div>
                <h3>Verify QR Code</h3>
                <p>Verify vaccination records</p>
              </div>

              <div className="action-card" onClick={() => changeView('about')}>
                <div className="card-icon">â„¹ï¸</div>
                <h3>About System</h3>
                <p>Learn how the system works</p>
              </div>
            </div>

            {/* Role-specific information */}
            <div className="role-info">
              {userRole === 'admin' && (
                <div className="info-card admin-info">
                  <h4>ðŸ‘‘ Administrator Access</h4>
                  <p>You have full system access including hospital authorization and management functions.</p>
                </div>
              )}
              {userRole === 'hospital' && (
                <div className="info-card hospital-info">
                  <h4>ðŸ¥ Hospital Access</h4>
                  <p>You can register children at birth and record vaccinations for authorized medical procedures.</p>
                </div>
              )}
              {userRole === 'parent' && (
                <div className="info-card parent-info">
                  <h4>ðŸ‘¨â€ðŸ‘©â€ðŸ‘§â€ðŸ‘¦ Parent Access</h4>
                  <p>View and manage your children's vaccination records. Your children are registered by hospitals at birth.</p>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    );
  }

  // Route to different components based on view
  const renderCurrentView = () => {
    switch (currentView) {
      case 'admin':
        return (
          <AdminPanel 
            contract={contract} 
            account={account} 
            onSuccess={() => handleSuccess('Admin action completed!')}
            onBack={() => changeView('dashboard')}
          />
        );

      case 'hospital-register':
        return (
          <HospitalRegistration 
            contract={contract} 
            account={account} 
            onSuccess={() => handleSuccess('Hospital registration completed!')}
          />
        );

      case 'child-register':
        return (
          <HospitalChildRegistration 
            contract={contract} 
            account={account} 
            onSuccess={() => handleSuccess('Child registered successfully! NFT sent to parent.')}
          />
        );

      case 'record-vaccination':
        return (
          <VaccinationRecord 
            contract={contract} 
            account={account} 
            onSuccess={() => handleSuccess('Vaccination recorded with QR code!')}
          />
        );

      case 'parent-dashboard':
        return (
          <ParentDashboard 
            contract={contract} 
            account={account}
          />
        );

      case 'verify':
        return (
          <QRVerification 
            contract={contract} 
            account={account}
            onBack={() => changeView('dashboard')}
          />
        );

      case 'about':
        return (
          <div className="App">
            <div className="main-content">
              <div className="about-section">
