import React, { Component } from 'react';
import { ethers } from 'ethers';
import './App.css';
import HospitalRegistration from './components/HospitalRegistration';
import ChildRegistration from './components/ChildRegistration';
import VaccinationRecording from './components/VaccinationRecording';
import ParentDashboard from './components/ParentDashboard';
import QRVerification from './components/QRVerification';
import VaccinationRegistryABI from './contracts/VaccinationRegistry.json';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      // Navigation
      currentView: 'welcome',

      // User Authentication - BLOCKCHAIN CONNECTED
      currentUser: null,
      isLoggedIn: false,
      userRole: null,

      // Blockchain Connection
      connectedWallet: null,
      web3Provider: null,
      contract: null,
      contractAddress: "0xb5a28A2c3715BA080576A2EaF80eB91e44E4499D", // Update with your deployed address

      // Real-time Blockchain Data
      systemStats: {
        totalHospitals: 0,
        totalChildren: 0,
        totalVaccinations: 0,
        pendingAuthorizations: 0
      },

      // User's Blockchain Data
      userChildren: [], // For parents
      userHospitalData: null, // For hospitals
      userVaccinations: [], // User-specific vaccinations

      // UI States
      isLoading: false,
      error: null,
      chainId: null,
      blockNumber: 0,

      // Authentication
      showAuthModal: false,
      authMode: 'login'
    };
  }

  componentDidMount() {
    this.detectWalletConnection();
    this.setupEventListeners();
  }

  componentWillUnmount() {
    this.removeEventListeners();
  }

  // ============ BLOCKCHAIN EVENT LISTENERS ============

  setupEventListeners = () => {
    if (window.ethereum) {
      window.ethereum.on('accountsChanged', this.handleAccountsChanged);
      window.ethereum.on('chainChanged', this.handleChainChanged);
    }
  };

  removeEventListeners = () => {
    if (window.ethereum) {
      window.ethereum.removeListener('accountsChanged', this.handleAccountsChanged);
      window.ethereum.removeListener('chainChanged', this.handleChainChanged);
    }
  };

  handleAccountsChanged = (accounts) => {
    if (accounts.length === 0) {
      this.handleDisconnect();
    } else {
      this.setState({ connectedWallet: accounts[0] });
      this.initializeBlockchainConnection();
    }
  };

  handleChainChanged = (chainId) => {
    console.log('Chain changed to:', chainId);
    this.setState({ chainId });
    window.location.reload();
  };

  // ============ BLOCKCHAIN CONNECTION ============

  detectWalletConnection = async () => {
    if (typeof window.ethereum !== 'undefined') {
      try {
        const accounts = await window.ethereum.request({ method: 'eth_accounts' });
        if (accounts.length > 0) {
          console.log('üë§ Wallet detected:', accounts[0]);
          this.setState({ connectedWallet: accounts[0] });
          await this.initializeBlockchainConnection();
        }
      } catch (error) {
        console.error('‚ùå Error detecting wallet:', error);
      }
    }
  };

  handleConnectWallet = async () => {
    if (typeof window.ethereum !== 'undefined') {
      try {
        this.setState({ isLoading: true, error: null });

        const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
        const chainId = await window.ethereum.request({ method: 'eth_chainId' });

        console.log('üîó Connected to chain ID:', chainId);

        // Check for Ganache network
        if (chainId !== '0x539' && chainId !== '0x1691') {
          alert('‚ö†Ô∏è Please switch to Ganache Local Network!\n\nNetwork: Ganache Local\nRPC URL: http://127.0.0.1:7545\nChain ID: 1337');
          this.setState({ isLoading: false });
          return;
        }

        this.setState({ 
          connectedWallet: accounts[0],
          isLoading: false
        });

        await this.initializeBlockchainConnection();

      } catch (error) {
        console.error('‚ùå Wallet connection failed:', error);
        this.setState({ error: error.message, isLoading: false });
      }
    } else {
      alert('Please install MetaMask wallet!');
    }
  };

  initializeBlockchainConnection = async () => {
    try {
      if (!window.ethereum || !this.state.connectedWallet) return;

      this.setState({ isLoading: true });

      const provider = new ethers.providers.Web3Provider(window.ethereum);
      const signer = provider.getSigner();
      const network = await provider.getNetwork();
      const blockNumber = await provider.getBlockNumber();

      console.log('üîó Connected to network:', network.name, 'Chain ID:', network.chainId);
      console.log('üì¶ Current block number:', blockNumber);

      const contract = new ethers.Contract(
        this.state.contractAddress,
        VaccinationRegistryABI.abi,
        signer
      );

      this.setState({
        web3Provider: provider,
        contract: contract,
        chainId: network.chainId,
        blockNumber: blockNumber,
        error: null,
        isLoading: false
      });

      // Auto-login user based on wallet address
      await this.autoLoginFromBlockchain();

      // Load system stats from blockchain
      await this.loadSystemStats();

      console.log('‚úÖ Blockchain connection initialized');

    } catch (error) {
      console.error('‚ùå Blockchain initialization failed:', error);
      this.setState({ 
        error: `Blockchain connection failed: ${error.message}`,
        isLoading: false
      });
    }
  };

  // ============ BLOCKCHAIN-BASED USER AUTHENTICATION ============

  autoLoginFromBlockchain = async () => {
    try {
      if (!this.state.contract || !this.state.connectedWallet) return;

      const contract = this.state.contract;
      const walletAddress = this.state.connectedWallet;

      console.log('üîç Auto-login attempt for wallet:', walletAddress);

      // Check if user is a registered hospital
      try {
        const hospitalData = await contract.hospitals(walletAddress);
        if (hospitalData.isRegistered) {
          console.log('üè• Hospital user found');
          const user = {
            walletAddress: walletAddress,
            role: 'hospital',
            fullName: hospitalData.name,
            licenseNumber: hospitalData.licenseNumber,
            isVerified: hospitalData.isAuthorized,
            isActive: hospitalData.isAuthorized
          };

          this.setState({
            currentUser: user,
            isLoggedIn: true,
            userRole: 'hospital',
            currentView: 'dashboard',
            userHospitalData: hospitalData
          });

          await this.loadUserSpecificData();
          return;
        }
      } catch (error) {
        console.log('Not a hospital user');
      }

      // Check if user is a parent (has children)
      try {
        const parentChildren = await contract.getParentChildren(walletAddress);
        if (parentChildren.length > 0) {
          console.log('üë®‚Äçüë©‚Äçüëß‚Äçüë¶ Parent user found with', parentChildren.length, 'children');
          const user = {
            walletAddress: walletAddress,
            role: 'parent',
            fullName: `Parent (${walletAddress.slice(0, 6)}...)`,
            isVerified: true,
            isActive: true
          };

          this.setState({
            currentUser: user,
            isLoggedIn: true,
            userRole: 'parent',
            currentView: 'dashboard',
            userChildren: parentChildren
          });

          await this.loadUserSpecificData();
          return;
        }
      } catch (error) {
        console.log('Not a parent user');
      }

      // Default to verifier role for new wallets
      console.log('üîç New user - defaulting to verifier role');
      const user = {
        walletAddress: walletAddress,
        role: 'verifier',
        fullName: `Verifier (${walletAddress.slice(0, 6)}...)`,
        isVerified: true,
        isActive: true
      };

      this.setState({
        currentUser: user,
        isLoggedIn: true,
        userRole: 'verifier',
        currentView: 'dashboard'
      });

    } catch (error) {
      console.error('‚ùå Auto-login failed:', error);
      this.setState({ error: `Auto-login failed: ${error.message}` });
    }
  };

  // Load user-specific data from blockchain
  loadUserSpecificData = async () => {
    try {
      const { contract, currentUser, connectedWallet } = this.state;

      if (!contract || !currentUser) return;

      this.setState({ isLoading: true });

      if (currentUser.role === 'parent') {
        // Load parent's children data
        const childrenIds = await contract.getParentChildren(connectedWallet);
        const childrenData = [];

        for (let tokenId of childrenIds) {
          try {
            const childData = await contract.children(tokenId.toNumber());
            const vaccinations = await contract.getChildVaccinations(tokenId.toNumber());

            childrenData.push({
              tokenId: tokenId.toNumber(),
              name: childData.name,
              birthDate: new Date(childData.birthDate.toNumber() * 1000),
              parentWallet: childData.parentWallet,
              hospitalWallet: childData.hospitalWallet,
              vaccinations: vaccinations
            });
          } catch (error) {
            console.error(`Error loading child ${tokenId}:`, error);
          }
        }

        this.setState({ 
          userChildren: childrenData,
          isLoading: false
        });

        console.log('‚úÖ Parent data loaded:', childrenData.length, 'children');
      }

      if (currentUser.role === 'hospital') {
        // Load hospital-specific data
        const hospitalData = await contract.hospitals(connectedWallet);
        this.setState({ 
          userHospitalData: hospitalData,
          isLoading: false
        });

        console.log('‚úÖ Hospital data loaded');
      }

    } catch (error) {
      console.error('‚ùå Error loading user data:', error);
      this.setState({ isLoading: false });
    }
  };

  // ============ BLOCKCHAIN USER REGISTRATION ============

  registerHospitalOnBlockchain = async (hospitalData) => {
    try {
      const { contract } = this.state;
      if (!contract) throw new Error('Contract not initialized');

      this.setState({ isLoading: true, error: null });

      console.log('üè• Registering hospital on blockchain...');

      // Call smart contract function
      const tx = await contract.registerHospital(
        hospitalData.name,
        hospitalData.licenseNumber
      );

      console.log('üì§ Transaction sent:', tx.hash);

      // Wait for confirmation
      const receipt = await tx.wait();
      console.log('‚úÖ Hospital registered on blockchain:', receipt.transactionHash);

      // Auto-login after registration
      await this.autoLoginFromBlockchain();

      this.setState({ 
        isLoading: false,
        showAuthModal: false,
        error: null
      });

      alert(`Hospital "${hospitalData.name}" registered successfully! Waiting for admin authorization.`);

    } catch (error) {
      console.error('‚ùå Hospital registration failed:', error);
      this.setState({ 
        error: `Registration failed: ${error.message}`,
        isLoading: false
      });
      throw error;
    }
  };

  // ============ BLOCKCHAIN DATA LOADING ============

  loadSystemStats = async () => {
    try {
      const { contract } = this.state;
      if (!contract) return;

      console.log('üìä Loading system stats from blockchain...');

      // Get real blockchain data
      const [totalChildren, totalHospitals, totalVaccinations] = await Promise.all([
        contract.getTotalChildren(),
        contract.getTotalHospitals(),
        contract.getTotalVaccinations()
      ]);

      // Count pending authorizations by checking hospital statuses
      // This is a simplified approach - in production, you'd have a better method
      let pendingAuthorizations = 0;

      this.setState({
        systemStats: {
          totalHospitals: totalHospitals.toNumber(),
          totalChildren: totalChildren.toNumber(),
          totalVaccinations: totalVaccinations.toNumber(),
          pendingAuthorizations: pendingAuthorizations
        }
      });

      console.log('‚úÖ System stats loaded from blockchain:', {
        hospitals: totalHospitals.toNumber(),
        children: totalChildren.toNumber(),
        vaccinations: totalVaccinations.toNumber()
      });

    } catch (error) {
      console.error('‚ùå Error loading system stats:', error);
    }
  };

  // ============ BLOCKCHAIN TRANSACTION HELPER ============

  sendTransaction = async (contractMethod, params = [], options = {}) => {
    try {
      const { contract } = this.state;
      if (!contract) throw new Error('Contract not initialized');

      this.setState({ isLoading: true, error: null });

      console.log('üì§ Sending transaction...');

      // Send transaction
      const tx = await contractMethod(...params, options);
      console.log('üîó Transaction hash:', tx.hash);

      // Wait for confirmation
      const receipt = await tx.wait();
      console.log('‚úÖ Transaction confirmed:', receipt.transactionHash);

      // Refresh data after successful transaction
      await this.loadSystemStats();
      await this.loadUserSpecificData();

      this.setState({ isLoading: false });
      return receipt;

    } catch (error) {
      console.error('‚ùå Transaction failed:', error);
      this.setState({ 
        error: `Transaction failed: ${error.message}`,
        isLoading: false
      });
      throw error;
    }
  };

  // ============ UNIQUE QR CODE GENERATION ============

  generateUniqueQRCode = (childData, vaccinationData) => {
    // Create unique QR data based on blockchain data
    const qrData = {
      childTokenId: childData.tokenId,
      childName: childData.name,
      parentWallet: childData.parentWallet,
      hospitalWallet: childData.hospitalWallet,
      vaccineName: vaccinationData.vaccineName,
      vaccinationDate: vaccinationData.vaccinationDate,
      batchNumber: vaccinationData.batchNumber,
      hospitalAddress: vaccinationData.hospitalAddress,
      blockchainVerified: true,
      contractAddress: this.state.contractAddress,
      timestamp: Date.now()
    };

    // Generate unique hash for QR code
    const qrString = JSON.stringify(qrData);
    const qrHash = ethers.utils.keccak256(ethers.utils.toUtf8Bytes(qrString));

    return {
      qrCode: qrString,
      qrHash: qrHash,
      verificationUrl: `${window.location.origin}/verify/${qrHash}`
    };
  };

