import React, { Component } from 'react';

// Enhanced vaccine schedule with automatic scheduling
const VACCINATION_SCHEDULE = {
  'BCG': { minAge: 0, description: 'Bacillus Calmette-GuÃ©rin', ageGroup: 'Birth' },
  'Hepatitis B (Birth)': { minAge: 0, description: 'Hepatitis B - Birth Dose', ageGroup: 'Birth' },
  'Hepatitis B (1st)': { minAge: 30, description: 'Hepatitis B - 1st Dose', ageGroup: '1 Month' },
  'Hepatitis B (2nd)': { minAge: 60, description: 'Hepatitis B - 2nd Dose', ageGroup: '2 Months' },
  'DPT (1st)': { minAge: 45, description: 'Diphtheria, Pertussis, Tetanus - 1st', ageGroup: '6 Weeks' },
  'DPT (2nd)': { minAge: 75, description: 'Diphtheria, Pertussis, Tetanus - 2nd', ageGroup: '10 Weeks' },
  'DPT (3rd)': { minAge: 105, description: 'Diphtheria, Pertussis, Tetanus - 3rd', ageGroup: '14 Weeks' },
  'Polio (OPV-1)': { minAge: 45, description: 'Oral Polio Vaccine - 1st', ageGroup: '6 Weeks' },
  'Polio (OPV-2)': { minAge: 75, description: 'Oral Polio Vaccine - 2nd', ageGroup: '10 Weeks' },
  'Polio (OPV-3)': { minAge: 105, description: 'Oral Polio Vaccine - 3rd', ageGroup: '14 Weeks' },
  'MMR (1st)': { minAge: 270, description: 'Measles, Mumps, Rubella - 1st', ageGroup: '9 Months' },
  'MMR (2nd)': { minAge: 450, description: 'Measles, Mumps, Rubella - 2nd', ageGroup: '15 Months' },
  'Rotavirus (1st)': { minAge: 45, description: 'Rotavirus - 1st Dose', ageGroup: '6 Weeks' },
  'Rotavirus (2nd)': { minAge: 75, description: 'Rotavirus - 2nd Dose', ageGroup: '10 Weeks' },
  'Rotavirus (3rd)': { minAge: 105, description: 'Rotavirus - 3rd Dose', ageGroup: '14 Weeks' }
};

class EnhancedChildRegistration extends Component {
  constructor(props) {
    super(props);
    this.state = {
      formData: {
        childName: '',
        dateOfBirth: '',
        parentName: '',
        contactInfo: '',
        parentEmail: '',
        parentAddress: '',
        emergencyContact: '',
        medicalHistory: '',
        allergies: '',
        preferredLanguage: 'English',
        notificationPreferences: {
          email: true,
          sms: false,
          push: true
        }
      },
      loading: false,
      registeredChild: null,
      profileImage: null,
      step: 1,
      isAuthorizedHospital: false,
      hospitalInfo: null,
      registeredChildren: [],
      generatedSchedule: []
    };
  }

  componentDidMount() {
    this.checkHospitalAuthorization();
    this.loadRegisteredChildren();
  }

  checkHospitalAuthorization = () => {
    const isRegistered = localStorage.getItem('hospitalRegistered') === 'true';
    const isAuthorized = localStorage.getItem('hospitalAuthorized') === 'true';
    
    if (isRegistered && isAuthorized) {
      const hospitalData = JSON.parse(localStorage.getItem('hospitalData') || '{}');
      this.setState({
        isAuthorizedHospital: true,
        hospitalInfo: hospitalData
      });
    }
  };

  loadRegisteredChildren = () => {
    const children = JSON.parse(localStorage.getItem('registeredChildren') || '[]');
    this.setState({ registeredChildren: children });
  };

  handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    
    if (name.includes('notificationPreferences.')) {
      const prefKey = name.split('.')[1];
      this.setState({
        formData: {
          ...this.state.formData,
          notificationPreferences: {
            ...this.state.formData.notificationPreferences,
            [prefKey]: checked
          }
        }
      });
    } else {
      this.setState({
        formData: {
          ...this.state.formData,
          [name]: type === 'checkbox' ? checked : value
        }
      });
    }

    // Generate vaccination schedule when birth date changes
    if (name === 'dateOfBirth' && value) {
      this.generateVaccinationSchedule(value);
    }
  };

  handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      this.setState({ profileImage: file });
    }
  };

  // Generate personalized vaccination schedule based on birth date
  generateVaccinationSchedule = (birthDate) => {
    const birth = new Date(birthDate);
    const schedule = [];

    Object.entries(VACCINATION_SCHEDULE).forEach(([vaccine, info]) => {
      const dueDate = new Date(birth.getTime() + (info.minAge * 24 * 60 * 60 * 1000));
      schedule.push({
        vaccine,
        description: info.description,
        ageGroup: info.ageGroup,
        dueDate: dueDate.toISOString(),
        dueDateFormatted: dueDate.toLocaleDateString(),
        status: 'Scheduled'
      });
    });

    // Sort by due date
    schedule.sort((a, b) => new Date(a.dueDate) - new Date(b.dueDate));
    
    this.setState({ generatedSchedule: schedule });
  };

  validateParentAddress = (address) => {
    const ethRegex = /^0x[a-fA-F0-9]{40}$/;
    return ethRegex.test(address);
  };

  validateForm = () => {
    const { childName, dateOfBirth, parentName, parentAddress, contactInfo, parentEmail } = this.state.formData;
    return (
      childName.trim() && 
      dateOfBirth && 
      parentName.trim() && 
      parentAddress.trim() && 
      contactInfo.trim() &&
      parentEmail.trim() &&
      this.validateParentAddress(parentAddress)
    );
  };

  // Enhanced registration with schedule generation
  registerChild = async () => {
    if (!this.validateForm()) {
      alert('Please fill in all required fields with valid information');
      return;
    }

    this.setState({ loading: true });

    try {
      await new Promise(resolve => setTimeout(resolve, 3500));

      const tokenId = Date.now().toString();
      
      // Create enhanced child record with vaccination schedule
      const childRecord = {
        tokenId: tokenId,
        ...this.state.formData,
        registeredByHospital: this.state.hospitalInfo.hospitalName,
        hospitalAddress: this.state.hospitalInfo.walletAddress,
        registrationDate: new Date().toISOString(),
        profileImage: this.state.profileImage ? URL.createObjectURL(this.state.profileImage) : null,
        vaccinationSchedule: this.state.generatedSchedule,
        qrData: {
          tokenId: tokenId,
          childName: this.state.formData.childName,
          parentAddress: this.state.formData.parentAddress,
          registeredBy: this.state.hospitalInfo.hospitalName,
          verificationUrl: `${window.location.origin}/verify/${tokenId}`
        }
      };

      // Save to localStorage
      const existingChildren = JSON.parse(localStorage.getItem('registeredChildren') || '[]');
      existingChildren.push(childRecord);
      localStorage.setItem('registeredChildren', JSON.stringify(existingChildren));

      // Save vaccination schedule separately for easy access
      localStorage.setItem(`schedule_${tokenId}`, JSON.stringify(this.state.generatedSchedule));

      // Save to parent's records
      const parentKey = `parent_${this.state.formData.parentAddress}`;
      const parentChildren = JSON.parse(localStorage.getItem(parentKey) || '[]');
      parentChildren.push(tokenId);
      localStorage.setItem(parentKey, JSON.stringify(parentChildren));

      // Generate reminders
      this.generateVaccinationReminders(tokenId, this.state.generatedSchedule);

      this.setState({
        loading: false,
        registeredChild: childRecord,
        step: 3,
        registeredChildren: existingChildren
      });

    } catch (error) {
      console.error('Error registering child:', error);
      alert('Error registering child: ' + error.message);
      this.setState({ loading: false });
    }
  };

  // Generate vaccination reminders
  generateVaccinationReminders = (tokenId, schedule) => {
    const reminders = schedule.map(vaccination => ({
      id: `${tokenId}_${vaccination.vaccine}`,
      tokenId,
      vaccine: vaccination.vaccine,
      dueDate: vaccination.dueDate,
      reminderDate: new Date(new Date(vaccination.dueDate).getTime() - (7 * 24 * 60 * 60 * 1000)).toISOString(), // 7 days before
      sent: false,
      type: 'vaccination_due'
    }));

    localStorage.setItem(`reminders_${tokenId}`, JSON.stringify(reminders));
  };

  resetForm = () => {
    this.setState({
      formData: {
        childName: '',
        dateOfBirth: '',
        parentName: '',
        contactInfo: '',
        parentEmail: '',
        parentAddress: '',
        emergencyContact: '',
        medicalHistory: '',
        allergies: '',
        preferredLanguage: 'English',
        notificationPreferences: {
          email: true,
          sms: false,
          push: true
        }
      },
      profileImage: null,
      registeredChild: null,
      step: 1,
      generatedSchedule: []
    });
  };

  renderStep1() {
    const { formData, loading, hospitalInfo } = this.state;

    return (
      <div className="registration-step">
        <div className="hospital-header">
          <h2>ð¥ Enhanced Child Birth Registration</h2>
          <div className="hospital-info">
            <p><strong>Hospital:</strong> {hospitalInfo?.hospitalName}</p>
            <p><strong>License:</strong> {hospitalInfo?.licenseNumber}</p>
          </div>
        </div>
        
        <div className="form-row">
          <div className="form-group">
            <label>Child's Full Name *</label>
            <input
              type="text"
              name="childName"
              value={formData.childName}
              onChange={this.handleInputChange}
              placeholder="Enter child's full name"
              required
              disabled={loading}
            />
          </div>

          <div className="form-group">
            <label>Date of Birth *</label>
            <input
              type="date"
              name="dateOfBirth"
              value={formData.dateOfBirth}
              onChange={this.handleInputChange}
              required
              disabled={loading}
            />
          </div>
        </div>

        <div className="form-row">
          <div className="form-group">
            <label>Parent/Guardian Name *</label>
            <input
              type="text"
              name="parentName"
              value={formData.parentName}
              onChange={this.handleInputChange}
              placeholder="Enter parent/guardian name"
              required
              disabled={loading}
            />
          </div>

          <div className="form-group">
            <label>Parent Email Address *</label>
            <input
              type="email"
              name="parentEmail"
              value={formData.parentEmail}
              onChange={this.handleInputChange}
              placeholder="parent@email.com"
              required
              disabled={loading}
            />
          </div>
        </div>

        <div className="form-group">
          <label>Parent's Wallet Address * 
            <span className="help-text">The NFT will be sent to this address</span>
          </label>
          <input
            type="text"
            name="parentAddress"
            value={formData.parentAddress}
            onChange={this.handleInputChange}
            placeholder="0x... (Parent's blockchain wallet address)"
            className={this.validateParentAddress(formData.parentAddress) || !formData.parentAddress ? '' : 'error'}
            required
            disabled={loading}
          />
          {formData.parentAddress && !this.validateParentAddress(formData.parentAddress) && (
            <span className="input-error">Invalid wallet address format</span>
          )}
        </div>

        <div className="form-row">
          <div className="form-group">
            <label>Parent Contact Information *</label>
            <input
              type="tel"
              name="contactInfo"
              value={formData.contactInfo}
              onChange={this.handleInputChange}
              placeholder="Phone number"
              required
              disabled={loading}
            />
          </div>

          <div className="form-group">
            <label>Emergency Contact</label>
            <input
              type="tel"
              name="emergencyContact"
              value={formData.emergencyContact}
              onChange={this.handleInputChange}
              placeholder="Emergency contact number"
              disabled={loading}
            />
          </div>
        </div>

        <div className="form-group">
          <label>Child's Photo (Optional)</label>
          <input
            type="file"
            accept="image/*"
            onChange={this.handleImageUpload}
            className="file-input"
            disabled={loading}
          />
          {this.state.profileImage && (
            <div className="image-preview">
              <img 
                src={URL.createObjectURL(this.state.profileImage)} 
                alt="Preview" 
                className="preview-img"
              />
            </div>
          )}
        </div>
      </div>
    );
  }

  renderStep2() {
    const { formData, loading, generatedSchedule } = this.state;

    return (
      <div className="registration-step">
        <h2>ð Medical Information & Notifications</h2>
        
        <div className="form-row">
          <div className="form-group">
            <label>Known Allergies</label>
            <textarea
              name="allergies"
              value={formData.allergies}
              onChange={this.handleInputChange}
              placeholder="List any known allergies (food, medicine, etc.)"
              rows="3"
              disabled={loading}
            />
          </div>

          <div className="form-group">
            <label>Medical History</label>
            <textarea
              name="medicalHistory"
              value={formData.medicalHistory}
              onChange={this.handleInputChange}
              placeholder="Any relevant medical history or conditions"
              rows="3"
              disabled={loading}
            />
          </div>
        </div>

        <div className="form-group">
          <label>Preferred Language</label>
          <select
            name="preferredLanguage"
            value={formData.preferredLanguage}
            onChange={this.handleInputChange}
            disabled={loading}
          >
            <option value="English">English</option>
            <option value="Hindi">Hindi</option>
            <option value="Tamil">Tamil</option>
            <option value="Telugu">Telugu</option>
            <option value="Bengali">Bengali</option>
          </select>
        </div>

        <div className="consent-section">
          <h4>ð² Notification Preferences</h4>
          <div className="consent-item">
            <input 
              type="checkbox" 
              id="emailNotifications" 
              name="notificationPreferences.email"
              checked={formData.notificationPreferences.email}
              onChange={this.handleInputChange}
              disabled={loading} 
            />
            <label htmlFor="emailNotifications">
              Send vaccination reminders via email
            </label>
          </div>
          <div className="consent-item">
            <input 
              type="checkbox" 
              id="smsNotifications" 
              name="notificationPreferences.sms"
              checked={formData.notificationPreferences.sms}
              onChange={this.handleInputChange}
              disabled={loading} 
            />
            <label htmlFor="smsNotifications">
              Send vaccination reminders via SMS
            </label>
          </div>
          <div className="consent-item">
            <input 
              type="checkbox" 
              id="pushNotifications" 
              name="notificationPreferences.push"
              checked={formData.notificationPreferences.push}
              onChange={this.handleInputChange}
              disabled={loading} 
            />
            <label htmlFor="pushNotifications">
              Send push notifications through mobile app
            </label>
          </div>
        </div>

        {/* Generated Vaccination Schedule Preview */}
        {generatedSchedule.length > 0 && (
          <div className="vaccination-schedule-preview">
            <h4>ð Generated Vaccination Schedule</h4>
            <p>Based on birth date: {formData.dateOfBirth}</p>
            <div className="schedule-list">
              {generatedSchedule.slice(0, 8).map((vaccine, index) => (
                <div key={index} className="schedule-item">
                  <div className="vaccine-info">
                    <strong>{vaccine.vaccine}</strong>
                    <span className="age-group">{vaccine.ageGroup}</span>
                  </div>
                  <div className="due-date">Due: {vaccine.dueDateFormatted}</div>
                </div>
              ))}
              {generatedSchedule.length > 8 && (
                <div className="more-vaccines">
                  ... and {generatedSchedule.length - 8} more vaccines
                </div>
              )}
            </div>
          </div>
        )}

        <div className="hospital-verification">
          <div className="verification-item">
            <input type="checkbox" id="identityVerified" required disabled={loading} />
            <label htmlFor="identityVerified">
              I have verified the identity of the parent/guardian
            </label>
          </div>
          <div className="verification-item">
            <input type="checkbox" id="informationAccurate" required disabled={loading} />
            <label htmlFor="informationAccurate">
              I confirm that all provided information is accurate
            </label>
          </div>
          <div className="verification-item">
            <input type="checkbox" id="scheduleGenerated" required disabled={loading} />
            <label htmlFor="scheduleGenerated">
              I confirm the vaccination schedule has been generated and reviewed
            </label>
          </div>
        </div>
      </div>
    );
  }

  renderStep3() {
    const { registeredChild, generatedSchedule } = this.state;

    return (
      <div className="registration-success">
        <div className="success-icon">â</div>
        <h2>Child Successfully Registered!</h2>
        <p>Enhanced vaccination system activated with automated scheduling and reminders.</p>
        
        <div className="child-info-card">
          <h3>{registeredChild.childName}</h3>
          <p><strong>Token ID:</strong> {registeredChild.tokenId}</p>
          <p><strong>Parent:</strong> {registeredChild.parentName}</p>
          <p><strong>Parent Email:</strong> {registeredChild.parentEmail}</p>
          <p><strong>Parent Wallet:</strong> {registeredChild.parentAddress}</p>
          <p><strong>Vaccination Schedule:</strong> {generatedSchedule.length} vaccines scheduled</p>
        </div>

        <div className="schedule-summary">
          <h4>ð Upcoming Vaccinations (Next 6 Months)</h4>
          <div className="upcoming-vaccines">
            {generatedSchedule.slice(0, 6).map((vaccine, index) => (
              <div key={index} className="upcoming-vaccine">
                <div className="vaccine-name">{vaccine.vaccine}</div>
                <div className="due-date">Due: {vaccine.dueDateFormatted}</div>
                <div className="age-group">{vaccine.ageGroup}</div>
              </div>
            ))}
          </div>
        </div>

        <div className="automation-summary">
          <h4>ð¤ Automated Features Activated</h4>
          <div className="automation-list">
            <div className="automation-item">
              <span className="automation-icon">ð§</span>
              <span>Email reminders 7 days before each vaccination</span>
            </div>
            <div className="automation-item">
              <span className="automation-icon">ð±</span>
              <span>NFT automatically sent to parent's wallet</span>
            </div>
            <div className="automation-item">
              <span className="automation-icon">ð</span>
              <span>QR codes for instant vaccination verification</span>
            </div>
            <div className="automation-item">
              <span className="automation-icon">â°</span>
              <span>Overdue vaccination alerts</span>
            </div>
          </div>
        </div>

        <div className="parent-instructions">
          <h4>ð Instructions for Parents</h4>
          <div className="instructions-card">
            <ul>
              <li><strong>NFT Token ID:</strong> {registeredChild.tokenId} - Check your wallet!</li>
              <li><strong>Email Confirmation:</strong> Sent to {registeredChild.parentEmail}</li>
              <li><strong>Next Vaccination:</strong> {generatedSchedule[0]?.vaccine} due {generatedSchedule[0]?.dueDateFormatted}</li>
              <li><strong>Reminders:</strong> Automatic notifications enabled</li>
              <li><strong>Mobile Access:</strong> Use Token ID to access records at any hospital</li>
            </ul>
          </div>
        </div>

        <button onClick={this.resetForm} className="continue-btn">
          Register Another Child
        </button>
      </div>
    );
  }

  render() {
    const { isAuthorizedHospital, step, loading } = this.state;

    if (!isAuthorizedHospital) {
      return (
        <div className="App">
          <div className="main-content">
            <div className="authorization-error">
              <div className="error-container">
                <div className="error-icon">ð¥</div>
                <h2>Hospital Authorization Required</h2>
                <p>Only authorized hospitals can register children in the vaccination system.</p>
                
                <div className="registration-prompt">
                  <p>Your hospital needs to be registered and authorized first.</p>
                  <p>Please:</p>
                  <ol>
                    <li>Register your hospital using the Hospital Registration form</li>
                    <li>Wait for admin authorization</li>
                    <li>Return here to register children</li>
                  </ol>
                </div>
              </div>
            </div>
            <button onClick={this.props.onBack} className="btn-secondary">
              â Back to Dashboard
            </button>
          </div>
        </div>
      );
    }

    return (
      <div className="App">
        <div className="main-content">
          <div className="hospital-child-registration">
            <div className="registration-container">
              <div className="progress-bar">
                <div className={`step ${step >= 1 ? 'active' : ''}`}>
                  <span>1</span>
                  <label>Child Info</label>
                </div>
                <div className={`step ${step >= 2 ? 'active' : ''}`}>
                  <span>2</span>
                  <label>Medical & Schedule</label>
                </div>
                <div className={`step ${step >= 3 ? 'active' : ''}`}>
                  <span>3</span>
                  <label>Complete</label>
                </div>
              </div>

              {step === 1 && this.renderStep1()}
              {step === 2 && this.renderStep2()}
              {step === 3 && this.renderStep3()}

              {step < 3 && (
                <div className="form-actions">
                  {step > 1 && (
                    <button 
                      onClick={() => this.setState({ step: step - 1 })}
                      className="btn-secondary"
                      disabled={loading}
                    >
                      â Previous
                    </button>
                  )}
                  
                  {step < 2 && (
                    <button 
                      onClick={() => this.setState({ step: 2 })}
                      className="btn-primary"
                      disabled={
                        !this.state.formData.childName || 
                        !this.state.formData.dateOfBirth || 
                        !this.state.formData.parentName || 
                        !this.state.formData.parentAddress ||
                        !this.state.formData.parentEmail ||
                        !this.validateParentAddress(this.state.formData.parentAddress)
                      }
                    >
                      Next: Generate Schedule â
                    </button>
                  )}
                  
                  {step === 2 && (
                    <button 
                      onClick={this.registerChild}
                      className="btn-primary"
                      disabled={loading}
                    >
                      {loading ? 'â³ Creating NFT & Schedule...' : 'ð Complete Registration'}
                    </button>
                  )}

                  <button onClick={this.props.onBack} className="btn-secondary" disabled={loading}>
                    Back to Dashboard
                  </button>
                </div>
              )}

              {loading && (
                <div className="loading-info">
                  <div className="loading-spinner">â³</div>
                  <p><strong>ð¡ Processing Enhanced Registration...</strong></p>
                  <p>â³ Creating NFT record on blockchain</p>
                  <p>ð Generating personalized vaccination schedule</p>
                  <p>ð¤ Sending NFT to parent's wallet</p>
                  <p>ð Setting up automated reminders</p>
                  <p>ð Generating verification QR codes</p>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default EnhancedChildRegistration;
