import React, { Component } from 'react';
import { ethers } from 'ethers';
import './App.css';

const CONTRACT_ADDRESS = "0xb5a28A2c3715BA080576A2EaF80eB91e44E4499D";

// Your contract ABI - Replace with your full ABI from build/contracts/VaccinationRegistry.json
const CONTRACT_ABI = [
  {
    "inputs": [
      {"internalType": "string", "name": "_name", "type": "string"},
      {"internalType": "uint256", "name": "_dateOfBirth", "type": "uint256"},
      {"internalType": "string", "name": "_parentName", "type": "string"},
      {"internalType": "string", "name": "_contactInfo", "type": "string"},
      {"internalType": "string", "name": "_tokenURI", "type": "string"}
    ],
    "name": "registerChild",
    "outputs": [{"internalType": "uint256", "name": "", "type": "uint256"}],
    "stateMutability": "nonpayable",
    "type": "function"
  },
  {
    "inputs": [
      {"internalType": "uint256", "name": "_tokenId", "type": "uint256"},
      {"internalType": "string", "name": "_vaccineName", "type": "string"},
      {"internalType": "string", "name": "_hospitalName", "type": "string"},
      {"internalType": "string", "name": "_batchNumber", "type": "string"},
      {"internalType": "uint256", "name": "_nextDueDate", "type": "uint256"},
      {"internalType": "string", "name": "_ipfsHash", "type": "string"}
    ],
    "name": "recordVaccination",
    "outputs": [],
    "stateMutability": "nonpayable",
    "type": "function"
  },
  {
    "inputs": [{"internalType": "uint256", "name": "_tokenId", "type": "uint256"}],
    "name": "getChildInfo",
    "outputs": [
      {
        "components": [
          {"internalType": "string", "name": "name", "type": "string"},
          {"internalType": "uint256", "name": "dateOfBirth", "type": "uint256"},
          {"internalType": "string", "name": "parentName", "type": "string"},
          {"internalType": "string", "name": "contactInfo", "type": "string"},
          {"internalType": "bool", "name": "isRegistered", "type": "bool"},
          {"internalType": "uint256", "name": "registrationDate", "type": "uint256"}
        ],
        "internalType": "struct VaccinationRegistry.Child",
        "name": "",
        "type": "tuple"
      }
    ],
    "stateMutability": "view",
    "type": "function"
  },
  {
    "inputs": [{"internalType": "uint256", "name": "_tokenId", "type": "uint256"}],
    "name": "getVaccinationHistory",
    "outputs": [
      {
        "components": [
          {"internalType": "string", "name": "vaccineName", "type": "string"},
          {"internalType": "uint256", "name": "dateAdministered", "type": "uint256"},
          {"internalType": "string", "name": "hospitalName", "type": "string"},
          {"internalType": "address", "name": "administeredBy", "type": "address"},
          {"internalType": "string", "name": "batchNumber", "type": "string"},
          {"internalType": "uint256", "name": "nextDueDate", "type": "uint256"},
          {"internalType": "bool", "name": "isVerified", "type": "bool"},
          {"internalType": "string", "name": "ipfsHash", "type": "string"}
        ],
        "internalType": "struct VaccinationRegistry.VaccinationRecord[]",
        "name": "",
        "type": "tuple[]"
      }
    ],
    "stateMutability": "view",
    "type": "function"
  },
  {
    "inputs": [{"internalType": "address", "name": "", "type": "address"}],
    "name": "authorizedHospitals",
    "outputs": [{"internalType": "bool", "name": "", "type": "bool"}],
    "stateMutability": "view",
    "type": "function"
  },
  {
    "anonymous": false,
    "inputs": [
      {"indexed": true, "internalType": "uint256", "name": "tokenId", "type": "uint256"},
      {"indexed": false, "internalType": "string", "name": "name", "type": "string"},
      {"indexed": true, "internalType": "address", "name": "parent", "type": "address"}
    ],
    "name": "ChildRegistered",
    "type": "event"
  },
  {
    "anonymous": false,
    "inputs": [
      {"indexed": true, "internalType": "uint256", "name": "tokenId", "type": "uint256"},
      {"indexed": false, "internalType": "string", "name": "vaccineName", "type": "string"},
      {"indexed": false, "internalType": "uint256", "name": "dateAdministered", "type": "uint256"}
    ],
    "name": "VaccinationRecorded",
    "type": "event"
  }
];

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      account: '',
      provider: null,
      contract: null,
      loading: false,
      currentView: 'welcome',
      networkId: null,
      isHospital: false
    };
  }

  connectWallet = async () => {
    try {
      if (typeof window.ethereum !== 'undefined') {
        this.setState({ loading: true });

        // Request account access
        const accounts = await window.ethereum.request({ 
          method: 'eth_requestAccounts' 
        });

        if (accounts.length > 0) {
          // Create provider and signer
          const provider = new ethers.providers.Web3Provider(window.ethereum);
          const signer = provider.getSigner();
          const network = await provider.getNetwork();

          console.log('Connected to network:', network);

          // Check if on correct network (you might want to check specific network)
          if (network.chainId !== 1337 && network.chainId !== 80001 && network.chainId !== 137) {
            alert('Please connect to the correct network (Local, Mumbai, or Polygon)');
            this.setState({ loading: false });
            return;
          }

          // Initialize contract
          const contract = new ethers.Contract(
            CONTRACT_ADDRESS,
            CONTRACT_ABI,
            signer
          );

          // Check if user is authorized hospital
          let isHospital = false;
          try {
            isHospital = await contract.authorizedHospitals(accounts[0]);
          } catch (error) {
            console.log('Error checking hospital authorization:', error);
          }

          this.setState({ 
            account: accounts[0], 
            provider: provider,
            contract: contract,
            currentView: 'dashboard',
            loading: false,
            networkId: network.chainId,
            isHospital: isHospital
          });

          console.log('‚úÖ Blockchain connection established!');
          console.log('Account:', accounts[0]);
          console.log('Contract:', CONTRACT_ADDRESS);
          console.log('Is Hospital:', isHospital);

        } else {
          this.setState({ loading: false });
        }
      } else {
        alert('Please install MetaMask wallet from metamask.io');
      }
    } catch (error) {
      console.error('Connection error:', error);
      alert('Error connecting wallet: ' + error.message);
      this.setState({ loading: false });
    }
  }

  disconnectWallet = () => {
    this.setState({ 
      account: '', 
      provider: null,
      contract: null,
      currentView: 'welcome',
      networkId: null,
      isHospital: false
    });
  }

  changeView = (view) => {
    this.setState({ currentView: view });
  }

  render() {
    const { account, loading, currentView, networkId, isHospital, contract } = this.state;

    // Welcome Screen
    if (currentView === 'welcome') {
      return (
        <div className="App">
          <div className="welcome-screen">
            <div className="welcome-content">
              <h1>üè• Blockchain Vaccination System</h1>
              <p>Secure, transparent, and tamper-proof vaccination records</p>
              <div className="contract-info">
                <p><strong>Smart Contract:</strong></p>
                <code>{CONTRACT_ADDRESS}</code>
              </div>
              <button 
                className="connect-btn"
                onClick={this.connectWallet}
                disabled={loading}
              >
                {loading ? 'Connecting...' : 'ü¶ä Connect MetaMask'}
              </button>
              <div className="network-info">
                <p><small>Make sure you're on the correct network!</small></p>
              </div>
              <div className="features">
                <p>‚úÖ Immutable Records</p>
                <p>‚úÖ Global Verification</p>
                <p>‚úÖ Automated Reminders</p>
                <p>‚úÖ Privacy Protection</p>
              </div>
            </div>
          </div>
        </div>
      );
    }

    // Dashboard
    if (currentView === 'dashboard') {
      return (
        <div className="App">
          <div className="dashboard">
            <div className="dashboard-header">
              <h2>üè• Vaccination Dashboard</h2>
              <div className="account-info">
                <p>Connected: {account.slice(0, 6)}...{account.slice(-4)}</p>
                <p>Network: {networkId}</p>
                {isHospital && <span className="hospital-badge">üè• Authorized Hospital</span>}
                <button onClick={this.disconnectWallet} className="disconnect-btn">
                  Disconnect
                </button>
              </div>
            </div>

            <div className="dashboard-actions">
              <div className="action-card" onClick={() => this.changeView('register')}>
                <div className="card-icon">üë∂</div>
                <h3>Register Child</h3>
                <p>Create new vaccination record</p>
              </div>

              <div className="action-card" onClick={() => this.changeView('record')}>
                <div className="card-icon">üíâ</div>
                <h3>Record Vaccination</h3>
                <p>{isHospital ? 'Add vaccination entry' : 'Hospital access required'}</p>
              </div>

              <div className="action-card" onClick={() => this.changeView('verify')}>
                <div className="card-icon">üîç</div>
                <h3>Verify Records</h3>
                <p>Check vaccination status</p>
              </div>
            </div>
          </div>
        </div>
      );
    }

    // Registration Form
    if (currentView === 'register') {
      return (
        <RegistrationForm 
          onBack={() => this.changeView('dashboard')} 
          account={account}
          contract={contract}
        />
      );
    }

    // Vaccination Record Form
    if (currentView === 'record') {
      return (
        <VaccinationForm 
          onBack={() => this.changeView('dashboard')} 
          account={account}
          contract={contract}
          isHospital={isHospital}
        />
      );
    }

    // Verification
    if (currentView === 'verify') {
      return (
        <VerificationPage 
          onBack={() => this.changeView('dashboard')}
          contract={contract}
        />
      );
    }

    return null;
  }
}

// Registration Component with Real Blockchain Integration
class RegistrationForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      childName: '',
      dateOfBirth: '',
      parentName: '',
      contactInfo: '',
      loading: false
    };
  }

  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value
    });
  }

  handleSubmit = async (e) => {
    e.preventDefault();

    if (!this.props.contract) {
      alert('Please connect your wallet first!');
      return;
    }

    try {
      this.setState({ loading: true });

      // Convert date to timestamp
      const birthTimestamp = Math.floor(new Date(this.state.dateOfBirth).getTime() / 1000);

      console.log('üöÄ Calling smart contract registerChild...');

      // Call the smart contract
      const tx = await this.props.contract.registerChild(
        this.state.childName,
        birthTimestamp,
        this.state.parentName,
        this.state.contactInfo,
        "" // Empty token URI for now
      );

      console.log('üìù Transaction submitted:', tx.hash);
      alert(`Transaction submitted!\nHash: ${tx.hash}\nWaiting for confirmation...`);

      // Wait for confirmation
      const receipt = await tx.wait();
      console.log('‚úÖ Transaction confirmed:', receipt);

      // Get the token ID from the event
      const event = receipt.events?.find(e => e.event === 'ChildRegistered');
      const tokenId = event?.args?.tokenId;

      alert(`üéâ Child registered successfully!\n\nToken ID: ${tokenId}\nChild: ${this.state.childName}\nParent: ${this.state.parentName}\nTransaction: ${tx.hash}`);

      // Reset form
      this.setState({
        childName: '',
        dateOfBirth: '',
        parentName: '',
        contactInfo: '',
        loading: false
      });

    } catch (error) {
      console.error('‚ùå Registration error:', error);
      alert('Error registering child: ' + error.message);
      this.setState({ loading: false });
    }
  }

  render() {
    const { childName, dateOfBirth, parentName, contactInfo, loading } = this.state;
    const { account, onBack } = this.props;

    return (
      <div className="App">
        <div className="form-container">
          <h2>üë∂ Register Child on Blockchain</h2>
          <p className="wallet-info">Parent Wallet: {account}</p>
          <p className="contract-info">Contract: {CONTRACT_ADDRESS}</p>

          <form onSubmit={this.handleSubmit}>
            <div className="form-group">
              <label>Child's Full Name *</label>
              <input
                type="text"
                name="childName"
                value={childName}
                onChange={this.handleChange}
                placeholder="Enter child's full name"
                required
                disabled={loading}
              />
            </div>

            <div className="form-group">
              <label>Date of Birth *</label>
              <input
                type="date"
                name="dateOfBirth"
                value={dateOfBirth}
                onChange={this.handleChange}
                required
                disabled={loading}
              />
            </div>

            <div className="form-group">
              <label>Parent/Guardian Name *</label>
              <input
                type="text"
                name="parentName"
                value={parentName}
                onChange={this.handleChange}
                placeholder="Enter parent/guardian name"
                required
                disabled={loading}
              />
            </div>

            <div className="form-group">
              <label>Contact Information *</label>
              <input
                type="text"
                name="contactInfo"
                value={contactInfo}
                onChange={this.handleChange}
                placeholder="Phone number or email"
                required
                disabled={loading}
              />
            </div>

            <div className="form-actions">
              <button type="button" onClick={onBack} className="btn-secondary" disabled={loading}>
                ‚Üê Back
              </button>
              <button type="submit" className="btn-primary" disabled={loading}>
                {loading ? '‚è≥ Processing...' : 'üîê Register on Blockchain'}
              </button>
            </div>
          </form>

          {loading && (
            <div className="loading-info">
              <p>üì° Sending transaction to blockchain...</p>
              <p>‚è≥ Please wait for confirmation...</p>
            </div>
          )}
        </div>
      </div>
    );
  }
}

// Vaccination Form Component with Real Blockchain Integration
class VaccinationForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tokenId: '',
      vaccineName: '',
      hospitalName: '',
      batchNumber: '',
      notes: '',
      loading: false
    };
  }

  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value
    });
  }

  handleSubmit = async (e) => {
    e.preventDefault();

    if (!this.props.contract) {
      alert('Please connect your wallet first!');
      return;
    }

    if (!this.props.isHospital) {
      alert('Only authorized hospitals can record vaccinations!');
      return;
    }

    try {
      this.setState({ loading: true });

      console.log('üöÄ Calling smart contract recordVaccination...');

      // Calculate next due date (30 days from now for demo)
      const nextDueDate = Math.floor((Date.now() + 30 * 24 * 60 * 60 * 1000) / 1000);

      // Call the smart contract
      const tx = await this.props.contract.recordVaccination(
        this.state.tokenId,
        this.state.vaccineName,
        this.state.hospitalName,
        this.state.batchNumber,
        nextDueDate,
        "" // Empty IPFS hash for now
      );

      console.log('üìù Transaction submitted:', tx.hash);
      alert(`Transaction submitted!\nHash: ${tx.hash}\nWaiting for confirmation...`);

      // Wait for confirmation
      const receipt = await tx.wait();
      console.log('‚úÖ Transaction confirmed:', receipt);

      alert(`üéâ Vaccination recorded successfully!\n\nChild Token ID: ${this.state.tokenId}\nVaccine: ${this.state.vaccineName}\nHospital: ${this.state.hospitalName}\nBatch: ${this.state.batchNumber}\nTransaction: ${tx.hash}`);

      // Reset form
      this.setState({
        tokenId: '',
        vaccineName: '',
        hospitalName: '',
        batchNumber: '',
        notes: '',
        loading: false
      });

    } catch (error) {
      console.error('‚ùå Recording error:', error);
      alert('Error recording vaccination: ' + error.message);
      this.setState({ loading: false });
    }
  }

  render() {
    const { tokenId, vaccineName, hospitalName, batchNumber, notes, loading } = this.state;
    const { account, onBack, isHospital } = this.props;

    if (!isHospital) {
      return (
        <div className="App">
          <div className="form-container">
            <h2>‚ö†Ô∏è Access Denied</h2>
            <p>Only authorized hospitals can record vaccinations.</p>
            <p>Your account is not authorized as a hospital.</p>
            <button onClick={onBack} className="btn-secondary">
              ‚Üê Back to Dashboard
            </button>
          </div>
        </div>
      );
    }

    return (
      <div className="App">
        <div className="form-container">
          <h2>üíâ Record Vaccination on Blockchain</h2>
          <p className="wallet-info">Hospital Account: {account}</p>
          <p className="hospital-badge">üè• Authorized Hospital</p>

          <form onSubmit={this.handleSubmit}>
            <div className="form-group">
              <label>Child Token ID *</label>
              <input
                type="text"
                name="tokenId"
                value={tokenId}
                onChange={this.handleChange}
                placeholder="Enter child's blockchain token ID"
                required
                disabled={loading}
              />
            </div>

            <div className="form-group">
              <label>Vaccine Name *</label>
              <select
                name="vaccineName"
                value={vaccineName}
                onChange={this.handleChange}
                required
                disabled={loading}
              >
                <option value="">Select Vaccine</option>
                <option value="BCG">BCG (Tuberculosis)</option>
                <option value="Hepatitis B">Hepatitis B</option>
                <option value="DPT">DPT (Diphtheria, Pertussis, Tetanus)</option>
                <option value="Polio (OPV)">Polio (OPV)</option>
                <option value="Hib">Hib</option>
                <option value="Pneumococcal">Pneumococcal</option>
                <option value="Rotavirus">Rotavirus</option>
                <option value="MMR">MMR (Measles, Mumps, Rubella)</option>
                <option value="Varicella">Varicella (Chickenpox)</option>
                <option value="Hepatitis A">Hepatitis A</option>
              </select>
            </div>

            <div className="form-group">
              <label>Hospital/Clinic Name *</label>
              <input
                type="text"
                name="hospitalName"
                value={hospitalName}
                onChange={this.handleChange}
                placeholder="Enter hospital or clinic name"
                required
                disabled={loading}
              />
            </div>

            <div className="form-group">
              <label>Vaccine Batch Number *</label>
              <input
                type="text"
                name="batchNumber"
                value={batchNumber}
                onChange={this.handleChange}
                placeholder="Enter vaccine batch number"
                required
                disabled={loading}
              />
            </div>

            <div className="form-group">
              <label>Additional Notes</label>
              <textarea
                name="notes"
                value={notes}
                onChange={this.handleChange}
                placeholder="Any additional notes or observations"
                rows="3"
                disabled={loading}
              />
            </div>

            <div className="form-actions">
              <button type="button" onClick={onBack} className="btn-secondary" disabled={loading}>
                ‚Üê Back
              </button>
              <button type="submit" className="btn-primary" disabled={loading}>
                {loading ? '‚è≥ Processing...' : 'üíâ Record on Blockchain'}
              </button>
            </div>
          </form>

          {loading && (
            <div className="loading-info">
              <p>üì° Sending transaction to blockchain...</p>
              <p>‚è≥ Please wait for confirmation...</p>
            </div>
          )}
        </div>
      </div>
    );
  }
}

// Verification Component with Real Blockchain Integration
class VerificationPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tokenId: '',
      verificationResult: null,
      loading: false
    };
  }

  handleChange = (e) => {
    this.setState({
      tokenId: e.target.value
    });
  }

  handleVerify = async () => {
    if (!this.state.tokenId.trim()) {
      alert('Please enter a Token ID');
      return;
    }

    if (!this.props.contract) {
      alert('Please connect your wallet first!');
      return;
    }

    try {
      this.setState({ loading: true });

      console.log('üîç Verifying Token ID:', this.state.tokenId);

      // Get child info from blockchain
      const childInfo = await this.props.contract.getChildInfo(this.state.tokenId);

      if (!childInfo.isRegistered) {
        alert('Child not found or not registered!');
        this.setState({ loading: false });
        return;
      }

      // Get vaccination history
      const vaccinationHistory = await this.props.contract.getVaccinationHistory(this.state.tokenId);

      const result = {
        tokenId: this.state.tokenId,
        childName: childInfo.name,
        parentName: childInfo.parentName,
        dateOfBirth: new Date(childInfo.dateOfBirth * 1000).toLocaleDateString(),
        registrationDate: new Date(childInfo.registrationDate * 1000).toLocaleDateString(),
        totalVaccinations: vaccinationHistory.length,
        vaccinations: vaccinationHistory.map(vac => ({
          vaccine: vac.vaccineName,
          date: new Date(vac.dateAdministered * 1000).toLocaleDateString(),
          hospital: vac.hospitalName,
          batch: vac.batchNumber,
          verified: vac.isVerified
        }))
      };

      this.setState({ 
        verificationResult: result,
        loading: false 
      });

      console.log('‚úÖ Verification successful:', result);

    } catch (error) {
      console.error('‚ùå Verification error:', error);
      alert('Error verifying records: ' + error.message);
      this.setState({ loading: false });
    }
  }

  render() {
    const { tokenId, verificationResult, loading } = this.state;
    const { onBack } = this.props;

    return (
      <div className="App">
        <div className="form-container">
          <h2>üîç Verify Vaccination Records</h2>
          <p>Enter a Token ID to verify vaccination status on blockchain</p>

          <div className="form-group">
            <label>Child Token ID</label>
            <input
              type="text"
              value={tokenId}
              onChange={this.handleChange}
              placeholder="Enter blockchain token ID"
              disabled={loading}
            />
          </div>

          <button onClick={this.handleVerify} className="btn-primary" disabled={loading}>
            {loading ? '‚è≥ Verifying...' : 'üîç Verify Records'}
          </button>

          {verificationResult && (
            <div className="verification-result">
              <h3>‚úÖ Blockchain Verification Result</h3>
              <div className="result-grid">
                <p><strong>Token ID:</strong> {verificationResult.tokenId}</p>
                <p><strong>Child:</strong> {verificationResult.childName}</p>
                <p><strong>Parent:</strong> {verificationResult.parentName}</p>
                <p><strong>Birth Date:</strong> {verificationResult.dateOfBirth}</p>
                <p><strong>Registered:</strong> {verificationResult.registrationDate}</p>
                <p><strong>Total Vaccinations:</strong> {verificationResult.totalVaccinations}</p>
              </div>

              {verificationResult.vaccinations.length > 0 && (
                <>
                  <h4>Vaccination History:</h4>
                  <div className="vaccination-list">
                    {verificationResult.vaccinations.map((vac, index) => (
                      <div key={index} className="vaccination-item">
                        <strong>{vac.vaccine}</strong> - {vac.date} - {vac.hospital}
                        <br />
                        <small>Batch: {vac.batch} | Verified: {vac.verified ? '‚úÖ' : '‚ùå'}</small>
                      </div>
                    ))}
                  </div>
                </>
              )}
            </div>
          )}

          <div className="verify-info">
            <h3>Blockchain Verification:</h3>
            <ul>
              <li>Data retrieved directly from smart contract</li>
              <li>Records are tamper-proof and immutable</li>
              <li>Globally accessible and verifiable</li>
              <li>No central authority required</li>
            </ul>
          </div>

          <button onClick={onBack} className="btn-secondary">
            ‚Üê Back to Dashboard
          </button>
        </div>
      </div>
    );
  }
}

export default App;
