import React, { Component } from 'react';
import { ethers } from 'ethers';
import './App.css';
import ChildRegistration from './components/ChildRegistration';
import VaccinationRecording from './components/VaccinationRecording';
import ParentDashboard from './components/ParentDashboard';
import QRVerification from './components/QRVerification';
import HospitalRegistration from './components/HospitalRegistration';
import AdminPanel from './components/AdminPanel';

const VACCINATION_ABI = [
  "function registerHospital(string memory _name, string memory _licenseNumber, string memory _contactInfo) public",
  "function setHospitalAuthorization(address _hospital, bool _authorized) public",
  "function registerChild(string memory _name, uint256 _dateOfBirth, string memory _parentName, string memory _contactInfo, address _parentAddress, string memory _tokenURI) public returns (uint256)",
  "function recordVaccination(uint256 _tokenId, string memory _vaccineName, string memory _batchNumber, uint256 _nextDueDate, string memory _ipfsHash) public",
  "function getChildInfo(uint256 _tokenId) public view returns (tuple(string,uint256,string,string,address,address,string,bool,uint256))",
  "function getVaccinationHistory(uint256 _tokenId) public view returns (tuple(string,uint256,string,address,string,uint256,bool,string,string)[])",
  "function getParentChildren(address _parent) public view returns (uint256[])",
  "function generateVerificationQR(uint256 _tokenId) public view returns (string,string,uint256,bool)",
  "function getHospitalInfo(address _hospital) public view returns (tuple(string,string,string,bool,uint256))",
  "function hospitals(address) public view returns (string,string,string,bool,uint256)",
  "function authorizedHospitals(address) public view returns (bool)",
  "function children(uint256) public view returns (string,uint256,string,string,address,address,string,bool,uint256)",
  "function owner() public view returns (address)",
  "event ChildRegistered(uint256 indexed tokenId, string name, address indexed parent, address indexed hospital)",
  "event VaccinationRecorded(uint256 indexed tokenId, string vaccineName, uint256 dateAdministered, string qrCodeHash)",
  "event HospitalRegistered(address indexed hospital, string name)",
  "event HospitalAuthorized(address indexed hospital, bool authorized)"
];

const CONTRACT_ADDRESS = "0x2CD357244c2F3962E3aCF039A3bED166fC23e83B";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentView: 'welcome',
      connectedWallet: null,
      contract: null,
      provider: null,
      signer: null,
      isLoading: false,
      error: null,
      networkId: null,
      isOwner: false,
      userRole: null
    };
  }

  componentDidMount() {
    this.checkWalletConnection();
    this.setupEventListeners();
  }

  componentWillUnmount() {
    this.removeEventListeners();
  }

  setupEventListeners = () => {
    if (window.ethereum) {
      window.ethereum.on('accountsChanged', this.handleAccountsChanged);
      window.ethereum.on('chainChanged', this.handleChainChanged);
    }
  };

  removeEventListeners = () => {
    if (window.ethereum) {
      window.ethereum.removeListener('accountsChanged', this.handleAccountsChanged);
      window.ethereum.removeListener('chainChanged', this.handleChainChanged);
    }
  };

  handleAccountsChanged = (accounts) => {
    if (accounts.length === 0) {
      this.disconnectWallet();
    } else {
      this.setState({ connectedWallet: accounts[0] }, this.checkUserRole);
    }
  };

  handleChainChanged = () => {
    window.location.reload();
  };

  checkWalletConnection = async () => {
    if (typeof window.ethereum !== 'undefined') {
      try {
        const accounts = await window.ethereum.request({ method: 'eth_accounts' });
        if (accounts.length > 0) {
          await this.connectWallet();
        }
      } catch (error) {
        console.error('Error checking wallet connection:', error);
      }
    }
  };

  connectWallet = async () => {
    try {
      this.setState({ isLoading: true, error: null });

      if (typeof window.ethereum === 'undefined') {
        throw new Error('MetaMask is not installed. Please install MetaMask to continue.');
      }

      const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      const signer = provider.getSigner();
      const network = await provider.getNetwork();
      const contract = new ethers.Contract(CONTRACT_ADDRESS, VACCINATION_ABI, signer);

      const owner = await contract.owner();
      const isOwner = owner.toLowerCase() === accounts[0].toLowerCase();

      this.setState({
        connectedWallet: accounts[0],
        provider,
        signer,
        contract,
        networkId: network.chainId,
        isOwner,
        isLoading: false,
        currentView: 'dashboard'
      }, this.checkUserRole);

    } catch (error) {
      console.error('Error connecting wallet:', error);
      this.setState({ error: error.message, isLoading: false });
    }
  };

  disconnectWallet = () => {
    this.setState({
      connectedWallet: null,
      contract: null,
      provider: null,
      signer: null,
      isOwner: false,
      userRole: null,
      currentView: 'welcome'
    });
  };

  checkUserRole = async () => {
    try {
      const { contract, connectedWallet } = this.state;
      if (!contract || !connectedWallet) return;

      const hospitalInfo = await contract.hospitals(connectedWallet);
      if (hospitalInfo[0]) {
        this.setState({ userRole: 'hospital' });
        return;
      }

      const children = await contract.getParentChildren(connectedWallet);
      if (children.length > 0) {
        this.setState({ userRole: 'parent' });
        return;
      }

      this.setState({ userRole: 'verifier' });

    } catch (error) {
      console.error('Error checking user role:', error);
      this.setState({ userRole: 'verifier' });
    }
  };

  sendTransaction = async (contractMethod, params = [], options = {}) => {
    try {
      this.setState({ isLoading: true, error: null });
      const tx = await contractMethod(...params, options);
      const receipt = await tx.wait();
      this.setState({ isLoading: false });
      return receipt;
    } catch (error) {
      this.setState({ error: error.message, isLoading: false });
      throw error;
    }
  };

  changeView = (view) => {
    this.setState({ currentView: view });
  };

  render() {
    const { currentView, contract, connectedWallet, isLoading, isOwner, userRole, networkId } = this.state;

    if (!connectedWallet && currentView !== 'welcome') return this.renderWelcome();
    if (isLoading) return <div className="loading-container"><p>⏳ Loading...</p></div>;

    switch (currentView) {
      case 'welcome': return this.renderWelcome();
      case 'dashboard': return this.renderDashboard();
      case 'admin': return <AdminPanel contract={contract} account={connectedWallet} onBack={() => this.changeView('dashboard')} />;
      case 'hospital-register': return <HospitalRegistration contract={contract} connectedWallet={connectedWallet} sendTransaction={this.sendTransaction} onBack={() => this.changeView('dashboard')} onChangeView={this.changeView} />;
      case 'child-register': return <ChildRegistration contract={contract} connectedWallet={connectedWallet} sendTransaction={this.sendTransaction} onBack={() => this.changeView('dashboard')} onChangeView={this.changeView} />;
      case 'vaccination-record': return <VaccinationRecording contract={contract} connectedWallet={connectedWallet} sendTransaction={this.sendTransaction} onBack={() => this.changeView('dashboard')} onChangeView={this.changeView} />;
      case 'parent-dashboard': return <ParentDashboard contract={contract} connectedWallet={connectedWallet} onBack={() => this.changeView('dashboard')} onChangeView={this.changeView} />;
      case 'qr-verification': return <QRVerification contract={contract} connectedWallet={connectedWallet} onBack={() => this.changeView('dashboard')} onChangeView={this.changeView} />;
      default: return this.renderDashboard();
    }
  }

  renderWelcome() {
    return (
      <div className="welcome-screen">
        <h1>🏥 Blockchain Vaccination Registry</h1>
        <p>Secure and verifiable health records</p>
        {this.state.error && <div className="error-message">❌ {this.state.error}</div>}
        <button onClick={this.connectWallet} className="connect-btn">
          {this.state.isLoading ? '🔄 Connecting...' : '🦊 Connect Wallet'}
        </button>
        <div className="contract-info">
          <strong>Contract Address:</strong>
          <p>{CONTRACT_ADDRESS}</p>
        </div>
      </div>
    );
  }

  renderDashboard() {
    const { connectedWallet, isOwner, userRole, networkId } = this.state;
    return (
      <div className="dashboard">
        <div className="dashboard-header">
          <h2>📋 Dashboard</h2>
          <p><strong>Wallet:</strong> {connectedWallet}</p>
          <p><strong>Network:</strong> {networkId}</p>
          <p><strong>Role:</strong> {userRole} {isOwner && '(Owner)'}</p>
          <button onClick={this.disconnectWallet} className="disconnect-btn">Disconnect</button>
        </div>
        <div className="dashboard-actions">
          {isOwner && <div onClick={() => this.changeView('admin')} className="action-btn">👑 Admin Panel</div>}
          <div onClick={() => this.changeView('hospital-register')} className="action-btn">🏥 Hospital Registration</div>
          <div onClick={() => this.changeView('child-register')} className="action-btn">👶 Register Child</div>
          <div onClick={() => this.changeView('vaccination-record')} className="action-btn">💉 Record Vaccination</div>
          <div onClick={() => this.changeView('parent-dashboard')} className="action-btn">👨‍👩‍👧‍👦 Parent Dashboard</div>
          <div onClick={() => this.changeView('qr-verification')} className="action-btn">📱 QR Verification</div>
        </div>
      </div>
    );
  }
}

export default App;
