import React, { Component } from 'react';
import { ethers } from 'ethers';
import './App.css';

const CONTRACT_ADDRESS = "0xb5a28A2c3715BA080576A2EaF80eB91e44E4499D";

// Enhanced ABI with all possible events
const CONTRACT_ABI = [
  {
    "inputs": [
      {"internalType": "string", "name": "_name", "type": "string"},
      {"internalType": "uint256", "name": "_dateOfBirth", "type": "uint256"},
      {"internalType": "string", "name": "_parentName", "type": "string"},
      {"internalType": "string", "name": "_contactInfo", "type": "string"},
      {"internalType": "string", "name": "_tokenURI", "type": "string"}
    ],
    "name": "registerChild",
    "outputs": [{"internalType": "uint256", "name": "", "type": "uint256"}],
    "stateMutability": "nonpayable",
    "type": "function"
  },
  {
    "inputs": [{"internalType": "address", "name": "owner", "type": "address"}],
    "name": "balanceOf",
    "outputs": [{"internalType": "uint256", "name": "", "type": "uint256"}],
    "stateMutability": "view",
    "type": "function"
  },
  {
    "inputs": [
      {"internalType": "address", "name": "owner", "type": "address"},
      {"internalType": "uint256", "name": "index", "type": "uint256"}
    ],
    "name": "tokenOfOwnerByIndex",
    "outputs": [{"internalType": "uint256", "name": "", "type": "uint256"}],
    "stateMutability": "view",
    "type": "function"
  },
  {
    "inputs": [],
    "name": "totalSupply",
    "outputs": [{"internalType": "uint256", "name": "", "type": "uint256"}],
    "stateMutability": "view",
    "type": "function"
  },
  {
    "anonymous": false,
    "inputs": [
      {"indexed": true, "internalType": "address", "name": "from", "type": "address"},
      {"indexed": true, "internalType": "address", "name": "to", "type": "address"},
      {"indexed": true, "internalType": "uint256", "name": "tokenId", "type": "uint256"}
    ],
    "name": "Transfer",
    "type": "event"
  }
];

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      account: '',
      provider: null,
      contract: null,
      loading: false,
      currentView: 'welcome',
      networkId: null,
      lastTokenId: null
    };
  }

  connectWallet = async () => {
    try {
      if (typeof window.ethereum !== 'undefined') {
        this.setState({ loading: true });

        const accounts = await window.ethereum.request({ 
          method: 'eth_requestAccounts' 
        });

        if (accounts.length > 0) {
          const provider = new ethers.providers.Web3Provider(window.ethereum);
          const signer = provider.getSigner();
          const network = await provider.getNetwork();

          const contract = new ethers.Contract(
            CONTRACT_ADDRESS,
            CONTRACT_ABI,
            signer
          );

          this.setState({ 
            account: accounts[0], 
            provider: provider,
            contract: contract,
            currentView: 'dashboard',
            loading: false,
            networkId: network.chainId
          });

          console.log('‚úÖ Connected successfully!');
        }
      } else {
        alert('Please install MetaMask wallet!');
      }
    } catch (error) {
      console.error('Connection error:', error);
      alert('Error connecting: ' + error.message);
      this.setState({ loading: false });
    }
  }

  disconnectWallet = () => {
    this.setState({ 
      account: '', 
      provider: null,
      contract: null,
      currentView: 'welcome',
      networkId: null,
      lastTokenId: null
    });
  }

  changeView = (view) => {
    this.setState({ currentView: view });
  }

  setLastTokenId = (tokenId) => {
    this.setState({ lastTokenId: tokenId });
  }

  render() {
    const { account, loading, currentView, networkId, contract, lastTokenId } = this.state;

    if (currentView === 'welcome') {
      return (
        <div className="App">
          <div className="welcome-screen">
            <div className="welcome-content">
              <h1>üè• Blockchain Vaccination System</h1>
              <p>Secure, transparent, and tamper-proof vaccination records</p>
              <div className="contract-info">
                <p><strong>Smart Contract:</strong></p>
                <code>{CONTRACT_ADDRESS}</code>
              </div>
              <button 
                className="connect-btn"
                onClick={this.connectWallet}
                disabled={loading}
              >
                {loading ? 'Connecting...' : 'ü¶ä Connect MetaMask'}
              </button>
              <div className="features">
                <p>‚úÖ Immutable Records</p>
                <p>‚úÖ Global Verification</p>
                <p>‚úÖ Automated Reminders</p>
                <p>‚úÖ Privacy Protection</p>
              </div>
            </div>
          </div>
        </div>
      );
    }

    if (currentView === 'dashboard') {
      return (
        <div className="App">
          <div className="dashboard">
            <div className="dashboard-header">
              <h2>üè• Vaccination Dashboard</h2>
              <div className="account-info">
                <p>Connected: {account.slice(0, 6)}...{account.slice(-4)}</p>
                <p>Network: {networkId}</p>
                {lastTokenId && (
                  <div className="last-token-info" style={{
                    background: '#d4edda',
                    padding: '10px',
                    borderRadius: '5px',
                    margin: '10px 0'
                  }}>
                    <strong>Last Token ID: {lastTokenId}</strong>
                  </div>
                )}
                <button onClick={this.disconnectWallet} className="disconnect-btn">
                  Disconnect
                </button>
              </div>
            </div>

            <div className="dashboard-actions">
              <div className="action-card" onClick={() => this.changeView('register')}>
                <div className="card-icon">üë∂</div>
                <h3>Register Child</h3>
                <p>Create new vaccination record</p>
              </div>

              <div className="action-card" onClick={() => this.changeView('manual-check')}>
                <div className="card-icon">üîç</div>
                <h3>Check Token ID</h3>
                <p>Manually verify your tokens</p>
              </div>
            </div>
          </div>
        </div>
      );
    }

    if (currentView === 'register') {
      return (
        <SuperRegistrationForm 
          onBack={() => this.changeView('dashboard')} 
          account={account}
          contract={contract}
          onTokenIdFound={this.setLastTokenId}
        />
      );
    }

    if (currentView === 'manual-check') {
      return (
        <ManualTokenChecker 
          onBack={() => this.changeView('dashboard')} 
          account={account}
          contract={contract}
        />
      );
    }

    return null;
  }
}

// SUPER REGISTRATION FORM - GUARANTEED TO GET TOKEN ID
class SuperRegistrationForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      childName: '',
      dateOfBirth: '',
      parentName: '',
      contactInfo: '',
      loading: false,
      step: 'form' // 'form', 'processing', 'success'
    };
  }

  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value
    });
  }

  handleSubmit = async (e) => {
    e.preventDefault();

    if (!this.props.contract) {
      alert('Please connect your wallet first!');
      return;
    }

    this.setState({ loading: true, step: 'processing' });

    try {
      console.log('üöÄ STARTING REGISTRATION PROCESS');

      const birthTimestamp = Math.floor(new Date(this.state.dateOfBirth).getTime() / 1000);

      // STEP 1: Get user's token balance BEFORE registration
      let balanceBefore = 0;
      try {
        const balance = await this.props.contract.balanceOf(this.props.account);
        balanceBefore = balance.toNumber();
        console.log('üë§ User balance before:', balanceBefore);
      } catch (e) {
        console.log('Could not get balance before:', e.message);
      }

      // STEP 2: Get total supply BEFORE registration
      let totalSupplyBefore = 0;
      try {
        const supply = await this.props.contract.totalSupply();
        totalSupplyBefore = supply.toNumber();
        console.log('üìä Total supply before:', totalSupplyBefore);
      } catch (e) {
        console.log('Could not get total supply before:', e.message);
      }

      // STEP 3: Submit the registration transaction
      console.log('üìù Submitting transaction...');
      const tx = await this.props.contract.registerChild(
        this.state.childName,
        birthTimestamp,
        this.state.parentName,
        this.state.contactInfo,
        ""
      );

      console.log('‚úÖ Transaction submitted:', tx.hash);

      // STEP 4: Wait for confirmation
      console.log('‚è≥ Waiting for confirmation...');
      const receipt = await tx.wait();
      console.log('‚úÖ Transaction confirmed!');

      // STEP 5: Multiple methods to get Token ID
      let tokenId = null;

      // Method A: Check events in receipt
      tokenId = this.parseTokenIdFromReceipt(receipt);

      // Method B: Check user's balance after transaction
      if (!tokenId) {
        console.log('üîç Method B: Checking user balance...');
        await this.sleep(2000); // Wait 2 seconds for blockchain to update

        try {
          const balanceAfter = await this.props.contract.balanceOf(this.props.account);
          const balanceAfterNum = balanceAfter.toNumber();
          console.log('üë§ User balance after:', balanceAfterNum);

          if (balanceAfterNum > balanceBefore) {
            // Get the newest token owned by user
            const latestTokenId = await this.props.contract.tokenOfOwnerByIndex(
              this.props.account, 
              balanceAfterNum - 1
            );
            tokenId = latestTokenId.toString();
            console.log('‚úÖ Method B - Found token ID:', tokenId);
          }
        } catch (e) {
          console.log('Method B failed:', e.message);
        }
      }

      // Method C: Check total supply difference
      if (!tokenId) {
        console.log('üîç Method C: Checking total supply...');
        try {
