  parseTokenIdFromReceipt = (receipt) => {
    console.log('üîç Parsing receipt for Token ID...');
    console.log('Receipt logs:', receipt.logs);

    if (!receipt.logs || receipt.logs.length === 0) {
      return null;
    }

    for (let log of receipt.logs) {
      try {
        // Look for Transfer event signature
        if (log.topics && log.topics.length >= 4) {
          const transferSig = "0xddf252ad1be2c89b69c2b068fc378daa952ba7f163c4a11628f55a4df523b3ef";

          if (log.topics[0] === transferSig) {
            const from = log.topics[1];
            const tokenIdHex = log.topics[3];

            // Check if it's a mint (from zero address)
            if (from.endsWith('000000000000000000000000000000000000000000')) {
              const tokenId = ethers.BigNumber.from(tokenIdHex).toString();
              console.log('‚úÖ Found Token ID from Transfer event:', tokenId);
              return tokenId;
            }
          }
        }
      } catch (e) {
        console.log('Could not parse log:', e.message);
      }
    }

    return null;
