// Manual Token Checker Component
class ManualTokenChecker extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tokens: [],
      loading: false
    };
  }

  checkUserTokens = async () => {
    if (!this.props.contract) {
      alert('Contract not connected');
      return;
    }

    this.setState({ loading: true });

    try {
      console.log('üîç Checking user tokens...');
      const balance = await this.props.contract.balanceOf(this.props.account);
      const balanceNum = balance.toNumber();

      console.log('User balance:', balanceNum);

      const tokens = [];
      for (let i = 0; i < balanceNum; i++) {
        try {
          const tokenId = await this.props.contract.tokenOfOwnerByIndex(this.props.account, i);
          tokens.push(tokenId.toString());
        } catch (e) {
          console.log(`Could not get token at index ${i}:`, e.message);
        }
      }

      this.setState({ tokens, loading: false });

      if (tokens.length === 0) {
        alert('No tokens found for your account. Register a child first!');
      }

    } catch (error) {
      console.error('Error checking tokens:', error);
      alert('Error checking tokens: ' + error.message);
      this.setState({ loading: false });
    }
  }

  componentDidMount() {
    this.checkUserTokens();
  }

  render() {
    const { tokens, loading } = this.state;
    const { onBack, account } = this.props;

    return (
      <div className="App">
        <div className="form-container">
          <h2>üîç Your Token IDs</h2>
          <p>Account: {account}</p>

          <button 
            onClick={this.checkUserTokens} 
            disabled={loading}
            style={{
              background: '#007bff',
              color: 'white',
              border: 'none',
              padding: '10px 20px',
              borderRadius: '5px',
              marginBottom: '20px'
            }}
          >
            {loading ? '‚è≥ Checking...' : 'üîÑ Refresh Tokens'}
          </button>

          {tokens.length > 0 ? (
            <div>
              <h3>Your Token IDs:</h3>
              {tokens.map((tokenId, index) => (
                <div key={index} style={{
                  background: '#f8f9fa',
                  padding: '15px',
                  margin: '10px 0',
                  borderRadius: '10px',
                  border: '2px solid #007bff'
                }}>
                  <strong style={{fontSize: '18px', color: '#007bff'}}>
                    Token ID: {tokenId}
                  </strong>
                </div>
              ))}
            </div>
          ) : !loading && (
            <div style={{
              background: '#fff3cd',
              padding: '20px',
              borderRadius: '10px',
              textAlign: 'center'
            }}>
              <p>No tokens found for your account.</p>
              <p>Register a child first to get a Token ID!</p>
            </div>
          )}

          <button 
            onClick={onBack}
            style={{
              background: '#6c757d',
              color: 'white',
              border: 'none',
              padding: '10px 20px',
              borderRadius: '5px',
              marginTop: '20px'
            }}
          >
            ‚Üê Back to Dashboard
          </button>
        </div>
      </div>
    );
  }
}

